INSERT INTO `broadcast_text` VALUES (398, 'I\'ll be a guard someday!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (399, 'Remember when Danath gathered all the mercenaries of Stormwind together and we marched to fight at Nethergarde?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (400, 'I tell ye, I don\'t miss the Great War at all. I remember when we fought at Darrowmere. All night in the fog, lying in a muddy trench.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
