INSERT INTO `broadcast_text` VALUES (402, 'Less than a hundred of us, and over a thousand orcs. Only a handful of us managed to walk away from that one.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (403, 'The best had to have been the look on Perenolde\'s face when our army comes marchin\' right up to his front door. What a battle!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (405, 'Battling that band of Twilight Hammer in the Morass, I could think of better places for a war.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
