INSERT INTO `broadcast_text` VALUES (510, 'Help! I\'ve only one hand to defend myself with.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (511, 'Send them on! I\'m not afraid of some scrawny beasts!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (512, 'Get him!', '', 0, 0, 6, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (513, '%s chants words of power...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (514, 'No! It can\'t be...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (515, 'Incredible! I know he who wrought these enchantments...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (516, 'This is unforseen. The being who enchanted this is known to me...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (517, '$n, I have the information you seek...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (518, 'My minions...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (519, 'The time for conquest approaches quickly.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (520, 'Soon, your thirst for destruction will be quenched.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (521, 'The rage burning within you will soon find fuel in our enemies.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (522, 'I come with news of our conflict with the Rogue Undead.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (523, 'The rebels of Lordaeron are small and weak, and know not the joy of serving our Lich King.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (525, 'The Dark Lady\'s followers are blind to think they can stand against our might.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (526, 'We grow strong, while our foes are surrounded by foes, and weakened by constant attacks.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (527, 'Our Lord commands us to make ready for war!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (528, 'Prepare yourselves, for we will soon launch an assault against they who called themselves \"The Forsaken.\"', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (529, 'Make ready for battle, my creations.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (530, 'We are the arm of His will. And we will crush His foes into grave dust!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (531, 'These undead rabble, and their dead-elf queen, will fall!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (532, 'We will drive our foes from Silverpine, then bring the tides of war to their very capital!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (533, 'Death and Praise for the Lich King!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (534, '', 'It\'s good to see you again, Erland.  What is your report?', 0, 0, 0, 0, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (535, 'Masses of wolves are to the east, and whoever lived at Malden\'s Orchard is gone.', '', 0, 0, 0, 0, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (536, 'If I am excused, then I\'d like to check on Quinn...', '', 0, 0, 0, 0, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (537, 'Hello, Quinn. How are you faring?', '', 0, 0, 0, 0, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (538, 'Try to take better care of yourself, Quinn. You were lucky this time.', '', 0, 0, 0, 0, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (539, 'I\'ve been better. Ivar the Foul got the better of me...', '', 0, 0, 33, 0, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (540, 'No two-bit traitor will stop the powder from getting to Ironband!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (541, 'Beware! A $n is upon us!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (542, '', 'Be ready, $n. I hear the council returning. Prepare to ambush!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (543, 'A $n attacks!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (544, 'Beware! I am under attack!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (545, '', 'Well done. A blow to Arugal no doubt!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (546, 'The Stonewrought Dam has been saved! Three cheers for $n!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (548, '', 'All is lost!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (549, 'Make Way! Make Way!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (551, '%s leaps from the first catapult to the second!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (552, 'I\'m cursed! First my ship sinks, and now...I\'m out of mead!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (553, 'Curse you, Halyndor! You led us to our doom!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (554, 'Don\'t mind me, or my smell...because I sure don\'t!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (556, 'Ah, beautiful Menethil Harbor, with the best mead this side of Ironforge.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (557, 'Oh, my brothers! May you find rest in your watery graves!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (558, '%s looks like he\'s going to be sick!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (559, '%s swaggers heavily.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (560, '%s mumbles incoherent words.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (561, '%s raises his mug to his eye, and frowns.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (562, '%s growls and looks around him aggressively', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (563, '%s begins translating ...', '', 2, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (564, '$n, the translation is finished!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (566, 'This is a dangerous line of business!$B$BMiran just left with a delivery. He should be back in a few minutes.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (567, 'They thought I was weak. Haha!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (568, 'I am the head of the family. Me. ME!!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (569, 'I\'m cold, lord. When will you keep your promise?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (570, 'My family hates me. Frightened idiots, all of them!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (571, 'Father? Are you proud of me now? HAHAH!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (572, 'Mother, oh mother. You should have listened to me...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (573, 'Thurman my brother, who\'s laughing now? Hah!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (574, 'Here to visit the family? Die, fool!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (575, 'The Agamand Mills is held by the Scourge, $C. Join us!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (576, 'I raise my brew and hope to be rid of the likes of you! Cheers, you no good scoundrel, $n!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (577, '%s cheers!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (578, '', 'Stay back vile creatures!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (579, 'Croak!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (580, '%s sniffs the elixir then eagerly digs in!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (581, 'Finally they show mercy! They\'ve given us something to drink!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (582, 'And Shindigger Stout too! Thank the Light! I was parched!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (583, 'Your undeath is not the end of your life, but the beginning. It is up to you to decide where your fate lies now.', '', 0, 0, 33, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (584, 'You are free of His control. And while you will be judged by lesser beings, at least you have chances that the Scourge do not!', '', 0, 0, 33, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (585, 'Terror, darkness, power? The Forsaken crave not these things; the Forsaken ARE these things.', '', 0, 0, 33, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (586, 'My bones are weak, my eyes see only darkness, and my body feels only pain; but these things are good because my will is my own.', '', 0, 0, 33, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (587, '', 'Hello, Charys. I have my list, could you get me all of that, especially the last ingredient.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (588, '', 'Mommy? Can I pet Fizzles?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (589, '', 'Sure, Paige. Just be gentle.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (590, '', 'Thanks, Charys. C\'mon Paige, sweetie.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (591, '%s starts to chant in an ancient tongue, summoning forth something evil in nature.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (592, 'Ah, it is good to be free of my bonds! Have you freed Tog\'thar yet? The humans split us up after our capture.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (593, 'Have you freed Drull yet? I don\'t know where he\'s being held.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (594, 'Go on ahead without me! I will make my own way to Tarren Mill.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (595, 'I will only slow you down, but no pink-skinned humans will keep me from freedom!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (596, 'Search this rubble.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (597, 'Find me something good, you dogs!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (598, 'Over here! Inside the room. Did you find the key to my chains? The human Borhuin has them.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (599, 'I rise from the grave to take my revenge!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (600, 'Die in the name of the Dark Lady, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (601, 'The undead shall feast on your soul, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (602, 'I have risen!', '', 1, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (603, 'Revenge shall be mine at last!', '', 1, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (604, 'Southshore shall pay in blood!', '', 1, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (605, 'Mmm... I love my delicious Southshore stout.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (606, 'Southshore stout, how do I love thee? Let me count the ways...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (608, 'Protect Foreman Bonds at all costs!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (609, 'Guards! Guards!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (610, 'You\'re going to pay for this outrage! Guards!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (611, 'Get to your posts! Let no invader reach Southshore!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (612, 'Anyone suspicious is to be taken down quickly. Get to your posts as quickly as possible!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (613, 'Protect the Magistrate at all costs. They could come from any direction!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (614, 'My trees.... they have withered up and died just like me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (615, 'This orchard is now as barren and lifeless as my rotting shell. Once these trees bloomed with fruit but now there is only rot.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (616, 'I spent my whole life caring for these trees. Pruning and nurturing them... only fitting that they have joined me in death.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (617, '', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (618, 'Greetings Citizens! Soon perhaps the tide will turn in our favor and the plague will end.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (619, 'Help me! Help! Assassins in the hills! To arms! To arms!', '', 1, 0, 7, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (620, 'Hear ye! Hear ye! *pant pant* Be warned citizens of Southshore: assassins are heading to our fair town!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (621, 'Guards to your posts! They must be after the Magistrate again. He must be protected! They could come from any direction!', '', 0, 0, 7, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (622, 'I\'m so tired *pant pant*', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (623, 'The Southshore Crier grabs the back of his neck and his eyes go wide.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (626, 'Assassins... save... th... Magistraaa...', '', 0, 0, 7, 65, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (627, 'They\'re here! They\'re over here! Help me!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (628, 'The assassins are over here! Quickly, before they reach the Magistrate!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (629, 'More agents of the Syndicate I\'ll wager! You\'ll never take back Alterac... or Southshore!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (630, 'The deed is done! The Magistrate is dead-- to the shadows, my brethren.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (631, 'The Magistrate has fallen! Make your escape, my brothers.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (632, 'Haha! Alterac and Hillsbrad shall come under our rightful rule once again now that the Magistrate has fallen. Retreat!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (633, 'The meddlers shall pay for their interference. Retreat to the shadows!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (634, 'The Magistrate is too well protected. Retreat!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (635, 'You may have succeeded in stopping us this time, but we shall return-- this we assure you!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (636, '', '%s takes out a bottle filled with some kind of green liquid and takes a large gulp.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (637, '%s\'s eyes glow red as he lights his dynamite and begins to cackle madly!', '', 2, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (638, 'Wahehe! I\'m taking you down with me!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (639, 'All right, you lazy bums, time to get up and get started. That bridge isn\'t getting built on its own.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (640, 'Ya\'ll get a few minutes to wake up, but let\'s get started soon.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (641, 'Hi Ho, Hi Ho, and all that... let\'s get a move on.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (642, 'Take a break, men! Half hour and then back to work.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (643, 'The bridge is looking great. We keep up this pace and it\'ll be done for the first expansion forces to head north.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (645, 'Okay, lunch time\'s over, guys. Let\'s get back to work. If the rest of the day goes this well, then maybe we\'ll cut out early.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (646, 'Good, good. Keep it up and this bridge\'ll be done in no time.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (647, 'Call it a day, men!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (648, 'Day\'s wasting. Let\'s get going.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (649, 'Good night, gents. Sleep well.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (650, 'Morning, my friends.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (651, 'Ahh... food It\'s \'bout time.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (652, 'Tis a good day to live, neh? Hard work and good food-- there are worse things we could be doing on such a beautiful day.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (654, '*Dimitri wipes sweat from his forehead and lays down his tools*', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (655, 'Another good day indeed.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (656, 'Good night, my friends. Until tomorrow.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (657, 'Can\'t we just sleep a while longer?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (658, 'Okay, okay... I\'m up.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (659, '\'Bout time. I was hungry an hour ago.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (660, 'Finally! I can\'t wait to get some sleep tonight.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (661, 'Ugh! Being on your feet all day is no way to make a living. I should have been a tailor.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (662, 'I love mornings! It\'s a great day, isn\'t it? Smell that air?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (664, 'By the Light, I\'m hungry today. Did you all see Darcy today? She looked great.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (665, 'You know, this bridge is kind of large for just the 6 of us to be building. You\'d think more people would\'ve been hired.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (666, 'You guys see her? I didn\'t see Darcy. I wonder where she is tonight.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (667, '%s looks up and down the docks.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (669, 'Oh well, I\'m sure she\'ll be around later.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (670, 'Night, all. I\'m gonna sit up for a bit. Maybe Darcy\'ll come by.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (671, 'Huh?! Darn it, it\'s lunch time! I overslept!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (672, '%s runs up to the other bridge workers, panting.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (674, 'Sorry, ah\'m late.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (675, 'This bridge isn\'t ever going to be finished, is it?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (676, 'My back is killing me today.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (677, 'Little food\'ll feel pretty good right about now.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (678, 'Mmm, good stuff. Back to work once again.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (679, 'Another day gone by...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (680, 'Night, night.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (681, '%s is struck dumb by the Soul Gem!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (682, '%s\'s spirit is freed...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (683, '%s lets out a deep sigh.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (684, 'I hate this part of my job. Oh Duskwood, be gentle with poor Antonio.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (685, 'Items of antiquity, weapons of war, armor or rarity. Who knows what Antonio will be carrying this week if you do not come look.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (686, 'This week it could be eye of newt, or perhaps a treasure map. You\'ll never know unless you look. What could Antonio have?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (687, 'Adventurers from all over sell things to Antonio-- come look for yourselves what little treasures he has for you this week.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (688, 'Travelers and adventurers make haste to the town circle and take advantage of Antonio\'s wonderful prices! Come one, come all!', '', 0, 0, 7, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (689, 'Well met, travelers of Duskwood! My incredible wares are on sale for a short time in Darkshire. Make haste!', '', 0, 0, 7, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (690, 'Leave your battles or errands, and browse my incredible wares! Only in Darkshire for a short time... praise the Light for that.', '', 0, 0, 7, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (691, 'Farewell, citizens of Darkshire. Until next time...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (692, 'Finally, Redridge. A pleasant change from the dark, cold fog of Duskwood.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (693, 'Ha ha! I have Betsy!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (694, '', 'Gimme my dolly!!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (695, '', 'Betsy! Give me Betsy back!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (696, '', 'Don\'t hurt Betsy, you meanie!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (697, '', 'WAAAHHH!!!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (698, '', 'I\'m telling Mommy!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (699, '', 'Stop pulling her hair out!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (700, '', 'You\'re hurting her!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (701, '', 'Stop! You\'ll pull her head off!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (702, 'Hello, hello, Amy. It\'s good to be back in Lakeshire and to see you, milady.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (703, '', 'Hello, Antonio. It\'s good to see you safely made it here again. Let me know if you need anything.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (704, 'Hear ye, hear ye! I\'ll pause to sell my wares for only a short time here in Lakeshire. Come to the town hall with haste!', '', 0, 0, 7, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (705, 'I wonder if your dolly can swim!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (706, 'I have your dolly! Nyah nyah!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (707, 'What happens when I do this?!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (708, 'If you want her back you\'re gonna have to beg!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (709, 'Crybaby! Crybaby!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (710, 'Baby wants her dolly!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (711, 'Oops!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (712, 'I\'ve finally reached Lakeshire-- come one, come all, and take advantage of Antonio Perelli\'s wonderful deals.', '', 0, 0, 7, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (713, 'Items powerful enough to help make the journey through Duskwood, Antonio Perelli\'s goods are now for sale here in Lakeshire.', '', 0, 0, 7, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (714, 'Good to see you again, Amy. Be safe, luv. I\'m off to Goldshire now.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (715, '', 'Take care of yourself, Antonio. I\'ll see you in another week.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (716, 'Hello, my friends. It is good to see you again. Still keeping our borders free I see. Antonio appreciates your hard work.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (717, 'You\'re very welcome! I must be on my way... be safe, all of you.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (718, 'Hello again, Antonio. Thank you again for the hat for my wife. She adored it.', 'Hello again, Antonio. Thank you again for the hat for my wife. She adored it.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (719, 'Be safe, Antonio.', 'Be safe, Antonio.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (720, 'Oh, I will, my friend. I will indeed. You, too, be safe.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (721, 'Wares for sale! Wares for sale! Come browse Antonio\'s wares before I must take my leave, citizens of Goldshire.', '', 0, 0, 7, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (722, 'Come one, come all! Antonio\'s wares are for sale. I may not return for a great deal of time, so come one, come all.', '', 0, 0, 7, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (723, 'Good people of Goldshire, come take advantage of my incredible prices on rare goods. I shall not return for some time!', '', 0, 0, 7, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (724, 'Good bye, Goldshire. I\'ll be back soon enough-- perhaps a week\'s time.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (725, 'Home again, home again.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (726, 'Known throughout the lands, Antonio Perelli\'s goods now for sale near Sentinel Hill! I must move on soon, so make haste.', '', 0, 0, 7, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (727, 'Long-lasting, well-crafted items for sale near the Hill! Come one, come all! Time is short, so make haste.', '', 0, 0, 7, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (728, 'The time has come for me to once again make my wares available to all who travel near Sentinel Hill! Come one, come all!', '', 0, 0, 7, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (729, 'Thank you, all. I shall return again in about a week.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (730, 'Off to Darkshire then, I suppose.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (731, 'Mmmh...I SMELL FOOD!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (732, '%s searches through the skulls...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (733, '%s continues searching, placing more skulls in his crate.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (734, 'I am done inspecting the skulls, $n, and I am relieved. Yenniku is not among them...', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (735, 'Ah, good $n. Now let us see what tale these heads tell...', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (736, '%s begins chanting...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (737, 'Where in the Outlands are those shipments? For days they keep me waiting!', '', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (738, 'Stupidity! I\'m surrounded by stupidity!', '', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (739, 'Orcs are smarter than those couriers! Where the blazes are they?', '', 0, 0, 7, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (740, 'There, $N. You may speak now speak to the Bloodscalp chief and his witchdoctor.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (741, '%s begins to summon in reinforcements!', '%s begins to summon in reinforcements!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (742, '%s presents the Mind\'s Eye and the Singing Crystals before him...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (743, 'I have fashioned Yenniku\'s Soul Gem, $n...', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (744, '%s tries opening Maury\'s Clubbed Foot...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (745, 'Bah! $N, this foot won\'t budge!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (746, 'Alright I\'m going! Stop yelling!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (747, 'This better be the last one!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (749, 'You better back me up on this one!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (750, 'Why did I have to get stuck with this repulsive, feeble excuse for a warlock?', '', 0, 0, 8, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (751, 'Make yourself useful and help me out here!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (752, 'Just release me already! I\'ve had enough!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (753, 'What? You mean you can\'t kill this one by yourself?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (754, 'If you touch me again it\'ll be the last thing you touch!', '', 0, 0, 8, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (757, '%s begins to crack and break apart.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (758, '%s\'s strength dwindles as chunks of rock break off', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (759, '%s is reduced to rubble, but still continues to fight', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (761, 'How interesting... the Stone possesses great magical potential, but in a purely raw form...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (763, 'AH, A FEAST! WHO LEFT THIS HERE...?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (764, 'SMOTTS! HEY, SMOTTS!! COME OUT AND PLAY!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (765, '', 'Thank you again for your aid. May the wind always be at your back during your travels.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (766, 'You have completed the quest!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (767, 'Here come the beasts!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (768, 'You have failed to protect me from the apes!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (769, 'What is happening? What force draws me to the Altar of the Tides!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (770, 'More beasts on the way!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (771, 'Dirty beasts! Here they come!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (772, 'YOU WERE SUCH GOOD SPORT THE LAST TIME...COME OUT!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (773, 'SMOTTS, I HAVE YOUR LITTLE BELT...COME GET IT.', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (774, 'I PROMISE I WON\'T KILL YOUR CREW THIS TIME. HAH! BUT MY BROTHER MIGHT EAT THEM!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (775, 'COME OUT TO JANEIRO\'S POINT. I\'M WAITING FOR YOU, SMOTTS...', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (776, 'I HAVE YOUR LITTLE BELT TIED AROUND MY FINGER, SMOTTS. DON\'T YOU WANT IT BACK?', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (777, 'HAVE YOU RUN OUT OF SHIPS TO SEND? WHERE\'S YOUR COURAGE?', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (778, 'DON\'T LET THIS STATUE BE MY ONLY COMPANY. COME OUT AND PLAY!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (779, '%s tosses down his bottle of poison in desperation!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (780, '%s takes out a remote control and tosses down a model siege engine!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (781, '%s begins to glow and smoke!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (782, 'Still no sign of the final shipment of explosives.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (783, 'No sign of the final explosives shipment to the west either. Where are those lollygaggers?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (784, 'This bridge should have been destroyed by now. How long does it take for those lazy sods to get here from the Highlands!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (785, 'Help me! Help me! Don\'t let them eat me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (786, 'Please save me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (787, 'I don\'t want to be a meal for an ogre! Please save me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (788, 'Heeeeeeeeeeeeellllllllllllllllllllllllp!!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (789, 'I tire of this nonsense! Prepare to die, $r!', 'I tire of this nonsense! Prepare to die, $r!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (790, 'Sure does seem like it\'s been a long time since anyone has relieved me from my post.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (791, 'Well, that\'s what I get for oversleeping!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (792, '', '%s reads Sully\'s letter page by page.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (793, '', 'This isn\'t fair!  My dearest Sully!  Come back to me!  No, $n!  Why did you bring me such news!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (794, 'Hiccup!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (795, 'These shards give me the creeps. Hey Greeby, did you find more motes?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (796, 'I wouldn\'t tell you if I did! Because as soon as I do, I\'m getting away from these crystals...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (797, 'Yeah, I don\'t want to be here if Myzrael wakes up. They say she has a mean temper...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (798, 'Easier than I expected... the sigil of Thoradin is whole, once again.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (799, 'May I bask in your shadow forever!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (800, 'Master, your wisdom is matched only by your hugeness!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (801, 'If I had a copper for each of your great deeds, I\'d be a rich kobold!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (802, 'You will keep us safe from the evil mistress, yes!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (803, 'Oh, praise! Your vigilance keeps Myzrael deep in the earth!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (804, 'My only wish is to serve you, great one, until I die by your foot!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (805, 'Your strength is of the mountain. Your grace is of the winds!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (806, 'My feet hurt...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (807, 'Die! You will not free Myzrael!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (808, 'The Great One will smash you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (809, 'Aren\'t you a little short for your kind?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (810, 'My life means nothing! Our sacred task is all!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (811, 'If you mean to free Myzrael, you will be sorry!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (812, 'Stand firm, brothers. And don\'t worry! Size is on our side!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (814, 'Take that! The Drywhiskers will prevail!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (815, 'No! Leave us! We must not fail our task!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (816, '', 'Well then, let\'s get this started. The longer we\'re here, the more damage the undead could be doing back in Hillsbrad.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (817, '', 'All right, this is where we really have to be on our paws. Be ready!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (818, '', 'Watch my rear! I\'ll see what I can find in all this junk...', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (819, '', '%s begins rummaging through the apothecary\'s belongings.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (820, '', 'Found something... I think.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (821, '', 'I bet Quae\'ll think this is important. She\'s pretty knowledgeable about these things--no expert, but knowledgeable.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (822, '', 'Okay, let\'s get out of here quick quick! Try and keep up. I\'m going to make a break for it.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (823, 'You made it?! I\'m surprised Kinelory didn\'t slow you down anymore than that. Kin, here\'s your socks. Give me what you found ins', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (824, '%s studies the circle of stones, looking for signs of stress.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (825, 'You will never defeat us!', 'You will never defeat us!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (826, 'My death matters little... the Hammer will still fall!', 'My death matters little... the Hammer will still fall!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (827, 'My life for my masters. Rejoice!', 'My life for my masters. Rejoice!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (828, 'Curse you!', 'Curse you!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (829, 'Glory and blood for the Old Gods!', 'Glory and blood for the Old Gods!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (830, 'My lords! I come to thee!', 'My lords! I come to thee!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (831, 'Your victory here only speeds your doom!', 'Your victory here only speeds your doom!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (832, 'Mommy!', 'Mommy!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (833, 'Intruders! Slay them!', 'Intruders! Slay them!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (834, 'For our Masters!', 'For our Masters!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (835, 'Masters, I pledge this $r to you!', 'Masters, I pledge this $r to you!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (836, 'Carnage! May I spill blood in His name!', 'Carnage! May I spill blood in His name!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (837, 'Embrace the end!', 'Embrace the end!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (838, 'Battle! I can smell your fear, $c!', 'Battle! I can smell your fear, $c!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (839, 'Death comes! Rejoice!', 'Death comes! Rejoice!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (840, 'Intruders! Good, I was getting bored...', 'Intruders! Good, I was getting bored...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (841, 'Stop! Foolish $c, we cannot let you summon the creature Myzrael!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (842, '', 'What?  $n, you served me well, but...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (843, '', 'Why have you summoned me so soon?  I haven\'t yet reached my full power!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (844, '', 'No matter.  You were a fool to help me, and now you will pay!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (845, 'Ok, $n. Follow me to the cave where I\'ll attempt to harness the power of the rune stone into these goggles.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (846, 'I discovered this cave on our first day here. I believe the energy in the stone can be used to our advantage.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (847, 'I\'ll begin drawing energy from the stone. Your job, $n, is to defend me. This place is cursed...trust me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (848, '%s begins tinkering with the goggles before the stone.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (849, 'Almost done! Just a little longer!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
