INSERT INTO `broadcast_text` VALUES (1016, '%s becomes dazed', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1017, 'I am slain! Summon Verog!', 'I am slain! Summon Verog!', 1, 0, 0, 0, 0, 0, 0, 0, 0);
