INSERT INTO `broadcast_text` VALUES (1019, 'Deep doom', '', 0, 0, 3, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1020, '%s watches as his blood begins to stir, and the guile of the raptor slips through him.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
