INSERT INTO `broadcast_text` VALUES (1024, 'Take that, beast!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1025, '%s tosses dynamite through the air and it lands near by.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1026, 'The power of the Tear of the Moons is mine! Mine I say!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1027, '%s begins to rub the Tear of the Moons.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1028, 'Power! Glorious power!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1029, '%s begins to make strange grunting noises. The Tear of the Moons drops to the ground and shatters.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1030, 'Whoa!...Whoa!...Ahh! That was close. Whoops!...Oh the pain, dear boy the pain!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1031, 'Alright, alright! I think I can figure out how to operate this thing...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1032, 'Okay, I think I\'ve got it, now. Follow me, $n!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1033, 'Looks like we\'re out of the woods, eh? Wonder what this does...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1034, 'I\'ve got to get this to Ratchet and nothing\'s going to stop me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1035, 'Help! Wait! It\'s like this thing has a mind of its own!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1036, 'See how you like a taste of this!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1037, 'I\'m going to die, I just know it!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1038, 'Take that! Oh... I must have hit the wrong button... Wait, try this one!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1039, 'Arrrgh! This isn\'t right!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1040, 'There\'s the stolen shredder! Stop it or Lugwizzle will have our hides!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1041, 'That was a close one, $n! Well, let\'s get going, it\'s still a ways to Ratchet!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1043, 'Come on, don\'t break down on me now!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1044, 'Hm... I don\'t think this blinking red light is a good thing...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1045, 'Looks like you\'ll have to go ahead to Ratchet and tell Sputtervalve that I\'ve wrecked the shredder.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1046, 'I\'ll stay behind and guard the wreck. Hurry! Hopefully no one will notice the smoke...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1047, '%s shimmers, flashes...and is no more!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1048, '%s calls more of his allies out of the shadows.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
