INSERT INTO `broadcast_text` VALUES (1064, '%s calls for help!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1065, 'Stay close, $n. I\'ll need all the help I can get to break out of here. Let\'s go!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1066, 'At last! Free from Northwatch Hold! I need a moment to catch my breath....', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1067, 'Now I feel better. Let\'s get back to Ratchet. Come on, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1068, 'Looks like the Southsea Freebooters are heavily entrenched on the coast. This could get rough.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1069, 'Almost back to Ratchet! Let\'s keep up the pace...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1070, 'Ah, the sweet salt air of Ratchet.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1071, 'Captain Brightsun, $n here has freed me! $n, I am certain the Captain will reward your bravery.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1072, '$n coming right at us!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1073, 'Get this $n off of me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1074, 'Help! $n attacking!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1075, '$n heading this way fast! Time for revenge!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1076, '%s places the spores within his alchemical instruments...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1077, 'Ah, good. These spores have rendered nicely. Thank you, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1078, 'Thank you for the spores, $n. They are now prepared for transport.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1079, 'I am summoned! Intruders, come to my tent and face your death!', '', 1, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1080, '%s digs its way out of the sand to protect its eggs.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1081, 'Thanks, $n. I\'ll be selling those drinks in no time!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1082, 'Many thanks, $n. These horns are going to make me a fortune!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1083, 'I\'m going to make a fortune with these horns, $n. Thanks!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1084, 'Thanks! And when I make my drinks...don\'t tell anyone what they\'re made of!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1085, '%s inspects the snapjaw shells...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1086, 'C\'mon, let\'s try somewhere else.', 'C\'mon, let\'s try somewhere else.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1087, 'Hm...', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1088, 'Strange. Very strange...', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1089, '$N. These shells tell me much, but I fear many more questions are now raised...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1090, 'Hey guys, let\'s try on the other side.', 'Hey guys, let\'s try on the other side.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1091, 'Billy says Fizzles used to be a great wizard. But he got turned into a rabbit when one of his spells went bad.', 'Billy says Fizzles used to be a great wizard. But he got turned into a rabbit when one of his spells went bad.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1092, 'Are we there yet?', 'Are we there yet?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1093, 'Why are we goin\' this way?', 'Why are we goin\' this way?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1094, 'Where we goin\'?', 'Where we goin\'?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1095, 'I need to pee.', 'I need to pee.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1097, 'I wanna see the Mage Tower.', 'I wanna see the Mage Tower.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1098, 'Is it true? Are there really crocilisks in the canals?', 'Is it true? Are there really crocilisks in the canals?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1099, 'My feet hurt.', 'My feet hurt.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1100, 'Why do we always go the same way?', 'Why do we always go the same way?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1101, 'Fresh bread for sale!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1102, 'Warm, wholesome bread!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1103, 'Rolls, buns and bread. Baked fresh!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1104, 'Freshly baked bread for sale!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1105, '', 'Where\'s the water, Emma? Get the water, Emma? If\'n it weren\'t fer me, that lot wouldn\'t know what water looks like.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1106, '', 'As if I don\'t have better things to do in my old age than carry buckets of water.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1107, '', 'Seems like a hundred times a day I walk all the way to the well to get more water. No respect for their elders I tell ya.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1108, '', 'O\'course I\'m talkin to myself. Only way to get decent conversation in this city.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1109, '', 'One of these days I\'m gonna drown him in that blue robe. And all his brooms too.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1110, '', 'Jack and Jill my wrinkled patoot! I do all the water luggin\' \'round here.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1111, '', 'Think I\'m starting to wear a rut in the paving stones.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1112, '', 'Deja vu.  For a moment, I thought I was back home... before the plague...', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1113, 'Oh now you\'ve gone and angered me! Time to see the true force of the Stone of Tides!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1114, '%s exchanges quiet words with $n, and hands $ghim:her; a small object.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1115, 'Take a stone from the table, $n. You will need it for your quest.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1116, '', 'By the strength of the earth, the power of storms, and...', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1117, '', 'ARRRRRGGGHHH!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1118, '', 'What has happened, Earthmother? Why has the spell failed?', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1119, 'Ok I am here. Come buy my wares while the getting is good. I mean I am giving you first crack at these.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1120, '', 'Sergra falls into a trance for a moment, then looks to the sky.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1121, '', 'I understand, Earthmother.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1122, 'Very well. Since you didn\'t buy me out I am forced to make my livelihood in your enemies\' town.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1123, 'Now you\'ve gone and done it. I thought you were better than the people at Southshore. They would have bought all my wares!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1124, '', 'Earthmother, grant me the power to undo this bloodshed!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1125, '', 'Balance has been restored!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1126, '%s examines the Moss-twined Heart...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1127, '$n, underneath the moss, this heart looks like a seed!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1128, 'Let\'s try planting it in some of my special, prepared soil...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1129, '', 'Here we have the Cathedral of Light, the center of spiritual enlightenment here in Stormwind.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1130, 'Why do we have to learn this stuff anyway?', 'Why do we have to learn this stuff anyway?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1131, 'It\'s better than the drawings in the history tomes.', 'It\'s better than the drawings in the history tomes.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1132, 'I wanna see the dragon skeleton in the great library.', 'I wanna see the dragon skeleton in the great library.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1133, 'Teacher, when are we gonna see the sparkly Mage Tower?', 'Teacher, when are we gonna see the sparkly Mage Tower?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1134, 'I heard there are still orcs buried underneath it.', 'I heard there are still orcs buried underneath it.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1136, 'Teacher, I have to pee!', 'Teacher, I have to pee!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1138, 'Teacher are there night elves in Stormwind? I\'ve never seen a night elf before.', 'Teacher are there night elves in Stormwind? I\'ve never seen a night elf before.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1139, 'Teacher, he keeps poking me!', 'Teacher, he keeps poking me!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1140, '', 'Here we have Stormwind Keep. Built upon the ruins of Stormwind Castle, which was destroyed by the Horde in the first Great War.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1141, '', 'TEST', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1142, 'You rabble get to work. The ship must be completed soon.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1143, 'Intruders! Slay them! Don\'t let them into the room!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1144, 'Next time I see one of you not working, I\'ll have Buzzer cut your head off!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1145, 'The cannons must be finished soon.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1146, 'Get those parts moving down to the ship!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1147, 'Anyone want to take a break? Well too bad! Get to work you oafs!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1148, 'You there! Check out that noise.', '', 0, 5775, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1149, 'We\'re under attack! A vast, ye swabs! Repel the invaders! ', '', 1, 5777, 7, 0, 0, 0, 0, 0, 0);
