INSERT INTO `broadcast_text` VALUES (1152, '%s plants the Moss-twined Heart...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1153, 'Is it true that the paladins train here?', 'Is it true that the paladins train here?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1154, '', 'Yes, that is true. Paladins and priests alike train their skills and research great truths behind the walls of the Cathedral.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1155, '', 'Children if you would please follow me, we will now be going to see the keep where King Anduin Wrynn himself sits on his throne.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1156, '%s removes the seeds from the Glowing Fruit...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1157, 'Let\'s see how these seeds grow in my prepared soil...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1158, '%s plants the fruit seeds.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1159, '%s hurries to his planter.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1160, 'I hope this frond takes root...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1161, '', 'When the Horde was shattered, men returned here and began to rebuild the once great city as a testament to our own survival.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1162, '', 'Yes, well...let\'s head on to the monument dedicated to the heroes of the two Great Wars, the Valley of Heroes. Follow me.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1163, '', 'Isn\'t it amazing, children? All who enter the city must walk beneath the watchful eyes of the greatest heroes of our lands.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1165, '', 'Breathtaking. Children, when we return to the school, you will each give an oral report on one of these legendary people.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1166, '', 'Now, take another long look before we make our way to the Holy District and the great Cathedral of Light.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1167, '', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1168, '', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
