INSERT INTO `broadcast_text` VALUES (1175, '%s rises from the soil!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1176, 'KILL!!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
