INSERT INTO `broadcast_text` VALUES (1178, 'I will protect you!', '', 0, 0, 2, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1179, '%s begins to manipulate the Stone of Relu over the two fossils.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1180, 'By the stars! A spirit has been summoned!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1181, 'It\'s a mystery of the past indeed! But a key to our future!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1182, 'Who hath summoned forth Aman?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1183, 'Ah, I see you toil with relics of the past.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1184, 'Be warned that even your creators are fallible.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1185, 'Digging too deep into your past might bring an abrupt end to your future.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1186, '', '%s hides among the bushes.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1187, '%s dissipates before your eyes.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1188, '', 'I can make it the rest of the way, $n.  THANKS!', 0, 0, 2, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1189, '', 'Ok, let\'s go!', 0, 0, 2, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1190, '', '%s disappears into the woods.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1191, '%s goes into a frenzy!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1192, 'Ask me how to get a free drink at the Blue Recluse!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1193, 'Free drinks at the Blue Recluse!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1194, 'Head on over to the Blue Recluse. Where everybody knows your name!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1195, 'Best drinks in Stormwind!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1196, 'Come for the beer, stay for the atmosphere!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1197, 'Magical studies stressing your brain? Relax at the Blue Recluse!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1198, 'The only place in Stormwind where magic and spirits mix, come to the Blue Recluse!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1199, 'Feeling blue? Come on down to the Blue Recluse for a good time!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1200, '', 'Feeling hot? Get something to cool you off down at the Pig and Whistle!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1201, '', 'Nightly brawls and great drinks! Get it all at the Pig and Whistle!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1202, '', 'Get a free beer at the Pig and Whistle!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1203, '', 'Talk to me for a free beer at the Pig and Whistle!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1205, '', 'Smoother than and elf, stronger than an orc! No better drinks in all of Stormwind!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1206, '', 'Tired of all those pansy spellcaster drinks? Try something stronger at the Pig and Whistle!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1207, '', 'Back from a long journey? Rest and relax at the Pig and Whistle!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1208, '', 'Best drinks in Stormwind!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1209, '%s is hungry...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1210, 'Cliffspring Falls lies along the mountains to the east and north.', '', 0, 0, 2, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1211, 'Thank you, $n. And luck to you.', '', 0, 0, 2, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1212, 'Best weapons in the Barrens here!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1213, 'Step right up! Finest selection of weapons this side of Ashenvale!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1214, 'Lizzarik has blades. Lizzarik has maces. Lizzarik has all your weapon needs right here!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1215, 'Act quick! Once it\'s gone, it\'s gone! Get your weapons here now!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1216, '%s studies the parchment from the Twilight Tome...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1217, '%s is struck by unleashed magic!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1218, 'Hmm...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1219, '%s continues his meditations...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1221, '%s leans down to examine his shot.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1222, 'Uthrok make gud shot', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1223, 'Athrikus points at you as he bellows forth words that scrape apart the inside of your ears. They are the last thing you hear.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1224, '', 'Finally, my soul may rest... Oh, dearest Cerellean...', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1225, '%s faces southeast and whimpers before looking back at you.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1226, '%s roars at Terenthis to get his attention.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1227, 'Grimclaw?! Easy there, my friend... where is your master Volcor?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1228, '%s begins to moan and roar at Terenthis while stomping his paws on the ground.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1229, 'Whoa, whoa there, my friend. One moment...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1230, 'There... that should help. Now, tell me what\'s happened, Grimclaw.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1231, '%s roars at Terenthis more, but this time the druid seems to understand the bear.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1232, 'I understand, my friend. I shall find someone to help your master. Go back to him now, or at least stay close.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1233, '%s roars in acknowledgement at Terenthis.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1234, '%s growls in your direction before taking time to sniff you.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1235, 'I figured Flagongut would send someone for that fossil eventually.', '', 0, 0, 7, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1236, 'Thank you again, $n. I\'ll make my way to the road now. When you can, find Terenthis and let him know we escaped.', '', 0, 0, 7, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1237, 'Well, now or never I suppose. Remember, once we get to the road safely, return to Terenthis to let him know we escaped.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1238, '%s roars as his form shifts and he runs off.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1239, 'Now let us place this rare earth in my planter...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1240, 'Thank you again, $n. This rare earth will be very helpful in my experiments.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1241, '%s roars in excitement as he rushes Volcor. When he reaches his master, Grimclaw licks his face.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1242, 'You are dismissed, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1243, 'We made it, my friend. Remember to find Terenthis and let him know we\'re safe. Thank you again.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1244, 'I\'m happy to see you too, Grim! It looks like not all strangers are to be feared after all, huh, Grim? Terenthis shall reward this adventurer well.', '', 0, 0, 7, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1245, 'What was that noise? It sounded like a roar.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1246, '%s begins to cast a spell on Grimclaw.', '', 2, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1247, 'I shall get started right away, $n.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1248, 'Now... where was my thread and needle. Ah! There it is...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1249, '%s begins to cast a spell as you take the cloak from him.', '', 2, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1250, 'Here they come!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1251, 'We can overcome these foul creatures!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1252, 'We shall earn our deaths at the very least!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1253, 'Don\'t give up! Fight to the death!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1254, '%s charges!', '%s charges!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1255, 'Exploring is like tasting a new ale or a fresh brew! Of course you get drunk eventually.\n\nExploring is way more healthy though. Unless you get some troggs intead of a good grog.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1256, 'These caverns were once a temple of promise for regrowth in the Barrens. Now, they are the halls of nightmares.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1257, 'Come. We must continue. There is much to be done before we can pull Naralex from his nightmare.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1258, 'Within this circle of fire I must cast the spell to banish the spirits of the slain Fanglords.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1259, 'The caverns have been purified. To Naralex\'s chamber we go!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1260, 'Down this way. Use caution, friends. I sense Naralex stirs in his troubled sleep....', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1261, 'From this spring I shall gather the sacred waters with which I will combine the blood of the fallen druids.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1262, 'Now I have what is required. Let us continue.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1263, 'Beyond this corridor, Naralex lies in fitful sleep. Let us go awaken him before it is too late.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1264, 'Protect me brave souls as I delve into the Emerald Dream to rescue Naralex and put an end to this corruption!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1265, '%s begins to perform the awakening ritual on Naralex.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1266, '[PH] This is a placeholder action until i can cancel channeling my spell.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1267, 'At last! Naralex awakes from the nightmare.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1268, '%s tosses fitfully in troubled sleep.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1269, '%s writhes in agony. The Disciple seems to be breaking through.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1270, '%s dreams up a horrendous vision. Something stirs beneath the murky waters.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1271, 'I AM AWAKE, AT LAST!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1272, 'Ah, to be pulled from the dreaded nightmare! I thank you, my loyal Disciple, along with your brave companions.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1273, 'Attacked! Help get this $n off of me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1274, 'Deal with this $n! I need to prepare to awake Naralex!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1275, 'I can\'t handle this $n alone! Help!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1276, 'This $n is a minion from Naralex\'s nightmare no doubt!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1277, 'Help!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1278, 'Anaya...? Do my eyes deceive me? Is it really you?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1279, '', 'The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1280, 'That the fates should be so cruel as to permit us only this after a thousand years apart...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1281, 'Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1282, '', 'Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1283, '', 'Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away...', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1284, '', 'Farewell, Cerellean, until we are joined once again...', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1285, 'Know that I love you always...', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1286, 'No! Anaya... Anaya! Don\'t leave me! Please...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1287, 'How, my love? How will I find the strength to face the ages of the world without you by my side...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1288, '%s waves to $n.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1289, 'Go now, $n. Find Volcor... and remember, the magics protecting you will fail if you interact with anyone, save for talking.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1290, '%s scrutinizes the camp\'s visitor.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1291, 'Ah. Welcome $gsir:my lady;!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1292, 'We must move quickly. Auberdine isn\'t too far away, but many enemies lurk in the forest.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1293, 'Prepare to die, paladin scum!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1294, 'Assassins from that cult you found... Let\'s get moving before someone else finds us out here.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1295, 'The spirit of Echeyakee flows through you, $n.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1296, '', '%s stretches and lets loose with a large yawn.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1297, 'The rage of Ishamuhale runs through you, $n.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1298, 'Lakota\'mani thunders within you, $n.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1299, 'The speed of Owatanka is with you, $n.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1300, 'Your path is steady, $n, for Washte Pawne\'s resolve is with you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1301, 'May the spirit of Isha Awak give you strength, $n.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1302, '', 'Terenthis, Raene sent me to find you. The Sentinels are in need of help in Ashenvale.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1303, '', 'Thank you, Terenthis. I shall remain here for as long as I can.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1304, '', 'Hopefully I can send a worthy few to help Ashenvale and the Sentinels.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1305, 'Hello, Selarin. I\'m afraid I cannot help you at the moment, but perhaps some of the adventurers here in Auberdine can...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1306, '', 'Thank you again, Terenthis. I shall return again if more help is needed. Until then, please send more adventurers to Ashenvale', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1307, '%s mixes the Bathran\'s Hair...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1308, 'The mixture is finished, $n. Now we must save the child.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1309, 'A paladin! Slaying him would please the master. Attack!', 'A paladin! Slaying him would please the master. Attack!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1310, 'They\'re coming out of the woodwork today. Let\'s keep moving or we may find more things that want me dead.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1312, 'You cannot hide from us, little paladin!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1313, 'Not quite so sure of yourself without the Purifier, hm?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1314, 'Tonight, your blood will run red on the stones of our altars.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1315, 'Well done! I should be fine on my own from here. Remember to talk to Delgren when you return to Maestra\'s Post in Ashenvale.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1316, 'Uh... the disease is potent. And it attacks the mind...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1318, 'I must... RESIST!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1319, 'I have survived the poisons of the Forsaken, $n, and I know now what must be done.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1320, 'Free from this wretched cell at last! Let me show you to the courtyard....', '', 0, 0, 1, 4, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1321, 'You are indeed courageous for wanting to brave the horrors that lie beyond this door.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1322, '%s fumbles with the rusty lock on the courtyard door.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1323, 'There we go!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1324, 'Good luck with Arugal. I must hurry back to Hadrec now.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1325, 'This statuette must hold a hidden compartment...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1326, 'It\'s locked! NO!!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1327, '%s jumps out of the shadows!', '%s jumps out of the shadows!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1328, 'About time someone killed the wretch.', '', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1329, 'For once I agree with you... scum.', '', 0, 0, 7, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1330, '', 'Mist! I feared I would never see you again! Yes, I am well, do not worry for me. You must rest and recover your health.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1331, 'Follow me and I\'ll open the courtyard door for you.', '', 0, 0, 7, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1332, 'I have just the spell to get this door open. Too bad the cell doors weren\'t locked so haphazardly.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1334, 'There it is! Wide open. Good luck to you conquering what lies beyond. I must report back to the Kirin Tor at once!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1335, 'Here you are, Relara. Take this...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1336, '', '%s drinks the cure brought by $n.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1337, '', 'Uh... Father...?  Are you there?  I\'m hungry... can I have some stew?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1338, 'What?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1339, 'You\'re awake?? Your cure must have worked! Thank you, $n.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1340, '%s growls in acknowledgement before straightening and making her way off into the forest.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1341, 'Go swiftly, $n. My fate is in your hands.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1342, '%s protects himself with magic.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1343, '%s vanishes.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1344, 'You land lubbers are tougher than I thought! I\'ll have to improvise! ', '', 0, 5778, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1345, 'D\'ah! Now you\'re making me angry!', '', 0, 5779, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1346, '%s crushes the moonstone into a cup of tea.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1347, 'Here you are, Relara. Drink this...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1348, '%s drops its weapon and lights a stick of dynamite!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1349, '', 'Mmh... my eyes are not so heavy now, father.  Can we go out and play?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1350, 'Relara... you\'re awake?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1351, 'Rest now, my daughter.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1352, '%s looks at $n, and then weeps with joy.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1353, 'Relara is saved, $n. Thank you.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1354, '%s searches the ancient statuette.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1355, '$n, I am in need of your help once again.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1356, '', 'The numbers of my companions dwindles, goddess, and my own power shall soon be insufficient to hold back the demons of Felwood.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1357, '', 'Goddess, grant me the power to overcome my enemies! Hear me, please, my need is desperate and my cause is just!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1358, '', 'What... what is this? Could this be the answer to my prayers? Elune has granted me a weapon--this scythe--to defeat the demons.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1359, '', 'I have failed... I have failed in my duty... failed my people... The worgen run rampant, and the Scythe of my goddess is lost.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1360, '', 'This evil I have unleashed... In whose hand does the power my goddess granted to me now lie... By whose hand is it directed?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1361, '%s beings to summon forth guardians!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1364, 'To reach The Crossroads, $n, follow the road east then north.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1365, '', 'Sidney is late. I thought he would be home by now.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1366, 'He\'s your husband, woman. How am I to know where he is?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1367, '', 'He must be overwhelmed with work at the Monastery. But still, he should have returned by now.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1368, 'Cleansing the world of impure scum isn\'t an easy job, Nancy. Someone\'s got to do it.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1369, '', 'Well, I\'m going outside to check. Something is amiss.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1370, '', '%s lets out a long, drawn out sigh.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1371, '', 'Oh well....', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1372, 'It looks like we\'re in trouble. Look lively, here they come!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1373, 'Hold! I sense an evil presence... Undead!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1374, 'These three again?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1376, 'Finally. The bastard got what he deserved.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1377, '%s breaks the seal on Anton\'s letter and begins to read it.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1378, 'By the Light! This is not good. There\'s something you don\'t understand, $n.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1379, '%s throws Anton\'s letter down on the table.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1380, '', 'The rod\'s design is simple. Dartol didn\'t use his arcane knowledge in its creation... it is more druidic in nature.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1381, '', 'Hehe... I made a pun, didn\'t I? Druidic in nature... I\'m so funny.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1382, '', 'Now, then... the rod. Let\'s see about fixing it, shall we?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1383, '', 'There! Now it just needs to be recharged...', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1384, 'If you have the time, Grimclaw and his master Volcor could use your help. If you\'re interested, speak with me further...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1385, '%s grabs for his poisoned dagger!', '%s grabs for her poisoned dagger!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1386, 'Eh? What have we here?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1387, 'Welcome to the Stockade!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1388, 'Outsiders! Kill em all!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1390, 'More of the Warden\'s errand boys!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1391, 'Tell the Warden this prison is ours now!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1392, 'It\'ll be quick, but it won\'t be painless!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1393, 'Fresh meat!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1394, 'I\'ll crush your skull beneath my boot!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1395, 'Death to the Warden\'s men!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1396, 'Who\'s there?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1397, 'We must hurry.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1398, 'Spirits of the forest come forth and heed nature\'s call!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1399, '', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1400, 'Spirits of the forest, you are needed! Make haste to the Charred Vale!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1401, '%s is not impressed.', '%s is not impressed.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1402, 'You\'re pushing it, $n.', 'You\'re pushing it, $n.', 0, 0, 7, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1403, 'Don\'t make me go medieval on you.', 'Don\'t make me go medieval on you.', 0, 0, 7, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1404, 'Keep it up, $n, and I\'ll beat some manners into you.', 'Keep it up, $n, and I\'ll beat some manners into you.', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1405, '%s drops the animal parts into the bubbling cauldron.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1406, 'Ah, feel the magic seep into the cave, my little elves... muhahaha.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1407, 'So much for the power of nature... muhahahaha.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1408, '%s lays an egg!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1409, '%s begins to crack and open...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1410, '%s splits open!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1411, '', '%s sees her cub being attacked and goes into a frenzied rage!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1412, '%s gives message to Apothecary Zamah.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1413, '%s hatches!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1414, '%s chitters for help...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1415, '%s arrives!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1416, '', '%s listens to $n\'s plea.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1417, '', 'You were right to come.  My wisdom will be yours.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1418, '', '%s smiles.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1419, '', 'Go, my servant.  Send word of Stonetalon to Apothecary Zamah.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1420, '', '%s summons a messenger...', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1421, '%s creates his toxin...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1422, 'There we are, $n. The toxin is ready.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1423, '', '%s fills a container with the toxin.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1424, '', 'The toxin is ready, $n.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1425, '', 'The spirits of Stonetalon still rage, $n.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1426, '', 'I fear we may never soothe them...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1427, 'All right, watch my back while I get you some of this ore.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1428, 'Hey! You\'re not supposed to be here! C\'mon boys!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1430, 'I think the sounds came from over here. We\'d better check it out.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1431, 'Look out! It sounds like more of them!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1432, 'Just a little while longer, I\'ve almost got it!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1433, 'What\'s going on here? Piznik! I knew we shouldn\'t have let you stick around!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1434, 'There, I\'ve got it!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1435, 'Who dares interfere with the Sons of Arugal?', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1436, 'You\'ve destroyed too much of my work! Prepare to join them in the Nether!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1437, 'This should only take a moment, $n.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1438, '%s crushes the tear into a bowl of stew.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1439, 'Eat this, my daughter. It will help you feel better.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1440, 'There you go...', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1441, '', '%s stirs from her delirium.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1442, '', 'Mmmmh...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1443, '%s places a poultice on Relara\'s brow.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1444, '%s breathes a sigh of relief.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1445, 'We\'ll cut your throats and bleed you dry!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1446, 'We\'ll have your heads!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1447, 'For the Dark Strand!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1448, 'sdsd', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1449, 'My... My powers have diminished... You shall pay for this insolence!', '', 0, 0, 10, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1450, 'No! You can\'t defeat me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1452, 'The race will start in 1 minute!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1454, '%s flees combat to get help!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1455, '%s sees his fans cheering and gains a burst of speed!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1456, 'I have changed my mind loyal servants, you do not need to bring the prisoner all the way to my study, I will deal with him here and now.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1458, 'Arrrgh!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1459, 'A simple enough task to break this soulstone!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1460, '%s bursts open!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1461, '%s intones, \"Assist him in any way he requires. Do not fail in this task.\"', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1462, '%s crumbles into dust.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1463, '%s sniffs the air hungrily, smelling fresh lion meat...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1464, '%s devours the carcass!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1465, '%s disappears into the snow.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1466, '%s is placed upon the Flame of Uzel.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1467, '%s looks from side to side...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1468, 'Hm... if you\'re looking to adle wits, $n, then the secret behind Zanzil\'s zombies might just do the trick!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1469, 'Thank you, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1470, 'I\'m sure I can find a use for this mixture...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1471, 'In the throes of the Sundering, Ameth\'Aran was at the whim of the terror that gripped the land. There was little hope for survival.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1472, 'Athrikus came to us. He told us that he could save us from harm. He cast a spell upon us to protect us from harm.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1473, 'Then we must put an end to Athrikus Narassin. Tell me, spirit, where might we find the other soulgems, to undo his magic?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1474, 'When the shaking stopped, his true motives were revealed. We were trapped, and he was slowly draining our powers.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1475, 'His lieutenant, Ilkrud Magthrull possesses a tome which might indicate the location of the remaining soulgems.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1476, '%s fades into the mist...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1477, '%s grabs a part and puts it in his pocket...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1478, 'There, that should do it...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1479, '%s places the crate of parts on the ground.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1480, '%s begins to dance.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1481, 'Hahah! $n, you make quite a partner!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1482, 'Woo hoo! Finally getting out of here. It\'s going to be rough though. Keep your eyes peeled for trouble.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1483, 'Up there is where Charlga Razorflank resides. Blasted old crone.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1484, 'There\'s blueleaf tuber in this trench! It\'s like gold waiting to be mined I tell you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1485, 'There could be danger around every corner here.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1486, 'I don\'t see how these foul animals live in this place... sheesh it smells!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1487, 'I think I see a way for us to get out of this big twisted mess of a bramble.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1488, 'Glad to be out of that wretched trench. Not much nicer up here though!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1489, 'This stout sure smells strong!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1490, 'Finally! I\'ll be glad to get out of this place.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1491, 'Let\'s have just a small taste...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1492, 'Wait! What am I thinking! We have a job to do with this stuff.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1493, 'I think I\'ll rest a moment and catch my breath before heading back to Ratchet. Thanks for all the help!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1494, 'Well, I\'m off to Ratchet now! Best of luck to you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1495, '%s listens as $n tells him of $ghis:her; adventures in the Badlands.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1496, 'Astounding! So then indurium is the key!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1497, 'There were hundreds imprisoned by his spell. Now only a few remain in their prisons. He would speak to us sometimes.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1498, 'He worried that his power weakened, that soon even we last of his precious soulgems would fade and die.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1499, 'I\'ll finish you off for good this time!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1500, '%s takes the indurium flakes to his forge.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1501, 'Now let\'s heat up these flakes...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1502, 'So far they\'re holding. Let\'s turn up the heat...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1503, 'By Orgrim! This indurium can withstand massive heat!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1504, 'A successful test! $n, this indurium is some amazing stuff.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1505, 'Thanks again, $n. Now I\'ll use this indurium to beef up my car!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1506, 'BANG! BANG! BANG! I\'m beefing up the car!!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1507, 'All done!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1508, 'Now for the next race, the car will be even FASTER than before!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1509, 'Open for business! The goblin car is looking sharp and it\'s sure to be a winner! Get your bets in before the race starts!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1510, 'Smart money\'s on the gnomes to take the next race! Get your money in while you still can!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1511, '%s begins to cast a spell over the hearts and tongues.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1512, 'The brood of Onyxia!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1513, '%s decomposes into dust.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1514, 'Me still boss of ogres. Haw!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1515, 'Puny $r wanna fight Overlord Mok\'Morokk? Me beat you! Me boss here!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (1517, 'And it\'s the $n coming through turn one!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
