INSERT INTO `broadcast_text` VALUES (2221, 'Do we have much further to go, $n?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (2222, 'I\'m thirsty.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (2223, 'Are we there yet?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (2224, 'I keep getting sand stuck in my toes.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
