INSERT INTO `broadcast_text` VALUES (2231, '', 'You will be easy prey, $C.', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (2241, 'Aku\'mai needs more innocents to be satisfied.', 'Aku\'mai needs more innocents to be satisfied.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (2242, 'Only the blood of an innocent can quench Aku\'mai\'s hunger.', 'Only the blood of an innocent can quench Aku\'mai\'s hunger.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (2243, 'Soon we shall bring about the return of the old gods.', 'Soon we shall bring about the return of the old gods.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
