INSERT INTO `broadcast_text` VALUES (3142, 'The Order of the Shattered Hand existed before Thrall and shall exist after Thrall. ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (3143, 'I require training, sir!', 'I require training, sir!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (3144, 'Zando\'zan of the Shattered Hand. How can Zando\'zan help you? He can\'t! He won\'t!\n', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (3145, 'Hello Zando\'zan, I would like to learn lockpicking.', 'Hello Zando\'zan, I would like to learn lockpicking.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (3146, 'Are you here for training? Or are you just here to waste a warrior\'s time.', 'Are you here for training? Or are you just here to waste a warrior\'s time.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (3147, 'I require warrior training.', 'I require warrior training.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (3148, 'The machine comes alive at your touch.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (3149, 'Press the yellow button labeled \'Thieves\' Tools.\'', 'Press the yellow button labeled \'Thieves\' Tools.\'', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (3161, 'He\'ll bring you mead$bHe\'ll bring you beer$bA grinning face from ear to ear$bHe\'s served us all from year to year$bWe call him Smiling Jim.', '', 0, 0, 7, 5, 11, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (3162, 'This is an exotic sample of flora, unseen in other parts of Teldrassil.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (3163, 'This plant stands out among the normal flora of Teldrassil, and emits a strange, purplish glow.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (3164, 'SQUAWK! Polly wants to crack you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (3165, 'What the squawk??? Squawk squawk, squawk? SQUAWK!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
