INSERT INTO `broadcast_text` VALUES (3167, 'MmmmmMmmmm... Enormous chemically altered cracker....', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (3168, '%s is littered with broken bones and cracker crumbs.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
