INSERT INTO `broadcast_text` VALUES (4838, '%s explodes into chunks of acidic slime!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4839, '', 'I know it\'ll work this time...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4840, '', 'This will be my best experiment yet!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4841, '', 'Now, if I add the yellow ones...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4842, '', 'I know it\'ll work this time... My last 13 tries were just practice!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4843, '', 'Ahh... My experiments never seem to work out right. Maybe I should go back downstream.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4844, 'I\'d like to purchase more Tharlendris seeds.', 'I\'d like to purchase more Tharlendris seeds.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
