INSERT INTO `broadcast_text` VALUES (4846, '', '%s begins to combine different colored crystals together.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4847, '', 'I think... I think it\'s working!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4848, 'I\'m not a journeyman herbalist -- am I able to still assist you in your work?', 'I\'m not a journeyman herbalist -- am I able to still assist you in your work?', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4849, 'Yes, but you\'ll find it more rewarding if you have skill in herbalism! You\'ll be nurturing and cultivating special seeds. While the Evergreen Pouch you\'ll receive allows even a novice to grow something, those who show some aptitude in herbalism will have more bountiful results.', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4850, 'Greetings, $c. What may this humble servant of the Feathermoon Stronghold do for you today?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4851, 'I\'d like to purchase more Tharlendris seeds.', 'I\'d like to purchase more Tharlendris seeds.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4852, '', 'OW! That\'s not right!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4853, '', 'Oh well... Maybe next time.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4854, 'Stand straight! Your weakness sickens me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
