INSERT INTO `broadcast_text` VALUES (4873, '', 'The death of our flesh marked the birth of our spirit and our sacred task.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4874, 'I am one of the Seven, and it is our doom to stand sentry. Forever.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4875, 'Our leader, Doom\'rel, has ears for the challenge.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4876, 'I am forever denied the touch of stone. I will never again know the glory of shaped iron...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4877, '', 'You may not pass.  You are not our kin, nor have you issued the challenge.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4878, 'Which trainer do you seek?', 'Which trainer do you seek?', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4879, 'Our fate is the doom of all who face the Great Fire.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4880, 'Hunter', 'Hunter', 0, 0, 0, 0, 0, 0, 0, 0, 0);
