INSERT INTO `broadcast_text` VALUES (4892, 'Your bondage is at an end, Doom\'rel. I challenge you!', 'Your bondage is at an end, Doom\'rel. I challenge you!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4893, 'The inn', 'The inn', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4894, 'You have challenged the Seven, and now you will die!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4895, 'The mailbox', 'The mailbox', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4896, 'A profession trainer', 'A profession trainer', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4897, 'Gloom\'rel, tell me your secrets!', 'Gloom\'rel, tell me your secrets!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4898, 'You wish to learn the old craft? You wish to smelt dark iron?$B$BAppease me, $r. Show me a sacrifice and I will consider it!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4899, 'Your will is strong, and your intent is clear.$B$BPerhaps you are worthy...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4900, 'I have payed your price, Gloom\'rel. Now, teach me your secrets!', 'I have payed your price, Gloom\'rel. Now, teach me your secrets!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4901, 'You have shown me your desire, and have payed with precious stone. I will teach you...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4902, 'Intruders! Attack the intruders!', 'Intruders! Attack the intruders!', 0, 0, 33, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4903, 'I know the way, insect. There is no need to prod me as if I were cattle.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4904, 'Surely you do not think that you will get away with this incursion. They will come for me and you shall pay for your insolence.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4905, 'RUN THEM THROUGH BROTHERS!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4906, 'I doubt you will be so lucky the next time you encounter my bretheren.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4907, 'They come for you, fool!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4908, 'What do you think you accomplish from this, fool? Even now, the Blackrock armies make preparations to destroy your world.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4909, 'On darkest wing they fly. Prepare to meet your end!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4910, 'You will not defile our sacred land!', 'You will not defile our sacred land!', 0, 0, 3, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4911, 'The worst is yet to come, $r.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4912, '%s laughs.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4913, 'Time to make your final stand, insect.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4914, 'The Kolkar will rule!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4915, 'Your Horde is nothing to the Kolkar!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4916, 'Shake in fear, dogs! I am here!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4917, 'The Kolkar are the strongest!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4918, '%s submits.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4919, 'You two-legs are nothing! My children could beat you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4920, 'I am here! Your rule of this land is over!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4921, 'For the Horde!', 'For the Horde!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4922, 'Defend the bunkers!', 'Defend the bunkers!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4923, 'The invaders are here!', 'The invaders are here!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4924, 'Beware! The Kolkar attack!', 'Beware! The Kolkar attack!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4925, 'Our foes will fall!', 'Our foes will fall!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4926, 'Beware, $n! Look to the west!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4927, 'You have come to play? Then let us play!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4928, 'Kneel, Grark.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4929, 'Grark Lorkrub, you have been charged and found guilty of treason against the Horde. How you plead is unimportant. High Executioner Nuzrak, step forward.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4930, '%s raises his massive axe over Grark.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4931, 'End him...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4932, '%s raises his hand and then lowers it.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4933, 'You, soldier, report back to Kargath at once!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4934, 'Get away from those kegs!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4935, 'I... I\'m not really sure how I got here...$B$BI seem to remember traveling... a raft...$B$B', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4936, 'You\'ll pay for that!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4937, 'Pay me and I\'ll talk...$B$BOh hey, it\'s you, $n! Nevermind about that...', '', 0, 0, 0, 25, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4938, 'Does my name sound familiar to you? Maybe we knew each other a long time ago...$B$BI think I\'m named after someone important...', '', 0, 0, 0, 6, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4939, 'Hi there! I\'m glad you came back, $n. Tell me, have you completed the first quest I gave you? If you have, I might have a second quest for you...', '', 0, 0, 0, 3, 6, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4940, 'When I was wandering the crater after I crashed here, I remembered something that I once was told... \"Go up, up the mountain ahead,\" said the voice in my head.$B$BListening to it is how I ended up here, $n!', '', 0, 0, 0, 1, 5, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4941, 'A long time ago, during my travels, I met a man that told me, \"Master using it and you can have this.\"$B$BWhat do you suppose that means? I didn\'t know then, and I still don\'t now, really...', '', 0, 0, 0, 1, 6, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4942, 'It\'s a secret to everybody... including me!$B$BI\'m glad you found me -- it\'s a good thing I\'m not hiding in a bush or underneath a rock, isn\'t it?', '', 0, 0, 0, 1, 6, 11, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4943, 'Thanks, $n! You\'re the hero of Un\'Goro Crater!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4944, 'Ragnaros will reign supreme!', 'Ragnaros will reign supreme!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4945, 'Have a drink, $c. It might be your last!', 'Have a drink, $c. It might be your last!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4946, 'If it weren\'t for the Grim Guzzler, I\'d spend my free time pulling the wings off dragon whelps.', 'If it weren\'t for the Grim Guzzler, I\'d spend my free time pulling the wings off dragon whelps.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4947, 'If it weren\'t for the Grim Guzzler, I\'d probably spend my free time pulling the wings off dragon whelps.', 'If it weren\'t for the Grim Guzzler, I\'d probably spend my free time pulling the wings off dragon whelps.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4948, 'The water elementals are at war with the fire elementals, which sort of means they\'re at war with us. One of their leaders, Hydraxis, is mustering agents for his cause...', 'The water elementals are at war with the fire elementals, which sort of means they\'re at war with us. One of their leaders, Hydraxis, is mustering agents for his cause...', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4949, 'Oh man, I thought I was dead for sure. Ugh... still dizzy...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4950, 'I heard that a noble among the water elementals, Hydraxis, wages war with the servants of fire. Let\'s hope he loses!', 'I heard that a noble among the water elementals, Hydraxis, wages war with the servants of fire. Let\'s hope he loses!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4951, 'The Waterlord Hydraxis hides off in Azshara, on an island in the Bay of Storms. What kind of threat can the fool muster from there?', 'The Waterlord Hydraxis hides off in Azshara, on an island in the Bay of Storms. What kind of threat can the fool muster from there?', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4952, 'I like the way you think! Let\'s get outta here!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4953, 'If Hydraxis recruits more in his war, then it might shift the balance of power...', 'If Hydraxis recruits more in his war, then it might shift the balance of power...', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4954, 'It\'s all guns and flames until someone loses and dies.', 'It\'s all guns and flames until someone loses and dies.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4955, 'All right, I think I can make it on my own from here. I don\'t know who you are... but I owe you one. Thank you!', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4956, 'My father told me there\'d be days like this, where a stranger would talk to me and I\'d have to restrain myself from killing $ghim:her;.', 'My father told me there\'d be days like this, where a stranger would talk to me and I\'d have to restrain myself from killing $ghim:her;.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4957, 'Welcome to Camp Mojache, brave $c. How may I assist you this day?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4958, 'Greetings $c -- if you\'re looking for leather goods and leatherworking supplies, you\'ve come to the right semi-retired Paladin.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4959, 'You look like you might be needing passage across the Great Sea. Well you have come to the right place, this here is the berth of the Maiden\'s Fancy, finest passenger ship to travel these waters. When she arrives, just climb aboard and the next thing you know you will find yourself basking on the sandy beaches of Stranglethorn.', 'You look like you might be needing passage across the Great Sea. Well you have come to the right place, this here is the berth of the Maiden\'s Fancy, finest passenger ship to travel these waters. When she arrives, just climb aboard and the next thing you know you will find yourself basking on the sandy beaches of Stranglethorn.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4960, 'I must say, Booty Bay has the best Clam Chowder to be had anywhere in the South Seas. Oh, not interested in cuisine eh? Well then let me be the first to welcome you to the berth of the Maiden\'s Fancy, the finest passenger ship to travel between the Eastern Kingdoms and Kalimdor. She\'s due back for another trip to Ratchet soon, so make yourself comfortable till she arrives.', 'I must say, Booty Bay has the best Clam Chowder to be had anywhere in the South Seas. Oh, not interested in cuisine eh? Well then let me be the first to welcome you to the berth of the Maiden\'s Fancy, the finest passenger ship to travel between the Eastern Kingdoms and Kalimdor. She\'s due back for another trip to Ratchet soon, so make yourself comfortable till she arrives.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4961, 'Intruders!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4962, 'What are you doing? Intruders!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4963, 'Of course there\'s no danger in everything catching flames and exploding like a huge helium bomb. This baby will get you to Undercity faster and safer than any boat, and the view is truly breathtaking. Speaking of breath, smoking is not allowed while on board the zeppelin, and fire spells are banned from being cast during the trip.', 'Of course there\'s no danger in everything catching flames and exploding like a huge helium bomb. This baby will get you to Undercity faster and safer than any boat, and the view is truly breathtaking. Speaking of breath, smoking is not allowed while on board the zeppelin', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4964, 'Hello! You\'re just the person I was looking for. Take a seat and listen, because I have a plan that will make us both rich!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4965, 'My brother said we could make great money with this trade route, but he didn\'t tell me I would be the one living on this side. If you come back this way on the Zeppelin, could you bring me something to eat? They sell some of the finest meat in Orgrimmar, and I sure could use something that didn\'t die weeks before it stopped moving.', 'My brother said we could make great money with this trade route, but he didn\'t tell me I would be the one living on this side. If you come back this way on the Zeppelin, could you bring me something to eat? They sell some of the finest meat in Orgrimmar, and I sure could use something that didn\'t die weeks before it stopped moving.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4966, 'I hope this works!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4967, '%s adds the haze to the Bloodpetal sprouts.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4968, 'Look at them go!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4969, '%s laughs as he watches the pests run off.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4970, 'It\'s GO TIME! Come get sum! <replace this>', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4971, 'Your family says hello, Ribbly. And they want your head!', 'Your family says hello, Ribbly. And they want your head!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4972, 'You\'re good for nothing, Ribbly. It\'s time to pay for your wickedness!', 'You\'re good for nothing, Ribbly. It\'s time to pay for your wickedness!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4973, 'No! Get away from me! Help!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4974, 'Look for Nogg\'s Machine Shop in the Valley of Honor northeast of Red Canyon Mining.', 'Look for Nogg\'s Machine Shop in the Valley of Honor northeast of Red Canyon Mining.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4975, 'You\'ll find that goblin and his toy shop in the Valley of Honor.', 'You\'ll find that goblin and his toy shop in the Valley of Honor.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4976, 'Engineering', 'Engineering', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4977, 'Help me choose wisely.', 'Help me choose wisely.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4978, 'I am a mage of great power, I have spent my days studying the mind and the memory as it pertains to learning and the retention of learned abilities. I would even now be perfecting my studies if not for the great voices that came to me in the night, informing me that my services were required. I will be here for only a short time offering my ability to wipe your talents for free. Soon I will leave again, and you will once more be stuck with whatever choices you make.', 'I am a mage of great power, I have spent my days studying the mind and the memory as it pertains to learning and the retention of learned abilities. I would even now be perfecting my studies if not for the great voices that came to me in the night, informing me that my services were required. I will be here for only a short time offering my ability to wipe your talents for free. Soon I will leave again, and you will once more be stuck with whatever choices you make.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4979, '', 'You got it!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4980, '', 'Coming right up!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4981, '', 'I\'ll bring it right over, baby...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4983, '', 'Yes $gsir:madam;.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4984, 'Hey Nagmara! How about a mug of Dark Iron Ale?', 'Hey Nagmara! How about a mug of Dark Iron Ale?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4985, 'Nagmara! Have any mutton left?', 'Nagmara! Have any mutton left?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4986, 'Mistress, you tell Plugger that I want more of his ale. I can\'t get enough!', 'Mistress, you tell Plugger that I want more of his ale. I can\'t get enough!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4987, 'Hey Nag, how about you slice me off a piece of that...', 'Hey Nag, how about you slice me off a piece of that...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4988, 'I\'d like to train in cooking.', 'I\'d like to train in cooking.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4989, 'Hello, $n. If you\'d like to learn a thing or two about cooking, please visit me at any time.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4990, '', '<Lady Prestor glares at you.>', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4991, 'Pardon the intrusion, Lady Prestor, but Highlord Bolvar suggested that I seek your advice.', 'Pardon the intrusion, Lady Prestor, but Highlord Bolvar suggested that I seek your advice.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4992, '', 'I was standing right next to you, imbecile. I know what you seek.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4993, 'My apologies, Lady Prestor.\n', 'My apologies, Lady Prestor.\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4994, '', 'You seek to lower our global defenses in order to appease a paranoid magistrate and an encampment of riff-raff that are most probably suffering from heat stroke.', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4995, 'Begging your pardon, Lady Prestor. That was not my intent.\n', 'Begging your pardon, Lady Prestor. That was not my intent.\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4996, '', 'Of course not, dear $g boy:girl;. You \'adventurer\' types are often victims of circumstance; subject to the political drama which tends to unfold between citizenry and royalty.$B$BMy advice to you, then, is to seek out something else, more specifically: A new career.$B$BThis world has enough heroes.', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4997, 'Thank you for your time, Lady Prestor.', 'Thank you for your time, Lady Prestor.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4998, '%s gets really dizzy!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (4999, '%s drunkenly miscasts a shadow bolt!', '%s drunkenly miscasts a shadow bolt!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5000, '', 'Hey, Rocknot!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5001, '', 'Let\'s go, honey.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5002, '', '%s kisses her lover.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5003, '%s kisses Mistress Nagmara', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5004, 'Please, help me to get through this cursed forest, $r.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5005, 'The corruption of the fel has not left any of the creatures of Felwood untouched, $n. Please, be on your guard.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5006, 'What you be wanting? *hic*', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5007, 'Official business, John. I need some information about Marshal Windsor. Tell me about the last time you saw him.', 'Official business, John. I need some information about Marshal Windsor. Tell me about the last time you saw him.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5008, 'That I must fight against my own kind deeply saddens me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5009, 'Windsor was particularly ornery that day - and believe me, for Windsor, that\'s a monumental accomplishment. He kept telling me that \'something feels off.\' Well he wasn\'t kidding!$B$BWe were in the middle of Blackrock Mountain when the filthy animals attacked. I\'m talking about the orcs, of course. Pay attention, will ya? All you could hear were grunts and the clanging of steel as they rushed us. ', '', 0, 0, 0, 1, 6, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5010, 'So what did you do?', 'So what did you do?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5012, 'Me versus fifty orcs? I\'m no fool, $n. My pappy always told me, \'Discreditin is the better part of a cracker,\' or something, and I knew what that meant.', '', 0, 0, 0, 6, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5013, 'Start making sense, dwarf. I don\'t want to have anything to do with your cracker, your pappy, or any sort of \'discreditin.\'\n', 'Start making sense, dwarf. I don\'t want to have anything to do with your cracker, your pappy, or any sort of \'discreditin.\'\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5014, 'Alright, alright. Anyhow, so I sorta slipped into the shadows.$B$BThat didn\'t sit too well with Windsor, seeing as how he was already extra cranky. Well he started spinnin\' old Ironfoe around and screaming like a mad man at the orcs. ', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5015, 'Ironfoe?\n', 'Ironfoe?\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5016, 'Yep. You never heard of Ironfoe? The legendary orc slaying hammer? Yep, yep, that was ol\' Windsor\'s hammer. He told me that Franclorn Forgewright himself made that hammer for his great, great, grand pappy. THE Franclorn Forgewright: The Dark Iron responsible for stonewrought archimetenc... building stuff. He also said the hammer had a twin that Franclorn kept for himself. Think he called it Ironfel or something.', '', 0, 0, 0, 6, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5017, 'Interesting... continue, John.', 'Interesting... continue, John.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5018, 'So where was I? Oh yea, so the orcs rushed Windsor and Windsor, well, he didn\'t move an inch. He stood tall as they charged him, ten at a time. All I could see was the glow from Ironfoe and a lot of blood. This went on for hours, maybe days. I don\'t remember. Anyhow, FINALLY, it stopped.', '', 0, 0, 0, 6, 5, 5, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5019, 'So that\'s how Windsor died...', 'So that\'s how Windsor died...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5020, 'Died? Are you cracked, $n? Excuse me, $g Mister:Miss; $n! Windsor wouldn\'t have died from no fifty orcs. As sure as Thelsamar blood sausage is the tastiest food the world may ever know, there he stood: He was covered in orc chunks from head to toe, drenched in about eighteen layers of their blood, but he was definitely alive... and really, really angry.', '', 0, 0, 0, 6, 6, 5, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5021, 'So how did he die?', 'So how did he die?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5022, 'Why do you keep saying he died? Who told you he died? I never said he died.$B$BHe went missing is all.$B$BYou see, apparently we had gotten into the middle of some big orc versus Dark Iron dwarf battle. The orcs, being the filthy, miserable curs that they are, were out early, setting up some traps and other diabolical things you probably wouldn\'t understand.', '', 0, 0, 0, 6, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5023, 'I can sense it now, $n. Ashenvale lies down this path.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5024, 'Ok, so where the hell is he? Wait a minute! Are you drunk?', 'Ok, so where the hell is he? Wait a minute! Are you drunk?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5025, 'Dwarves don\'t get drunk, $g mister:missy;. I\'m just a little sloppy.$B$BAnyhow, Windsor? I figure he\'s somewhere in Blackrock Depths. That\'s the Dark Iron city for you unedumecated peoples.', '', 0, 0, 0, 14, 6, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5026, 'I feel... something strange...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5027, 'WHY is he in Blackrock Depths?', 'WHY is he in Blackrock Depths?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5028, 'Slow down! I was getting to that!$B$BSo there he was, standing tall with all the blood and guts dripping off him when who shows up?$B$BThe Dark Irons! Didn\'t you hear a word I said??$B$BWell, the Dark Irons are a little craftier than those Blackrock orcs. They came prepared. By prepared I mean there were about 300 of em... *hic* \'scuse me.', '', 0, 0, 0, 5, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5029, '$n, my form has now changed! The true strength of my spirit is returning to me now... The cursed grasp of the forest is leaving me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5030, '300? So the Dark Irons killed him and dragged him into the Depths?', '300? So the Dark Irons killed him and dragged him into the Depths?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5031, '$G Mister:Missy;, if I didn\'t know better, I\'d think you were one of them \'special\' peoples. We call em Troggs.$B$BWindsor didn\'t have no beef with the Dark Irons, after all, his great, great, grand pappy\'s best friend was a Dark Iron. Which is also probably why that army of Dark Irons didn\'t kill him on sight.', '', 0, 0, 0, 25, 1, 5, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5032, 'Your knowledge is lacking. Return to me when your skills in smelting have grown!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5033, 'You may leave me here -- I wish to be alone in my final moments.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5034, 'Ahhh... Ironfoe.\n', 'Ahhh... Ironfoe.\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5035, 'Finally! Put some fingers in your ears, your brain mighta just grown five sizes and I\'m worried it might leak out.$B$BSo, the Dark Irons spared his life and took him prisoner. Their leader, some self-important, uppity fella named Thauris-something, took Ironfoe for himself. And that was the last I saw of ol\' Windsor... *hic* \'scuse me.\n', '', 0, 0, 0, 5, 1, 33, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5036, 'Thank you, $n. Now my spirit will finally be at peace.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5037, 'The corruption of Felwood has trapped my spirit here... I may never rest...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5038, 'Thanks, Ragged John. Your story was very uplifting and informative. ', 'Thanks, Ragged John. Your story was very uplifting and informative. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5039, '', 'Welcome to the Grim Guzzler!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5040, 'Why don\'t you and Rocknot go find somewhere private...', 'Why don\'t you and Rocknot go find somewhere private...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5041, '', 'Good idea!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5042, '', 'Thanks again, $n.  Rocknot and I are really hitting it off!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5043, 'Another Engineer! Hey, this is a great chance for me to show off some new gadgets I\'ve been building!', 'Another Engineer! Hey, this is a great chance for me to show off some new gadgets I\'ve been building!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5044, '', 'If you\'re going to venture into Blackrock Depths, come speak with me. I may have some work for you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5045, '%s looks tired. He then yawns and lies down next to A-Me 01', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5046, '%s snores loudly and cuddles up next to A-Me 01.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5047, '', 'Have you seen all the wonderful things here in Ungoro Crater, $n? It\'s simply amazing. Not as amazing as some of the things I can make with my skill at engineering, but for it being all natural here... it\'s not too bad.$B$BMy uncle Remtravel loves old stuff like this place. I\'m following in his footsteps kinda--I want to discover new things from lost cultures too!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5048, '', 'Hmm, I don\'t have any mithril casings. I\'m all out. Is that all you need to get to fix A-Me? Oh, that would be fantastic!$B$BMithril casings aren\'t too hard to find back in civilization. Just ask any skilled engineer to make one for you. I think it takes a few bars of mithril, but if you could help me out, I\'ll make sure you\'re well rewarded!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5049, 'Do you have any mithril casings?', 'Do you have any mithril casings?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5050, '', 'Be sure to try our Dark Iron Ale.  I hear the dwarves around here can\'t get enough of it.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5051, '', 'My master, Plugger Spazzring, is a good boss.  And he makes a lot of money.  His pockets are bursting!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5052, 'This here is the finest, most state of the art mode of transportation money can build. What? Deeprun Tram? Gnomes? Listen pal, If you believe that garbage then I\'ve got a statue down in Stranglethorn to sell ya. Now, are you walking to Grom\'Gol or are you gonna ride in style in this fine flying machine? Your choice.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5053, 'Hey! Get away from that!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5054, 'What are you doing over there?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5055, 'No stealing the goods!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5056, 'Hey! Stop that!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5057, 'Phalanx, I think you need to teach thos person a lesson.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5058, 'You are deemed a threat!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5059, 'I will be reborn, mortals! Incendius shall raise me from these ashes!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5060, 'That\'s it! You\'re going down!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5061, 'I cannot be destroyed! By the will of Ragnaros, I shall be reborn!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5062, 'A-Me good. Good, A-Me. Follow... follow A-Me. Home. A-Me go home.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5063, 'Good... good, A-Me. A-Me good. Home. Find home.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5064, 'Aid me! $n draws near!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5065, 'Help me with this $n!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5066, 'Have you tried that Thunderbrew Lager? It\'s good stuff!', 'Have you tried that Thunderbrew Lager? It\'s good stuff!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5067, 'I have duty in an hour, but until then... bottom\'s up!', 'I have duty in an hour, but until then... bottom\'s up!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5068, 'You looking at me, $gladdy:lassy;?', 'You looking at me, $gladdy:lassy;?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5069, 'I\'ll take a pint of Dark Iron ale. Oh, you\'re not the $gwaiter:waitress;?', 'I\'ll take a pint of Dark Iron ale. Oh, you\'re not the $gwaiter:waitress;?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5070, 'I think Plugger filled one of his kegs too much. It looks like it\'s about to blow!', 'I think Plugger filled one of his kegs too much. It looks like it\'s about to blow!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5071, 'Don\'t bother me while I\'m partying...', 'Don\'t bother me while I\'m partying...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5072, 'Ye be needing directions $glad : lass;?', 'Ye be needing directions $glad : lass;?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5073, 'I love the smell of Ironforge in the morning! Eh? And what would you be needing $c?', 'I love the smell of Ironforge in the morning! Eh? And what would you be needing $c?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5074, 'Whatcha need?', 'Whatcha need?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5075, 'IF they say... IF! There are no if\'s about Ironforge! Eh? What now?', 'IF they say... IF! There are no if\'s about Ironforge! Eh? What now?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5076, 'Can you help me with some directions?', 'Can you help me with some directions?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5077, 'What do ye need directions to?', 'What do ye need directions to?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5078, 'Bank of Ironforge', 'Bank of Ironforge', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5079, 'Do ye have some new treasures that ye want to keep safe n\' sound? Then ye\'ll be wanting to store them at The Vault with the Stonemantles. \n\nYe\'ll find The Vault just a hammer\'s throw northeast from the entrance to Ironforge.', 'Do ye have some new treasures that ye want to keep safe n\' sound? Ye\'ll be wanting to store them at The Vault with the Stonemantles. Ye\'ll find The Vault just a hammer\'s throw northeast from the entrance to Ironforge.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5080, 'The bank? Oh, ye mean The Vault! No safer place to store yer valuables than The Vault. Ye\'ll find it just northeast of the entrance to Ironforge. ', 'The bank? Oh, ye mean The Vault! No safer place to store yer valuables than The Vault. Ye\'ll find it just northeast of the entrance to Ironforge. ', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5081, 'Gryphon Master', 'Gryphon Master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5083, 'Looking to take a ride on a gryphon, now are ye? Gryth Thurden is the one ye\'ll want to be seeing about that. \n\nYe can find him on the eastern side of The Great Forge in the middle of Ironforge.', 'Looking to take a ride on a gryphon, now are ye? Gryth Thurden is the one ye\'ll want to be seeing about that. \n\nYe can find him on the eastern side of The Great Forge in the middle of Ironforge.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5085, 'Ah, nothing quite like a ride on a gryphon, is there? The night elves can keep those hippo\'s of theirs, just give me a gryphon and the wind in me hair! \n\nWell now, I won\'t keep ye any longer, just head over to Gryth Thurden on the east side of The Great Forge and he\'ll get ye on yer way.', 'Ah, nothing quite like a ride on a gryphon, is there? The night elves can keep those hippo\'s of theirs, just give me a gryphon and the wind in me hair! \n\nWell now, I won\'t keep ye any longer, just head over to Gryth Thurden on the east side of The Great Forge and he\'ll get ye on yer way.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5086, 'Guild Master', 'Guild Master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5087, 'If yer thinking about forming a guild, then ye\'ll need to put it past Jondor Steelbrow o\'er in the Ironforge Visitor\'s Center. Just head southeast from the gates of Ironforge to see him.', 'If yer thinking about forming a guild, then ye\'ll need to put it past Jondor Steelbrow o\'er in the Ironforge Visitor\'s Center. Just head southeast from the gates of Ironforge to see him.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5088, 'Class Trainer', 'Class Trainer', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5089, 'Which class trainer might ye be looking for?', 'Which class trainer might ye be looking for?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5090, 'The Inn', 'The Inn', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5091, 'The Inn? Ah, ye looking to toss back some of the best ales Ironforge has to offer? The Stonefire Tavern is where ye\'ll want to go. $B$BYe\'ll find it just to the left of the entrance to Ironforge if ye be coming, or to the right if ye be going.', 'The Inn? Ah, ye looking to toss back some of the best ales Ironforge has to offer? The Stonefire Tavern is where ye\'ll want to go. $B$BYe\'ll find it just to the left of the entrance to Ironforge if ye be coming, or to the right if ye be going.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5092, 'If yer of need of an Inn, The Stonefire Inn is where ye should be. Ye\'ll find it just to the left of the entrance to Ironforge if ye be coming, or to the right if ye be going. $B$BOh, and if yer going that way, then tell them to have a tankard ready for me.', 'If yer of need of an Inn, The Stonefire Inn is where ye should be. Ye\'ll find it just to the left of the entrance to Ironforge if ye be coming, or to the right if ye be going. $B$BOh, and if yer going that way, then tell them to have a tankard ready for me.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5093, 'Mailbox', 'Mailbox', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5094, 'You can find a mailbox right o\'er by The Stonefire Tavern. It\'s just north of the gates of Ironforge.', 'You can find a mailbox right o\'er by The Stonefire Tavern. It\'s just north of the gates of Ironforge.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5095, 'Hunter', 'Hunter', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5096, 'I overheard Regnus Thundergranite in the Hall of Arms bragging about a new trophy for his den the other day. If yer looking for training from a skilled hunter, Regnus is the one to see.', 'I overheard Regnus Thundergranite in the Hall of Arms bragging about a new trophy for his den the other day. If yer looking for training from a skilled hunter, Regnus is the one to see.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5097, 'Mage', 'Mage', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5098, 'That wee wizardly lass, Bink, over in the Hall of Mysteries would probably be able to help ye out with any magical needs you have. \n\nThe Hall of Mysteries is just north of the gates of Ironforge.', 'That wee wizardly lass, Bink, over in the Hall of Mysteries would probably be able to help ye out with any magical needs you have.\n\nThe Hall of Mysteries is just north of the gates of Ironforge.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5099, 'Well ye\'d be sure to find some training in the magical arts over in the Hall of Mysteries, north of the gates of Ironforge. Juli Stormkettle can always be found there with her nose in a book.', 'Well ye\'d be sure to find some training in the magical arts over in the Hall of Mysteries, north of the gates of Ironforge. Juli Stormkettle can always be found there with her nose in a book.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5100, 'Priest', 'Priest', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5101, 'Ah, Braenna Flintcrag. She has magic hands, that one. Braenna is the one to see about Priestly matters. \n\nYe\'ll want to travel north from the gates of Ironforge to the Hall of Mysteries. That\'s where ye\'ll find her.', 'Ah, Braenna Flintcrag. She has magic hands, that one. Braenna is the one to see about Priestly matters. \n\nYe\'ll want to travel north from the gates of Ironforge to the Hall of Mysteries. That\'s where ye\'ll find her.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5102, 'Paladin', 'Paladin', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5103, 'Brandur Ironhammer would be the one ye\'d want to see. Ye can find him in the Hall of Mysteries north of the gates of Ironforge.', 'Brandur Ironhammer would be the one ye\'d want to see. Ye can find him in the Hall of Mysteries north of the gates of Ironforge.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5104, 'Ah, good ol\' Brandur Ironhammer. There\'s no doubt he\'ll provide ye with the guidance ye seek, $glad : lass;. Ye\'ll find him in the Hall of Mysteries north of the gates of Ironforge.', 'Ah, good ol\' Brandur Ironhammer. There\'s no doubt he\'ll provide ye with the guidance ye seek, $glad : lass;. Ye\'ll find him in the Hall of Mysteries north of the gates of Ironforge.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5105, 'Rogue', 'Rogue', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5106, 'Now why would ye be needing the services of one of those types? We get enough trouble out of that rogue, Hulfdan Blackbeard, over in The Forlorn Cavern, do I need to be turning my eye to you as well?', 'Now why would ye be needing the services of one of those types? We get enough trouble out of that rogue, Hulfdan Blackbeard, over in The Forlorn Cavern, do I need to be turning my eye to you as well?', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5107, 'All the roguish types hang out in The Forlorn Cavern, north of the gates of Ironforge. I\'d wager ye\'ll find Fenthwick there, no doubt counting the coin of another cut purse. I\'ll catch him one of these days! \n\nIn the meantime I\'ll have my eye on you as well, $glad : lass;.', 'All the roguish types hang out in The Forlorn Cavern, north of the gates of Ironforge. I\'d wager ye\'ll find Fenthwick there, no doubt counting the coin of another cut purse. I\'ll catch him one of these days! \n\nIn the meantime I\'ll have my eye on you as well, $glad : lass;.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5108, 'Warlock', 'Warlock', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5109, 'Where better to look for a dark and brooding lot, than the dark section of Ironforge, aptly named The Forlorn Cavern. Thistleheart is sure to be found there, likely summoning some manner of vile creature to do his bidding.', 'Where better to look for a dark and brooding lot, than the dark section of Ironforge, aptly named The Forlorn Cavern. Thistleheart is sure to be found there, likely summoning some manner of vile creature to do his bidding.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5110, 'Warrior', 'Warrior', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5111, 'Och! Ye seek training in the way of the warrior, do ye? Kelstrum Stonebreaker will put ye through yer paces! Just march yerself over to the Hall of Arms east of the gates of Ironforge. ', 'Och! Ye seek training in the way of the warrior, do ye? Kelstrum Stonebreaker will put ye through yer paces! Just march yerself over to the Hall of Arms east of the gates of Ironforge. ', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5112, 'Profession Trainer', 'Profession Trainer', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5113, 'Which profession trainer would ye be looking for?', 'Which profession trainer would ye be looking for?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5114, 'Alchemy', 'Alchemy', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5115, 'An Alchemy trainer is what you need? Well, I just came from there, I did. Ye\'ll find Ms. Berryfizz over in Tinker Town where the gnomes gather.', 'An Alchemy trainer is what you need? Well, I just came from there, I did. Ye\'ll find Ms. Berryfizz over in Tinker Town where the gnomes gather.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5116, 'Yer looking for an Alchemy trainer are ye? Just came from there, I did. Ye\'ll find Ms. Berryfizz over in Tinker Town where the gnomes gather. \n\n::sigh:: ...why o\' why didn\'t I take the blue potion.', 'Yer looking for an Alchemy trainer are ye? Just came from there, I did. Ye\'ll find Ms. Berryfizz over in Tinker Town where the gnomes gather. \n\n::sigh:: ...why o\' why didn\'t I take the blue potion.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5117, 'Blacksmithing', 'Blacksmithing', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5118, 'Ye\'ve come to the right place $glad : lass;! People come here from all o\'er the world to train to be a blacksmith. Ye\'ll find Bengus Deepforge hammering away at an anvil in the middle of our fine city by The Great Forge itself.', 'Ye\'ve come to the right place $glad : lass;! People come here from all o\'er the world to train to be a blacksmith. Ye\'ll find Bengus Deepforge hammering away at an anvil in the middle of our fine city by The Great Forge itself.', 0, 0, 0, 5, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5119, 'Cooking', 'Cooking', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5120, 'A Cooking trainer is it? Well then, get yerself over to the Bronze Kettle on the northeastern side of The Great Forge. Daryl Riknussun will teach ye to make a mighty fine dish that\'s sure to put the life back in ye!', 'A Cooking trainer is it? Well then, get yerself over to the Bronze Kettle on the northeastern side of The Great Forge. Daryl Riknussun will teach ye to make a mighty fine dish that\'s sure to put the life back in ye!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5121, 'Enchanting', 'Enchanting', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5122, 'Ah, I believe Gimble Thistlefuzz is who ye\'ll be needing if yer looking to learn the Enchanter\'s profession. Ye\'ll find Gimble in his shop, Thistlefuzz Arcanery, right by the Gryphon Master at The Great Forge.', 'Ah, I believe Gimble Thistlefuzz is who ye\'ll be needing if yer looking to learn the Enchanter\'s profession. Ye\'ll find Gimble in his shop, Thistlefuzz Arcanery, right by the Gryphon Master at The Great Forge.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5123, 'Engineering', 'Engineering', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5124, 'The tinkerer\'s craft for you is it? No doubt ye\'ll want to go see Springspindle Fizzlegear in Tinker Town. \n\nJust head east from the gates of Ironforge, or follow the sound of the explosions.', 'The tinkerer\'s craft for you is it? No doubt ye\'ll want to go see Springspindle Fizzlegear in Tinker Town. \n\nJust head east from the gates of Ironforge, or follow the sound of the explosions.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5125, 'First Aid', 'First Aid', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5126, 'A valuable skill First Aid is. Our resident trainer, Nissa Firestone, can show ye how it\'s done. Look for her on the southern side of The Great Forge at the Ironforge Physician.', 'A valuable skill First Aid is. Our resident trainer, Nissa Firestone, can show ye how it\'s done. Look for her on the southern side of The Great Forge at the Ironforge Physician.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5127, 'Fishing', 'Fishing', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5128, 'Grimnur Stonebrand will tell ye some whoppers about the one that got away, but he\'s still one o\' the best fishermen around. He\'s got a shop set up in The Forlorn Cavern called the Traveling Fisherman.', 'Grimnur Stonebrand will tell ye some whoppers about the one that got away, but he\'s still one o\' the best fishermen around. He\'s got a shop set up in The Forlorn Cavern called the Traveling Fisherman.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5129, 'Herbalism', 'Herbalism', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5130, 'Herbalism', 'Herbalism', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5131, 'Flowers and seeds and such? That would be Reyna Stonebranch\'s area of expertise. Ye can find her tending to her plants over at the Ironforge Physician on the southern side of The Great Forge.', 'Flowers and seeds and such? That would be Reyna Stonebranch\'s area of expertise. Ye can find her tending to her plants over at the Ironforge Physician on the southern side of The Great Forge.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5132, 'Oh, ye want to learn to pick some daisies do ye? Ha! I\'m just teasing ye $gladdie : lass;. \n\nA fine profession it is, and Reyna Stonebranch can show ye how it\'s done. She works at the Ironforge Physician on the southern side of The Great Forge.', 'Oh, ye want to learn to pick some daisies do ye? Ha! I\'m just teasing ye $gladdie : lass;. \n\nA fine profession it is, and Reyna Stonebranch can show ye how it\'s done. She works at the Ironforge Physician on the southern side of The Great Forge.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5133, 'Leatherworking', 'Leatherworking', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5135, 'Fimble Finespindle has a shop set up on the northwest side of The Great Forge. I wager he could show ye how to make some fine armor.', 'Fimble Finespindle has a shop set up on the northwest side of The Great Forge. I wager he could show ye how to make some fine armor.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5136, 'Lockpicking', 'Lockpicking', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5137, 'Durtham Greldon over in The Forlorn Cavern can teach ye the in\'s and out\'s of lockpicking. Just don\'t be practicing any of those in\'s or out\'s here in Ironforge, if ye understand my meaning.', 'Durtham Greldon over in The Forlorn Cavern can teach ye the in\'s and out\'s of lockpicking. Just don\'t be practicing any of those in\'s or out\'s here in Ironforge, if ye understand my meaning.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5138, 'Mining', 'Mining', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5139, 'Do we have Mining trainers here? Where do ye think ye are!? \n\nWhy you\'ll find one o\' the best miners in all Dun Morogh at the Deepmountain Mining Guild on the northern side of The Great Forge. There, Geofram Bouldertoe will show ye the true art of mining.', 'Do we have Mining trainers here? Where do ye think ye are!? \n\nWhy you\'ll find one o\' the best miners in all Dun Morogh at the Deepmountain Mining Guild on the northern side of The Great Forge. There, Geofram Bouldertoe will show ye the true art of mining.', 0, 0, 0, 5, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5140, 'Skinning', 'Skinning', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5141, 'Balthus Stoneflayer is the one to see about Skinning. If it can be skinned, he can teach ye how. Just walk yerself over to Finespindle\'s Leather Goods on the northern side of The Great Forge.', 'Balthus Stoneflayer is the one to see about Skinning. If it can be skinned, he can teach ye how. Just walk yerself over to Finespindle\'s Leather Goods on the northern side of The Great Forge.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5142, 'Survival', 'Survival', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5143, 'Tresa Farmountain has seen her share of bad weather, but with her knowledge of Survival skills she\'s come through with her spirits high. \n\nShe\'s over in The Military Ward south of the gates of Ironforge if you wish to speak with her.', 'Tresa Farmountain has seen her share of bad weather, but with her knowledge of Survival skills she\'s come through with her spirits high. \n\nShe\'s over in The Military Ward south of the gates of Ironforge if you wish to speak with her.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5144, 'Tailoring', 'Tailoring', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5145, 'That Jormund Stonebrow has a knack for the needle, he does. Should you be needing some training, then you\'ll find him at Stonebrow\'s Clothier on the northern side of The Great Forge.', 'That Jormund Stonebrow has a knack for the needle, he does. Should you be needing some training, then you\'ll find him at Stonebrow\'s Clothier on the northern side of The Great Forge.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5146, 'Tracking', 'Tracking', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5147, 'Tracking is it? Then Denmul Farmountain is the one you should pay a visit to. He\'ll teach ye the tricks of the trade like no other. \n\nJust head south from the gates of Ironforge to The Military District to find him.', 'Tracking is it? Then Denmul Farmountain is the one you should pay a visit to. He\'ll teach ye the tricks of the trade like no other. \n\nJust head south from the gates of Ironforge to The Military District to find him.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5148, 'I will need to focus my energies onto this knife. By doing so, I should catch some of the stronger psychic impressions associated with it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5149, 'I... I\'ve seen enough I think... so very draining...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5150, 'I have suffered, but now I can feel it slowly fading from me. I feel... free.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5151, 'The Crystal Pylons of Un\'Goro are a mystery to all. To the few inhabitants here, they are a source of legend, with much speculation as to their purpose.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5152, 'I want to examine this pylon.', 'I want to examine this pylon.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5153, 'A closer inspection of the pylon leads you to find a small table containing blue, yellow, and green crystals arranged in what seems like a specific formation.$B$BA large blue circle of crystals is flanked on either side by a smaller circle of yellow crystals and another of green crystals.$B$BYou carefully take note of the colors of the crystals, knowing that J.D. would be very interested in hearing about what you have found.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5154, 'A closer inspection of the pylon leads you to find a small table containing yellow, red, and green crystals arranged in what seems like a specific formation.$B$BA large yellow circle of crystals is flanked on either side by a smaller circle of red crystals and another of green crystals.$B$BYou carefully take note of the colors of the crystals, knowing that J.D. would be very interested in hearing about what you have found.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5155, 'A closer inspection of the pylon leads you to find a small table containing red, blue, and green crystals arranged in what seems like a specific formation.$B$BA large red circle of crystals is flanked on either side by a smaller circle of blue crystals and another of green crystals.$B$BYou carefully take note of the colors of the crystals, knowing that J.D. would be very interested in hearing about what you have found.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5156, 'A-Me home! A-Me, good! Good A-Me. Home. Home. Home.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5157, 'Bad, $n. $n, bad!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5158, '$n, no hurt A-Me. A-Me, good.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5159, 'Danger. Danger! $n try hurt A-Me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5160, 'A-Me, home. A-Me go home! $n, no stop A-Me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5161, '', 'Get this, $n off of me!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5162, '', 'Help!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5163, '', 'I need help!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5164, '', '$n attacking! Help!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5166, 'I want more ale! Give me more ale!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5167, 'That one\'s empty!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5168, 'Ah, empty again!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5169, 'ALE!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5170, '%s looks at you expectantly.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5171, '', 'You probably weren\'t expecting to find me in this cave, were you?$B$BMy name\'s J.D., and I really can\'t tell you much about how I got here, but what I can tell you is that this cave is my home now!$B$BI spend my time studying the strange crystals that can be found all over the crater.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5172, 'Ah, hits the spot!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5173, 'Sweet Elune, thanks for getting me out of that... thing!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5175, 'Come with me if you want to live.', 'Come with me if you want to live.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5176, 'Burning Steppes - Tablet of the Seven', '', 0, 0, 6, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5177, 'Transcribe the tablet.', 'Transcribe the tablet.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5178, 'The symbols on the tablet are carefully transcribed...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5179, 'Greetings, $c. For some, the hunter is a way of life, a chosen profession. For others, it is something of a pastime, a badge of courage, or a rite of passage depending on your tribe.$B$BFor me, race and profession mean nothing--it is the testing of your spirit and mind that I respect. For one to stand against the forces of nature and test all of their skills is the true mark of honor.$B$BIf you wish to test yourself and earn my respect, then speak to me more.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5181, 'You\'ve started down an honorable path, $n. My ancestors respect your willingness, but you will not have my approval until you have followed through with my tasks.$B$BYou know what you must do if you wish to prove yourself a hunter in spirit.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5182, 'I give you warm greetin\'s, $c, as does the Shattered Hand.$B$BDo not look so surprised, $n. There be shadows even on the highest perches of Orgrimmar--somethin\' must hide there. The Shattered Hand take that role willingly. Thrall, even in his good heart, acknowledge that the Hand be necessary to ensure orc and troll safety.$B$BBut you not here to talk \'bout my business, you here for somethin\' else.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5183, 'Un\'Goro Crater be far to the south of Kalimdor. Beyond the Thousand Needles in Tanaris. Look for the large floating stones... they mark the entrance to the Crater. But be careful... it is no easy path. The raptors are not far from the entrance.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5184, 'Where is Un\'Goro Crater?', 'Where is Un\'Goro Crater?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5185, '', 'Thanks to you, we can now unlock the secrets of the crystals and the mysterious pylons, $n. If you need more information at any time, please come back and visit me again!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5186, 'Tell me more about the Eastern Crystal Pylon.', 'Tell me more about the Eastern Crystal Pylon.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5187, 'Who is this Lar\'korwi you spoke of?', 'Who is this Lar\'korwi you spoke of?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5188, 'Tell me more about the Northern Crystal Pylon.', 'Tell me more about the Northern Crystal Pylon.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5189, 'The ravasaur here in Un\'Goro seem to be related to the raptors of the barrens--highly intelligent, volatile, and with a sense or code of conduct. The greatest of these that I have encountered is Lar\'korwi. He is one of the most dangerous beasts I\'ve ever seen. In taurahe, Lar\'korwi means \"sharp claw,\" but that does not do him justice.$B$BDo not underestimate Lar\'korwi if you ever plan on hunting him, but I will help you in any way I can if you do.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5190, 'Tell me more about the Western Crystal Pylon.', 'Tell me more about the Western Crystal Pylon.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5191, 'I lost my Crystal Pylon User\'s Manual and need another one.', 'I lost my Crystal Pylon User\'s Manual and need another one.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5192, '', 'The Eastern Pylon accepts blue, yellow, and green crystals.$B$BIt focuses on the energy that can be created using blue crystals as the main source of power. There are two possible effects: one provides bonus to spirit, and one that creates a damaging shield.$B$BCrystal Force requires blue and green crystals. This will provide you with an increase in spirit.$B$BCrystal Spire requires blue and yellow crystals. This will create a shield that does damage to attackers.\n\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5193, '', 'The Northern Pylon accepts yellow, red, and green crystals.$B$BThe focus is the energy that can be created using yellow crystals as the main source of power. There are two possible effects: one that will produce damage, and one that will negate damage. $B$BCrystal Charge requires red and yellow crystals. This will produce a large explosion, stunning your enemies.$B$BCrystal Restore requires yellow and green crystals. This will provide you with a healing aura.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5194, '', 'The Western Pylon accepts red, green, and blue crystals.$B$BThe Pylon focuses on the energy that can be created using red crystals as the main source of power. There are two possible effects: one that increases your protection from damage, and one that weakens an enemy\'s defenses.$B$BCrystal Ward requires red and green crystals. This will provide you with an increase to armor.$B$BCrystal Yield requires blue and red crystals. This can lower the armor of a specific target.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5195, '', 'Sure thing, $n. Here\'s another for you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5196, 'You see the faint outline of a humanoid figure inside the pod; you now know that this is indeed the location you need to be at. While you think you will eventually be able to open the pod, it remains tighly shut even to your most forceful attempts to open it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5197, '%s attacks a new target!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5198, 'You make perfect little hat, $r!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5199, 'Ograbisi needs new hat!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5200, 'What manner of filth dares enter my chambers!?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5201, 'Ah, just in time for your cleansing, $r. I shall wax the impurities from your soul.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5202, 'Is that you, my dear Tobias???', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5203, 'Perhaps Ograbisi will use your head as a tiny little hat when I\'m through with you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5204, '', 'These once beautiful woods suffer under a terrible curse!  Corrupting forces twist the life here into shadows of their former selves.  If you are not an agent of this corruption, then perhaps you can aid me in reclaiming Felwood!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5205, 'Let\'s get a move on. My gear should be in the storage area up this way...', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5206, '', '\"Taurahe?\"$B$BThe tauren druid looks at you expectantly, but it is clear you cannot verbally communicate with her.  Undaunted, she points to herself and smiles.  She points at you and smiles once more.  She finally motions several times down the path to the south.$B$BPerhaps she is indicating that there is a druid you can speak to further down the road?\n', 0, 0, 0, 6, 274, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5207, 'Check that cell, $n. If someone is alive in there, we need to get them out.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5208, '', 'These once beautiful woods suffer under a terrible curse!  Corrupting forces twist the life here into shadows of their former selves.  If you are not an agent of this corruption, then perhaps you can aid me in reclaiming Felwood!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5209, '', '\"Darnassae?\"$B$BThe night elf druid looks at you expectantly, but it is clear you cannot verbally communicate with her.  Undaunted, she points to herself and smiles.  She points at you and smiles once more.  She finally motions several times down the path to the north.$B$BPerhaps she is indicating that there is a druid you can speak to further up the road?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5210, 'Thank you, $n! I\'m free!!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5211, 'HELP!!!!', '', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5212, 'You\'re free, Dughal! Get out of here!', 'You\'re free, Dughal! Get out of here!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5213, 'Good work! We\'re almost there, $N. This way.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5214, 'This is it, $n. My stuff should be in that room. Cover me, I\'m going in!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5215, 'Ah, there it is!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5216, 'Can you feel the power, $n??? It\'s time to ROCK!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5217, 'Now we just have to free Tobias and we can get out of here. This way!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5218, 'Finally!! I can leave this dump.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5219, 'Did Crest send you?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5220, 'Get out of here, Tobias, you\'re free!', 'Get out of here, Tobias, you\'re free!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5221, 'Excellent work, $n. Let\'s find the exit. I think I know the way. Follow me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5222, 'Open it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5223, 'I never did like those two. Let\'s get moving.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5224, 'Open it and be careful this time!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5225, 'That intolerant dirtbag finally got what was coming to him. Good riddance!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5226, '%s spits on the ground.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5227, 'Alright, let\'s go.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5228, 'Open it. We need to hurry up. I can smell those Dark Irons coming a mile away and I can tell you one thing, they\'re COMING!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5229, 'He has to be in the last cell. Unless... they killed him.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5230, 'Get him out of there!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5231, 'We made it!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5232, 'Meet me at Maxwell\'s encampment. We\'ll go over the next stages of the plan there and figure out a way to decode my tablets without the decryption ring.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5233, 'Grurk open soon. Then we split treasure.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5234, 'Stupid box! Stupid, stupid box! OPEN! GRURK SAY YOU OPEN!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5236, 'Let me try! You too stupid!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5237, 'Grurk stupid? You try open box! Maybe you smart as gnome???', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5238, 'Lumurk open this box easy!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5239, 'Box stupid. Lumurk sleepy.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5240, '', 'There are plants in Felwood that may be saved from corruption when Cenarion plant salve is applied to them.  I know how to make the salve from several different kinds of items; based on your skill set, you may find that you have multiple options to assist me.$B$BYou will also need a Cenarion beacon for your work here; it is a device that allows you to see the taint and corruption of nature that the naked eye will miss.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5241, '', 'There are plants in Felwood that may be saved from corruption when Cenarion plant salve is applied to them.  I know how to make the salve from several different kinds of items; based on your skill set, you may find that you have multiple options to assist me.$B$BYou will also need a Cenarion beacon for your work here; it is a device that allows you to see the taint and corruption of nature that the naked eye will miss.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5243, 'I need a Cenarion beacon.', 'I need a Cenarion beacon.', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5244, '', 'Here is a beacon for you, $n.  You must keep it in your possession if you are to find the corrupted items we will need.', 0, 0, 1, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5245, '', 'Here is a beacon for you, $n.  You must keep it in your possession if you are to find the corrupted items we will need.', 0, 0, 7, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5246, 'The remains are covered in flies and maggots.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5247, 'I summon you, Sha\'ni Proudtusk! ', 'I summon you, Sha\'ni Proudtusk! ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5248, '%s looks as though he\'s been stymied. He takes a few moments to recover from his wounds.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5249, 'Administering fists of fury on $n!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5250, 'You better hold me back or $n is going to feel some prison house beatings.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5251, 'This is gonna leave a mark!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5252, 'I bet you\'re sorry now, aren\'t you $n!?!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5253, 'You locked up the wrong Marshal, $n. Prepare to be destroyed!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5254, 'The large shrine was built many years ago by the night elves that once inhabited this land.$B$BWhat secrets could it hold?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5255, 'I need another sparkling stone.', 'I need another sparkling stone.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5256, 'Continue...', 'Continue...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5257, 'The door is open, Kharan. You are a free man.', 'The door is open, Kharan. You are a free man.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5258, 'Where I come from, you get shanked for opening another inmate\'s cell door!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5259, 'Doesn\'t anybody KNOCK anymore???!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5260, 'I have destroyed my Azsharite weaponry! I need your assistance in defeating the triad of power.', 'I have destroyed my Azsharite weaponry! I need your assistance in defeating the triad of power.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5261, 'I regret that I must fight this $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5262, 'Hey! What\'s gives?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5263, 'You\'ll pay for that!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5264, 'Oh, now I\'m pissed!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5265, 'No!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5266, 'Hey, my pockets were picked!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5267, 'That\'s it! No more beer until this mess is sorted out!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5268, 'Ha! Ha! Ha! Thank you for freeing me, fools. Now let me repay you by charring the flesh from your bones.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5269, '', 'We showed that one!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5270, 'The secrets hidden within the Pylons of Un\'Goro Crater are now yours. Visit the Pylons at any time to access their power and gain useful resources.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5271, 'You can\'t hide from us. Prepare to burn!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5274, 'Welcome to Tanaris, $n. How can I be of service to you today? You don\'t look like a blacksmith to me, but I know how looks can be deceiving.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5275, '', 'Uh oh!  I\'m out of here!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5281, 'I\'m getting out of here!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5283, '%s begins to summon a lava spawn!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5284, 'I do not enjoy conversation.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5285, '', 'Oh, no! We\'ve run out of time. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5286, '%s cries out an alarm!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5287, '', 'Now, let\'s see... If I am correct, then... Hmmm...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5288, '', 'Blue plus red... I predict...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5289, '', '$n, that\'s it! I have finally figured it out!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5290, 'They are lost, $r. Librams written by the elven masters. Scattered across the world when the Scourge was unleashed upon Quel\'Thalas.$B$BNow we search for these librams. Search to find the secrets they hold - to restore power once had.$B$BI search for the ten written by Master Kariel Winthalus. Should you find a libram, allow me to absorb its magical properties and for you I shall craft an amalgamation of power... of course, all things come at some cost. You will find my cost to be high; but pay you shall.', '', 0, 0, 0, 1, 1, 25, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5291, 'I present to you the Libram of Rumination.', 'I present to you the Libram of Rumination.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5292, 'Ah, so... you have found it.$B$B<Mathredis points to the book in your possession.>$B$BHave you collected the necessary components?$B$BThe manifestations listed in the libram, imbecile!', '', 0, 0, 0, 1, 6, 5, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5293, '<Mathredis gazes intently at the libram.>$B$BYou come for an amalgamation? Have you done as the libram instructed?', '', 0, 0, 0, 6, 1, 6, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5294, 'Give me the arcana and I shall craft the amalgamation, mortal.', '', 0, 0, 0, 25, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5295, 'A rare find, indeed.$B$B<Mathredis nods.>$B$B If you wish to continue, I will need the components.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5296, 'Greed has led you back to me.$B$B<Mathredis laughs.>$B$BA wise Draenei once told me that it - greed - is the single quality that all of the races on this world share. I would have to agree.$B$BHave you collected the components listed within the libram?', '', 0, 0, 0, 153, 1, 6, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5297, 'Intruders in the Manufactory? My constructs will destroy you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5298, 'May the Light protect you this day.', 'May the Light protect you this day.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5299, 'I would like to train further in the ways of the Light.', 'I would like to train further in the ways of the Light.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5300, 'Violence! Property damage! None shall pass!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5301, 'You must leave the bar!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5302, 'You have outworn your welcome!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5303, 'NO SERVICE!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5304, 'What want from Raze??', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5305, '', 'Hello, $n. Should you require assistance in completing your mission, you have only to ask. My guardian, Raze, can assist you in defeating the shadowsilk poachers. Use this Staff of Command to command Raze in battle. He is trained to ONLY attack shadowsilk poachers.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5306, 'I need help in defeating the shadowsilk poachers.', 'I need help in defeating the shadowsilk poachers.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5307, 'Drink up! There\'s more where that came from!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5308, 'Enjoy! You won\'t find better ale anywhere!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5309, 'Have you tried the Dark Iron Ale? It\'s the best!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5310, 'Try the boar! It\'s my new recipe!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5311, 'Thank you for clearing out those foolish senators. Now prepare to meet your doom at the hands of Ragnaros\' most powerful servant.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5312, 'Your efforts are utterly pointless, fools! You will never be able to defeat me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5313, 'They were just getting in the way anyways.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5314, 'Ha! You can\'t even begin to imagine the futility of your efforts.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5315, 'Is that the best you can do? Do you really expect that you could defeat someone as awe inspiring as me?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5316, 'Auction House', 'Auction House', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5317, 'Where is the Auction House? Why it\'s right by the gates of Ironforge as you enter our fine city. Ye\'ll find it just across from The Vault.', 'Where is the Auction House? Why it\'s right by the gates of Ironforge as you enter our fine city. Ye\'ll find it just across from The Vault.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5318, 'Anu therador mali $c. Where can I guide you to?', 'Anu therador mali $c. Where can I guide you to?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5319, 'How may I aid you in your journey?', 'How may I aid you in your journey?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5320, 'Do you require guidance to your destination?', 'Do you require guidance to your destination?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5321, 'What direction can I point you in?', 'What direction can I point you in?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5324, 'Can you help me with some directions?', 'Can you help me with some directions?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5325, 'Of all the wondrous places within Darnassus, which would you have me direct you to?', 'Of all the wondrous places within Darnassus, which would you have me direct you to?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5326, 'The Bank', 'The Bank', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5328, '', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5329, 'You may find storage for your worldly goods at the bank of Darnassus, which lies in the center of our wondrous city.', 'You may find storage for your worldly goods at the bank of Darnassus, which lies in the center of our wondrous city.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5330, 'Hippogryph Master', 'Hippogryph Master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5331, 'Vesprystus\' hippogryphs will bear you swiftly to your destination. Seek him out in Rut\'theran Village, through the portal which lies in the western section of Darnassus.', 'Vesprystus\' hippogryphs will bear you swiftly to your destination. Seek him out in Rut\'theran Village, through the portal which lies in the western section of Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5332, 'Guild Master', 'Guild Master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5333, 'If you desire to form a fellowship then it is the guild master, Lysheana, that you must see. You will find her in the Craftsmen\'s Terrace in the northeastern section of Darnassus.', 'If you desire to form a fellowship then it is the guild master, Lysheana, that you must see. You will find her in the Craftsmen\'s Terrace in the northeastern section of Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5334, 'The Inn', 'The Inn', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5335, 'Though I find the cool grass which covers Teldrassil as good a bed as any, the beds which Saelienne keeps at the Inn in Craftsman\'s Terrace are quite suitable for those with a taste for luxury.', 'Though I find the cool grass which covers Teldrassil as good a bed as any, the beds which Saelienne keeps at the Inn in Craftsman\'s Terrace are quite suitable for those with a taste for luxury.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5336, 'Mailbox', 'Mailbox', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5337, 'There is a mailbox outside of the Bank which you\'ll find in the center of Darnassus.', 'There is a mailbox outside of the Bank which you\'ll find in the center of Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5338, 'A class trainer', 'A class trainer', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5339, 'Here in Darnassus you will find skilled trainers who have perfected the skills of their chosen class through ages of training and dedication. I will guide you to one who can become your mentor, you have but to name your chosen path.', 'Here in Darnassus you will find skilled trainers who have perfected the skills of their chosen class through ages of training and dedication. I will guide you to one who can become your mentor, you have but to name your chosen path.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5340, 'Hunter', 'Hunter', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5341, 'You are fortunate to have come to Darnassus where you can find such an accomplished Hunter as Jocaste among our trainers. I encourage you to visit her over in the Cenarion Enclave and seek her tutelage.', 'You are fortunate to have come to Darnassus where you can find such an accomplished Hunter as Jocaste among our trainers. I encourage you to visit her over in the Cenarion Enclave and seek her tutelage.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5342, 'Druid', 'Druid', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5343, 'In grizzly times a druid can be a dear friend to those around them. Ask any of the druids in the Cenarion Enclave and they will tell you the same.', 'In grizzly times a druid can be a dear friend to those around them. Ask any of the druids in the Cenarion Enclave and they will tell you the same.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5344, 'Priest', 'Priest', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5345, 'The Temple of the Moon is home to the priests of Elune. There you will find Jandria and Lariia, who will provide you with further guidance in the path of priesthood.', 'The Temple of the Moon is home to the priests of Elune. There you will find Jandria and Lariia, who will provide you with further guidance in the path of priesthood.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5346, 'The power of Elune compels you! The power of Elune compels you! \n\nGo now! Seek out Jandria in the central hall of the Temple of the Moon in the southern section of Darnassus!', 'The power of Elune compels you! The power of Elune compels you! \n\nGo now! Seek out Jandria in the central hall of the Temple of the Moon in the southern section of Darnassus!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5347, 'Rogue', 'Rogue', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5348, 'It is Syurna that you seek if you are to follow the rogue\'s path. She dwells in the Cenarion Enclave down the spiraling path within the western most tree.', 'It is Syurna that you seek if you are to follow the rogue\'s path. She dwells in the Cenarion Enclave down the spiraling path within the western most tree.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5349, 'There are those that attempt to follow the path of the Rogue, but only the night elves are truly embraced by the night. If you seek to walk the shaded path then find Syurna in the Cenarion Enclave.', 'There are those that attempt to follow the path of the Rogue, but only the night elves are truly embraced by the night. If you seek to walk the shaded path then find Syurna in the Cenarion Enclave.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5350, 'Warrior', 'Warrior', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5351, 'You will find Sildinair at the Warrior\'s Terrace among the guards, her keen eyes scanning the forest for enemies that might dare to enter our fair city. ', 'You will find Sildinair at the Warrior\'s Terrace among the guards, her keen eyes scanning the forest for enemies that might dare to enter our fair city. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5352, 'Profession Trainer', 'Profession Trainer', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5353, 'Here among the craftsmen of Darnassus, there is a millenium of knowledge for the benefit of your chosen craft. Now tell me, which craft do you find the most intriguing?', 'Here among the craftsmen of Darnassus, there is a millenium of knowledge for the benefit of your chosen craft. Now tell me, which craft do you find the most intriguing?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5354, 'Alchemy', 'Alchemy', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5355, 'If you truly have the desire to learn this profession, then I would point you in the direction of Ainethil, a skilled alchemist at the Craftsmen\'s Terrace in northern Darnassus.', 'If you truly have the desire to learn this profession, then I would point you in the direction of Ainethil, a skilled alchemist at the Craftsmen\'s Terrace in northern Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5356, 'Cooking', 'Cooking', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5357, 'Alegorn is a masterful chef who has had centuries with which to perfect his cooking. You should consider yourself blessed should he decide to teach you. If you believe you are worthy, you can find him in the Craftsmen\'s Terrace.', 'Alegorn is a masterful chef who has had centuries with which to perfect his cooking. You should consider yourself blessed should he decide to teach you. If you believe you are worthy, you can find him in the Craftsmen\'s Terrace.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5358, 'The dishes that Alegorn whips up are to die for. He\'s always busy preparing new delicacies over in the Craftsman\'s Terrace. Be sure to try his Cenarion Green recipe if he has any left. \n\nNow, if you don\'t mind, I must investigate this rash of missing deer. ', 'The dishes that Alegorn whips up are to die for. He\'s always busy preparing new delicacies over in the Craftsman\'s Terrace. Be sure to try his Cenarion Green recipe if he has any left. \n\nNow, if you don\'t mind, I must investigate this rash of missing deer. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5360, 'Class Trainer', 'Class Trainer', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5362, 'Alchemy', 'Alchemy', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5363, 'Enchanting', 'Enchanting', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5364, 'Because the blade can never be too sharp nor the armor too tough a talented enchanter will always be needed. Such an enchanter is Taladan, who can show you how to properly weave enchantments to improve your weapons and armor. Seek him out in the Craftsmen\'s Terrace.', 'Because the blade can never be too sharp nor the armor too tough a talented enchanter will always be needed. Such an enchanter is Taladan, who can show you how to properly weave enchantments to improve your weapons and armor. Seek him out in the Craftsmen\'s Terrace.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5365, 'Och! Ye should be careful what ye get enchanted $glad : lass;. I had me mug enchanted so it wouldn\'t spill a drop, but now I can\'t drink from it! Such a tragedy. \n\nYou be sure to heed me advice when ye see Gimble. He\'ll be in his shop right by the Gryphon Master at The Great Forge.', 'Och! Ye should be careful what ye get enchanted $glad : lass;. I had me mug enchanted so it wouldn\'t spill a drop, but now I can\'t drink from it! Such a tragedy. \n\nYou be sure to heed me advice when ye see Gimble. He\'ll be in his shop right by the Gryphon Master at The Great Forge.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5366, 'First Aid', 'First Aid', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5367, 'When you have not a healer to tend to your wounds, the knowledge of First Aid can be a lifesaver. I would consult Dannelor over at the Craftsmen\'s Terrace for instruction on this skill.', 'When you have not a healer to tend to your wounds, the knowledge of First Aid can be a lifesaver. I would consult Dannelor over at the Craftsmen\'s Terrace for instruction on this skill.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5368, 'Fishing', 'Fishing', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5369, 'A time consuming, yet rewarding skill. Speak with Astaia in the Tradesman\'s Terrace and maybe you\'ll learn something useful.', 'A time consuming, yet rewarding skill. Speak with Astaia in the Tradesman\'s Terrace and maybe you\'ll learn something useful.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5370, 'Herbalism', 'Herbalism', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5371, 'Leatherworking', 'Leatherworking', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5372, 'The skill with which Telonis crafts armor is beyond compare. You should be grateful that he does not charge more for the knowledge he passes to others. Go now and seek him out in the Craftsmen\'s Terrace in the northeast section of Darnassus.', 'The skill with which Telonis crafts armor is beyond compare. You should be grateful that he does not charge more for the knowledge he passes to others. Go now and seek him out in the Craftsmen\'s Terrace in the northeast section of Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5373, 'We plant the vegetables. Our vegetables are eaten by the rabbit. The rabbit is devoured by the wolf. The wolf is slain by us. We take the meat and hide from the wolf\'s corpse. It\'s that Magic Circle of life, or whatever you call it. \n\nNow go see Telonis in the Cenarion Enclave to get started and let me return to my duties.', 'We plant the vegetables. Our vegetables are eaten by the rabbit. The rabbit is devoured by the wolf. The wolf is slain by us. We take the meat and hide from the wolf\'s corpse. It\'s that Magic Circle of life, or whatever you call it. \n\nNow go see Telonis in the Cenarion Enclave to get started and let me return to my duties.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5374, 'Lockpicking', 'Lockpicking', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5375, '\"A skill for those who wish to overcome obstacles.\" I believe that is how Faelyssa describes her skill set. \n\nIf you believe as she does then find your way to the Cenarion Enclave in the northern section of Darnassus where she conducts her class.', '\"A skill for those who wish to overcome obstacles.\" I believe that is how Faelyssa describes her skill set. \n\nIf you believe as she does then find your way to the Cenarion Enclave in the northern section of Darnassus where she conducts her class.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5376, 'Skinning', 'Skinning', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5377, 'In death, the animals of the world bestow both life and protection unto us. If you feel that you can offer the appropriate respect for their sacrifice, then the skinner\'s profession may suit you. Speak with Eladriel in the Craftsmen\'s Terrace in northern Darnassus to learn more.', 'In death, the animals of the world bestow both life and protection unto us. If you feel that you can offer the appropriate respect for their sacrifice, then the skinner\'s profession may suit you. Speak with Eladriel in the Craftsman\'s Terrace in northern Darnassus to learn more.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5378, 'Survival', 'Survival', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5379, 'If you need help surviving in the woods, you had best not stray far from the city walls. However if you do plan to stray, then you had better have a talk with Malygen in the Cenarion Enclave so that you might learn to set a proper campfire.', 'If you need help surviving in the woods, you had best not stray far from the city walls. However if you do plan to stray, then you had better have a talk with Malygen in the Cenarion Enclave so that you might learn to set a proper campfire.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5380, 'Tailoring', 'Tailoring', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5381, 'You would certainly be looking for Me\'Lynn if you have any hope of learning to be a tailor. She resides at the Craftsmen\'s Terrace in northeastern Darnassus.', 'You would certainly be looking for Me\'Lynn if you have any hope of learning to be a tailor. She resides at the Craftsmen\'s Terrace in northeastern Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5382, 'Tracking', 'Tracking', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5383, 'Not a creature in the forest can elude Corand, such is his skill at tracking. You would do well to listen to his teachings if you wish to be even half as successful. \n\nI will relieve you of the burden of tracking him and mark his location on your map, there in the Cenarion Enclave.', 'Not a creature in the forest can elude Corand, such is his skill at tracking. You would do well to listen to his teachings if you wish to be even half as successful. \n\nI will relieve you of the burden of tracking him and mark his location on your map, there in the Cenarion Enclave.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5384, 'What plants are in Felwood that might be corrupted?', 'What plants are in Felwood that might be corrupted?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5385, '', 'I have observed four different kinds of plants that have been affected by the corruption: the songflower, the windblossom, the whipper root, and the night dragon plant.  The songflower and windblossom are common plants in Felwood, while the whipper root and night dragon are seen less frequently.  All are a vital part of Felwood\'s ecosystem.', 0, 0, 7, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5386, '', 'I have observed four different kinds of plants that have been affected by the corruption: the songflower, the windblossom, the whipper root, and the night dragon plant.  The songflower and windblossom are common plants in Felwood, while the whipper root and night dragon are seen less frequently.  All are a vital part of Felwood\'s ecosystem.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5387, 'This was the site of a great battle. Many great battles.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5388, 'Speak with Regthar. He is wise and can help you find what you seek.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5389, 'HALT! There were...reports...of a...disturbance.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5390, 'Everyone you meet has something that they can teach you. Is that why you have come to see me?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5391, 'Well, I\'m not sure how far I\'ll make it in this state... I\'m feeling kind of faint...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5392, 'Remember, if I faint again, the water that Spraggle gave you will revive me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5393, '%s faints and falls onto the ground.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5394, 'Uuuuuuggggghhhhh....', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5395, 'I\'m not feeling so well...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5396, 'The heat... I can\'t take it...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5397, 'Maybe... you could carry me?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5398, 'Yes, I must go on.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5399, 'I am feeling a little better now, thank you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5400, 'Where... Where am I?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5401, 'How am I feeling? Quite soaked, thank you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5402, 'Spraggle! I didn\'t think I\'d make it back!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5403, 'Oh... I\'m feeling faint...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5404, 'Ugh.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5405, '', 'Ringo! You\'re okay!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5406, '', 'Ringo? Wake up! Don\'t worry, I\'ll take care of you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5407, 'Ach! Thank you so much, $n! I\'m free! Finally!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5408, 'You take care of yourself! I\'m gettin\' out o\' here.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5409, 'Hello? ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5410, 'Um... sorry to bother you, but could I see Goodsteel\'s ledger again... if you\'re not using it.', 'Um... sorry to bother you, but could I see Goodsteel\'s ledger again... if you\'re not using it.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5411, 'Yeah, no problem, $n! Sorry... thought you were done with it already.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5412, '', 'You\'ll have to give me a moment, $c, the naga are attacking us right now. I\'ll speak to you as soon as the attack is done!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5413, 'Tell me a story, John.', 'Tell me a story, John.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5414, 'Aright *hic*... Say, you look familiar *hic*. Have I ever told you about the time me and Windsor fought off thir, er... a thous... a million orcs?$B$BOh settle down friend, this is a good one!', '', 0, 0, 0, 6, 5, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5415, 'Let me grab the popped corn kernels, sire.', 'Let me grab the popped corn kernels, sire.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5416, 'I present to you the Libram of Constitution.', 'I present to you the Libram of Constitution.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5417, 'I present to you the Libram of Tenacity.', 'I present to you the Libram of Tenacity.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5418, 'I present to you the Libram of Resilience.', 'I present to you the Libram of Resilience.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5419, 'I present to you the Libram of Voracity.', 'I present to you the Libram of Voracity.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5420, '%s bursts into the hatchery once you apply the lure to the crystal!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5421, 'The Orgrimmar Auction House is in the Valley of Strength. Look for it on the east side next to Boomstick Imports.', 'The Orgrimmar Auction House is in the Valley of Strength. Look for it on the east side next to Boomstick Imports.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5422, 'Your voice is hoarse from yelling huh? Find the Orgrimmar Auction House in the Valley of Strength.', 'Your voice is hoarse from yelling huh? Find the Orgrimmar Auction House in the Valley of Strength.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5423, 'The auction house', 'The auction house', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5424, 'Welcome to the Bank of Darnassus. We offer financial accounts and safety deposit boxes for valuable items. Do you already have an account with us $g sir : ma\'am;?', 'Welcome to the Bank of Darnassus. We offer financial accounts and safety deposit boxes for valuable items. Do you already have an account with us $g sir : ma\'am;?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5425, 'Amazing, aren\'t they? All these artifacts and remains.$B$BI\'m not sure what I\'m more impressed by, their size, or the stories that go along with them. There\'s so much to study and learn. And the connections we can make...$B$BDid you know rumor has it that many races have the same heritage as Night elves?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5426, 'Image dissipation in sixty seconds.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5427, '%s begins to cast a powerful spell.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5428, '%s transforms the items into a totem.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5429, '', '%s is visibly shaken by the death of Emperor Thaurissan.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5430, 'Emperor Thaurissan does not wish to be disturbed! Turn back now or face your doom, weak mortals!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5431, 'Hail to the king, baby!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5432, '', 'Hello there, my name is Petra.  To my right is my kodo, Dadanga.  We\'re with Marshal Expeditions - been so for a few years now, ever since Dadanga was a babe.$B$BIf you have any lit torches or any open sources of flame, I\'d like to ask you to keep them stashed if you could.  Dadanga dislikes smoke.', 0, 0, 0, 3, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5433, 'For the love of all that is holy and dear, please tell me that you\'ve come to deliver us from this forsaken land. Though I\'m with Marshal Expeditions, I would give you the gold teeth from my mouth to climb inside your backpack and leave this place.', '', 0, 0, 0, 20, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5434, '', 'Oh, hello there!  I\'m Hol\'anyee, the chief scientist and biologist of Marshal Expeditions.  My brother and I, along with our companions, travel Azeroth exploring the wild and untamed majesty.  It is exciting... but, um... I think we got ourselves into a little too much excitement this time!', 0, 0, 0, 3, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5435, 'Hello stranger, and welcome to the untamed majesty of Un\'Goro Crater!$B$BMy name is Williden Marshal. My sister, our companions, and I make up Marshal Expeditions - scientists and explorers of Azeroth\'s wild and great unknown.$B$BWe\'ve hit a bit of a rough patch here, and we\'re looking for some help if you have the time!', '', 0, 0, 0, 3, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5436, '', 'This is where things get interesting.  My plan is to follow the mountail wall, keeping it to our left.  That way, we won\'t get lost in the jungle and we\'ll cut down on the hostiles.  You ready?', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5437, '', 'Here\'s the eastern mountain wall!  I\'m cutting towards the south now; try and keep up.  We\'re not out of the thick of it yet!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5439, '', 'Here\'s the path out of Un\'Goro!  We made it!  All we have to worry about is the Tanaris desert now.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5440, '', 'Here it is -- the Tanaris desert.  Funny how an inhospitable place like this looks better than a lush jungle once you\'ve been trapped in it forever.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5441, 'You have been sentenced to death for crimes against the Dark Iron nation!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5442, 'The Sons of Thaurissan shall watch you perish in the Ring of the Law!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5443, 'Unleash the fury and let it be done!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5444, 'Haha! I bet you thought you were done!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5445, 'But your real punishment lies ahead.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5446, 'Good riddance!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5447, 'Incredible! Amazing! I don\'t even know what this means!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5448, 'Look at these samples! What does it mean? What does it mean? I\'m going to need time, time to look at all this information. And I\'ll need more information. Information I can\'t get... damn! Think, Laris, think!$B$BWhat are you going to do? What?!$B$BThere has to be a solution!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5449, 'There must be someplace on Azeroth that we can find oozes uncorrupted by the Scourge or anything else. But where?!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5450, 'Is there a difference between ooze and slime?', 'Is there a difference between ooze and slime?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5451, 'No, not that I can tell. It seems to be mostly based on region. Some races have labled them slimes, some oozes. It seems to be preference mostly.$B$BEvery time I\'ve encountered ooze, sludge, or slime they have basically been the same creatures. The differences seem to be based on their environment, which is why we need a pure sample.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5452, 'What do you mean by \"pure?\"', 'What do you mean by \"pure?\"', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5453, 'A pure sample would be one that\'s uncorrupted by unintentional influences. We need to find the creatures in the most basic, normal environment we can.$B$BThat means somewhere devoid of civilization or the effects of the dominant races of Azeroth. Someplace... untouched.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5454, 'Are there any areas you can think of that would be so untouched?', 'Are there any areas you can think of that would be so untouched?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5455, 'It\'ll definitely not be anywhere in the Eastern Kingdoms, that\'s for sure.$B$BIt\'ll have to be somwhere on Kalimdor, but still far enough away from the Horde and other races. Someplace unaffected by the Sundering--that means no where near the Night elves\' civilization either.$B$BI would guess it\'d be someplace protected also... like a valley, or a crater. I don\'t know. I\'m just guessing now. I\'m sorry I couldn\'t help you more.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5456, 'I bid you welcome, $c. Rare do we have visitors down to these levels of the Apothecarium... unless of course there\'s something someone wants... or needs.$B$B<Chemist Coely laughs.>$B$BHow can I help you today, hmm?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5457, 'Come to the aid of the Throne!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5458, '%s groans.', '%s groans.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5459, '%s weeps pitifully.', '%s weeps pitifully.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5460, '%s begs to be put out of his misery.', '%s begs to be put out of her misery.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5461, '', 'Well, we\'re not getting any closer just standing here.  Come on Dadanga!  Yah!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5462, '', 'Step lively Dadanga!  Come on, girl!  The quicker we get through here, the happier we\'ll both be.', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5463, '$n, you have disturbed me from my rest!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5464, 'Leave me now in peace. I must rest.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5465, 'Who dares awaken Aquementas?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5466, '', 'Get away from my kodo, you freak of nature!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5467, '', 'Dadanga!  Attack!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5468, '', 'That has to be the biggest $n I have ever seen... and it\'s coming right for us!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5469, '', 'Watch out for the $n!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5470, 'The gathering and knowledge of herbs is a specialty of Firodren Mooncaller. Seek him out in the Temple Gardens in the southwestern area of Darnassus.', 'The gathering and knowledge of herbs is a specialty of Firodren Mooncaller. Seek him out in the Temple Gardens in the southwestern area of Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5471, 'A budding herbalist are we? Then you should visit with Firodren Mooncaller over in the Temple Gardens. He may have some tricks of the trade to impart with you.', 'A budding herbalist are we? Then you should visit with Firodren Mooncaller over in the Temple Gardens. He may have some tricks of the trade to impart with you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5472, 'Why you would want to leave these sunny shores and crystal blue waters I have no idea. It\'s like paradise down here. But don\'t tell my brother Zapetta that, he\'s bitter enough already about getting stuck up there in Tirisfal all day long. This Zeppelin here drops off at Orgrimmar so you shouldn\'t have to worry about having to listen to him whine. But if you are in Tirisfal anytime soon, heed my warning and don\'t get him started.', 'Why you would want to leave these sunny shores and crystal blue waters I have no idea. It\'s like paradise down here. But don\'t tell my brother Zapetta that, he\'s bitter enough already about getting stuck up there in Tirisfal all day long. This Zeppelin here drops off at Orgrimmar so you shouldn\'t have to worry about having to listen to him whine. But if you are in Tirisfal anytime soon, heed my warning and don\'t get him started.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5473, 'I sense the taint of corruption upon this $n. Help me destroy it!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5474, 'This creature suffers from the effects of the fel... We must end its misery.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5475, 'This $n has been driven mad from the corruption! ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5476, 'The herbs found in nature can be a powerful ally when combined with respect. You will want to seek out Bena at her alchemy shop on the middle rise to further your training.', 'The herbs found in nature can be a powerful ally when combined with respect. You will want to seek out Bena at her alchemy shop on the middle rise to further your training.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5477, 'You may shed yourself of your worldly possessions at the Thunder Bluff Bank on the lower rise.', 'You may shed yourself of your worldly possessions at the Thunder Bluff Bank on the lower rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5478, 'Metal, as with all things, should be used in balance with nature. Karn Stonehoof has a smithy setup near the pond on the lower rise. Seek out his wisdom.', 'Metal, as with all things, should be used in balance with nature. Karn Stonehoof has a smithy setup near the pond on the lower rise. Seek out his wisdom.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5479, 'You honor the spirits of the animals you must slay by not letting them go to waste. Aska Mistrunner will show you how. She has a kitchen on the high rise.', 'You honor the spirits of the animals you must slay by not letting them go to waste. Aska Mistrunner will show you how. She has a kitchen on the high rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5480, 'Greetings honored one. Turak Runetotem and the other druids have been expecting you at the Hall of Elders on Elder Rise.', 'Greetings honored one. Turak Runetotem and the other druids have been expecting you at the Hall of Elders on Elder Rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5481, 'The profession of Enchanting displays the cycle of destruction and creation that is at the heart of nature. Teg at the Dawnstrider Enchanters on the middle rise will show you how.', 'The profession of Enchanting displays the cycle of destruction and creation that is at the heart of nature. Teg at the Dawnstrider Enchanters on the middle rise will show you how.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5482, 'Life is a struggle, and the saying that which does not kill you makes you stronger is not always true. Sometimes wounds act slowly, which is why we have bandages. Pand Stonebinder of the Spirit Rise can teach you the art of First Aid.', 'Life is a struggle, and the saying that which does not kill you makes you stronger is not always true. Sometimes wounds act slowly, which is why we have bandages. Pand Stonebinder of the Spirit Rise can teach you the art of First Aid.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5483, 'Kah Mistrunner at Mountaintop Bait & Tackle on the high rise will show you the way to find inner peace and serenity while fishing for your next meal.', 'Kah Mistrunner at Mountaintop Bait & Tackle on the high rise will show you the way to find inner peace and serenity while fishing for your next meal.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5484, 'A fellowship of kindred spirits can be a powerful force. Krumn will help you to achieve this. You will find him at the Thunder Bluff Civic Information center on the lower rise next to the lifts.', 'A fellowship of kindred spirits can be a powerful force. Krumn will help you to achieve this. You will find him at the Thunder Bluff Civic Information center on the lower rise next to the lifts.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5485, 'Nature provides all that we need. Speak to Komin Winterhoof at Holistic Herbalism on the middle rise.', 'Nature provides all that we need. Speak to Komin Winterhoof at Holistic Herbalism on the middle rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5486, 'Any friend of the animals is a friend of mine. The Hunter\'s Hall is what you seek. It sits atop the Hunter Rise.', 'Any friend of the animals is a friend of mine. The Hunter\'s Hall is what you seek. It sits atop the Hunter Rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5487, 'There is a symmetry in the idea of the hide of your enemy providing you with protection. To further your leatherworking skills look for Una inside the Thunder Bluff Armorers on the middle rise.', 'There is a symmetry in the idea of the hide of your enemy providing you with protection. To further your leatherworking skills look for Una inside the Thunder Bluff Armorers on the middle rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5488, 'Very sad to think what has befallen those poor undead souls. You can find Archmage Shymm reflecting near the Pools of Vision under the Spirit Rise.', 'Very sad to think what has befallen those poor undead souls. You can find Archmage Shymm reflecting near the Pools of Vision under the Spirit Rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5489, 'The Earth Mother holds many secrets and Brek Stonehoof can help you to discover some of them. You will find him on the lower rise at Stonehoof Geology.', 'The Earth Mother holds many secrets and Brek Stonehoof can help you to discover some of them. You will find him on the lower rise at Stonehoof Geology.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5490, 'Despite their tragedy the Undead have come all this way to aid us. I only hope that we can be as helpful to them. Seek out Father Cobb near the Pools of Vision under the Spirit Rise.', 'Despite their tragedy the Undead have come all this way to aid us. I only hope that we can be as helpful to them. Seek out Father Cobb near the Pools of Vision under the Spirit Rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5491, 'The Earth Mother calls and you answer. You will find the Hall of Spirits on the Spirit Rise.', 'The Earth Mother calls and you answer. You will find the Hall of Spirits on the Spirit Rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5492, 'Every scrap used makes us less wasteful and brings us closer to the Earth Mother. Mooranta can show you the way. Seek her just outside the Thunder Bluff Armorers on the middle rise.', 'Every scrap used makes us less wasteful and brings us closer to the Earth Mother. Mooranta can show you the way. Seek her just outside the Thunder Bluff Armorers on the middle rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5493, 'You, in harmony with nature, and only your skills to survive. This is the way that the Earth Mother intended it to be. You will want to find Bale on the Hunter Rise.', 'You, in harmony with nature, and only your skills to survive. This is the way that the Earth Mother intended it to be. You will want to find Bale on the Hunter Rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5494, 'Clothing the tribe is as important a job as any other. You should look for Tepa inside Thunder Bluff Armorers. She has a natural way with the needle.', 'Clothing the tribe is as important a job as any other. You should look for Tepa inside Thunder Bluff Armorers. She has a natural way with the needle.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5495, 'Ah, the true sign of the hunter! To stalk one\'s prey in the never-ending game of life. Look to Drayl on the Hunter Rise for your answers.', 'Ah, the true sign of the hunter! To stalk one\'s prey in the never-ending game of life. Look to Drayl on the Hunter Rise for your answers.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5496, 'Strong of body and spirit. There can be no stronger combination! Find your destiny with Sark Ragetotem in the Hunter\'s Hall.', 'Strong of body and spirit. There can be no stronger combination! Find your destiny with Sark Ragetotem in the Hunter\'s Hall.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5497, 'Ah, to soar with the great birds of the sky. Seek out Tal at the top of the Winder Rider Roost. He will steer you toward where you\'re going.', 'Ah, to soar with the great birds of the sky. Seek out Tal at the top of the Winder Rider Roost. He will steer you toward where you\'re going.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5498, 'Look for Innkeeper Pala in the building just south of the Thunder Bluff Bank on the lower rise.', 'Look for Innkeeper Pala in the building just south of the Thunder Bluff Bank on the lower rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5499, 'Innkeeper Pala will help you rest your body and spirit. You can find her in the building just south of the bank on the lower rise.', 'Innkeeper Pala will help you rest your body and spirit. You can find her in the building just south of the bank on the lower rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5500, 'You can find a mailbox just outside the Thunder Bluff Bank on the lower rise.', 'You can find a mailbox just outside the Thunder Bluff Bank on the lower rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5501, 'I believe I noticed a new shipment of mail being delivered not too long ago. You\'ll find the mailbox just outside the Thunder Bluff Bank on the lower rise.', 'I believe I noticed a new shipment of mail being delivered not too long ago. You\'ll find the mailbox just outside the Thunder Bluff Bank on the lower rise.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5502, 'It is found on the lowest section of Thunder Bluff. Search near the bank. It is not far from there.', 'It is found on the lowest section of Thunder Bluff. Search near the bank. It is not far from there.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5503, 'The auction house can be found on the central spire of Thunder Bluff. Search near the bottom of the Windrider\'s Tower. May your ancestors watch over you.', 'The auction house can be found on the central spire of Thunder Bluff. Search near the bottom of the Windrider\'s Tower. May your ancestors watch over you.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5504, 'This may come as a shock to you, but business hasn\'t been all that good as of late...', '', 0, 0, 0, 18, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5505, '', 'I welcome you to Theramore, $n.$B$BI apologize for being abrupt and not having a lot of time to exchange pleasantries, but my city must come first. Too many things threaten our way of life and the sanctity of the Alliance. I must stay ever vigilant against forces outside of our control if I\'m to ensure my people remain safe and happy.$B$BPerhaps we\'ll have more time to speak later.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5506, '', 'Hendel is in our custody now, $n, thanks to you.$B$BAlthough the questioning goes slowly, I assure you, he will tell us everything he knows.$B$BFor now, please be patient. I will contact you immediately if we have need of your assistance.$B$BUntil then, please, enjoy Theramore and I invite you to stay for as long as you like within our city\'s walls as a friend of the Alliance.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5508, '', 'If you\'re ready, then Dadanga and I are ready.  Let\'s go!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5509, 'Yes $gbrother : sister;, how may I assist you?', 'Yes $gbrother : sister;, how may I assist you?', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5511, '', 'All right then... I know the way to and from Gadgetzan.  What I need for you to do is to keep the beasts off of us.  I\'ve trained Dadanga how to defend herself pretty well, but we can\'t handle the overgrown madness out there for long.  Let me know when you\'re ready to go, $n.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5512, 'What do you think of Dadanga?\n', 'What do you think of Dadanga?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5513, 'The inn', 'The inn', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5514, 'The mailbox', 'The mailbox', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5515, 'The auction house', 'The auction house', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5516, 'Just outside the city\'s main gate is a signpost. It will point you toward the zeppelin tower. Speak to Zeppelin Master Frezza at the top of the tower.', 'Just outside the city\'s main gate is a signpost. It will point you toward the zeppelin tower. Speak to Zeppelin Master Frezza at the top of the tower.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5517, 'Look for the Zeppelin Landing Tower outside of the city to the southeast.', 'Look for the Zeppelin Landing Tower outside of the city to the southeast.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5518, 'The zeppelin master', 'The zeppelin master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5519, 'My brother and I have dedicated ourselves to Sylvanas. She is our beacon of light when times were darkest. We sacrifice ourselves day in and day out to ensure our kind overcome the destiny that\'s chosen us.$B$BWe will not remain weak. We will take that which is owed to us. We will return tenfold, the pain that was done to us. And we shall do so with smiles on our faces.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5520, 'Lady Sylvanas has ordered the investigation of some of the natural phenomena of Azeroth. She feels there is something great to be learned from its secretive past.$B$BI know not how she learned such things, but I trust her completely, so I will do my best.$B$BI am currently investigating slimes, oozes and such. The creatures all seem to be similar in make up, but greatly affected by their environment.$B$BI could use your help in gathering samples of the creatures..', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5521, '', '%s becomes enraged from seeing a cub die!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5522, 'Three cheers for $n -- Admiral o\' the Bloodsail Bucaneer Navy!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5523, 'Hip hip... arrrrr...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5524, '<Volcor doesn\'t look at you at all as you get close to him. He seems to be concentrating on keeping his bandages from falling off. After a moment, he looks up at you, a little startled.>$B$BOh, sorry, $c. Now\'s not a good time. I hope you understand.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5525, 'Well, the way I see it, $n, we have two options to consider for leaving this cave.$B$BWe can make our way stealthfully--I use the cloak you\'ve given me, and you\'d be left to your own devices, or, and this may be a harder option considering my wounds, we fight our way out back to back.$B$BI\'ll let you decide since you know your strengths better than I.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5526, 'Greetings $g lad : lass;, if you\'re looking for the best deals you\'ll find outside of Ironforge itself, then you\'ve come to the right place.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5527, '', 'Have you seen the befouled water elementals in our lake, $n? It is a sorry sight, but I do not know where they are coming from.$B$BYour name has been mentioned in conversations past, perhaps you could help me with this problem.$B$BThe first thing we must learn is who is in control of these elementals.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5528, 'Hi there. I\'m Gubber Blump.', '', 0, 0, 0, 3, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5529, 'Gubber, I don\'t know how to fish.\n', 'Gubber, I don\'t know how to fish.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5530, 'Gubber, I don\'t have a fishing pole.', 'Gubber, I don\'t have a fishing pole.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5531, 'Fishing is real easy. Just take a pole with a line on it, maybe put some bait on it, and cast it out in the water. Sure enough, them fish will start biting.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5532, 'I got some extras with me; if you pay me back what I paid for them, you can have one. I got some bait too.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5533, '...pan fried, deep fried, and stir fried...', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5534, 'Do you need assistance?', 'Do you need assistance?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5535, '', 'Watch my back, this could get real messy.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5536, 'The liquids and mixtures all bubble and spasm as you approach. Next to the equipment lies a large pair of tongs that look as though they would be adequate for dropping in samples of ooze.$B$BThere is safety gear present, but it appears to be dusty and well-ignored.$B$BYou don\'t think touching anything would be wise.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5537, '', 'The plans are in there!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5538, 'Intruders are destroying our eggs! Stop!!', 'Intruders are destroying our eggs! Stop!!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5539, 'SQUAWK!! *cough* *cough* I need to quit smoking!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5540, 'Ask Kibler about the treasure! SQUAWK!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5541, 'Bijou lost! SQUAWK! *cough*', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5542, 'Bijou knows where treasures are hidden.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5543, 'Opus wants some Cuergo\'s Gold... with worm.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5544, 'They all go to the Spire but none ever come back... just like Bijou.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5545, 'Kibler is being investigated by G.E.T.A.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5546, 'Dragons controlling orcs!? SQUAWK!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5547, 'I am here! Now, puny little worms, you will pay for your intrusion!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5548, '%s lets loose a guttural growl as her body collapses. A horrifying howl can be heard echoing through the halls of Blackrock Spire. Something is very, very angry.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5549, 'Hello, $n. Welcome to Refuge Pointe.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5550, 'Don\'t mind Skuerto, $n. He doesn\'t mean to be rude, he\'s just terribly busy. While we wait for him to finish scouting, why don\'t we work on something more productive?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5551, '', 'Hi $n!$B$BI have to work on my devices, but eventually... I\'m going to need someone to get me dragon eggs!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5552, '', 'The Burning Steppes is a dangerous place, but scholars like me will do almost anything to advance our knowledge.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5553, 'I\'m going back to the Rookery, Tinkee. I need an Eggscilliscope!', 'I\'m going back to the Rookery, Tinkee. I need an Eggscilliscope!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5554, 'May your skills in smelting dark iron serve you well, $n. But do not let your knowledge corrupt you... as it corrupted us.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5555, 'At last - one among you is worthy of my knowledge. Worry not - I have probed your thoughts, no harm will come to you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5556, 'Listen carefully, for the lord of Blackrock will surely destroy you, should you not be prepared.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5557, '', 'Kar Aman, $n. You look to be a strong $c--you would do well to mind yourself in this area. Touching things you know nothing about, or showing disrespect to your elders is just cause for punishment.$B$BYou may not respect me or my companions, but you will learn to respect our power if you prove foolish enough.$B$BNow, enough with the pleasantries, was there something I can do for you?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5558, '', 'Ah, a fellow warlock who has proven $ghis:her; worth to some degree.$B$BI am pleased to meet your acquaintance. My name is Menara Voidrender... and not without just cause.$B$BYou seem powerful and well-learned enough that other tools... more powerful tools... would be welcome and not foolishly imparted. If this is true, then speak to me further. If not, well then, you know your way  back to the docks and off into the ocean.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5559, 'Humans are usually vunerable on their left side between the 4th and 5th rib. Let\'s practice striking at this area like so...', 'Humans are usually vunerable on their left side between the 4th and 5th rib. Let\'s practice striking at this area like so...', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5560, 'Now, you try.', 'Now, you try.', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5561, 'Night Elves like to stand off on one leg while casting. Strike at that leg like so...', 'Night Elves like to stand off on one leg while casting. Strike at that leg like so...', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5562, 'Dwarves will spin around leaving their backs open for attack at times. Take this opportunity to strike like this...', 'Dwarves will spin around leaving their backs open for attack at times. Take this opportunity to strike like this...', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5563, 'Gnomes can be hard to hit due to their size. However, they have a pinchant for dancing around and thrusting their hips. When they do this, strike at them like so...', 'Gnomes can be hard to hit due to their size. However, they have a pinchant for dancing around and thrusting their hips. When they do this, strike at them like so...', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5564, 'Good. You are showing promise to join the elite ranks. Now, more practice.', 'Good. You are showing promise to join the elite ranks. Now, more practice.', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5565, '%s begins to regain its strength!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5566, '%s is nearly at full strength!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5567, '%s regains its power and breaks free of its bonds!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5568, 'In decades past - of now ancient wars and conflicts - our kind was driven to the brink of extinction. Much we have endured, $r.$B$BThe onslaught of the Burning Legion. The Demon Soul. The Aspects divided. The ascension of Deathwing. The enslavement of our Queen, Alexstrasza, by the Dragonmaw.$B$BThrough it all, we have been the unshakable will of the dragons - the undaunted force that has fought to represent our waning existence in this world.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5569, 'Your kind?', 'Your kind?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5570, 'Indeed. I stand before you in a form that you can comprehend, mortal. Surely, you could not bear to stand in the presence of the dragon known as Vaelastrasz.$B$B<Vaelan laughs.>$B$BShall I continue?', '', 0, 0, 0, 1, 6, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5571, 'Please.', 'Please.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5572, 'Perhaps I should tell you why I am here.$B$BDo you know of the Aspect, Deathwing? Or of his progeny, Nefarian and Onyxia?', '', 0, 0, 0, 1, 6, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5573, 'No, Vaelan, I do not.\n', 'No, Vaelan, I do not.\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5574, 'Deathwing, or Neltharion as he was once known, was the Aspect that embodied the strength of the world - granted almost limitless power over this mortal realm by the Titan shaper known as Khaz\'goroth: Forger of the world.$B$BThrough treachery and deceit, the foul one convinced the four other Aspects to imbue an item, known as the Demon Soul, with their powers - greatly weakening them, forcing a division among the once great Aspects. \n', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5575, 'Demon Soul?', 'Demon Soul?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5576, 'Indeed. The item was ultimately destroyed and Deathwing nearly slain by the combined and renewed power of the four remaining Aspects. Where Deathwing is now is unknown. What we do know, however, is that one of his progeny, Nefarian, rules this citadel and wishes to continue where his father left off.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5577, 'Where he left off?', 'Where he left off?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5578, 'Before his banishment, Deathwing was attempting to create impure offspring. Half breeds that would not only be immune to the innate natural powers and abilities of their Aspects but also under the complete control of Deathwing.$B$BIt seems that where his father failed, Nefarian may have succeeded - at least partially.$B$BIt was Ysera, Aspect of Dreams, who saw the abominations and forced us to act. If these \'Chromatic\' dragonkin exist, they must be destroyed.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5579, 'Why not destroy them with the combined might of the Aspects?', 'Why not destroy them with the combined might of the Aspects?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5580, 'It is not our way, nor would it be possible. Nefarian is guarded by ancient wards, as old as the Aspects themselves. It is what prevents me from entering the inner sanctum of this citadel. The dark magic prevents any of the other flight to enter without being ordained... unless.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5581, 'Unless?', 'Unless?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5582, 'It is possible for one, not of dragon kind, to obtain the Adorned Seal of Ascension and pass through the protected barrier. While possession of such an item would surely destroy me, perhaps you could utilize it and lower the barrier.\n', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5583, 'I am willing to try but I will require instruction.', 'I am willing to try but I will require instruction.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5584, 'You must find one amongst the wretch in the lower portion of this citadel that has attained an Unadorned Seal of Ascension. It is a great honor for one of the lesser beings to obtain this item, as it is the first step in gaining access to the upper citadel and becoming one of Nefarian\'s elite guardsmen - the Blackhand legion. Find such an item and return it to me so that I may examine its properties. I will know how to proceed once my inspection of the seal is complete.', '', 0, 0, 0, 25, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5585, 'I will do my best, Vaelan.', 'I will do my best, Vaelan.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5586, 'Be warned, mortal, it is exceptionally rare that one of the lesser clans in this citadel would hold such an item. Your search could take decades. I recommend you destroy these beasts without impunity.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5587, 'It will be done.', 'It will be done.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5588, 'Do not bring it any closer, mortal. I can sense the evil coursing through the seal from where I stand.$B$BGive me a moment to inspect the item - hold it firmly.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5589, 'As you wish, Vaelan.', 'As you wish, Vaelan.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5590, '%s is surrounded by a barrier of anti-magic.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5591, 'I have discerned the path that you must take. Are you prepared?', '', 0, 0, 0, 1, 6, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5592, 'Begone, mortal. You are not worthy of my knowledge.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5593, 'Ah, $r, you have returned!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5594, '%s\'s will falters.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5595, 'At last, I have caught up with you, Nefarian. Surely you did not think you would escape the might of the other Aspects?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5596, 'Enough! Playtime is over!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5597, 'I would like to train.', 'I would like to train.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5598, 'What do you mean you don\'t know how to use an axe? ', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5599, 'It\'s small but it does it\'s job if you know what you are doing. Let me show you just the right places to hit.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5600, 'Take tha blade like dis, den throw. Just flip joo wrist mon, one twirl and joo got jooself lunch.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5601, '', 'We have used bows since before even my father\'s time. They are solid and sure and take down our foes swiftly. I see no reason to use the guns that the goblins and tauren seem so fond of.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5602, '', 'Come to learn have ya, mon?', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5603, '', 'The fists of an Orc are already a powerful weapon, so why not add blades or studs of solid steel. Now the fists I use to tame the wolves of the plains I can also use to shatter the armor of my foes.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5604, 'Joo may tink this ma walkin stick mon. Dat\'s jus wat I want joo to tink.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5605, 'A bow is a great ting ma friend. Joo jus have to take care of it, and it will take care of joo.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5606, 'Dis ain\'t no game mon. Joo want to use da axe, joo got to say da line. Say it wif me... \"Say hello, to ma little friend.\"', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5607, 'Never had much use for a bow, but these rifles the goblins make are very handy when hunting. Still can\'t get used to the smell though.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5608, '$N, I\'ve come to find out how rampant the effects of Fel Moss are throughout Darkshore. The corruption of Teldrassil and our surrounding lands really concerns the druids of the Cenarian Circle, and all Night elves.$B$BIt is my goal to find out why this has happened and to put an end to it.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5609, 'It may look simple, a large stick with a huge weight on the end. But when swung with the might of a tauren behind it, this stick can break stone walls.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5610, 'Just a stick? Want me to show you what \'just a stick\' can do?', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5611, '', '%s begins to summon in a Blackhand Dreadweaver!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5612, '', '%s summons a Blackhand Dreadweaver to his aid!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5613, '', '%s begins to summon in a Blackhand Veteran!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5614, '', '%s summons a Blackhand Veteran to his aid!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5615, 'I\'m flattered, but really I\'m not interested... What? You want to know where to learn enchanting? Oh, how embarrassing! Well, you should... I\'m sure you could... go see, um... Taladan! Yes, see Taladan over in the Craftsmen\'s Terrace!', 'I\'m flattered, but really I\'m not interested... What? You want to know where to learn enchanting? Oh, how embarrassing! Well, you should... I\'m sure you could... go see, um... Taladan! Yes, see Taladan over in the Craftsmen\'s Terrace!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5616, 'What the... nobody cleanses a furbolg in MY camp! Face the wrath of Xabraxxis!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5617, '%s is lured out by the sweet scent of the purified food.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5618, '%s begins to greedily devour the food.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5619, 'VanCleef pay big for you heads!', '', 1, 5774, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5621, 'Nuttin\' be \'fraid of. Come.', 'Nuttin\' be \'fraid of. Come.', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5622, 'We\'re doomed!', 'We\'re doomed!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5623, 'The only weapon I know of that has no use other than killing someone. Not something, but someone. These blades were invented to end the lives of men. Beautiful aren\'t they?', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5624, '', 'Keep your foes at bay and carve them into cutlets at the same time. The only problem is that the long shaft keeps me from getting blood on my hands.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5625, '', 'Swift and sure. Cut a tendon here, slit an artery there. I can show you how to turn any knife into a perfect killing machine.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5626, 'The long reload takes some getting used to, but nothing else is better for starting off a fight. ', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5627, 'Welcome to my shop, $c. What is it the Goodstitch brothers can do for you?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5628, 'Ah, one of my favorite customers. You look to be of a warlock of some power, $n. I am pleased to meet you. If there is anything I can do for you, please, just ask.$B$BMy brother and I feel that it will be your kind who lead us into the next great era. One with such power... such control... it\'s only a matter of time before the Legion itself bends to your will.$B$BI am but a little envious.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5629, 'What of the Alliance, Rend?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5630, 'They have freed the human, Windsor, sire. It will not be long until Onyxia is uncovered.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5631, 'Then we must not waste anymore time. Are the chromatic dragonflight ready?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5632, 'The next clutch is due to hatch soon, sire. We will be ready to launch the attacks by week\'s end.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5633, '', 'I can teach you both the finesse of a short blade and the bone shattering cleaving power of a two handed behemoth. Or we can get crazy and I can teach you both, could come in handy for when the situation changes on ya.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5634, '', 'Sure I can teach you the fine art of wielding a polearm, but it\'s gonna cost you.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5635, 'Excellent... it would appear as if the meddlesome insects have arrived just in time to feed my legion. Welcome, mortals!', '', 1, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5636, 'Sure it takes longer to reload but this baby will punch a hole the size of my fist through any set of armor.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5637, '', 'I\'ve seen farmers with staves hold off trained soldiers. Do not ever underestimate the power and skill it takes to wield one of these properly.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5638, '', 'If you think I\'m good, you should see my husband fight.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5639, '%s turns to face you.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5640, 'Let not even a drop of their blood remain upon the arena floor, my children. Feast on their souls!', '', 1, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5641, 'A good hammer in yer hands makes the world feel safer. Like ye can face even the mightiest of foes with a large hunk of solid metal gripped in yer fists.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5642, 'I\'ll teach ye to chop down yer foes like saplings in the forest.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5643, '', 'Hound on the horizon moving at thirty feet per second. The only question is, right or left eye. That\'s right $g lad : lass;, I\'m that good.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5644, '', 'See this? This is my boom stick! Dwarven top of the line. That\'s right, this sweet baby was made in Ironforge, Khaz Modan. Retails for about one hundred and nine gold, ninety five copper. It\'s got a walnut stock, thorium steel, and a hair trigger.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5645, 'Och I love a good brawl, but sometimes yer foe has a skull thicker than a Thorium heat shield. Times like that ye need a wee bit o assistance. ', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5646, 'The chromatic flight shall deal with these intruders. Let us talk of more pressing matters.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5647, 'Modernization is the key. With a crank and a lever you can fire this baby with the power of an ogre pulling a normal bow.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5648, 'Lets go, $n. I am ready to reach Whitereach Post.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5649, '', 'Zoom, zing, and BAM! He may have had a huge two handed cleaver made from a boat anchor but after a few well placed slices from my dinner knife, he was the one going crash!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5650, '', 'A hidden blade up the sleeve can mean the difference between you laying dead in the alley, and your opponent laying face down in his own blood.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5651, 'The slow blade penetrates the shield.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5652, 'The secret to the knife is to keep it hidden until the moment you strike.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5653, '', 'So, you wish to learn more about our elven weaponry? Let your first lesson begin.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5654, 'Now this looks familiar. If we keep heading east, I think we can... Ahh! Wyvern on the attack!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5655, 'You want a weapon that acts as an extension of your very fist? Yes, I believe I can show you something.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5656, 'I do not know this Eye of the Tiger some speak of, but I can show you how to use the claws of the tiger.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5657, '', 'Many think the staff an inferior weapon. I challenge any who think so to prove their strength against my staff. ', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5658, 'The skill of the bow is a great asset to anyone, not just the hunter. You can strike swiftly, silently, and before your prey can blink... they are dead.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5659, '', 'Ah, $r... I possess great knowledge a\' da world and how ta create charms a\' great power. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5660, 'Many times my blades have felled my target before they even drew near. The secret is in the flick of your wrist.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5661, '', 'Mau\'ari know why you seek her out...$B$BBring me da E\'ko you find in the world, an\' I can transform it for you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5662, 'How do I use the Cache of Mau\'ari?', 'How do I use the Cache of Mau\'ari?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5663, 'Sssplendid... Then let us watch as my children tear these mortals to pieces.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5664, 'Use the freezing breath, imbecile!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5665, 'Foolsss... Kill the one in the dress!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5666, 'Inconceivable!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5667, 'Do not force my hand, children! I shall use your hides to line my boots.', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5668, 'Your efforts will prove fruitless. None shall stand in our way!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5669, 'I promise you an eternity of dung clean up duty for that failure!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5670, 'Curse you, mortal.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5671, 'Concentrate your attacks upon the healer!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5672, 'Sire, let me join the fray! I shall tear their spines out with my bare hands!', '', 1, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5673, 'Impossible!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5674, 'Flee while you still have a chance, mortals. You will pray for a swift death should I enter the arena.', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5675, 'You will learn of the sanctuary only death can offer...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5676, 'It appears as if I will have to step in and take care of you myself.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5677, 'I will tear off your limbs and use them to beat you into submission!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5678, 'Defilers!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5679, 'I can stand no more of this!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5680, 'Vincent! You and your pathetic ilk will find no more success in routing my sons and I than those beggardly remnants of the Kirin Tor.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5681, 'If you will not serve my Master with your sword and knowledge of his enemies...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5682, 'Your moldering remains will serve ME as a testament to what happens when one is foolish enough to trespass in my domain!\n', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5683, 'Thanks a bunch... I can find my way back to Whitereach Post from here. Be sure to talk with Motega Firemane; perhaps you can keep him from sending me home.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5684, '%s spies you tampering with the camp\'s fruit stores and attacks you!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5685, '%s emerges from the log hovel and catches you tampering with the grain stores!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5686, '', 'More and more of Kalimdor is turning into the twisted landscape to our left, and the critters who dwell within are NOT friendly.  Watch your step.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5687, '', 'Blast... ogres.  I forgot about these buffoons.  There\'s no easy way around them, so expect plenty of fighting.  Look sharp!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5688, '', 'Come on Dadanga!  March girl, march!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5689, '', 'There it is -- Gadgetzan!  We\'re in the home stretch now!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5690, '', 'We\'re finally here, thanks to you $n!  Without your help, we would have been some over-grown lizard\'s lunch!', 0, 0, 0, 10, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5691, '', 'It was quite a haul, but now I can load up on supplies for the trip back into the Crater.  I should be able to unload some of this exotic cargo as well.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5692, '', 'Look out, incoming!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5693, '', 'I\'m gonna need some help with this $n!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5694, 'Yeah yeah, I know Dadanga dislikes smoke. From the smell of it though, seems like Dadanga also dislikes bathing.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5695, 'Have you seen Petra or Dadanga around?\n', 'Have you seen Petra or Dadanga around?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5696, 'They took off for Gadgetzan a while ago to bring us back some supplies. Frankly, I would have given at least four of my fingers to have had the chance to crawl inside one of Dadanga\'s packs in order to leave this forsaken place.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5698, 'What is E\'ko?', 'What is E\'ko?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5699, '', 'Da Cache of Mau\'ari holds great secrets... The shards used ta make it are reflective, and you can use its shiny surface ta siphon power from other tings and den reflect it onto yourself.$B$BKeeping da charm in your inventory lets you collect e\'ko, da spiritual power from the beasts you fight. Once you find e\'ko, you bring it ta Mau\'ari.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5700, '', 'E\'ko is da power dat each creatures possesses. Da charm I made for ya allows you ta capture it in a physical manifestation.$B$BI know an incantation dat can transform da e\'ko you find, and turn it into juju.$B$BYou can den take the Cache of Mau\'ari, an\' use it ta reflect da power of da juju onto yourself.$B$BC\'mon, give it a try. Bring da e\'ko ta Mau\'ari an\' we have some fun.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5701, '', 'As nice as Gadgetzan is compared to Un\'Goro, I have some good folks counting on us to bring them some supplies.  If you\'re ready to go, then so are Dadanga and I.  Let\'s mount up!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5702, '', 'The Tanaris desert is littered with gigantic bones just like this one to my right.  I\'d hate to have ever ran across that thing.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5703, '', 'Whoa, whoa, hold up Dadanga girl.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5704, '%s snorts deeply as she comes to a halt.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5705, '', 'This doesn\'t look... right.  Look at how alien the desert is!  Going through here might be a terrible mistake.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5706, '', 'What the... what are those things crawling at us?  We... we have incoming!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5707, '', 'That was not what I would call \'escort friendly\'.  I will find a way to go around these things.  Let\'s move to the south, toward that rock.  Maybe we can wedge through it and still avoid the ogres.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5708, '', 'This will work -- keep heading through this gap.  The path to Un\'Goro is due west of here once we round these infernal bugs.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5709, 'THIS CANNOT BE!!! Rend, deal with these insects.', '', 1, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5710, 'The mortals have fallen! The new age of the Blackrock legion is at hand!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5711, '%s is knocked off his drake!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5712, 'WOOT!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5713, 'That will surely save me some work.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5714, 'I want those boots! Nobody touch that corpse!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5715, 'Consider yourself lucky, mortal. At least your corpse is still somewhat presentable.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5716, 'Do not consume the entire corpse just yet, children! Save room for dessert!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5717, 'Served!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5718, 'You actually believed that you stood a chance?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5719, 'The Blackhand legion shall wear your skin as fashion accessories!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5720, 'The Warchief shall make quick work of you, mortals. Prepare yourselves!', '', 1, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5721, '%s paces back and forth in anticipation of the battle.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5722, 'With pleasure...', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5723, 'Good day to ye $g lad : lass;. Can I be of service?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5724, 'Here for training eh? Just began my training meself, but I would be glad ta teach ye what I can.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5725, 'Ye learn fast $g lad : lass;, much faster than I can keep up with. If ye want ta continue yer training, I suggest ye see my teacher. His name\'s Rotgath, Rotgath Stonebeard and ye\'ll find him in Ironforge at The Great Forge.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5726, 'Taste in my power!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5727, 'Eh? I don\'t have time ta chat, my da\' wants me ta finish this order fer him.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5728, 'Ye\'ve got a little skill on ye. Enough to learn ye a few things I wager. Let\'s see what I can teach ye, shall we?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5729, 'Ye know more than I do, that\'s a certainty. Tell ye what though, go talk with me da\'. He\'ll want ye ta prove yerself, but he is a fine teacher indeed. His name\'s Rotgath, and ye\'ll find him near the anvils workin steel for ol\' Bengus.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5730, 'Well my friend, ye have more skill than I do. Why don\'t ye go find me old friend Rotgath Stonebeard. He stayed in Ironforge when I left to join Jaina\'s army. I\'m sure he\'ll be able ta teach ye somethin, or at least know someone who can. If he\'s still around, I\'m sure he\'ll be at The Great Forge.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5731, 'Where should I look for E\'ko?', 'Where should I look for E\'ko?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5732, '', 'The creatures in Winterspring are a good place ta start.$B$BHoweva\', der may also be other places you can look...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5733, 'Of course I can help you with training. I\'m just a journeyman myself, but I\'m sure there\'s something I can show you.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5734, 'I don\'t admit this lightly, but you are a far better smith than I am. If you want to learn more than my meager teachings then I suggest you seek out a Dwarf named Therum Deepforge in Stormwind. You will find him in the Dwarven District.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5735, 'I\'m sorry, I\'m a bit busy right now making horseshoes for Verner Osgood in Lakeshire. ', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5736, 'Do I look like I have the time to spend jawin?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5737, 'You\'re not bad, but let me show you a few tricks you might find handy. ', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5738, 'A far better blacksmith than I am, that\'s for sure. Though I\'ll deny it if you tell anyone I said that. You should go speak with Therum Deepforge. If anyone can teach you more, it\'s him.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5739, 'I hear one comment about waterfowl, and you will be eating through a tube.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5740, 'Just swinging a hammer at an anvil doesn\'t make you a smith. Give me that and let me teach you something before you embarrass us all.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5741, 'Not bad. Go talk with Snarl, he just moved to Orgrimmar to study under Saru. Look for him in the Warrior district near Saru\'s smithy. Oh and he\'ll test you, so you better be as ready as you think you are.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5742, 'Snarl calls me simple because I am too slow. I just want to do the job right, and that takes time.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5743, 'I can show you what you wish to know, but you must be patient. Take your time to work the steel and feel the way it shapes beneath your hammer. When you become one with the steel, then you will understand.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5744, 'You learn quickly, that is not a bad thing. Just be sure to do the job right, as long as it takes to do so. Now, I fear I must send you to Snarl to learn more. Learn what he can teach, but do not listen to his angry words. He gets that way when Saru\'s daughters laugh at him.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5745, 'I listen and I listen but I don\'t hear these spirits that Karn speaks of. ', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5746, 'Karn says we need to listen to the spirits of the forge and of the steel, that they will guide us. I hear nothing but a ringing in my ears. Here, take a hammer and let us try to hear the spirits together.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5747, 'I guess the spirits like you better than me, I still have not heard their call but you seem to have gathered their lessons well. Go speak to Karn, he will be excited to see one who hears them as he does.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5748, 'All is not lost! Battle these beasts without fear. Your wounds I shall heal, bones I shall mend. Be renewed, heroes!', '', 1, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5749, '', 'Your interruption is costing me valuable time, this had better be good.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5750, '', 'Ah yes, another who will stoke the fires and forge the weapons of war. Of course I will teach you how.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5751, '', 'You have learned well my child. Go to the Undercity, find a smith named James Van Brunt. He will be able to teach you much that I cannot. I understand he spends his time near the Warriors training hall, forging their weapons and armor.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5752, 'I hope to one day forge weapons and armor from the bones of my enemies.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5753, 'You are not trying to kill the anvil with your hammer, you are trying to create a weapon to kill your foes with. Let me show you what you are doing wrong.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5754, 'You have learned much, more than I can teach. Go to James Van Brunt, he will teach you what I cannot.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5755, 'You must continue! Their resolve is weakening. Drink from my power and be renewed once again!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5756, 'What is Juju?', 'What is Juju?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5757, '', 'E\'ko is da unfocused form of the supernatural power, and Juju is da concentrated essence. By using a stong incantation, I can transform da E\'ko into Juju.  Once it becomes Juju, you can den use the Cache of Mau\'ari ta reflect da powers onto yourself.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5758, 'I lost the Cache of Mau\'ari. What can I do?', 'I lost the Cache of Mau\'ari. What can I do?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5759, '', 'Ah am very surprised that one wit such a careless nature has gotten dis far...$B$BYah, you may have another charm, but Mau\'ari is very displeased wit your actions!$B$BGo, now. ', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5760, 'You have come too far to fail. Stand back, mortals.', '', 1, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5761, 'You will suffer in this defeat, Nefarian!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5762, 'This should do it! At least it better do it!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5763, 'Holy Krakle! The temperature is 428,000 degrees Kraklenheit! ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5764, 'The temperature is 9280 degrees Kraklenheit! That\'s HOT!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5765, 'The temperature is 564 degrees Kraklenheit.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5766, 'The temperature is 122 degrees Kraklenheit.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5767, 'The temperature is 12 degrees Kraklenheit. How exciting.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5768, 'Um, Ozzie... that doesn\'t look very safe.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5769, 'OZZIE!!!! OH NO!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5770, 'Didn\'t even leave his boots behind...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5771, '%s shrugs.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5772, '%s collapses but the broken body rises again!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5773, 'The living are here!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5774, 'Ow! OK, I\'ll get back to work, $n!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5775, 'When I get all shaken up, hear me shout...', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5776, 'Vaelastrasz, when this world belongs to the black flight, your flight shall know its end.', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5777, 'But for now, your death will have to suffice...', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5778, 'You merely destroy an image, fool. I shall hunt you until the end of days.', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5779, 'Aye, it appears as if ye have learned quite a bit, I\'m impressed. But do ye know how many I have that seek to be my apprentice? Tell ye what, do a small task fer me and I\'ll take ye on.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5780, 'Ye have some skill about ye with hammer and anvil, but not enough. Go speak to Dane Lindgren, he\'ll get ye ready to learn what I have to teach ye.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5781, 'I said ye had the blood of a blacksmith in ye, and I was right. Go to Ironforge, to The Great Forge, and find yerself a Dwarven smith by the name of Bengus Deepforge. My teacher, and one of the greatest smiths I\'ve ever known.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5783, 'The life of a smith is not an easy one, but it puts steel in yer bones and fire in yer heart. My da\' was a smith, my grandfather before him in the forges beneath Grim Batol. And now my son follows in our footsteps, that boy makes me proud.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5784, 'Sorry $g lad : lass;, ye aren\'t quite up to the challenge of learning from me. Go see my son Groum, it would make his day to get a new apprentice.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5785, 'Yer skill is not without merit, but I need to put one last test to ye before I take ye on.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5786, 'Aye, I would love ta teach ye a thing or two about smithing. Grab an apron and a hammer and let\'s see what we can accomplish shall we?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5787, 'A fast learner is what ye are, and a good smith to boot. I would sign off on yer work any day of the week. But I sense ye still have some learnin in ye. Go talk to Bengus Deepforge, he\'s a hard taskmaster but he\'s also one of the best.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5788, '%s lets out a shrill cry.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5789, '', 'Don\'t come out of the shadows like that. Next time give a girl some warning. ', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5790, '', 'Ever since the shadows claimed Duskwood I haven\'t been able to sleep. I am kept awake by the ghostly sounds of screaming on the breeze. It sounds almost like lambs screaming, and they won\'t stop.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5791, '', 'Hmm, your work is allright but it\'s nowhere near the quality I look for in a student. Go talk with Smith Argus in Goldshire, I\'m sure he can teach you some basics. Then when you\'re done, come on back to me and I\'ll give you a little test to make sure you are ready.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5792, '', 'Yeah, I\'ve heard through the grapevine that I might be seeing you sooner or later. But I don\'t just take on anyone, I need to make sure you have what it takes first. You willing to take my little test and find out?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5793, '', 'You do fine work, but it\'s a bit rough around the edges. Don\'t worry about it, it will come with practice. Speaking of which, how about we see what you\'ve been working on?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5794, '', 'Now I\'m the one who feels rough around the edges, I honestly don\'t think there\'s anything left I can teach you. But you know who can? Bengus Deepforge in Ironforge, now there\'s a dwarf that knows his steel. Go look him up in The Great Forge of Ironforge, see if he\'s up to having a new trainee under him.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5795, 'If I prove myself to Saru he will have no choice but to let me fight for one of his daughters. Then we will see who is sneering.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5796, 'Stay away from Sumi and Tumi. They are....spoken for.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5797, 'Come to me again with such pathetic work and I will send you home in a small iron box that I tempered in goat urine. Now go see that idiot Ug\'thok, he is better suited to your needs.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5798, 'So you think you are good enough to learn from me? The great Snarl? You are no more than a lowly worm beneath my boot. However, taking on an underling might make me seem more appealing to Sumi and Tumi. Let\'s see what meager skills you possess.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5799, 'Hmm? Oh yes, I can teach you something if you desire. But stay away from Sumi and Tumi, they are not for the likes of you. Saru only let\'s those of great skill approach his daughters.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5800, 'There is nothing more that I can teach - now get out! And don\'t even think of approaching Saru, for one as unworthy as you he will use your blood to temper his steel.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5801, 'The spirits have brought you to me, and so I feel blessed by them. ', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5803, 'You need to heed the guidance of the spirits a little better young one. Your path lies with another, Thrag Stonehoof in Thunder Bluff. Perhaps you can learn something from one another.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5805, 'Tell me, do your feel prepared to face the challenge I have laid out for you? The spirits tell me that you will pass, but do you have faith in them or in yourself?', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5806, 'Join me, together we will weave the spirits of the forge and find the true shape that the metal seeks.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5807, 'Our paths through life intersect only briefly and now is the time for ours to part ways. I consider myself blessed to have spent this time teaching you and now send you along to join paths with your next mentor. Seek out Saru Steelfury of Orgrimmar, he will teach what you need to learn.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5808, 'You have great potential, but you must achieve understanding first, you must accept the spirits and listen to their calling. Only then will you be prepared for the path you must follow. Go speak with Thrag, I think perhaps you can learn a lot from one another.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5809, 'You are now ready to follow the path that joins your life with my own, if only for a short time. However we must make sure that you understand the things of which the spirits speak.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5810, 'You have learned much, but I sense that there is more left for the spirits to teach you. I can place your foot on the path but cannot guide your steps. Go and find Saru Steelfury in Orgrimmar, the next stretch of your journey begins there.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5811, 'You waste my time, go talk with my student Basil.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5812, 'Perhaps you have what it takes, perhaps not. If you do then I will take you on and teach you what you wish to know. If you do not... well, I\'ll just leave it at that.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5813, 'Very fine equipment you make, very fine indeed. Go to the Orc\'s home of Orgrimmar and seek out a smith named Saru Steelfury. One thing you must give the Orcs, they make beautiful instruments of death.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5814, 'I\'m sorry $g lad : lass; but ye just don\'t have the foundation to learn what I have to teach. But don\'t fret, go speak with my student Rotgath, he\'ll help ye out.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5815, 'Looks like ye may have the makings of a great smith. But before I teach ye anything I need to know that ye\'ve got what it takes. ', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5816, 'I can\'t say enough about yer work, truly amazing things that ye can do with a hammer and an anvil. Now, don\'t be tellin anyone that I suggested ye go find a goblin to teach ye but that\'s exactly what I\'m gonna do. Brikk Keencraft is his name, and last I heard he was operating a shop down in Booty Bay.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5817, 'This isn\'t a game you toy with youngling! Go, speak with Snarl outside. He may be a serpent in my lair but he does know his way around an anvil.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5818, 'Still a bit weak, unbalanced, unsure. Not your work youngling, I am speaking of you! Now, are you ready to be shaped into a real smith or not?', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5819, 'I see you know more of the secret of steel than I do. That is good, for it is meant to be taken. Now you must travel to the jungle south of the human lands, to the goblin city of Booty Bay. There you will find a Goblin named Brikk Keencraft. I do not like Goblins but his knowledge of the secret of steel is unmatched by any I know of.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5820, 'Yeah? Of course I\'m a smith. What, you think Dwarves are the only ones worth their weight in anvils? Fine, be that way. Go get a dwarf to help you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5821, 'Are you kidding me? You must be kidding me! Either that or you think I\'m a clown here to amuse you. Is that what you think? That I\'m a clown? Huh, is it! You think I\'m a clown don\'t you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5822, 'You act like this is the best junk I\'ve ever seen. Sure it\'s ok, but I see stuff this \'OK\' every day. Tell you what, you find a way to wow me, and I\'ll teach you things that make dwarves smile in their sleep.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5823, 'Imagination is the limit my friend, if you can figure out how to smith it then you can make it, box it, and ship it to retailers all over the world. Of course, then you have to deal with marketing. Tell you what, let\'s just stick with the smithing part for now, ok?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5824, 'Your victory shall be short lived. The days of both the Alliance and Horde are coming to an end! The next time we meet shall be the last.', '', 1, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5825, 'What did Gryphe tell boss?? Gryphe wasn\'t lying! There she is, trying to steal battle plans!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5826, 'Gryfe do his job! Gryfe go now!!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5827, 'Oh, it\'s on now! Bet you thought I\'d be alone too, huh?!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5828, 'Whoa! This is way more than what I bargained for, you\'re on your own, Slim!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5829, 'Though I hate to admit it, the bear did get the better of me. My oh my, that\'s one big ol\' bear!$b$bNonetheless, if you can take that bear down, it\'d be a feat to be proud of! It gave more than a few of our best hunters some trouble.$b$b<He claps you on the back.>$b$bBest the bear and we\'ll accept you as a regular member of the Farstrider Lodge.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5830, 'Here at the Farstrider lodge, we prize hunting skills above all else. Wish to gain my approval? You\'ll have to earn it.$B$BYou look up to the challenge, $Glad:lass;!', '', 0, 0, 0, 4, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5831, 'You\'ve become quite the hunter, $n! Not only have you proven your skills, you also bested Daryl, which gets you extra points in my book!$B$BYou\'re welcome here any time.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5832, 'You have summoned me, fool! Now DIE!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5833, 'Milk me, John.', 'Milk me, John.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5834, 'With pleasure... just let me savor this moment for a minute longer.', '', 0, 0, 0, 5, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5835, 'Do it... Do it now.', 'Do it... Do it now.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5836, 'They\'s in Magic Quarter. Look for them in scary skull building.', 'They\'s in Magic Quarter. Look for them in scary skull building.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5837, 'Us know where they are! Skull building in Magic Quarter on outer ring of city.', 'Us know where they are! Skull building in Magic Quarter on outer ring of city.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5838, 'Bank in Trade Quarter central tower. You not miss it.', 'Bank in Trade Quarter central tower. You not miss it.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5839, 'We have one eye and see better than you! Bank in center of Undercity!', 'We have one eye and see better than you! Bank in center of Undercity!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5840, 'Michael Garrett on upper ring in Trade Quarter.', 'Michael Garrett on upper ring in Trade Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5841, 'Bat man on upper level of Trade Quarter.', 'Bat man on upper level of Trade Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5842, 'Yep, you looking for Christopher Drakul in Trade Quarter. He on eastern platform.', 'Yep, you looking for Christopher Drakul in Trade Quarter. He on eastern platform.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5843, 'We join guild? No? Ok, you find Guild Master on east platform in Trade Quarter.', 'We join guild? No? Ok, you find Guild Master on east platform in Trade Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5844, 'Innkeeper Norman on upper ring of Trade Quarter is one to talk to.', 'Innkeeper Norman on upper ring of Trade Quarter is one to talk to.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5845, 'No rest for wicked, but us guess you ok. You find Innkeeper Norman on upper ring of Trade Quarter.', 'No rest for wicked, but us guess you ok. You find Innkeeper Norman on upper ring of Trade Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5846, 'Mailbox in middle of city, Trade Quarter, upper ring... next to inn.', 'Mailbox in middle of city, Trade Quarter, upper ring... next to inn.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5847, 'Us need check to see if our Brain... Brain... Brain Hacker sold at auction. You find mailbox next to inn on upper ring of Trade Quarter.', 'Us need check to see if our Brain... Brain... Brain Hacker sold at auction. You find mailbox next to inn on upper ring of Trade Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5848, 'We have auction house in central ring.', 'We have auction house in central ring.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5849, 'Central ring. You go!', 'Central ring. You go!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5850, 'Little goblin on top of building north of Ruins of Lordaeron. Leave Undercity and you go there.', 'Little goblin on top of building north of Ruins of Lordaeron. Leave Undercity and you go there.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5851, 'Us see balloon north of ruins up above. It take you to far off places.', 'Us see balloon north of ruins up above. It take you to far off places.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5852, 'Priest trainer? You go to outer ring of War Quarter and look for them there.', 'Priest trainer? You go to outer ring of War Quarter and look for them there.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5853, 'Uh huh. Unholies in War Quarter standing around main building on outer ring of city.', 'Uh huh. Unholies in War Quarter standing around main building on outer ring of city.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5854, '', 'Get that head and it\'s sure to give Daryl a fright. Not that he doesn\'t deserve it, the bloody arrogant bastard. But ha! I can\'t wait to see the look on his face when you show it to him.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5855, '', 'Welcome to the Farstrider Lodge, $n!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5856, 'Rogues in building on outer ring of Rogues\' Quarter.', 'Rogues in building on outer ring of Rogues\' Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5857, 'They teach me to sneak good. You find them in Rogues\' Quarter on outer ring of Undercity.', 'They teach me to sneak good. You find them in Rogues\' Quarter on outer ring of Undercity.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5858, '', 'You\'re always welcome here, $n. Thanks for the laugh -- and don\'t worry about Daryl, he had one coming!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5859, 'Warlock trainers in Magic Quarter in building on outer ring with Mages.', 'Warlock trainers in Magic Quarter in building on outer ring with Mages.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5860, 'Us want felhound for pet. We feed it and treat it good and teach it play fetch. You find Warlocks in Magic Quarter on outer ring of city.', 'Us want felhound for pet. We feed it and treat it good and teach it play fetch. You find Warlocks in Magic Quarter on outer ring of city.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5861, 'In building of War Quarter on outer ring of city.', 'In building of War Quarter on outer ring of city.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5862, 'Warrior trainers fight good. You find them in War Quarter on outer ring of Undercity.', 'Warrior trainers fight good. You find them in War Quarter on outer ring of Undercity.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5863, 'You look in The Apothecarium. That where Doctor Herbert Halsey and Doctor Marsh practice.', 'You look in The Apothecarium. That where Doctor Herbert Halsey and Doctor Marsh practice.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5864, 'Doctor Herbert Halsey in The Apothecarium where us was born. Him could sew you back together better like us.', 'Doctor Herbert Halsey in The Apothecarium where us was born. Him could sew you back together better like us.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5865, 'James Van Brunt in War Quarter on inner ring.', 'James Van Brunt in War Quarter on inner ring.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5866, 'Inner ring of War Quarter where James Van Brunt work. Him make us good chain.', 'Inner ring of War Quarter where James Van Brunt work. Him make us good chain.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5867, 'That Eunice Burch in Trade Quarter.', 'That Eunice Burch in Trade Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5868, 'Eunice Burch sell leftover parts. You find her in Trade Quarter.', 'Eunice Burch sell leftover parts. You find her in Trade Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5869, 'You go to inner ring of The Apothecarium. Lavinia Crowe is there.', 'You go to inner ring of The Apothecarium. Lavinia Crowe is there.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5870, 'Lady teach you make magic items. She at inner ring of The Apothecarium.', 'Lady teach you make magic items. She at inner ring of The Apothecarium.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5871, 'Graham Van Talen in Rogues\' Quarter on outer ring of Undercity.', 'Graham Van Talen in Rogues\' Quarter on outer ring of Undercity.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5872, 'Heh, bombs, us like bombs. Graham Van Talen show you how to make. He in outer ring of Rogues\' Quarter.', 'Heh, bombs, us like bombs. Graham Van Talen show you how to make. He in outer ring of Rogues\' Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5873, 'Us know her good. She Mary Edras at inner ring of Rogues\' Quarter.', 'Us know her good. She Mary Edras at inner ring of Rogues\' Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5874, 'Lady stitch us up and bandage when we get hit. You find her in inner ring of Rogues\' Quarter.', 'Lady stitch us up and bandage when we get hit. You find her in inner ring of Rogues\' Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5875, 'You not miss Armand Cromwell in Magic Quarter. He stand on dock.', 'You not miss Armand Cromwell in Magic Quarter. He stand on dock.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5876, 'Armand Cromwell always on dock in Magic Quarter. Fish in green goo taste extra good. You try.', 'Armand Cromwell always on dock in Magic Quarter. Fish in green goo taste extra good. You try.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5877, 'You look for Martha Alliestar. She on inner ring of The Apothecarium.', 'You look for Martha Alliestar. She on inner ring of The Apothecarium.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5878, 'You need go to The Apothecarium on inner ring. That where Martha Alliestar teach you how to pick flowers.', 'You need go to The Apothecarium on inner ring. That where Martha Alliestar teach you how to pick flowers.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5879, 'Him\'s at inner ring of Rogues\' Quarter.', 'Him\'s at inner ring of Rogues\' Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5880, 'Arthur Moore in Rogues\' Quarter inner ring is one who teach leatherworking.', 'Arthur Moore in Rogues\' Quarter inner ring is one who teach leatherworking.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5881, 'Go to inner ring of War Quarter. Brom Killian there.', 'Go to inner ring of War Quarter. Brom Killian there.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5882, 'Brom Killian teach you how dig up shiny for me. Him on inner ring of War Quarter.', 'Brom Killian teach you how dig up shiny for me. Him on inner ring of War Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5883, 'Killian Hagey teach skinning in Rogues\' Quarter on inner ring.', 'Killian Hagey teach skinning in Rogues\' Quarter on inner ring.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5884, 'Us have skinning hook. We get it from Killian Hagey who teach us how to skin kills. You have nice skin. You find him on inner ring of Rogues\' Quarter.', 'Us have skinning hook. We get it from Killian Hagey who teach us how to skin kills. You have nice skin. You find him on inner ring of Rogues\' Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5885, 'You look for Josef Gregorian. He in Magic Quarter on inner ring side of green canal.', 'You look for Josef Gregorian. He in Magic Quarter on inner ring side of green canal.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5886, 'Hmmm, that good idea. Maybe we head there to get some stitches replaced? Him sew good. You find Josef Gregorian on inner circle of Magic Quarter.', 'Hmmm, that good idea. Maybe we head there to get some stitches replaced? Him sew good. You find Josef Gregorian on inner circle of Magic Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5887, '', 'Oh, ok!  Here you are, $n.  It\'s the least I can do for all the help you\'ve given me.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
