INSERT INTO `broadcast_text` VALUES (5916, 'A profession trainer', 'A profession trainer', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5917, 'Us think you need help.', 'Us think you need help.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5918, 'We help you find them.', 'We help you find them.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5919, 'Our eye say you look lost.', 'Our eye say you look lost.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5920, 'Us hear and obey.', 'Us hear and obey.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5921, 'Can you help me with some directions?', 'Can you help me with some directions?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5922, 'Listen to the stories of an elder. $n. I have many to speak of...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5923, '', 'Welcome to the refuge, $n. Some come here with a purpose, others found their way here by accident... But everyone here seems to have a unique story to tell.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5926, '', 'Be on guard... Arnak has some strange power over the Grimtotem... they will not be happy to see me escape.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5927, '', 'Look out, the Grimtotem are upon us!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5928, '', 'Here they come.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5929, '', 'More Grimtotems are coming this way!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5930, '', 'Finally, free at last... I must be going now, thanks for helping me escape. I can get back to Freewind Post by myself.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5931, '', '$n!  You\'re back!  And not a moment too soon!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5932, '', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5933, '', 'Hi, $n.  No hard feelings, ok?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5934, 'I present to you the rabid thistle bear, Tharnariun.', 'I present to you the rabid thistle bear, Tharnariun.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5935, 'Grrr...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5936, 'I want to release the corrupted saber to Winna.', 'I want to release the corrupted saber to Winna.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5937, 'Be cleansed!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5938, '%s recasts the cleansing spell.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5939, '%s jumps into the moonwell, and goes underwater...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5940, '%s follows $n obediently.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5941, '%s snarls ferociously.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5942, 'I came to the Crossroads from the south seeking help, $c, but I find only cowards who turn their backs on me--even the mighty Thork insults me so. He is a good leader, but his dismissal of my plea causes me greater anger.$B$BHe claims my rage makes me hasty--that it clouds my judgement--but I will not rest until I have vengeance!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5943, 'I thank you, $n. Your dedication to my plea gives rest to my lost wife\'s spirit, and for that I could never repay you enough.$B$BI cannot say that my hatred will ever cease for the quilboar, but it is satiated for now.$B$BNow I must begin to rebuild my life.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5944, '%s points to the parchment on the ground.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5945, '', 'I\'m very curious to see what this sample of water will do!', 0, 0, 0, 11, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5946, '', '%s pours the corrupted water onto the cat.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5947, '', 'Haha! Perfect!', 0, 0, 0, 11, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5948, '%s has been encaged.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5949, 'Use staff, tell Raze what do! Show Raze poacher! Raze smash!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5950, 'Now this is an example of what not to summon. Look, it\'s frail and brittle. One good whack from a mace will send bone shards flying.', 'Now this is an example of what not to summon. Look, it\'s frail and brittle. One good whack from a mace will send bone shards flying.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5951, 'Note the weak binding structure of this one. Be sure to finish your incantations or this is what you will end up with.', 'Note the weak binding structure of this one. Be sure to finish your incantations or this is what you will end up with.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5952, 'This one is slightly better than the last. However, it still suffers from the same flimsy bone structure as the others. When you summon one of these, you are on the right path.', 'This one is slightly better than the last. However, it still suffers from the same flimsy bone structure as the others. When you summon one of these, you are on the right path.', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5953, 'Wow, this one is just plain useless. Let me try again.', 'Wow, this one is just plain useless. Let me try again.', 0, 0, 0, 11, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5954, 'Hmmm, this one looks like something that would be better off as a windchime. Take notes class... This is NOT what you want to summon in the heat of battle.', 'Hmmm, this one looks like something that would be better off as a windchime. Take notes class... This is NOT what you want to summon in the heat of battle.', 0, 0, 0, 94, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5955, 'What could be taking so long?', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5956, 'How long can it take to pick a handful of weeds?', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5957, 'At this rate I could have gathered them myself!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5958, 'If you want something done right, do it yourself!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5959, 'As if I had all eternity.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5960, 'Ah, this must be him now... no? Bah!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5961, 'Maybe I should have just bought some off of Faruza?', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5962, 'I had to go and requisition an Abomination... an Abomination!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5963, '', 'The body is that of a female orc warrior.$B$BIt is clear that she died in battle, probably against the quilboar in the area.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5964, 'I inspect the body further.', 'I inspect the body further.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5965, '', 'You turn the body over and see the marks from blades and spells upon the deceased orc--her armor all but destroyed, her hair matted to her face.$B$BBased on Mankrik\'s description of her, there is no doubt in your mind that this was his wife.$B$BHe will be upset by the news, but you are sure he should know the truth.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5966, 'Start running!', 'Start running!', 0, 0, 3, 0, 25, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5967, 'Bring it on!', 'Bring it on!', 0, 0, 3, 0, 5, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5968, 'Do not push me, or I will impale you on my horns!', 'Do not push me, or I will impale you on my horns!', 0, 0, 3, 0, 5, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5969, 'Welcome to the Crossroads, $c. My name is Thork--I lead the defenses here in the name of the Warchief.$B$BPlease, feel free to make yourself at home. As long as you mind your business, no one should have any problem with you taking up some space.$B$BThere are many threats to our safety here. If you\'ve already become familiar with the lay of the land, speak to the others here--perhaps they have tasks for you to do.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5970, '<Goretooth spits.>$B$BI am a warrior of the Horde, $n. It fills me with rage to know that our enemies sit in a fortress, less than a day\'s ride from Kargath, plotting against us.$B$BHow I long to break free from the bonds of duty and destroy them all in the name of the Warchief.$B$BAlas, we all have roles we must fulfill.\n', '', 0, 0, 0, 14, 5, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5971, 'What roles?', 'What roles?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5972, 'Truly, I envy you: To battle in the name of the Horde, at the forefront of conflict. Mine is a job of command.$B$BCertainly, in my youth, I lived out the dreams that make my old age bearable. I now revel in the glory of victory, the bitter taste of defeat - all through the soldiers I command.\n', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5973, 'Dreams?', 'Dreams?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5974, 'I dream, $n... oh yes, I dream. In my dreams I die a thousand times in a thousand different ways. Some would call dreams such as mine nightmares. NOT I! In dreams I die in the thick of battle. For each single death of my own, ten thousand enemies of the Horde fall before me. It is glorious!', '', 0, 0, 0, 1, 1, 5, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5975, 'I pray for such things.', 'I pray for such things.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5976, 'Aye, I could tell just by looking at you, $n. You remind me very much of myself as a young warrior of the Horde.$B$BYou will undoubtedly go far in life and even further in death.$B$B<Goretooth salutes.>', '', 0, 0, 0, 1, 1, 66, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5977, 'I do not deserve such praise, Warlord Goretooth.', 'I do not deserve such praise, Warlord Goretooth.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5978, 'And modest too... Perhaps it is your time, then, $n.$B$BAre you prepared to enter the fortress of the Blackrock orcs? To wreak havoc? To tear asunder the foundation of the mountain citadel?', '', 0, 0, 0, 25, 6, 6, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5979, 'Sir! Until my body ceases to function!', 'Sir! Until my body ceases to function!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5980, 'I did not doubt you for even one second, $n.$B$BHere are your orders, $n. I know that you will not let me down.\n', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5981, 'Yes, sir. Thank you, sir!', 'Yes, sir. Thank you, sir!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5982, '%s bows before Krang Stonehoof', '%s bows before Krang Stonehoof', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5983, 'Ah a visitor! Must be an adventurer, we don\'t get many tourists down this way. Grab a chair and a drink, make yourself comfortable. Say, you look like you have some meat on your bones. You thinkin about competing in the cage matches?', 'Ah a visitor! Must be an adventurer, we don\'t get many tourists down this way. Grab a chair and a drink, make yourself comfortable. Say, you look like you have some meat on your bones. You thinkin about competing in the cage matches?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5984, '%s attacks the pike!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5985, '%s destroys the pike!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5986, '%s and Raider Kerr let their wolves rest a bit and drink...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5987, '%s and Raider Kerr charge into battle...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5988, '%s takes aim...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5989, 'The Horde has changed from what it once was. There was a time where I left the Horde, disillusioned by the growing influence of the power-hungry pawns of the Burning Legion.\n\nDuring my exile, I was taken prisoner by a group of humans, but was rescued by the Warchief.\n\nHearing his vision of the Horde free of demonic influence, guided by the shamans, I returned.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5990, 'Hello, Eitrigg. I bring news from Blackrock Spire.', 'Hello, Eitrigg. I bring news from Blackrock Spire.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5991, 'Rend, son of Blackhand: Impotent chieftain of the Blackrock clan. A more stubborn bastard does not exist.$B$BAt the end of the second War, Rend and his brother, Maim, fled to find sanctuary within the mountainous stronghold of Blackrock Spire. It was there that Rend declared himself Warchief of the weakened Blackrock clan.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5992, 'There is only one Warchief, Eitrigg!', 'There is only one Warchief, Eitrigg!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5993, 'Yes, young one, you need not convince me of such things, the Warchief and I have much history. Your dedication, however, is noted.$B$BAs I was saying, Rend had found a new home for his people, and I was among those who would wearily follow.$B$BFrom one conflict arose others... countless others. It was in those battles for Blackrock Spire between the Blackrock clan and the Dark Iron dwarves that my sons were both slain. It was then that I realized my time among the Blackrock clan had come to an end.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5994, 'What do you mean?', 'What do you mean?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5995, 'I left. I would not remain with the Blackrock. No, the deaths of my children would not be in vain. I would not serve under an inept, power hungry maniac such as Rend.$B$BI took shelter in the woods of Lordaeron, living as a hermit near Hearthglen.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5996, 'Hearthglen?? But...\n', 'Hearthglen?? But...\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5997, '<Eitrigg laughs.>$B$BYoung one, the world has not always been in such disarray. It was a time before the Scourge had sacked the land. Before those parts of Lordaeron were known as the Plaguelands.$B$BIt was there that Thrall rescued me from death and it was then that I joined the new Horde... but I digress. This is a story for another time. Yes, another time... Perhaps...', '', 0, 0, 0, 11, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5998, 'I will take you up on that offer, Eitrigg.', 'I will take you up on that offer, Eitrigg.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (5999, 'I\'m certain you will. Perhaps one day you could deliver a message to an old friend of mine. I hear he still wanders those woods.$B$B<Eitrigg bows his head for a moment, as if in prayer.>$B$BSo Rend, yes... Rend and his brother Maim ruled the Spire, in constant conflict with the Dark Iron dwarves. They managed to displace the dwarves in the upper portion of the city, but only after the dragons came and only after the death of Maim.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6000, 'Ah, so that is how they pushed the Dark Iron to the lower parts of the Spire.', 'Ah, so that is how they pushed the Dark Iron to the lower parts of the Spire.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6001, 'Indeed. Nefarian, brood of Deathwing, rules the Spire now. Rend is under the command of the dragon.$B$BIf only you could somehow manage to pass through the Halls of Ascension.$B$B<Eitrigg sighs.>$B$BAlas, only those deemed worthy by the black flight earn privilege to ascend.\n', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6002, 'Perhaps there exists a way?', 'Perhaps there exists a way?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6003, 'Perhaps. Go now, young one. Seek council with the Warchief. He will surely wish to know of your discovery.', '', 0, 0, 0, 1, 25, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6004, 'As you wish, Eitrigg.', 'As you wish, Eitrigg.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6005, 'Hulfnar! Thontek! Stand and address me.', 'Hulfnar! Thontek! Stand and address me.', 0, 0, 3, 0, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6006, 'It\'s 428,000 degrees Kraklenheit... What\'s happening, hot stuff!?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6007, 'DING! 428,000 degrees Kraklenheit, exactly! Well, approximately. Almost. Somewhere around there...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6008, 'Measuring by Kraklenheit, it is 428,000 degrees! That\'s Krakley!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6009, '18 degrees Kraklenheit. It\'s kind of cold, actually...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6010, 'It\'s 40 degrees Kraklenheit. Can you feel the heat? No? I really couldn\'t either.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6011, 'Wow, it\'s 3 degrees Kraklenheit. Keep looking.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6012, 'The stake is covered with the remnants of those foolish enough to anger the Warchief.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6013, 'Honor your heroes! On this day, they have dealt a great blow against one of our most hated enemies! The false Warchief, Rend Blackhand, has fallen!', '', 6, 0, 0, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6014, 'Be bathed in my power! Drink in my might! Battle for the glory of the Horde!', '', 6, 0, 0, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6015, 'Be bathed in the power of the Warchief! Drink in his might! Battle for the glory of the Horde!', '', 6, 0, 0, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6016, 'The Valley of Trials will temper even the weakest into a warrior worthy to join the Horde. Those who cannot rise to its tests will be left to bake in the scorching desert sun.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6017, 'Place Rend\'s head on the stake.', 'Place Rend\'s head on the stake.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6018, '', 'Hello there, $N.  I am known as Chronormu, but you may call me Chromie.  I am comfortable with such familiarity.$B$BNow - how did I know your name, you ask?  I am one of the bronze dragonflight, forever attuned to the ebb and flow of time.  You could say that we have met before.  To that end, we shall also meet again.$B$BI hope that my choice of visual demeanor does not disturb you; while I might be charged to be a sentry of sorrowful places, I choose not to drown in the sorrow of the locale.\n\n\n', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6019, '', 'Hello once again, $n.  I sense that the flow of time remains threatened by forces that are quite powerful indeed.  Your continued assistance is always welcomed by the bronze dragonflight.$B$BWhile the past is set in its ways, the future is always changing.  The decisions you make in the here and now help guide that change.', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6020, 'I want you both to demonstrate the techniques I have taught you for the benefit of the younger students here. Ready... begin!', 'I want you both to demonstrate the techniques I have taught you for the benefit of the younger students here. Ready... begin!', 0, 0, 3, 0, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6021, '', 'This is where I get off, suckers!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6022, '', 'Tell Lexlort if he wants the information, all he has to do is pay! Unfortunately, that means he can\'t kill me if he wants the information - since I burned the battle plans and all...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6023, '', 'Listen to your elders, $n. If you are strong of will and dedicated, there is no power you cannot control.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6024, '', 'Not all Forsaken are driven by Sylvanas\' will, $n. Some of us are driven by true power--power we earn for ourselves.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6025, 'Enough talk you two! Commence fighting!', 'Enough talk you two! Commence fighting!', 0, 0, 3, 0, 5, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6026, '', 'Now\'s not a good time, $n.$B$BAre you insane?!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6027, '', 'It... is... not... the best... time, $n. I must concen... trate.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6028, '', 'Your errand must mean much, $c, to draw you so deep into the marsh...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6029, '', 'The infernal is one of the most powerful demons a warlock could hope to have at $Ghis:her; side in battle. The creature is stong, well-armored, and delivers devastating blows upon any foe. If you were to choose to put the spirit of an infernal into your weapon, you would gain some of those benefits--greater health and regeneration and the like.$B$BIf you wish to know more about felhounds, then you should speak to Wytula across from me.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6030, '', 'The felhound... a vicious creature indeed. It is not as physically strong as an infernal, but a wise warlock knows that brute force is not always the answer--if it was, you would have chosen the path of a warrior instead, wouldn\'t you?$B$BThe felhound draws upon the mana of its foes, using that and its quickness to overcome enemies. Its strength comes from the arcane and using that arcane power for its own ends.$B$BIf you wish to know more about infernals, then you should speak to Magaz across from me.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6031, 'ROOKERY MINION SPAWN DEBUG', 'ROOKERY MINION SPAWN DEBUG', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6032, 'ROOKERY MINION death DEBUG', 'ROOKERY MINION death DEBUG', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6033, '%s is summoned through a temporal rift!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6034, '', 'Have you spoken to either of my acolytes inside the tower yet, $n? We cannot complete your orb until you\'ve done one of their tasks. Speak to them now, if you\'re ready.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6035, 'Help me kill $n!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6036, 'Protect me from this $n! I must return to my brood.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6037, 'Don\'t let me die!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6038, 'Well Done!', 'Well Done!', 0, 0, 3, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6039, '', '%s roars out for her mate!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6040, '%s answers the call of Shy-Rotam.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6043, 'Please, help me $g brother : sister;...$B$BI will soon pass from this world, and though I suffer I know I shall not any more once I pass. The wildlife though, my $g brother : sister;... the wildlife suffers too, and it knows no release. I ask for your aid with my last breaths of life.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6045, 'Yes, I speak a language you understand $r. We are not animals, yet most of your kind continues to treat us as such! Our differences though pale to the tragedy that has befallen this land. Please, put aside your differences with my kind and hear my dying plea.$B$BI came to this land to mend what I could, and now that will be for naught unless you can aid me. I ask for such aid with my last breaths of life.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6046, '\"...\"$B$BThe body of the tauren druid lies still and unmoving. It would seem as though he has finally passed from this world to his promised great beyond.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6061, 'None may challenge the Brotherhood!', '', 1, 5780, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6062, 'And stay down!', '', 1, 5781, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6063, 'Lapdogs, all of you!', '', 1, 5782, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6064, 'Fools! Our cause is righteous!', '', 1, 5783, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6065, 'The Brotherhood shall prevail!', '', 1, 5784, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6066, 'Leaping leper gnomes! I\'ve been stuck in there for months. Thanks, gang.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6067, 'Who dares step into my domain! Come! Come, and be consumed!', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6068, 'Ever vigilant in their protection of the natural world, the druids are guided by Arch Druid Fandral Staghelm who resides in the Cenarion Enclave. You will find him and the other Druid trainers in the northern section of Darnassus.', 'Ever vigilant in their protection of the natural world, the druids are guided by Arch Druid Fandral Staghelm who resides in the Cenarion Enclave. You will find him and the other Druid trainers in the northern section of Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6069, 'I am an officer of the city, working in the capacity of the Undercity\'s census. Since our liberation from the Scourge, we have tried to keep an accurate track of our city\'s populace. Through arcane methods and sharpened mental discipline, I am able to access these records. If you have business with me accordingly, please proceed. You should know if you do, at any rate.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6070, 'The Warchief has instructed me to study all kinds of weapons and armor. He has sent me here to the hub of both the tauren and orc cultures in the Barrens to meet as many travelers as possible, and to learn about the cultures of the Barrens.$B$BHe feels there\'s something to be learned from even the most pathetic of cultures... like the quilboar or centaur.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6071, 'You surrendered. I win.', 'You surrendered. I win.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6072, 'Now that\'s a War Stomp! Good job!', 'Now that\'s a War Stomp! Good job!', 0, 0, 3, 0, 21, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6073, 'That\'s how it\'s done class! You better be paying attention!', 'That\'s how it\'s done class! You better be paying attention!', 0, 0, 3, 0, 21, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6074, 'Now that\'s putting your hoof down!', 'Now that\'s putting your hoof down!', 0, 0, 3, 0, 21, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6075, 'You! $R! Come here. <snort>$B$BTime is short, and my end <snort> is near. Mangletooth shall win; you will see. <snort>$B$BMy capture can still aid <snort> my people. The Razormane tribe is more powerful than the Bristlebacks!$B$BThe Horde looks to find out who leads the raids on their people. You <snort> shall aid Mangletooth in return for the information only I know. Mangletooth can help you! <snort> But you must help Mangletooth.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6076, 'Welcome to Stormwind! I am one of the city\'s officers, aiding the citizenry of the city through our census bureau. If you are in need of locating someone specific, then I might be able to assist you. Through arcane methods and sharpened mental discipline, I am able to access these records.$B$BIf you have business with me, then please proceed!', '', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6077, 'My hatred is strong <snort>, but the time is right to tell you a tale, $r.$B$BLong ago a battle took place between gods. Agamaggan, our great boar god, fell to the earth after being defeated in the War of the Ancients, his blood raining down and his bones torn asunder. <snort>$B$BGreat thorns reached up out of the earth where his blood touched the ground, and his skull adorns one of the caverns to the south.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6078, 'Whew... I need a smoke and the touch of a fine gnomish woman - not particularly in that order!$B$BI\'ve been stuck inside the belly of that thing for months! What day is it anyway? Ah, nevermind. All that matters is that I\'m finally out.$B$BWOOO HOOO!', '', 0, 0, 0, 1, 5, 4, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6079, 'How the hell did you get in there to begin with?\n', 'How the hell did you get in there to begin with?\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6080, 'How?? Well, it\'s a long story.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6081, 'I\'m all ears.', 'I\'m all ears.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6082, 'Alrighty then! So I was down at the lava pits of the Molten Span, testing out my lava suit. Everything was going great. I was even able to swim around in that stuff without suffering a single injury!$B$BSo there I am, minding my own business, doing a few backstrokes, when out of nowhere this thing swallows me up whole! Had it not been for my lava suit and built in food pellet dispenser, I would have been done for!', '', 0, 0, 0, 1, 1, 5, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6083, 'So let me get this straight. You were swimming, nay, doing backstrokes in molten lava with this... this lava suit of yours? Yes? And out of nowhere, this here beast swallowed you whole? ', 'So let me get this straight. You were swimming, nay, doing backstrokes in molten lava with this... this lava suit of yours? Yes? And out of nowhere, this here beast swallowed you whole? ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6084, 'Say, I sense a measure of disbelief in your voice - but yes, that\'s how it happened.', '', 0, 0, 0, 1, 25, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6085, 'I must know something: How did you go to the bathroom with that thing on?', 'I must know something: How did you go to the bathroom with that thing on? ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6086, 'I\'m not out of the belly of this beast for more than two minutes and someone is already asking me about my bathroom behavior??? $G Mister:Missy;, I\'d have smacked you silly for that question had you not been the one to rescue me!$B$BAnyhow! I\'m grateful for the assistance. Maybe I can pay you back?', '', 0, 0, 0, 6, 1, 6, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6087, 'Oh?', 'Oh?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6088, 'Well, maybe \'pay\' isn\'t the best word to use. See, when I was stuck in the belly of that thing, I got to thinking:$B$BThis creature lives in hot, flaming magma and yet it doesn\'t take a lick of damage from the fire!$B$BIf only I could find a pristine hide from one of these beasts. I could take it back to my dear friend, Malyfous Darkhammer, in Everlook. If anyone could do anything with the hide of a beast like this, it would be him. \n', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6089, 'I\'ll consider that the next time I run into one of these things.', 'I\'ll consider that the next time I run into one of these things.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6090, 'So how about you deliver a message for me?', '', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6091, 'Ish nu! That\'s enough!', 'Ish nu! That\'s enough!', 0, 0, 3, 0, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6092, 'To Malyfous, I assume.', 'To Malyfous, I assume.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6093, 'Wanna buy a cockroach?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6094, 'You will never wake the dreamer!', '', 1, 5785, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6095, 'The coils of death will crush you!', '', 1, 5787, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6096, 'I am the serpent king! I can do anything! ', '', 1, 5788, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6097, '', 'None can stand against the Serpent Lords!', 1, 5786, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6098, 'Good, good. Now bow to each other.', 'Good, good. Now bow to each other.', 0, 0, 3, 0, 21, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6099, 'Now bow to me.', 'Now bow to me.', 0, 0, 3, 0, 2, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6100, 'Good job you two. Now go have a seat.', 'Good job you two. Now go have a seat.', 0, 0, 3, 0, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6101, '%s wimpers.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6102, '%s grumbles.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6103, '%s snorts.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6104, '%s cries.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6105, 'Hahah! Revenge is mine!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6106, '%s takes a more careful aim...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6107, '%s picks the orc target...', '', 2, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6108, '%s takes aim again...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6109, '%s takes aim one last time...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6110, '', 'Time is running out for me... for us all...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6111, '', 'I have so much to do... so much before the plague comes...\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6112, '', 'John, honey, where are you?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6113, '', 'What is happening to everyone?  I feel... cold...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6114, '', 'The spirit looks upon you with a sense of completeness and cognizance that had not been present before.$B$B\"I feel as though a great burden has been lifted from my soul.  Soon I will rest forever in peace.  I now know this, and I await this time patiently.$B$BThank you, stranger.\"', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6115, 'You, too, shall serve!', '', 1, 5793, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6116, 'Another falls!', '', 1, 5795, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6117, 'You have <snort> aided Mangletooth more than I would have dreamed, $r.$B$BThank you.$B$BAs long as I remain a captive here, you may bring me blood shards from the Bristleback quilboar and I will bless you with Agamaggan\'s power.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6118, '%s casually points at the orc target...', '', 2, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6119, '%s blows the smoke off his rifle\'s barrel..', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6120, '%s takes aim at the bottles...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6121, '%s takes aim at the bottles again...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6122, '%s then takes aim at the bottles...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6123, '%s only hits the side of the box!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6124, '%s quickly shoots the middle target dead center...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6126, 'Rawww! Urok is dead!! Hahah!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6127, 'Revenge is mine!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6128, 'The Thorium Brotherhood does not waste time on ants. Be gone!', '', 0, 0, 0, 25, 5, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6129, 'Tell me about the hide.', 'Tell me about the hide.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6130, 'It\'s a simple process for a craftsman of my caliber.$B$BThe most difficult aspect of crafting items out of beast hides and carapace is the actual acquisition of said parts. Now, if you could bring me a pristine hide from this beast, I could make several different powerful items from the flesh.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6131, 'What are the chances of obtaining such a hide?', 'What are the chances of obtaining such a hide?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6132, 'I reckon that the odds of obtaining such a piece would be a one in a million! A once in a lifetime skin! Even the most disciplined artisans of the craft would no doubt fail several hundred times in the process of skinning the beast before a pristine sample was recovered.\n', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6133, 'So let us pretend I get this hide, what else would you need and what could you actually make?', 'So let us pretend I get this hide, what else would you need and what could you actually make?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6134, 'If you actually brought me the hide of the beast? Well... Let me show you...', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6135, 'Ok!', 'Ok!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6136, '%s turns to the paper target...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6137, '%s quickly shoots the orc dummy...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6138, '%s shoots from the hip at the bottles...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6139, 'Read through my catalogue, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6140, '%s misses...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6141, '%s misses again...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6142, '%s hits!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6143, 'Left Combat', 'Left Combat', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6144, 'The Thorium Brotherhood Fall Catalogue:$B$BInside you will find the hottest deals on all of your adventuring needs! We\'ll even show you where to pick up those difficult to find components and supplies.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6145, 'Chapter on Frayed Abomination Stitchings.', 'Chapter on Frayed Abomination Stitchings.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6146, 'Chapter on Enchanted Scarlet Thread.', 'Chapter on Enchanted Scarlet Thread.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6147, 'Chapter on Frostwhisper\'s Embalming Fluid.', 'Chapter on Frostwhisper\'s Embalming Fluid.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6149, 'Here they come! Defend yourselves!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6150, 'TIMMY!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6151, 'Chapter on Arcane Crystals and Arcanite.', 'Chapter on Arcane Crystals and Arcanite.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6152, 'You\'ve accepted my surrender.', 'You\'ve accepted my surrender.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6153, '', 'Please, follow me.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6154, '', 'I haven\'t really figured out what it is, but there\'s something strange about the hot springs...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6155, '', '%s tosses the sword into the shallow pool.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6156, '', 'I\'ve found that when I throw something into this water, it has improved incredibly when I retrieve it.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6157, '', 'Well, whatever it is, it works quite well!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6158, '', 'And I am determined to find out more... So if you need my help again, you know where I\'ll be.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6159, 'The title of this book reads with a handwritten flourish: Maureen Dalson\'s Diary.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6160, 'A diary with yellowing pages lies open to the last entry:$B$B\"I don\'t know what\'s going to happen now that Harold\'s turned. It took everything out of me to drag him into the only place I could find that\'d lock him up tight and hold him. I hid the key on me, and I pray it works...$B$BI hope I can get out of here, but I need money to do that. Our stuff\'s all in the cabinet I think... and now that I think about it, doesn\'t Harold keep the key on him?$B$BI\'ll rest on it, I\'m dead tired now.\"', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6161, 'Chapter on Skin of Shadow.', 'Chapter on Skin of Shadow.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6162, 'Intruders at the Service Gate! Baron Rivendare must be warned!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6163, 'If it\'s frayed abomination stitchings you be wanting, to Stratholme you\'ll be jaunting.$B$BFormerly the hometown of the Lightbringer hisself, now overrun by the minions of Kel\'Thuzad.$B$BBrave the perils of the burning city and strike down the meaty abominations! Take from them your prize: Frayed abomination stitchings!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6164, 'The final sanctum of crazed zealots. \'Tis where they create their scarlet uniforms, magically imbued by the enchanted scarlet thread!$B$BTo Stratholme, adventurer!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6165, 'The finest preservative of flesh known to man and Scourge alike - Frostwhisper\'s embalming fluid. Only one place an adventurer could find this and one would think that the lich the fluid is named after would not take too kindly to anyone going to said place.$B$BBut... if it\'s the fluid you want, the Scholomance you\'ll haunt...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6166, 'The ground upon which the Scholomance was built was given to Kel\'Thuzad by the Barov family. In exchange for this land - a place where the Cult of the Damned could learn the foul magics of the Scourge - Kel\'Thuzad granted the Barov\'s immortality through undeath.$B$BIt is in Shadow Vault that you will find both the skin of shadow and its owner, Lady Illucia Barov.$B$BA finer mirror for the vain and dead never existed.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6167, 'A staple of the Thorium Brotherhood.$B$BArcanite crystals are harvested from rich thorium lodes. Through the alchemical prowess of powerful alchemists, arcanite bars are born.$B$BSeek out a miner, if you aren\'t one... and find a good alchemist! You\'ll need both.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6168, '%s paces back and forth nervously.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6169, '', 'You should not be here! Slay them!', 1, 5799, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6170, '', 'Paltry kill.', 1, 5801, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6171, 'Who dares disturb my meditation?', '', 1, 5802, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6172, 'Dust to dust.', '', 1, 5803, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6173, 'Usurpers! Gnomeregan is mine!', '', 1, 5807, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6174, 'My machines are the future! They\'ll destroy you all!', '', 1, 5808, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6175, 'And STAY dead!', '', 1, 5810, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6176, 'Explosions! MORE explosions! I got to have more explosions!', '', 1, 5809, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6177, 'Electric justice!', '', 1, 5811, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6178, 'Victory! For Agamaggan!', '', 1, 5812, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6179, '', 'Troublesome whelps. I\'ll teach you to interfere!', 1, 5813, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6180, '', 'Who\'s next?', 1, 5814, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6181, '', 'You outsiders will pay for encroaching on our land!', 1, 5815, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6182, '', 'Bah! My power rules here!', 1, 5816, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6183, '', 'Our new allies will avenge us!', 1, 5818, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6184, 'Slay them, my brethren! For the Scourge!', '', 1, 5822, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6185, 'Me smell stench of the living!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6186, 'Me feast on you all!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6187, 'You\'ll never leave this place alive.', '', 1, 5825, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6188, 'Too easy.', '', 0, 5826, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6189, 'To me, my servants!', '', 1, 5828, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6190, 'Come, spirits - attend your master!', '', 1, 5829, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6191, 'Your skulls I shall crack with all the might of Blackrock.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6192, 'I am the hand of the Lich King!', '', 1, 5827, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6193, '%s wipes his brow.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6194, 'Ah - I\'ve been waiting for a real challenge!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6195, 'Light, give me strength!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6196, 'Is that all?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6197, 'Unworthy.', '', 1, 5836, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6198, '', 'The Light has spoken!', 1, 5839, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6199, 'You will not defile these mysteries!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6200, 'Burn in righteous fire!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6201, 'We hunger for vengeance.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6202, 'No rest... for the angry dead!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6203, 'More... More souls!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6204, 'Tell me... tell me everything!', '', 0, 5847, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6205, 'Purged by pain!', '', 0, 5848, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6206, 'Naughty secrets!', '', 0, 5849, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6207, 'I\'ll rip the secrets from your flesh!', '', 0, 5850, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6208, 'Take a look at this, Malyfous.', 'Take a look at this, Malyfous.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6209, 'What have you got there, $n?$B$B<Malyfous points to the unfired plate gauntlets hanging out of your pack.>', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6210, '', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6211, 'Don\'t forget to get some Flame in a Bottle from the box here. You\'re going to need it to destroy their command tent.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6212, 'By Thaurissan\'s beard! Slay them!', '', 1, 5852, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6213, 'Me Grimlok, king!', '', 1, 5853, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6214, 'Die! Die!', '', 1, 5854, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6215, 'Reckless mortal!', '', 1, 5858, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6216, 'I\'ll feast on your bones!', '', 0, 0, 14, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6217, 'The Soulflayer comes!', '', 1, 5862, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6219, 'Hakkar shall live again!', '', 1, 5865, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6220, 'Turn back! Do not wake the dreamer!', '', 1, 5866, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6221, 'Sands consume you!', '', 1, 5872, 14, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6222, 'Fall!', '', 1, 5873, 14, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6223, 'Let the executions begin!', '', 1, 5874, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6224, 'Justice is done!', '', 1, 5875, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6225, 'This desert be mine!', '', 1, 5876, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6226, 'Die, outlander!', '', 1, 5877, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6227, 'Feel the fury of the sands!', '', 1, 5879, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6228, 'The Sandfury reign supreme!', '', 1, 5878, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6229, 'Welcome to the front lines, $c. While politicians may connive and scheme amongst themselves, they\'ll find no such luxury afforded to them here. This is the Bulwark, and we fight for our continued survival on a daily basis here.$B$BIf you\'ve come here looking for a chance to prove yourself as a hero, then you\'ll find plenty of opportunities to do so... especially as we push towards Andorhal and whatever malign force that controls the Scourge there.', '', 0, 0, 0, 66, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6230, 'Welcome to the front lines, $c. The Kingdom of Stormwind, with the aid of its allies, has sent me here to counter the growing threat of the Scourge. Here at Chillwind Point, we fight for our continued survival on a daily basis.$B$BIf you\'ve come here looking for a chance to prove yourself as a hero, then you\'ll find plenty of opportunities to do so... especially as we push towards Andorhal and whatever malign force that controls the Scourge there.', '', 0, 0, 0, 66, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6231, '', '%s looks around nervously.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6232, '', 'Are you sure you weren\'t followed here? I have a very bad feeling all of a sudden...', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6233, '', 'Be on your guard!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6234, 'We are betrayed!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6235, 'It is good to see others who fight the Scourge in Stratholme, for it is here they are strongest.$B$BGreetings. I am Aurius. I was once a paladin of the Silver Hand, but like others whose faith wavered... I fell from grace. The Scourge seduced me and nearly transformed me into a death knight!$B$BFinally sensing my peril, I fled to this chapel. It is a sacred place, where the first paladin, Uther the Lightbringer, was ordained into the order of the Silver Hand.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6236, 'Rivendare! I come for you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6237, 'Argh!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6238, 'I do not have much time in this world, but Light willing, it is time enough to fulfill my destiny. When you face Baron Rivendare, I will be with you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6239, 'Leave me. My wounds are great and I fear they will overtake me. Thank you, $n. I will die here, but because of you... I\'ll die in peace.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6240, 'Test not my patience, mortal.', '', 0, 0, 8, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6241, 'You will have to excuse me, Lorax, I do not speak \'crazy.\'', 'You will have to excuse me, Lorax, I do not speak \'crazy.\'', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6242, 'Bite your tongue, $r, lest I rip it from your skull case and use it as a horn warmer.', '', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6243, 'My apologies, I did not realize that you could understand what I was saying. What is it you are doing out here?', 'My apologies, I did not realize that you could understand what I was saying. What is it you are doing out here?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6244, 'What it is that I do, of course. I brood. I lament. I hate. I plot. When that cycle is complete, I brood again. What is it you want from me? I have much on my mind.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6245, 'Do you? Perhaps you should tell me what it is that is bothering you.', 'Do you? Perhaps you should tell me what it is that is bothering you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6246, 'You?$B$B<Lorax laughs.>$B$BThat is doubtful.$B$BI stand in these ruins and dream. Dream of the day that I catch that miserable thief, Goraluk Anvilcrack.$B$BWe had a deal, him and I. A deal that he broke. Betrayed.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6247, 'What deal?', 'What deal?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6248, 'I would teach him how to demon forge in exchange for his soul. The soul of a Blackrock orc is worth very little but in these trying times, a soul, any soul, is better than none at all.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6249, 'So how did he break the deal?', 'So how did he break the deal?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6250, 'He took the unforged rune covered breastplate, used in the making of the demon forged breastplate and ran away without making payment. A fool, I am not, however... the unforged chest piece is useless without knowledge or the recipe to apply said knowledge.$B$BI wonder... does he sit safely in his city of Blackrock Spire and attempt to create the item from the raw material? I know he dare not leave!\n', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6251, 'Perhaps I can be of some assistance. I will make a deal with you, Satyr. I shall recover this unforged breastplate and slay the beast, Goraluk Anvilcrack. In exchange for this task, you will teach me how to create the breastplate.', 'Perhaps I can be of some assistance. I will make a deal with you, Satyr. I shall recover this unforged breastplate and slay the beast, Goraluk Anvilcrack. In exchange for this task, you will teach me how to create the breastplate.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6252, 'I TASTE THE BLOOD OF LIFE!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6253, 'I AM NEAR!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6254, 'HAKKAR LIVES!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6255, '%s breaks apart and loses strength!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6256, 'The liquids and mixtures all bubble and spasm as you approach. Next to the equipment lies a large pair of tongs that look as though they would be adequate for dropping in samples of ooze.$B$BThere is safety gear present, but it appears to be dusty and well-ignored.$B$BDo you place some of your samples into the beakers?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6257, '%s explodes and releases several Bile Slimes!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6258, '%s belches out a disgusting Bile Slime!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6259, '', 'Ha ha, $n! I won\'t ever forget the look on Daryl\'s face when you showed him that head!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6260, 'Leave this place!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6261, 'My turn, meathead! I get to throw now.', 'My turn, meathead! I get to throw now.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6262, 'You threw last one, idiot. It\'s my turn.', 'You threw last one, idiot. It\'s my turn.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6263, '%s start pushing each other back and forth.', '%s start pushing each other back and forth.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6264, 'What does Dadanga like to eat?\n', 'What does Dadanga like to eat?\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6265, '', 'Since we\'ve begun our stay in the crater, Dadanga\'s taken a liking to the large bloodpetal sprouts that grow all over the place here.$B$BI\'m sure she\'d enjoy a snack, if you feel like bringing her some...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6266, 'OK, OK! It\'s your turn!', 'OK, OK! It\'s your turn!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6267, 'Heya mon. Go talk to mah Master Gadrin right over there. Take care of that Zalazane crazy for him and I fix ya right up!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6268, 'Ya mon, I be Gadrin\'s assistant. Dis here volunteer work be my way of gettin through Witch Doctor School. Since you help him, I help ya for free. Whatcha need fixin?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6269, 'Low spirits', 'Low spirits', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6270, '%s whimpers.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6271, '%s breathes erratically. ', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6272, '%s cries for help.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6273, 'I... I am badly injured... ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6274, 'Save me...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6275, 'You will be ok, Awbee. Your assailants have been terminated.', 'You will be ok, Awbee. Your assailants have been terminated.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6276, 'Listen, $r. Listen well...$B$BThe dragon riders came in the night, mounted atop the black flight. They struck fast and with deadly precision, taking young whelps - such as me - and escaping under the veil of darkness.$B$BI watched in horror as they used my brothers and sisters in their experiments. From our essence would be created the chromatic flight.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6277, 'Continue please...', 'Continue please...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6278, 'Nefarian had ordered that unsuitable subjects be destroyed immediately, but these retched beasts would not do so without first inflicting an \'ample\' amount of torture.$B$BWhen we were thoroughly \'broken,\' they would round up what remained and throw us to these thugs. Our bodies used as instruments in their sick game: thrown into the lava or worse; to the open and waiting arms of the bloodthirsty legion below.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6279, 'Horrifying.', 'Horrifying.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6280, 'You came along just as they were about to throw me to the fiends below - for that, I thank you. Perhaps, I - rather - we, of the blue flight, can assist you further?$B$BYou have come this far, $r, risked much. Will you go a ways further to battle Nefarian and his legion of Blackrock? ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6281, 'Absolutely.', 'Absolutely.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6282, 'You cannot save him!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6283, 'Bad hang nail', 'Bad hang nail', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6284, 'Feeling underpowered', 'Feeling underpowered', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6285, 'Jungle Fever', 'Jungle Fever', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6286, 'Uni-brow', 'Uni-brow', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6287, 'Whiplash', 'Whiplash', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6288, 'I don\'t want to go back to work', 'I don\'t want to go back to work', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6289, 'The Ash\'ari Crystals have been destroyed! The Slaughterhouse is vulnerable!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6290, 'If you are here to hunt dragons, be my guest. Where should I send my condolences?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6291, 'Your stench will attract every dragon for miles. Tell me what you want, and quickly.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6292, 'The spirits told me you would come, that you would seek me for some reason, but that even you may not know that reason. So, do you know why you are here?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6293, '', 'Oh hi! My name\'s Sarah. What can I do for you?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6294, '', 'The forest told me of your presence, but not why you were here. So, speak and enlighten me.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6295, 'How you doin\' mon!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6296, '', 'I welcome you, $N. And the Emerald Circle welcomes you.$B$BHopefully together we can help rejuvenate and protect this once-mighty forest. Many forces seek to destroy it from without... and within.$B$BDo not be persuaded by the powere here--it will only corrupt you, like it has this entire land.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6297, 'While Greta focuses her energy on the recovery of Jaedenar and those who she thinks is mostly responsible for the corruption here in Felwood. I spend my time looking into the effects the corruption has had on the land.$B$BHow has it affected the creatures of the land? What must be done to end the pain of their suffering?$B$BUnfortunately, the corruption runs so deep that eternal sleep is too often the answer.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6298, 'Tucked away in the Mage Quarter, there is a little shop called Alchemy Needs. You\'re bound to see all sort of strange shops on your way through the Mage Quarter, but if you stick to the outer rim you\'ll be sure to find it.', 'Tucked away in the Mage Quarter, there is a little shop called Alchemy Needs. You\'re bound to see all sort of strange shops on your way through the Mage Quarter, but if you stick to the outer rim you\'ll be sure to find it.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6299, 'Stormwind Counting House is located by the front gates in the Trade District of Stormwind. And when you get tired of counting your money, be sure to stop by the Gilded Rose for a drink.', 'Stormwind Counting House is located by the front gates in the trade district of Stormwind. And when you get tired of counting your money, be sure to stop by the Gilded Rose for a drink.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6300, 'You mean you can\'t smell the smoke of the Dwarven forges from here? Well, you will be looking for Therum Deepforge if you want to learn blacksmithing. He\'s over in the Dwarven District which is just north of the Cathedral Square.', 'You mean you can\'t smell the smoke of the Dwarven forges from here? Well, you will be looking for Therum Deepforge if you want to learn blacksmithing. He\'s over in the Dwarven District which is just north of the Cathedral Square.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6301, '', '%s jumps in fright!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6302, '', 'Ahhhhh!!!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6303, '', 'You big meanie! Who put you up to this?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6304, '', 'It was Umi, wasn\'t it?! She\'s always playing jokes on me, and now she\'s got you in on it too!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6305, '', '%s sighs.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6306, '', '%s jumps in fright!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6307, '', 'Ahhhh!!! What is that thing?!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6308, '', 'Umi sent you, didn\'t she?! She told me to expect a surprise, but I never thought that this is what she meant!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6309, 'There\'s a great cook who works at the Pig and Whistle Tavern in Old Town. I\'ve watched him use a knife when he cooks and just between you and me, I don\'t think he\'s always been a cook. Don\'t get me wrong... if you still want to learn, he\'s the best we have.', 'There\'s a great cook who works at the Pig and Whistle Tavern in Old Town. I\'ve watched him use a knife when he cooks and just between you and me, I don\'t think he\'s always been a cook. Don\'t get me wrong... if you still want to learn, he\'s the best we have.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6310, 'Ah, you\'re looking for Lucan Cordell. He runs a shop on the outside of the Magic Quarter on your way to the Stockade. ', 'Ah, you\'re looking for Lucan Cordell. He runs a shop on the outside of the Magic Quarter on your way to the Stockade. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6311, '%s attempts to get help!', '%s attempts to get help!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6312, 'Hey, that reminds me, I need to have my rocket boots repaired. Word to the wise my friend, rocket boots and gryphons don\'t mix. \n\nOh, but you need directions... just head on over to the Dwarven District, you\'ll find Lilliam Sparkspindle towards the back.', 'Hey, that reminds me, I need to have my rocket boots repaired. Word to the wise my friend, rocket boots and gryphons don\'t mix. \n\nOh, but you need directions... just head on over to the Dwarven District, you\'ll find Lilliam Sparkspindle towards the back.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6313, 'No better place to learn First Aid than under the watchful eye of Shaina Fuller at the Cathedral of Light. \n\nTo get there, just look for the yellow rooftops, or the towering spire of the Cathedral itself which you can see from almost anywhere in the city.', 'No better place to learn First Aid than under the watchful eye of Shaina Fuller at the Cathedral of Light. \n\nTo get there, just look for the yellow rooftops, or the towering spire of the Cathedral itself which you can see from almost anywhere in the city.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6314, 'Oh!!! Get that thing away from me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6315, 'That bum Leland... all he does is sit out on the dock outside the Trade District and fish all day. I sure hope he\'s good at it for all the time he spends fishing. If you want to learn, why don\'t you go talk to him?', 'That bum Leland... all he does is sit out on the dock outside the Trade District and fish all day. I sure hope he\'s good at it for all the time he spends fishing. If you want to learn, why don\'t you go talk to him?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6316, 'There\'s nothing quite like communing with nature... or at least that\'s what the night elves tell me. I\'m not much for talking to flowers and trees, but I\'m sure Tannysa could help you out. She\'s over in the Mage Quarter outside of Alchemy Needs.', 'There\'s nothing quite like communing with nature... or at least that\'s what the night elves tell me. I\'m not much for talking to flowers and trees, but I\'m sure Tannysa could help you out. She\'s over in the Mage Quarter outside of Alchemy Needs.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6317, 'Why do you chase me, Mechanical Yeti?! WHY?!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6318, '%s runs quickly away from Umi\'s Mechanical Yeti.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6319, 'A Hunter is it? Well, then you will want to speak with Thorfin Stoneshield over in the Dwarven District. \n\nOh, and you be sure to tell him that if I step in his pet\'s business one more time, he\'s going to be training other hunters from behind bars in the Stockade!', 'A Hunter is it? Well, then you will want to speak with Thorfin Stoneshield over in the Dwarven District. \n\nOh, and you be sure to tell him that if I step in his pet\'s business one more time, he\'s going to be training other hunters from behind bars in the Stockade!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6320, 'I\'m jumpy as it is... and people insist on scaring me... Next time, though, I\'ll be ready!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6321, 'You\'re into leather, eh? Sounds like you should talk to Simon Tanner over in Old Town. He runs a shop there called the Protective Hide.', 'You\'re into leather, eh? Sounds like you should talk to Simon Tanner over in Old Town. He runs a shop there called the Protective Hide.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6322, '%s looks relieved.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6323, 'Leave it to the dwarves to send a mining specialist from Ironforge to tell us how to mine. How hard is it to take a pick and chip away at some rock? Well, since he\'s here, he\'s probably the one you should see anyhow. His name is Gelman and he resides over in the Dwarven District.', 'Leave it to the dwarves to send a mining specialist from Ironforge to tell us how to mine. How hard is it to take a pick and chip away at some rock? Well, since he\'s here, he\'s probably the one you should see anyhow. His name is Gelman and he resides over in the Dwarven District.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6324, 'You know, I could run an orc through or slice up a troll. I could even carve my initials in an ogre\'s flank, but I just can\'t stand to skin an animal. Go figure, eh. \n\nWell, if you have the stomach for it, then go see Maris Granger at the Protective Hide in Old Town.', 'You know, I could run an orc through or slice up a troll. I could even carve my initials in an ogre\'s flank, but I just can\'t stand to skin an animal. Go figure, eh. \n\nWell, if you have the stomach for it, then go see Maris Granger at the Protective Hide in Old Town.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6325, 'A tailor is it? Well, if you hang out here long enough you\'ll eventually hear word of mouth about Georgio Bolero. He\'s been setting the trends for some months now. If you wish to learn from him, you can find him over in the Mage Quarter in the outer rim.', 'A tailor is it? Well, if you hang out here long enough you\'ll eventually hear word of mouth about Georgio Bolero. He\'s been setting the trends for some months now. If you wish to learn from him, you can find him over in the Mage Quarter in the outer rim.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6326, 'Warlocks, eh. Well, you didn\'t hear it from me, but I\'ve heard rumors that those types gather at a bar called the Slaughtered Lamb in the Mage Quarter. ', 'Warlocks, eh. Well, you didn\'t hear it from me, but I\'ve heard rumors that those types gather at a bar called the Slaughtered Lamb in the Mage Quarter. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6327, '%s marches around, roaring and making a ruckus.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6328, 'Deeprun Tram', 'Deeprun Tram', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6329, 'RAAAAAAAR!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6330, 'You don\'t have the stomach for the gryphon ride, huh. Well lucky for you, we have an alternative where you can keep your feet on the ground... more or less. It\'s the gnomish mover of people, the Deeprun Tram. Conveniently located in the back of the Dwarven District.', 'You don\'t have the stomach for the gryphon ride, huh. Well lucky for you, we have an alternative where you can keep your feet on the ground... more or less. It\'s the gnomish mover of people, the Deeprun Tram. Conveniently located in the back of the Dwarven District.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6331, 'You\'ve heard about the Tram have you? What a ride that thing is! You\'ll find it in the Dwarven District towards the back. Oh, and be sure to keep your arms and legs inside the tram while the tram is in motion.', 'You\'ve heard about the Tram have you? What a ride that thing is! You\'ll find it in the Dwarven District towards the back. Oh, and be sure to keep your arms and legs inside the tram while the tram is in motion.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6332, 'Study it, $n: The Catalogue of the Wayward.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6333, 'Aye, everyone wants to ride the tram, it\'s a real hoot and a half. Well, if ye want to find the tram, it\'s located over in Tinker Town just east of the gates of Ironforge.', 'Aye, everyone wants to ride the tram, it\'s a real hoot and a half. Well, if ye want to find the tram, it\'s located over in Tinker Town just east of the gates of Ironforge.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6334, 'Oh, the tram, the tram... everyone wants to ride the tram. What happened to the good ol\' days of flying on the back of a gryphon, gripping the saddle for dear life as you flew up high in the sky above? \n\nBah! You go take the tram then... it\'s over in Tinker Town with the rest of the gnomish contraptions.', 'Oh, the tram, the tram... everyone wants to ride the tram. What happened to the good ol\' days of flying on the back of a gryphon, gripping the saddle for dear life as you flew up high in the sky above? \n\nBah! You go take the tram then... it\'s over in Tinker Town with the rest of the gnomish contraptions.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6335, 'Are ye really askin\' me where the Blacksmith is? Have ye not heard of The Great Forge!? All our best blacksmiths gather there to craft their weapons and armor! \n\nIt\'s dead smack in the middle of Ironforge, and that\'s where you\'ll find one of our most brilliant blacksmiths, Bengus Deepforge. ', 'Are ye really askin\' me where the Blacksmith is? Have ye not heard of The Great Forge!? All our best blacksmiths gather there to craft their weapons and armor! \n\nIt\'s dead smack in the middle of Ironforge, and that\'s where you\'ll find one of our most brilliant blacksmiths, Bengus Deepforge. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6336, 'Och! That Daryl Riknussun is a brilliant cook! He makes a chilli so hot ye could temper steel with it! I don\'t think he\'ll teach ye the recipe, but he\'ll certainly help ye to become a good cook. Ye\'ll find him at the Bronze Kettle o\'er on the northeastern side of The Great Forge.', 'Och! That Daryl Riknussun is a brilliant cook! He makes a chilli so hot ye could temper steel with it! I don\'t think he\'ll teach ye the recipe, but he\'ll certainly help ye to become a good cook. Ye\'ll find him at the Bronze Kettle o\'er on the northeastern side of The Great Forge.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6337, 'Oh, you\'re looking for Springspindle Fizzlegear then. Why that little gnome and his gadgets... Let me tell you, if you\'re going to his shop in Tinker Town, keep your hands to yourself and don\'t pet any of his mechanical sheep! \n\nExploding sheep... now why would you even make such a thing!?', 'Oh, you\'re looking for Springspindle Fizzlegear then. Why that little gnome and his gadgets... Let me tell you, if you\'re going to his shop in Tinker Town, keep your hands to yourself and don\'t pet any of his mechanical sheep! \n\nExploding sheep... now why would you even make such a thing!?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6338, 'First Aid? Well ye don\'t look hurt to me. If yer really interested in learning First Aid, then Nissa Firestone is the one to learn from. Go see her over by The Great Forge in her shop, The Ironforge Physician.', 'First Aid? Well ye don\'t look hurt to me. If yer really interested in learning First Aid, then Nissa Firestone is the one to learn from. Go see her over by The Great Forge in her shop, The Ironforge Physician.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6339, 'I once heard ol\' Grimnur say he fished up a sword. Or was it a swordfish? Damn that Grimnur, he\'s told so many fish tales I can\'t remember which is which. Well, why don\'t you go see him over in The Forlorn Cavern and have him tell you what it was.', 'I once heard ol\' Grimnur say he fished up a sword. Or was it a swordfish? Damn that Grimnur, he\'s told so many fish tales I can\'t remember which is which. Well, why don\'t you go see him over in The Forlorn Cavern and have him tell you what it was.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6340, 'Well now, ye\'ll need papers to form a guild, and Jondor Steelbrow can help you get started with the process. He can be found over at the Ironforge Visitor\'s Center when yer ready to get started.', 'Well now, ye\'ll need papers to form a guild, and Jondor Steelbrow can help you get started with the process. He can be found over at the Ironforge Visitor\'s Center when yer ready to get started.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6341, 'Do ye have the potential to be a skilled hunter? Well $gladdie : lassie;, only time will tell. Just don\'t ye go shooting at apples atop peoples\' heads till ye\'ve spoken with Regnus o\'er in the Hall of Arms.', 'Do ye have the potential to be a skilled hunter? Well $gladdie : lassie;, only time will tell. Just don\'t ye go shooting at apples atop peoples\' heads till ye\'ve spoken with Regnus o\'er in the Hall of Arms.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6342, 'Sure ye don\'t want to be a Blacksmith? Well, if yer heart is set on Leatherworking, then you best talk to Fimble Finespindle over at his shop on the northwest side of The Great Forge.', 'Sure ye don\'t want to be a Blacksmith? Well, if yer heart is set on Leatherworking, then you best talk to Fimble Finespindle over at his shop on the northwest side of The Great Forge.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6343, 'Bah! Mailboxes... all I get is junk mail lately! \n\nIncrease the size of this... decrease the size of that... act now for your free Sword of Dragonslaying. I have to change my mailbox number every other month! \n\nWell, ye can\'t say I didn\'t warn ye. If you\'re looking for the mailbox it\'s just outside the Stonefire Tavern.', 'Bah! Mailboxes... all I get is junk mail lately! \n\nIncrease the size of this... decrease the size of that... act now for your free Sword of Dragonslaying. I have to change my mailbox number every other month! \n\nWell, ye can\'t say I didn\'t warn ye. If you\'re looking for the mailbox it\'s just outside the Stonefire Tavern.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6344, 'Well well! You\'ve come to the right place to learn Mining, $gboyo : girlie;! Geofram Bouldertoe himself resides at the Deepmountain Mining Guild. Ye couldn\'t ask for a better instructor than him.', 'Well well! You\'ve come to the right place to learn Mining, $gboyo : girlie;! Geofram Bouldertoe himself resides at the Deepmountain Mining Guild. Ye couldn\'t ask for a better instructor than him.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6345, 'The book tells of dragons uniting against another, more powerful dragon, named Deathwing. From what you can make out, the chromatic dragonflight had almost been unleashed upon the world. The Aspects were prepared.\n', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6346, 'Ah, nothing better than a priest to cure what ails ye. Of course a nice tall pint cures a lot of what ails ye too, but I don\'t think Braenna Flintcrag teaches brewing. I guess ye\'ll have to settle for some priest training over in the Hall of Mysteries instead.', 'Ah, nothing better than a priest to cure what ails ye. Of course a nice tall pint cures a lot of what ails ye too, but I don\'t think Braenna Flintcrag teaches brewing. I guess ye\'ll have to settle for some priest training over in the Hall of Mysteries instead.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6347, 'Ye might not know it, but in addition to being a skilled tailor, Jormund is also a slayer of giants! Why he must have overheard a few of us sharing stories when he shouted that he\'d killed seven with one blow! \n\nYe should pay him a visit at his store on the north side of The Great Forge an\' see if he\'ll tell ye the tale!', 'Ye might not know it, but in addition to being a skilled tailor, Jormund is also a slayer of giants! Why he must have overheard a few of us sharing stories when he shouted that he\'d killed seven with one blow! \n\nYe should pay him a visit at his store on the north side of The Great Forge an\' see if he\'ll tell ye the tale!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6348, 'Skinning? The only thing ye look like ye could skin is yer own knee! Ha! Now why don\'t you go see Balthus Stoneflayer so he can teach you how to properly skin some animals. You\'ll find him in Finespindle\'s Leather Goods on the northern side of The Great Forge.', 'Skinning? The only thing ye look like ye could skin is yer own knee! Ha! Now why don\'t you go see Balthus Stoneflayer so he can teach you how to properly skin some animals. You\'ll find him in Finespindle\'s Leather Goods on the northern side of The Great Forge.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6349, 'A warlock, eh... Well, if you\'ve got a mind to be doing that sort of thing around here then ye best keep yer demons on a leash $gboyo : girlie;. I don\'t like demons, but I love putting mah axe to them. Ye get me? \n\nNow you just leave me be and go get lost in The Forlorn Cavern. That\'s where you\'ll find that warlock, Thistleheart.', 'A warlock, eh... Well, if you\'ve got a mind to be doing that sort of thing around here then ye best keep yer demons on a leash $gboyo : girlie;. I don\'t like demons, but I love putting mah axe to them. Ye get me? \n\nNow you just leave me be and go get lost in The Forlorn Cavern. That\'s where you\'ll find that warlock, Thistleheart.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6350, 'Kelstrum is a loudmouth and a braggart... and if that weren\'t enough he\'s also one of the most skilled warriors ye\'ll ever meet! \n\nI tell ye, if ye\'re looking to be trained in the ways of a warrior, that\'s who ye should be talking to. I\'m sure he can be found over in the Hall of Arms east of the Gates of Ironforge.', 'Kelstrum is a loudmouth and a braggart... and if that weren\'t enough he\'s also one of the most skilled warriors ye\'ll ever meet! \n\nI tell ye, if ye\'re looking to be trained in the ways of a warrior, that\'s who ye should be talking to. I\'m sure he can be found over in the Hall of Arms east of the Gates of Ironforge.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6351, 'Deeprun Tram', 'Deeprun Tram', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6352, 'NO!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6353, 'An alchemist? No trouble at all $c. Ainethil, in all her labor and toil, has become the most accomplished alchemist in the Craftsmen\'s Terrace. I would head over there on the double if I were you. On the double! Now off with you!', 'An alchemist? No trouble at all $c. Ainethil, in all her labor and toil, has become the most accomplished alchemist in the Craftsmen\'s Terrace. I would head over there on the double if I were you. On the double! Now off with you!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6354, 'Another material $gboy : girl; are we? Well, if you are obsessed with your precious items then you will probably want to store them at the Darnassus Bank in the center of our city.', 'Another material $gboy : girl; are we? Well, if you are obsessed with your precious items then you will probably want to store them at the Darnassus Bank in the center of our city.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6355, 'Astaia has fished up some curious treasures over the ages she has spent fishing. Why don\'t you go see her over in the Tradesman\'s terrace and have her show you some of them.', 'Astaia has fished up some curious treasures over the ages she has spent fishing. Why don\'t you go see her over in the Tradesman\'s terrace and have her show you some of them.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6356, 'Lysheana can offer you the proper documents, she resides in the Craftsmen\'s Terrace in northeasten Darnassus. May your fellowship bring glory to the Night Elves!', 'Lysheana can offer you the proper documents, she resides in the Craftsmen\'s Terrace in northeasten Darnassus. May your fellowship bring glory to the Night Elves!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6357, 'It is Vesprystus that you seek. He watches over the hippogryphs that come and go from the base of Teldrassil. You will find him through the portal in western Darnassus that will take you to Rut\'theran Village.', 'It is Vesprystus that you seek. He watches over the hippogryphs that come and go from the base of Teldrassil. You will find him through the portal in western Darnassus that will take you to Rut\'theran Village.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6358, 'Jocaste was the greatest of Hunters of her time. These days you can find her over in the Cenarion Enclave where she teaches those who are just starting down the Hunter\'s path.', 'Jocaste was the greatest of Hunters of her time. These days you can find her over in the Cenarion Enclave where she teaches those who are just starting down the Hunter\'s path.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6359, 'Has the sun risen already? There are just never enough hours in the night! Well, Saelienne will take good care of you. You can find her tending to her guests over in the Craftsmen\'s Terrace in northeastern Darnassus.', 'Has the sun risen already? There are just never enough hours in the night! Well, Saelienne will take good care of you. You can find her tending to her guests over in the Craftsmen\'s Terrace in northeastern Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6360, 'A mailbox? You have a letter to send, or perhaps have received one? I hope it\'s something good! There\'s a mailbox right outside of the Bank in the center of Darnassus.', 'A mailbox? You have a letter to send, or perhaps have received one? I hope it\'s something good! There\'s a mailbox right outside of the Bank in the center of Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6361, 'As a culture we believe in letting nothing go to waste. When we are forced to slay a creature we take what we can to make sure its sacrifice was not in vain. \n\nIf you wish to learn the proper way to take the skin of a slain beast, then seek out Eladriel in the Craftsmen\'s Terrace.', 'As a culture we believe in letting nothing go to waste. When we are forced to slay a creature we take what we can to make sure its sacrifice was not in vain. \n\nIf you wish to learn the proper way to take the skin of a slain beast, then seek out Eladriel in the Craftsmen\'s Terrace.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6362, 'With the garments that Me\'Lynn tailors, one might think that her needle was blessed by Elune herself. Unless your needle is so blessed, you best pay her a visit and listen to her teachings. You can find her in The Craftsmen\'s Terrace in northeastern Darnassus.', 'With the garments that Me\'Lynn tailors, one might think that her needle was blessed by Elune herself. Unless your needle is so blessed, you best pay her a visit and listen to her teachings. You can find her in The Craftsmen\'s Terrace in northeastern Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6363, 'Sildinair has trained many of the Sentinels who guard our beloved city. I am certain she can make a warrior out of the likes of you. Seek her out at the Warrior\'s Terrace near the entrance to our city.', 'Sildinair has trained many of the Sentinels who guard our beloved city. I am certain she can make a warrior out of the likes of you. Seek her out at the Warrior\'s Terrace near the entrance to our city.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6364, 'You want to learn how to make a campfire? Well then you had best see Malygen in the Cenarion Enclave before you set fire to Teldrassil itself. ', 'You want to learn how to make a campfire? Well then you had best see Malygen in the Cenarion Enclave before you set fire to Teldrassil itself. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6365, 'One has opened up on the Tradesman\'s Terrace. If you take the path leading Southeast from the bank, it should be easy to find.', 'One has opened up on the Tradesman\'s Terrace. If you take the path leading Southeast from the bank, it should be easy to find.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6366, 'Oh, the ever-present obsession with material wealth. Well, fortune smiles on you, as you can find the auction house in the Tradesman\'s Terrace.', 'Oh, the ever-present obsession with material wealth. Well, fortune smiles on you, as you can find the auction house in the Tradesman\'s Terrace.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6367, 'It is located in the Trade District citizen. It is nearly straight on from the entrance of Stormwind.', 'It is located in the Trade District citizen. It is nearly straight on from the entrance of Stormwind.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6368, 'Another entrepreneur, eh? Well, the Auction House can be found in the Trade District. Not far from the bank.\n', 'Another entrepreneur, eh? Well, the Auction House can be found in the Trade District. Not far from the bank.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6369, 'Auction House', 'Auction House', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6370, 'Auction House', 'Auction House', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6371, 'THIS CANNOT BE!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6372, 'All hands to battle stations! Naga incoming!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6373, '%s explodes into poisonous goo!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6374, 'You dare defile this holy ground?', 'You dare defile this holy ground?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6375, 'Be cleansed by blade, filth!', 'Be cleansed by blade, filth!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6376, 'Enemies have reached the door! Move in! We must hold!', 'Enemies have reached the door! Move in! We must hold!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6377, 'Move back and hold the line! We cannot fail or all will be lost!', 'Move back and hold the line! We cannot fail or all will be lost!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6378, 'Move to the stairs and defend!', 'Move to the stairs and defend!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6379, 'They have broken into the Hall of Lights! We must stop the intruders!', 'They have broken into the Hall of Lights! We must stop the intruders!\n', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6380, 'This will not be the end of the Scarlet Crusade! You will not break our line!', 'This will not be the end of the Scarlet Crusade! You will not break our line!\n', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6381, 'Hello, traveler. Not many find me out this way.$B$BMy name is Rivern, and I train the Winterspring Frostsabers to become mounts.$B$BThese cats are the most feral and difficult to subdue of all the sabers. Only the most trusted and exalted may attempt to ride one.$B$BI will not sell such a mount to just anyone; no, you must work for it and earn your acceptance not only with me, but with the sabers themselves.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6382, 'Information on the Chromatic Carapace.', 'Information on the Chromatic Carapace.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6383, 'Information on Brilliant Chromatic Scales.', 'Information on Brilliant Chromatic Scales.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6384, 'Information on the Blood of Heroes.', 'Information on the Blood of Heroes.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6385, 'Information on Frayed Abomination Stitchings.', 'Information on Frayed Abomination Stitchings.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6386, 'Information on the Skin of Shadow.', 'Information on the Skin of Shadow.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6387, 'Presumed to exist. Only a greater chromatic dragonkin or dragon could possess such a carapace.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6388, 'Commonly found on the lesser chromatic flight: Whelps, broodlings, lesser wyrmkin.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6389, 'Legend claims that in places of extreme conflict, across what is now known as the Plaguelands; clean and holy springs may erupt from the ground. The water from those springs has been named \'Blood of Heroes,\' by artisans and commonfolk alike - in honor of those that fell in the war against the Scourge.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6390, 'The threads that holds together the abominations of Stratholme are highly sought after by artisan craftsmen: Extremely resilient - nearly unbreakable.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6391, 'Used by the Barov family of the Scholomance as a mirror into the souls that they no longer possess. A valuable keepsake.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6392, 'There are many establishments where you can get a drink in Stormwind, but only one will let you spend the night. Head over to the Gilded Rose in the Trade District. I\'ve heard the cost of a room is reasonable and the sheets are clean. ', 'There are many establishments where you can get a drink in Stormwind, but only one will let you spend the night. Head over to the Gilded Rose in the Trade District. I\'ve heard the cost of a room is reasonable and the sheets are clean. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6393, 'Ah, The Gilded Rose... with its soft down beds and warm baths... just thinking about that Inn makes me want to... ::yawn:: Lucky you if you\'re heading over there... you will find it in the west side of the Trade District.  ...nice soft pillows, warm blankets. ::yawn::', 'Ah, The Gilded Rose... with its soft down beds and warm baths... just thinking about that Inn makes me want to... ::yawn:: Lucky you if you\'re heading over there... you will find it in the west side of the Trade District.  ...nice soft pillows, warm blankets. ::yawn::', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6394, 'There is a mailbox located conveniently right between the Stormwind Bank and The Gilded Rose Inn in the Trade District.', 'There is a mailbox located conveniently right between the Stormwind Bank and The Gilded Rose Inn in the Trade District.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6395, 'I don\'t know how they do it... those mailboxes must be magic portals or something. Why I see people gathered around the mailbox next to the bank all day pulling swords and shields and various armor all from that small box!', 'I don\'t know how they do it... those mailboxes must be magic portals or something. Why I see people gathered around the mailbox next to the bank all day pulling swords and shields and various armor all from that small box!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6396, 'The Inn', 'The Inn', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6397, 'Mailbox', 'Mailbox', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6398, 'So you see fit to toy with the Lich King\'s creations? Ramstein, be sure to give the intruders a proper greeting.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6399, 'I would like to buy from you.', 'I would like to buy from you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6400, '', 'Hi! I just got back from exploring the Ruins of Kel\'Theril, to the southwest. Now I\'m intent on investigating the Wildkin that lie just up the ridge here, in Owl Wing Thicket.$B$BHave you spoken to Erelas Ambersky in Rut\'theran Village? He requested that I come here, and perhaps he has a task for you as well.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6401, 'Time to take matters into my own hands. Come. Enter my domain and challenge the might of the Scourge!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6402, 'I don\'t get paid enough for this bull excrement!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6403, 'I can\'t take these taxes! F.I.C.A. me? F.I.C.A. YOU!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6404, 'Hey! Do you like the sauce? The sauce is good, no?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6405, 'And then the Tauren said, \'13 INCHES!\'', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6406, 'I come from the land down under... where women plague and men sunder.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6407, 'The what-bringer? Oh, ASHbringer? Never heard of it... What? Stop bothering me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6408, 'I should have taken the translucent pill.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6409, 'Just sit right back and you\'ll hear a ta... *hic* a tai... *hic* a taiiii *hic* Damnit!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6410, '', 'Have you seen Watcher Backus? So brave...$B$BI think he\'s the toughest of all the Night Watch. I wonder if he even notices I\'m around....', 0, 0, 0, 6, 24, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6411, 'The temperature is... not hot enough!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6412, '*sigh* ...113 degrees Kraklenheit. Can\'t you do any better?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6413, 'You\'re getting warmer... 7039 degrees Kraklenheit!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6414, '8536 degrees Kraklenheit is the reading... Tired of me yet?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6415, 'Who dares disturb our master?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6416, '', 'Hurray!  My doll!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6417, '', 'You\'ll keep the bad people away, won\'t you?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6418, '', 'Let\'s find out the whole story.  That\'s going to take some magic...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6419, '', 'There, it is done.  The book has been enhanced...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6420, 'Looking for these???? You\'ll never have em!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6421, 'The bastion is falling! Burn the evidence!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6422, 'I\'m going to wear your skin as a smoking jacket! The stogies? You\'ll have to pry them from my cold dead... er... RAWR!!!!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6423, 'Leave nothing behind!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6424, 'Destroy the records before the bastion falls!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6425, 'Ramstein hunger for flesh!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6426, 'Welcome to flavor country!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6427, 'Thank you! You freed me!', 'Thank you! You freed me!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6428, 'Oh, thank you! I have been trapped in this nightmare for so long...', 'Oh, thank you! I have been trapped in this nightmare for so long...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6429, 'At last, my soul is free!', 'At last, my soul is free!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6430, 'Thank you! The battle for Darrowshire doomed me, but you have set me free!', 'Thank you! The battle for Darrowshire doomed me, but you have set me free!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6431, 'Ah! Please, if you see a survivor of Darrowshire, tell them I am free!', 'Ah! Please, if you see a survivor of Darrowshire, tell them I am free!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6432, '', 'The Emerald Circle strives to heal the wounded land of Felwood. Although the land is named for the very thing that has corrupted it, we hope that one day we will be able to eradicate the corruption from this land.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6433, '', 'I\'m ready, $n. Let\'s find my equipment and get out of here. I think I know where it is.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6434, '', 'We\'ve failed, $n!!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6435, '', 'Hello, $c. I am Jessir Moonbow. I have come from Darnassus seeking two friends who came looking for signs of the Shadow Council in Felwood.$B$BIt seems those vile creatures who claim responsibility for much of the orcish race\'s suffering seeks to regain some of its power.$B$BMy two friends went looking in Jaedenar to prove one way or the other what was happening.$B$BI expected them to be here when I arrived, but now they are days late.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6436, 'The Scourge have broken into the Bastion! Redouble your efforts! We must not fail!', 'The Scourge have broken into the Bastion! Redouble your efforts! We must not fail!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6437, 'Skeletons have advanced past the Hall of Lights! Destroy them!', 'Skeletons have advanced past the Hall of Lights! Destroy them!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6438, 'Our defenses are failing! By the light we must prevail!', 'Our defenses are failing! By the light we must prevail!', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6439, 'The Scourge have broken through in all wings! May the light defeat these foul creatures! We shall fight to the last!', 'The Scourge have broken through in all wings! May the light defeat these foul creatures! We shall fight to the last!', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6440, 'Zug zug, brave $c! I have the finest bred wolf mounts for you to peruse. You\'ll not find another animal with the level of endurance and survival instincts that the wolf has anywhere! Please, inspect them for yourself.', 'Zug zug, brave $c! I have the finest bred wolf mounts for you to peruse. You\'ll not find another animal with the level of endurance and survival instincts that the wolf has anywhere! Please, inspect them for yourself.', 0, 0, 0, 66, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6441, 'Today you have unmade what took me years to create! For this you shall all die by my hand!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6442, 'Damn you mortals! All my plans of revenge, all my hate... all burned to ash...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6443, '', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6444, 'I only make my mounts available to those who are considered friendly to Orgrimmar and the Orcish race. Go prove yourself to us, and I\'ll make my mighty wolves available for your inspection.', 'I only make my mounts available to those who are considered friendly to Orgrimmar and the Orcish race. Go prove yourself to us, and I\'ll make my mighty wolves available for your inspection.', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6445, 'And a good day to ya, brave $c. I know ya be lookin\' for a fine steed. There\'s no finer steed than a raptor under your reigns, lemme tell you $g mon : sis;! Take a look at the ones I got to offer - bred from the fiercest stock anywhere.', 'And a good day to ya, brave $c. I know ya be lookin\' for a fine steed. There\'s no finer steed than a raptor under your reigns, lemme tell you $g mon : sis;! Take a look at the ones I got to offer - bred from the fiercest stock anywhere.', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6446, 'I\'m sorry $g mon : sis;, but I won\'t be sellin\' my raptors to someone who hasn\'t proven themselves to be a friendly to the Darkspear tribe. When ya make a stand and show yourself to be at least friendly to us, then I\'ll make my raptors available to ya.', 'I\'m sorry $g mon : sis;, but I won\'t be sellin\' my raptors to someone who hasn\'t proven themselves to be a friendly to the Darkspear tribe. When ya make a stand and show yourself to be at least friendly to us, then I\'ll make my raptors available to ya.', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6447, 'You fools think you can defeat me so easily? Face the true might of the Nathrezim!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6448, 'Thieves! I shall smelt your remains!', 'Thieves! I shall smelt your remains!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6449, 'Payment for those plans comes in bones - YOUR BONES!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6450, 'Thank you, $n. My torment is now at an end.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6451, 'FREE!!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6452, 'The curse ends!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6453, 'Praise be to Egan!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6454, 'May Kel\'Thuzad one day feel our vengeance.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6455, 'Rivendare must be destroyed!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6456, '', 'Oh my! Look at this... all these candles. I\'m sure they\'re used for some terrible ritual or dark summoning. We best make haste!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6457, '', 'There! Over there!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6458, '', 'All I need now is a golden lasso.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6459, '', 'Let\'s get out of here, $n. I\'m ready now!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6460, '', 'DIE, DEMON DOGS!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6461, '', 'Ah! Fresh air at last! I never thought I\'d see the day...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6463, '', 'What was that?! Trey? TREY!?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6464, 'They hand the corpses to me, I toss them in the pit.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6465, 'I wish to make a purchase.', 'I wish to make a purchase.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6466, 'BETRAYER!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6467, '', 'NO! What have they done to you!?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6468, '', 'No! My friend... what\'s happened? This is all my fault...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6469, '', 'I can travel faster on my own from here, $n. Meet Jessir Moonbow near Ashenvale. She should be with the druids of the Emerald Circle. She\'ll tell you what to do next. Thank you so much for your help!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6470, 'No tampering with the mail!', 'No tampering with the mail!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6471, 'Try again!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6472, 'Certainly!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6473, 'No chance!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6474, 'Here comes the postmaster!', 'Here comes the postmaster!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6475, 'Greetings, $n. I am Myolor Sunderfury, keeper of the Great Forge.', '', 0, 0, 0, 1, 2, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6476, 'I wish to become an armorsmith.', 'I wish to become an armorsmith.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6477, 'I wish to become a weaponsmith.', 'I wish to become a weaponsmith.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6478, 'Your skill in blacksmithing is exceptional, $n. The time has come to make a decision. There are two paths you may choose: The way of the weaponsmith or the art of the armorsmith.$B$BBe warned, when you have chosen to dedicate yourself to a path, there is no going back. The discipline each path requires leaves no room for hybrids.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6479, '', 'The time has come, my $g brother : sister; in arms, to take back the night from the Scourge!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6480, '', 'The time has come, my $g brother : sister; in arms, to take back the night from the Scourge!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6481, 'Alas $c, you are not considered to be a true friend of Darnassus and the night elves of Teldrassil. Only when you prove yourself to be friendly to us through deeds - not just mere words - will I then make available my selection of mounts for your perusal.', 'Alas $c, you are not considered to be a true friend of Darnassus and the night elves of Teldrassil. Only when you prove yourself to be friendly to us through deeds - not just mere words - will I then make available my selection of mounts for your perusal.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6482, 'By the grace of Elune, I am pleased to offer you the finest steed found anywhere in Teldrassil, and perhaps the world - the nightsaber. For years, the Sentinel Army has relied on the value of these companions.$B$BTake your time friend and examine each one to your satisfaction. I am sure you will not be disappointed.', 'By the grace of Elune, I am pleased to offer you the finest steed found anywhere in Teldrassil, and perhaps the world - the nightsaber. For years, the Sentinel Army has relied on the value of these companions.$B$BTake your time friend and examine each one to your satisfaction. I am sure you will not be disappointed.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6483, 'Erm... I really don\'t know you that well, sorry. I mean, as an authorized Mechano-strider dealer, I really only can sell my mounts to those people who are considered a friend of the gnomes. If you ever are recognized as friendly to Gnomeregan, I\'d be happy to show you what I have to offer.', 'Erm... I really don\'t know you that well, sorry. I mean, as an authorized Mechano-strider dealer, I really only can sell my mounts to those people who are considered a friend of the gnomes. If you ever are recognized as friendly to Gnomeregan, I\'d be happy to show you what I have to offer.', 0, 0, 0, 24, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6484, 'Um... I think you\'re a little too big to ride a Mechano-strider there, $g mister : ma\'am;. You\'d likely crush the finely tuned servo-balancers!', 'Um... I think you\'re a little too big to ride a Mechano-strider there, $g mister : ma\'am;. You\'d likely crush the finely tuned servo-balancers!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6485, 'If you have come looking for the gift of fast and safe mobility, then you have arrived at your destination! This year\'s model of the new Mechano-striders have recently arrived, and I am ready to show you all they have to offer!', 'If you have come looking for the gift of fast and safe mobility, then you have arrived at your destination! This year\'s model of the new Mechano-striders have recently arrived, and I am ready to show you all they have to offer!', 0, 0, 0, 4, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6486, 'Speak with Ironus, $n. He\'ll be the one training you in the way of the weaponsmith.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6487, 'Grumnus be your man, $n. He\'ll school you in the art of the armorsmith.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6488, 'When the work of a blacksmith reaches your level of expert craftsmanship, I present them with a choice.$B$BWhile you are not required to make a choice, it is recommended that you do - after all, do you want to be a neophyte forever? It is the artisan armorsmiths and weaponsmiths that supply our armies, win our wars and garner acclaim!$B$BBe warned, $n. Once a choice is made and the tasks required completed - there is no going back. One can only walk down one path.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6489, 'I am Krathok, overseer of these craftsmen.', '', 0, 0, 0, 1, 25, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6490, 'Borgosh! $n is ready to be trained.', '', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6491, 'Okothos will train you, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6492, 'One of the Ash\'ari Crystals has been destroyed! Slay the intruders!', 'One of the Ash\'ari Crystals has been destroyed! Slay the intruders!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6493, 'An Ash\'ari Crystal has been restored!', 'An Ash\'ari Crystal has been restored!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6494, '$R flesh... must feed!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6495, 'Who dares to approach this cauldron? Taste my dark blade!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6496, 'The Scourge beckons you, foolish $r.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6497, '$C - I will consume your light!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6498, 'Greetings, $r.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6499, 'I require training.', 'I require training.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6500, 'Hail, $c!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6501, 'I require training.', 'I require training.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6502, 'What do you want?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6503, 'I require training.', 'I require training.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6504, 'Prepare to be Malowned!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6505, '', 'Highborne spirits! Be at peace! The Crystal of Zin-Malor is whole once again!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6506, '', 'Cursed spirits! Let go of the past! The Crystal of Zin-Malor no longer binds you!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6507, '', 'Spirits of the Highborne! You may now rest! The curse of the crystal releases you!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6509, 'Ya may not follow the ways of the Light like a Knight of the Silver Hand, but ya can always ask us for aid.', 'Ya may not follow the ways of the Light like a Knight of the Silver Hand, but ya can always ask us for aid.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6510, 'The Light be with ya, $c. How can I help ya?', 'The Light be with ya, $c. How can I help ya?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6511, '%s raises an undead servant back to life!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6512, '%s attempts to casts Death Pact on his servants!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6513, 'I wish to purchase arcane quickener from you.', 'I wish to purchase arcane quickener from you.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6514, 'A fierce wind is coming - I can tell by the ache in my exposed knee. I just hope we don\'t get caught up in it.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6515, 'Well now $n, let\'s get to work on those cauldrons! If you\'re going to collect the components we need, then get a vitreous focuser from me. You\'ll need to have it on you to identify the necessary resources found on the Scourge that will turn those cauldrons.$B$BYou\'ll also need a dose of arcane quickener for the cauldrons. It\'s not cheap, nor is it stable. Once you have it, you\'ll only have thirty minutes to use it before it expires on you; be smart about buying it.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6516, 'What\'s needed for the cauldron at Felstone Field?', 'What\'s needed for the cauldron at Felstone Field?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6517, 'What\'s needed for the cauldron at Dalson\'s Tears?', 'What\'s needed for the cauldron at Dalson\'s Tears?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6518, 'What\'s needed for the cauldron at the Writhing Haunt?', 'What\'s needed for the cauldron at the Writhing Haunt?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6519, 'What\'s needed for the cauldron at Gahrron\'s Withering?', 'What\'s needed for the cauldron at Gahrron\'s Withering?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6520, 'Based on the sample of the toxin you provided for us, we think you will need the following: an arcane quickener, six osseous agitators, and four pieces of runecloth. With the vitreous focuser in tow, acquire the agitators from skeletal undead here in the Plaguelands!$B$BOnce you have these items, you should be able to load the counter agent into that cauldron.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6521, 'Based on the sample of the toxin you provided for us, we think you will need the following: an arcane quickener, five somatic intensifiers, and and four pieces of runecloth. With the vitreous focuser in tow, acquire the intensifiers from zombie or ghoul undead here in the Plaguelands!$B$BOnce you have these items, you should be able to load the counter agent into that cauldron.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6522, 'Based on the sample of the toxin you provided for us, we think you will need the following: an arcane quickener, five somatic intensifiers, and and four pieces of runecloth. With the vitreous focuser in tow, acquire the intensifiers from zombie or ghoul undead here in the Plaguelands!$B$BOnce you have these items, you should be able to load the counter agent into that cauldron.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6523, 'Based on the sample of the toxin you provided for us, we think you will need the following: an arcane quickener, four ectoplasmic resonators, and four pieces of runecloth. With the vitreous focuser in tow, acquire the resonators from wraith and ghostly undead here in the Plaguelands!$B$BOnce you have these items, you should be able to load the counter agent into that cauldron.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6524, 'This job is definitely not worth sixteen-five a year.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6525, 'Well now $n, let\'s get to work on those cauldrons! If you\'re going to collect the components we need, then get a vitreous focuser from me. You\'ll need to have it on you to identify the necessary resources found on the Scourge that will turn those cauldrons.$B$BYou\'ll also need a dose of arcane quickener for the cauldrons. It\'s not cheap, nor is it stable. Once you have it, you\'ll only have thirty minutes to use it before it expires on you; be smart about buying it.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6526, 'An Ash\'ari Crystal has fallen! Stay true to the Lich King, my brethren, and attempt to resummon it.', 'An Ash\'ari Crystal has fallen! Stay true to the Lich King, my brethren, and attempt to resummon it.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6527, 'An Ash\'ari Crystal has been toppled! Restore the ziggurat before the Necropolis is vulnerable!', 'An Ash\'ari Crystal has been toppled! Restore the ziggurat before the Necropolis is vulnerable!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6528, 'Our summoning is complete! The Ash\'ari crystal has been restored!', 'Our summoning is complete! The Ash\'ari crystal has been restored!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6529, 'Success is ours! The Ash\'ari Crystal has been restored!', 'Success is ours! The Ash\'ari Crystal has been restored!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6530, 'You\'ve been MALOWNED!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6531, 'I need a vitreous focuser.', 'I need a vitreous focuser.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6532, '', 'Arise, my champion!', 1, 5840, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6533, 'At your side, milady!', '', 1, 5837, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6534, 'Blades of light!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6535, 'Release your rage!', '', 1, 5797, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6536, 'Awake, ye servants! Defend the Disks!', '', 1, 5856, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6537, 'To my side, brothers! For the Makers!', '', 1, 5857, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6538, 'Join us!', '', 1, 5864, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6539, '', 'Shhh... do you smell something?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6540, 'Liladris has been waiting for me at Maestra\'s Post, so we should make haste, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6541, '%s begins to drift off...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6542, '%s suddenly falls asleep.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6543, 'Yaaaaawwwwwnnnn...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6544, 'Oh, I am so tired...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6545, 'You don\'t mind if I stop here for a moment, do you?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6546, '%s emerges from the shadows to defend the cauldron!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6547, 'The Scourge\'s plague still dominates the zone, and we have a long way to go before the plague will be altered enough to affect them. Collect the necessary resources and help out the war effort!', 'The Scourge\'s plague still dominates the zone, and we have a long way to go before the plague will be altered enough to affect them. Collect the necessary resources and help out the war effort!', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6548, 'We are having difficulty determining the status of the plague within the area; check back shortly and we may have a better idea.', 'We are having difficulty determining the status of the plague within the area; check back shortly and we may have a better idea.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6549, 'The Scourge\'s plague is almost to the turning point; a handful more cauldron subversions will push us over the top! Be sure to do your part to help out!', 'The Scourge\'s plague is almost to the turning point; a handful more cauldron subversions will push us over the top! Be sure to do your part to help out!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6550, 'We have begun to turn the Scourge\'s plague against them, but our hold is tenuous at best! Be sure to aid the cause by subverting their cauldrons!', 'We have begun to turn the Scourge\'s plague against them, but our hold is tenuous at best! Be sure to aid the cause by subverting their cauldrons!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6551, 'We are dominating the Scourge - the effects of the altered plague will affect them for at least another full day! We need to stay on top of it though, so be sure to sabotage a cauldron when you can.', 'We are dominating the Scourge - the effects of the altered plague will affect them for at least another full day! We need to stay on top of it though, so be sure to sabotage a cauldron when you can.', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6552, 'How are we doing in the battle to subvert the cauldrons?', 'How are we doing in the battle to subvert the cauldrons?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6553, '%s comes to rest as it begins to release the counter agent into the air.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6554, 'The deeds are mine, brother! Soon you shall be out of my way for good!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6555, 'The fortune is mine, Alexi! Your end comes soon!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6556, 'The Hunter Trainer\'s name is Thotar. Seek him out inside the burrow on the southwest corner of Razor Hill.', 'The Hunter Trainer\'s name is Thotar. Seek him out inside the burrow on the southwest corner of Razor Hill.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6557, 'You\'re a hunter? Well anyway, you\'ll find Thotar hiding from the sun inside the burrow at the southwest corner of town.', 'You\'re a hunter? Well anyway, you\'ll find Thotar hiding from the sun inside the burrow at the southwest corner of town.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6558, 'Speak with Un\'Thuwa to the south in Sen\'jin Village.', 'Speak with Un\'Thuwa to the south in Sen\'jin Village.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6559, 'That\'s troll business. Look for Un\'Thuwa in Sen\'jin Village down south.', 'That\'s troll business. Look for Un\'Thuwa in Sen\'jin Village down south.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6560, 'You\'re looking for Tai\'jin. Find her in the barracks on the east side of town.', 'You\'re looking for Tai\'jin. Find her in the barracks on the east side of town.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6561, 'Tai\'jin is her name and you\'ll find her inside the barracks on the east side of Razor Hill. She\'ll teach you to respect the gods. Make certain that you show her the proper respect in return!', 'Tai\'jin is her name and you\'ll find her inside the barracks on the east side of Razor Hill. She\'ll teach you to respect the gods. Make certain that you show her the proper respect in return!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6562, 'Find Kaplak on top of the burrow in the southwest section of town.', 'Find Kaplak on top of the burrow in the southwest section of town.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6563, 'Why would you want to talk to Kaplak? Nevermind. Look for him on top of the burrow in the southwest corner of Razor Hill. Just make sure that you keep your hands in your own pockets!', 'Why would you want to talk to Kaplak? Nevermind. Look for him on top of the burrow in the southwest corner of Razor Hill. Just make sure that you keep your hands in your own pockets!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6564, 'You need to find Swart. Look for him inside the barracks on the east side of Razor Hill.', 'You need to find Swart. Look for him inside the barracks on the east side of Razor Hill.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6565, 'The Warchief brings change that will bring the Horde victory! Look for Swart inside the barracks on the east side of town.', 'The Warchief brings change that will bring the Horde victory! Look for Swart inside the barracks on the east side of town.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6566, 'On the east side of Razor Hill, behind the barracks is where you will find Dhugru Gorelust.', 'On the east side of Razor Hill, behind the barracks is where you will find Dhugru Gorelust.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6567, 'Dhugru Gorelust is hiding behind the barracks on the east side of town. Don\'t let me find out that you\'re in league with the Burning Legion!', 'Dhugru Gorelust is hiding behind the barracks on the east side of town. Don\'t let me find out that you\'re in league with the Burning Legion!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6568, 'Look for Tarshaw Jaggedscar in the barracks on the east side of town.', 'Look for Tarshaw Jaggedscar in the barracks on the east side of town.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6569, 'Find Tarshaw Jaggedscar inside the barracks on the east side of Razor Hill. Bring pride and honor to the Horde youngling!', 'Find Tarshaw Jaggedscar inside the barracks on the east side of Razor Hill. Bring pride and honor to the Horde youngling!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6570, 'Seek out Miao\'zan in Sen\'jin Village to the south.', 'Seek out Miao\'zan in Sen\'jin Village to the south.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6571, 'I hear that the trolls in Sen\'jin Village have a potion maker named Miao\'zan. Look there.', 'I hear that the trolls in Sen\'jin Village have a potion maker named Miao\'zan. Look there.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6572, 'That would be Dwukk. Find him next to the heated forge at the northwest corner of town.', 'That would be Dwukk. Find him next to the heated forge at the northwest corner of town.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6573, 'If you think you have the strength to pound good metal into weapons and armor for the Horde look for Dwukk next to the heated forge in the northwest corner of Razor Hill.', 'If you think you have the strength to pound good metal into weapons and armor for the Horde look for Dwukk next to the heated forge in the northwest corner of Razor Hill.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6574, 'Head north up the road to Orgrimmar. You can ask one of the grunts there for directions to the Cooking Trainer.', 'Head north up the road to Orgrimmar. You can ask one of the grunts there for directions to the Cooking Trainer.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6575, 'Mmmmm food. It\'s not too long of a run, but you need to head north to Orgrimmar and ask one of the grunts there.', 'Mmmmm food. It\'s not too long of a run, but you need to head north to Orgrimmar and ask one of the grunts there.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6576, 'There\'s one in Orgrimmar. A grunt up there will know what directions to give you. Just head north up the road here, you can\'t miss it.', 'There\'s one in Orgrimmar. A grunt up there will know what directions to give you. Just head north up the road here, you can\'t miss it.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6577, 'I don\'t know of one of those around here. Your best bet is to go to Orgrimmar to the north and speak to one of their grunts.', 'I don\'t know of one of those around here. Your best bet is to go to Orgrimmar to the north and speak to one of their grunts.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6578, 'There is an Engineering Trainer in Razor Hill. He is called Mukdrak and can usually be found standing over near the forge.', 'There is an Engineering Trainer in Razor Hill. He is called Mukdrak and can usually be found standing over near the forge.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6579, 'Bah, that\'s goblin work! Still, we have an orc here in Razor Hill that will teach you of such things. Look for Mukdrak over near the forge.', 'Bah, that\'s goblin work! Still, we have an orc here in Razor Hill that will teach you of such things. Look for Mukdrak over near the forge.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6580, 'There\'s one of those inside the barracks on the east side of Razor Hill. Look for Rawrk.', 'There\'s one of those inside the barracks on the east side of Razor Hill. Look for Rawrk.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6581, 'With all of the fighting we do of course we have a First Aid Trainer in Razor Hill. Find Rawrk inside the barracks on the east side of town.', 'With all of the fighting we do of course we have a First Aid Trainer in Razor Hill. Find Rawrk inside the barracks on the east side of town.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6582, 'I\'ve seen a troll named Lau\'Tiki fishing down south at the Darkspear Strand. That\'s just on the outskirts of Sen\'jin Village.', 'I\'ve seen a troll named Lau\'Tiki fishing down south at the Darkspear Strand. That\'s just on the outskirts of Sen\'jin Village.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6583, 'You\'re looking for Lau\'Tiki. You\'ll find him on Darkspear Strand out beyond Sen\'jin Village to the south.', 'You\'re looking for Lau\'Tiki. You\'ll find him on Darkspear Strand out beyond Sen\'jin Village to the south.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6584, 'You\'re looking for Mishiki. She has a hut to the south in Sen\'jin Village.', 'You\'re looking for Mishiki. She has a hut to the south in Sen\'jin Village.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6585, 'Flowers? In this wasteland? If anyone can teach you how to find them it will be Mishiki down in Sen\'jin Village to the south.', 'Flowers? In this wasteland? If anyone can teach you how to find them it will be Mishiki down in Sen\'jin Village to the south.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
