INSERT INTO `broadcast_text` VALUES (6986, 'What do ye need directions to?', 'What do ye need directions to?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6987, 'Bank', 'Bank', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6988, 'The nearest bank ye\'ll find is The Vault in Ironforge, $glad : lass;. ', 'The nearest bank ye\'ll find is The Vault in Ironforge, $glad : lass;. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6989, 'The Vault in Ironforge is the closest bank ye\'ll find in these parts.', 'The Vault in Ironforge is the closest bank ye\'ll find in these parts.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6990, 'Gryphon Master', 'Gryphon Master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6991, 'Ye won\'t find a gryphon master just anywhere around Dun Morogh, $glad : lass;. Ye\'re going to have to go to Ironforge if ye want to take a gryphon somewhere.', 'Ye won\'t find a gryphon master just anywhere around Dun Morogh, $glad : lass;. Ye\'re going to have to go to Ironforge if ye want to take a gryphon somewhere.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6992, 'The closest gryphon master is located up in Ironforge near The Great Forge itself. Ye\'ll need to head to Ironforge if ye want to travel by gryphon.', 'The closest gryphon master is located up in Ironforge near The Great Forge itself. Ye\'ll need to head to Ironforge if ye want to travel by gryphon.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6993, 'Guild Master', 'Guild Master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6994, 'All those official types reside in Ironforge. Ye\'ll be wanting to check out the Ironforge Visitor\'s Center for guild creation papers.', 'All those official types reside in Ironforge. Ye\'ll be wanting to check out the Ironforge Visitor\'s Center for guild creation papers.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6995, 'If ye need to get yer hands on some guild creation papers ye\'ll have to head up to Ironforge and visit the Ironforge Visitor\'s Center.', 'If ye need to get yer hands on some guild creation papers ye\'ll have to head up to Ironforge and visit the Ironforge Visitor\'s Center.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6996, 'The Inn', 'The Inn', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6997, 'The Thunderbrews run a fine Inn in Kharanos which bears their name. Stop in for a pint and a bit of rest.', 'The Thunderbrews run a fine Inn in Kharanos which bears their name. Stop in for a pint and a bit of rest.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6998, 'Oooh, an Inn is it? Well, ye just can\'t beat the Thunderbrew Distillery. Rest if ye must, but be sure to have an ale or two while ye\'re there.', 'Oooh, an Inn is it? Well, ye just can\'t beat the Thunderbrew Distillery. Rest if ye must, but be sure to have an ale or two while ye\'re there.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (6999, 'Class Trainer', 'Class Trainer', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7000, 'Which class trainer might ye be looking for?', 'Which class trainer might ye be looking for?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7001, 'Hunter', 'Hunter', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7002, 'There\'s only one hunter around here I would have ye talk to, and that would be Grif Wildheart. Ye\'ll find him standing just outside the smithy in Kharanos.', 'There\'s only one hunter around here I would have ye talk to, and that would be Grif Wildheart. Ye\'ll find him standing just outside the smithy in Kharanos.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7003, 'If ye ask me, I think Grif spends a little too much time with the animals. Still, nobody else knows the hunter\'s path quite like he does. Look for him outside the smithy in Kharanos.', 'If ye ask me, I think Grif spends a little too much time with the animals. Still, nobody else knows the hunter\'s path quite like he does. Look for him outside the smithy in Kharanos.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7004, 'Mage', 'Mage', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7005, 'There\'s a mage at the Thunderbrew Distillery who might be able to teach you some new spells. I believe his name is Magis Sparkmantle.', 'There\'s a mage at the Thunderbrew Distillery who might be able to teach you some new spells. I believe his name is Magis Sparkmantle.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7006, 'Ye sure ye don\'t want to learn to be a warrior? Well, it\'s yer hide on the battlefield. If ye really want to learn to waggle yer finger, ye best talk to Magis Sparkmantle in the Thunderbrew Distillery.', 'Ye sure ye don\'t want to learn to be a warrior? Well, it\'s yer hide on the battlefield. If ye really want to learn to waggle yer finger, ye best talk to Magis Sparkmantle in the Thunderbrew Distillery.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7007, 'Paladin', 'Paladin', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7008, 'Och... a paladin? Well $glad : lass;, ye\'re in luck! Azar Stronghammer is back from training and is currently staying at the Thunderbrew Distillery in Kharanos.', 'Och... a paladin? Well $glad : lass;, ye\'re in luck! Azar Stronghammer is back from training and is currently staying at the Thunderbrew Distillery in Kharanos.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7009, 'Ye need some training? Well, Azar Stronghammer is always up for showing the younger paladins a thing or two. Why don\'t ye go see him in the Thunderbrew Distillery.', 'Ye need some training? Well, Azar Stronghammer is always up for showing the younger paladins a thing or two. Why don\'t ye go see him in the Thunderbrew Distillery.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7010, 'Priest', 'Priest', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7011, 'Oh, a priest is it? Well good ol\' Maxan is sure to be found having a drink in the Thunderbrew Distillery if ye need a bit of training.', 'Oh, a priest is it? Well good ol\' Maxan is sure to be found having a drink in the Thunderbrew Distillery if ye need a bit of training.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7012, 'Ye\'ll surely find Maxan Anvol tossin\' back a few in the Thunderbrew Distillery. Why don\'t ye go have yerself a pint and see if he\'ll teach ye something new.', 'Ye\'ll surely find Maxan Anvol tossin\' back a few in the Thunderbrew Distillery. Why don\'t ye go have yerself a pint and see if he\'ll teach ye something new.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7013, 'Rogue', 'Rogue', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7014, 'Well then, it seems we\'re both looking for Hogral Bakkan. He\'s a dwarf to be sure, so sooner or later he\'ll be heading for the Thunderbrew Distillery for an ale -- he might even be there now.', 'Well then, it seems we\'re both looking for Hogral Bakkan. He\'s a dwarf to be sure, so sooner or later he\'ll be heading for the Thunderbrew Distillery for an ale -- he might even be there now.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7015, 'Ye looking for Hogral? Well did ye check the tavern? Och! If yer looking for a rogue, and a dwarf one at that, ye should start in the Inn! Go have a look already.', 'Ye looking for Hogral? Well did ye check the tavern? Och! If yer looking for a rogue, and a dwarf one at that, ye should start in the Inn! Go have a look already.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7016, 'Warlock', 'Warlock', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7017, 'Warlocks... I don\'t care for their lot. Like that Grimrizz who\'s set up shop outside the Thunderbrew Distillery. The ale will probably go flat there because of him! Warlocks... bah!', 'Warlocks... I don\'t care for their lot. Like that Grimrizz who\'s set up shop outside the Thunderbrew Distillery. The ale will probably go flat there because of him! Warlocks... bah!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7018, 'That kind of magic is just bad news, I tell ye. I don\'t know why that Grimrizz has been allowed to set up outside the Thunderbrew Distillery of all places! You stay away from him, you here!', 'That kind of magic is just bad news, I tell ye. I don\'t know why that Grimrizz has been allowed to set up outside the Thunderbrew Distillery of all places! You stay away from him, you here!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7019, 'Warrior', 'Warrior', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7020, 'Och! Now ye\'re talkin\'! I bet Granis Swiftaxe could show ye a thing or two about swinging steel. He\'s over in the Thunderbrew Distillery in Kharanos at the moment.', 'Och! Now ye\'re talkin\'! I bet Granis Swiftaxe could show ye a thing or two about swinging steel. He\'s over in the Thunderbrew Distillery in Kharanos at the moment.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7021, 'That Granis Swiftaxe could drink a fish under the table and yet his axe still swings true. However, if yer looking for training, ye best catch him while ye can understand him.', 'That Granis Swiftaxe could drink a fish under the table and yet his axe still swings true. However, if yer looking for training, ye best catch him while ye can understand him.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7022, 'Profession Trainer', 'Profession Trainer', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7023, 'Which profession trainer would ye be looking for?', 'Which profession trainer would ye be looking for?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7024, 'Alchemy', 'Alchemy', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7025, 'Blacksmithing', 'Blacksmithing', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7026, 'Cooking', 'Cooking', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7027, 'Enchanting', 'Enchanting', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7028, 'Engineering', 'Engineering', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7029, 'First Aid', 'First Aid', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7030, 'Fishing', 'Fishing', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7031, 'Herbalism', 'Herbalism', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7032, 'Leatherworking', 'Leatherworking', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7033, 'Mining', 'Mining', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7034, 'Skinning', 'Skinning\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7035, 'Tailoring', 'Tailoring', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7036, 'Nope, no alchemists out here in Dun Morogh. Ye best take a look in Ironforge for them... try the gnomes in Tinker Town.', 'Nope, no alchemists out here in Dun Morogh. Ye best take a look in Ironforge for them... try the gnomes in Tinker Town.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7037, 'I hear there\'s a shop up in Ironforge run by the gnomes that specializes in alchemy. You should go have a look.', 'I hear there\'s a shop up in Ironforge run by the gnomes that specializes in alchemy. You should go have a look.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7038, 'Och! Now that\'s a skill worth knowing. Tognus beats the anvil with a fury ye don\'t see often. Why don\'t ye go talk him up and see what he can teach ye?', 'Och! Now that\'s a skill worth knowing. Tognus beats the anvil with a fury ye don\'t see often. Why don\'t ye go talk him up and see what he can teach ye?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7039, 'Ye can probably hear Tognus beating away on the anvil when yer in Kharanos. Although, if ye haven\'t cleared the wax out of yer ears recently, then why don\'t ye just have a look in the smithy.', 'Ye can probably hear Tognus beating away on the anvil when yer in Kharanos. Although, if ye haven\'t cleared the wax out of yer ears recently, then why don\'t ye just have a look in the smithy.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7040, 'A Thunderbrew ale and a bit of Gremlock\'s cooking makes for a nice meal indeed. Ye really should hit him up for some training if ye want to be a cook.', 'A Thunderbrew ale and a bit of Gremlock\'s cooking makes for a nice meal indeed. Ye really should hit him up for some training if ye want to be a cook.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7041, 'Gremlock doesn\'t always have the best of luck with cooking, but he does have some tasty recipies worth learning. Go chat him up in the Thunderbrew Distillery and see what he\'ll teach ye.', 'Gremlock doesn\'t always have the best of luck with cooking, but he does have some tasty recipies worth learning. Go chat him up in the Thunderbrew Distillery and see what he\'ll teach ye.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7042, 'There aren\'t any enchanters out here in Dun Morogh that I know of. Ye\'re going to have to visit Ironforge if ye want to learn the enchanter\'s profession.', 'There aren\'t any enchanters out here in Dun Morogh that I know of. Ye\'re going to have to visit Ironforge if ye want to learn the enchanter\'s profession.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7043, 'Ye should take a look in Ironforge if ye have yer mind set on enchanting. I think I saw a shop over by the gryphon master last time I was up there.', 'Ye should take a look in Ironforge if ye have yer mind set on enchanting. I think I saw a shop over by the gryphon master last time I was up there.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7044, 'Ye should check over at Steelgrill\'s Depot for Bronk. When he\'s not tinkering with some odd device, he\'s usually chatting up the pilots about their steam tanks.', 'Ye should check over at Steelgrill\'s Depot for Bronk. When he\'s not tinkering with some odd device, he\'s usually chatting up the pilots about their steam tanks.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7045, 'With all the screws that Bronk has been tightening, I think he\'s let a few of his own go if ye get me. All the same he\'s still got a good head for engineering the darndest devices. Ye should go pay him a visit over at Steelgrill\'s Depot.', 'With all the screws that Bronk has been tightening, I think he\'s let a few of his own go if ye get me. All the same he\'s still got a good head for engineering the darndest devices. Ye should go pay him a visit over at Steelgrill\'s Depot.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7046, 'First Aid may not make up for a priest\'s healing, but it\'s handy in a pinch. I recommend ye go talk to Thamner in the Thunderbrew Distillery and learn what ye can.', 'First Aid may not make up for a priest\'s healing, but it\'s handy in a pinch. I recommend ye go talk to Thamner in the Thunderbrew Distillery and learn what ye can.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7047, 'Good ol\' Thamner bandaged me up just the other day when I had a run in with a nasty trogg. I\'m good as new I tell ye! I\'m sure Thamner would be happy to show ye how to make a good bandage.', 'Good ol\' Thamner bandaged me up just the other day when I had a run in with a nasty trogg. I\'m good as new I tell ye! I\'m sure Thamner would be happy to show ye how to make a good bandage.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7048, 'Not much fishing to be had around here, but what little there is to be had is Paxton\'s ye can be sure. Ye can find him fishing out on Iceflow Lake.', 'Not much fishing to be had around here, but what little there is to be had is Paxton\'s ye can be sure. Ye can find him fishing out on Iceflow Lake.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7049, 'Only a few spots to get any fishing done around here. Paxton knows them all and probably a few he won\'t tell ya about. Why don\'t ye go over to Iceflow Lake and see what he will tell ya.', 'Only a few spots to get any fishing done around here. Paxton knows them all and probably a few he won\'t tell ya about. Why don\'t ye go over to Iceflow Lake and see what he will tell ya.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7050, 'Amazing to see little flowers persist in the cold of Dun Morogh... but ye won\'t find any herbalists out here. Ye\'ll have to go to Ironforge to get some training in that profession.', 'Amazing to see little flowers persist in the cold of Dun Morogh... but ye won\'t find any herbalists out here. Ye\'ll have to go to Ironforge to get some training in that profession.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7051, 'Nope, nobody who can teach ye herbalism out here. I think ye\'ll have better luck in Ironforge though.', 'Nope, nobody who can teach ye herbalism out here. I think ye\'ll have better luck in Ironforge though.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7052, 'If ye\'re looking to learn the profession of leatherworking, ye\'re gonna need to travel to Ironforge. I hear there\'s a nice shop near the Great Forge where ye can learn the profession.', 'If ye\'re looking to learn the profession of leatherworking, ye\'re gonna need to travel to Ironforge. I hear there\'s a nice shop near the Great Forge where ye can learn the profession.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7053, 'Ironforge is going to be your best bet for leatherworkers. If memory servers, there\'s a shop by The Great Forge that should meet yer needs.', 'Ironforge is going to be your best bet for leatherworkers. If memory servers, there\'s a shop by The Great Forge that should meet yer needs.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7054, 'Surely ye didn\'t think ye would find a town without a resident miner about, did ye? Yarr can usually be found over at Steelgrill\'s Depot just outside of Kharanos.', 'Surely ye didn\'t think ye would find a town without a resident miner about, did ye? Yarr can usually be found over at Steelgrill\'s Depot just outside of Kharanos.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7055, 'A miner? Ye don\'t need to ask if $glad : lass;, only where! Yarr is who yer looking for, and I think ye can find him hiding out over in Steelgrill\'s Depot.', 'A miner? Ye don\'t need to ask if $glad : lass;, only where! Yarr is who yer looking for, and I think ye can find him hiding out over in Steelgrill\'s Depot.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7056, 'Ye won\'t find any skinners out in here Dun Morogh $glad : lass;, they\'re mostly to be found in Ironforge if ye\'re looking to be trained.', 'Ye won\'t find any skinners out in here Dun Morogh $glad : lass;, they\'re mostly to be found in Ironforge if ye\'re looking to be trained.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7057, 'The skinners usually take all their hides into Ironforge to dry out. Why don\'t you head there and see if you can find one to train ye?', 'The skinners usually take all their hides into Ironforge to dry out. Why don\'t you head there and see if you can find one to train ye?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7058, 'Ah, Stonebrow doesn\'t leave Ironforge much these days so if ye\'re looking to learn the tailor\'s profession ye\'ll have to go to see him up in Ironforge.', 'Ah, Stonebrow doesn\'t leave Ironforge much these days so if ye\'re looking to learn the tailor\'s profession ye\'ll have to go to see him up in Ironforge.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7059, 'Well, ye won\'t have much luck with finding a tailor out here. Ironforge is home to a quite a few talented tailors though. Why don\'t ye go ask a guard there.', 'Well, ye won\'t have much luck with finding a tailor out here. Ironforge is home to a quite a few talented tailors though. Why don\'t ye go ask a guard there.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7060, 'Anu therador mali $c. Where can I guide you to?', 'Anu therador mali $c. Where can I guide you to?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7061, 'How may I aid you in your journey?', 'How may I aid you in your journey?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7062, 'Do you require guidance to your destination?', 'Do you require guidance to your destination?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7063, 'What direction can I point you in?', 'What direction can I point you in?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7064, 'I need directions.', 'I need directions.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7065, 'There are many places that you might visit in Teldrassil. Tell me what you seek.', 'There are many places that you might visit in Teldrassil. Tell me what you seek.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7066, 'The Bank', 'The Bank', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7067, 'The bank is located in the serene city of Darnassus. You must go there if you wish to store your possessions.', 'The bank is located in the serene city of Darnassus. You must go there if you wish to store your possessions.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7068, 'If a bank is what you desire, then your travels take you to the heart of Darnassus. There you will find storage for your treasures.', 'If a bank is what you desire, then your travels take you to the heart of Darnassus. There you will find storage for your treasures.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7069, 'Rut\'theran Ferry', 'Rut\'theran Ferry', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7070, 'To reach the Rut\'theran Ferry, you will need to travel to Darnassus and take the portal to Rut\'theran Village at the base of Teldrassil.', 'To reach the Rut\'theran Ferry, you will need to travel to Darnassus and take the portal to Rut\'theran Village at the base of Teldrassil.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7071, 'A Rut\'theran Ferry leaves for Auberdine from Rut\'theran Village at the base of Teldrassil. The portal in Darnassus will allow you to reach Rut\'theran safely.', 'A Rut\'theran Ferry leaves for Auberdine from Rut\'theran Village at the base of Teldrassil. The portal in Darnassus will allow you to reach Rut\'theran safely.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7072, 'The Guild Master.', 'The Guild Master.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7073, 'Matters of such an official nature are only handled in Darnassus. You must travel there to begin the process of your guild\'s creation.', 'Matters of such an official nature are only handled in Darnassus. You must travel there to begin the process of your guild\'s creation.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7074, 'Documents such as guild creation papers can be acquired from the Guild Master in the city of Darnassus. ', 'Documents such as guild creation papers can be acquired from the Guild Master in the city of Darnassus. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7075, 'The Inn', 'The Inn\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7076, 'A restful sleep is sure to be had in the beds which Innkeeper Keldamyr keeps at the Inn in Dolanaar. ', 'A restful sleep is sure to be had in the beds which Innkeeper Keldamyr keeps at the Inn in Dolanaar. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7077, 'If you require time to rest and regain your strength, you will find the best at the Inn in Dolanaar. Innkeeper Keldamyr will see to it that you are taken care of.', 'If you require time to rest and regain your strength, you will find the best at the Inn in Dolanaar. Innkeeper Keldamyr will see to it that you are taken care of.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7078, 'Class Trainer', 'Class Trainer', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7079, 'You will find numerous trainers around Dolanaar with ages of experience with which to tutor you. Which would you have me point you to?', 'You will find numerous trainers around Dolanaar with ages of experience with which to tutor you. Which would you have me point you to?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7080, 'Druid', 'Druid', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7081, 'You seek the aid of a Druid? Then you should go see Kal at the Moonwell just south of the Inn in Dolanaar.', 'You seek the aid of a Druid? Then you should go see Kal at the Moonwell just south of the Inn in Dolanaar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7082, 'The druid, Kal, often spends his time at the moonwell in Dolanaar. I am certain he would be able to provide you with the training you require.', 'The druid, Kal, often spends his time at the moonwell in Dolanaar. I am certain he would be able to provide you with the training you require.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7083, 'Hunter', 'Hunter', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7084, 'If a hunter is whom you seek, then it is Dazalar that you should visit. He can usually be found just outside the Inn in Dolanaar.', 'If a hunter is whom you seek, then it is Dazalar that you should visit. He can usually be found just outside the Inn in Dolanaar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7085, 'Dazalar is well known around Dolanaar for his hunting skill. Why don\'t you talk to him and see what he can teach you?', 'Dazalar is well known around Dolanaar for his hunting skill. Why don\'t you talk to him and see what he can teach you?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7086, 'Priest', 'Priest', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7087, 'A priestess by the name of Laurna Morninglight calls Dolanaar home. I believe she can be found just north of the Inn.', 'A priestess by the name of Laurna Morninglight calls Dolanaar home. I believe she can be found just north of the Inn.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7088, 'Laurna Morninglight offers healing to those that live around Dolanaar. Perhaps she would be able to teach you in the ways of a priest.', 'Laurna Morninglight offers healing to those that live around Dolanaar. Perhaps she would be able to teach you in the ways of a priest.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7089, 'Rogue', 'Rogue', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7090, 'A rogue? I\'m not in the habit of referring people to those types, but I believe Jannok would be the one you\'re looking for. You will find him at the Inn in Dolanaar.', 'A rogue? I\'m not in the habit of referring people to those types, but I believe Jannok would be the one you\'re looking for. You will find him at the Inn in Dolanaar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7091, 'You seek a rogue? I believe Jannok would fit that description. Why don\'t you go talk to him over at the Inn in Dolanaar.', 'You seek a rogue? I believe Jannok would fit that description. Why don\'t you go talk to him over at the Inn in Dolanaar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7092, 'Warrior', 'Warrior', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7093, 'Kyra Windblade wields her blade with great skill. She should be able to provide you with some training. You\'ll find her at the Inn in Dolanaar.', 'Kyra Windblade wields her blade with great skill. She should be able to provide you with some training. You\'ll find her at the Inn in Dolanaar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7094, 'I had the pleasure of seeing Kyra Windblade in combat once. Considering what I witnessed, I am confident she will train you well. Look for her at the Inn in Dolanaar.', 'I had the pleasure of seeing Kyra Windblade in combat once. Considering what I witnessed, I am confident she will train you well. Look for her at the Inn in Dolanaar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7095, 'Profession Trainer', 'Profession Trainer', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7096, 'The path of those who undertake a profession is long and rewarding. Which path do you choose to walk?', 'The path of those who undertake a profession is long and rewarding. Which path do you choose to walk?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7097, 'Alchemy', 'Alchemy', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7098, 'While Cyndra is not bubbly like her potions, her kind manner is suitable for teaching her trade. You can find her just east of the Inn in Dolanaar.', 'While Cyndra is not bubbly like her potions, her kind manner is suitable for teaching her trade. You can find her just east of the Inn in Dolanaar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7099, 'Cyndra\'s knowledge in alchemy is somewhat limited, but I\'m sure it would be sufficient to get you started. She practices her craft just east of the Inn in Dolanaar.', 'Cyndra\'s knowledge in alchemy is somewhat limited, but I\'m sure it would be sufficient to get you started. She practices her craft just east of the Inn in Dolanaar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7100, 'Cooking', 'Cooking', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7101, 'Zarrin seems to have a good knowledge of the cooking trade. Why don\'t you go see if he will show you some recipes? He is usually cooking over by the Inn in Dolanaar.', 'Zarrin seems to have a good knowledge of the cooking trade. Why don\'t you go see if he will show you some recipes? He is usually cooking over by the Inn in Dolanaar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7102, 'We could certainly use another cook around Dolanaar, not that Zarrin\'s cooking is lacking mind you. Why don\'t you go see what he can teach you?', 'We could certainly use another cook around Dolanaar, not that Zarrin\'s cooking is lacking mind you. Why don\'t you go see what he can teach you?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7103, 'Enchanting', 'Enchanting', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7104, 'Alanna Raveneye attributes part of her talent in enchanting to her communion with the spirits at the Oracle Glade. You should be able to find her there now.', 'Alanna Raveneye attributes part of her talent in enchanting to her communion with the spirits at the Oracle Glade. You should be able to find her there now.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7105, 'Alanna spends most of her day at the Oracle Glade where she practices her profession. Should you wish to learn the Enchanter\'s profession you will need to go there to find her.', 'Alanna spends most of her day at the Oracle Glade where she practices her profession. Should you wish to learn the Enchanter\'s profession you will need to go there to find her.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7106, 'First Aid', 'First Aid', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7107, 'Byancie has often lent her services to the sentinels who patrol the local wilds. You should talk to her if you desire to learn First Aid.', 'Byancie has often lent her services to the sentinels who patrol the local wilds. You should talk to her if you desire to learn First Aid.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7108, 'Byancie\'s skill in First Aid has proven useful on numerous occasions. You can find her just north of the Inn in Dolanaar if you wish to learn from her.', 'Byancie\'s skill in First Aid has proven useful on numerous occasions. You can find her just north of the Inn in Dolanaar if you wish to learn from her.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7109, 'Fishing', 'Fishing', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7110, 'All the fisherman have moved to Darnassus with the recent attacks around Lake Al\'Ameth. You should try looking for someone to train you there.', 'All the fisherman have moved to Darnassus with the recent attacks around Lake Al\'Ameth. You should try looking for someone to train you there.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7111, 'The Timberlings have made it too difficult for the fishermen to catch anything around Dolanaar. You best look for someone to train you in Darnassus.', 'The Timberlings have made it too difficult for the fishermen to catch anything around Dolanaar. You best look for someone to train you in Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7112, 'Herbalism', 'Herbalism', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7113, 'Malorne somehow manages to pick the most delicate herbs without damaging them. Certainly he is the one you should learn from. He lives just east of the Inn in Dolanaar.', 'Malorne somehow manages to pick the most delicate herbs without damaging them. Certainly he is the one you should learn from. He lives just east of the Inn in Dolanaar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7114, 'Herbalism requires a gentle touch that few such as Malorne can teach. Seek him out east of the Inn in Dolanaar.', 'Herbalism requires a gentle touch that few such as Malorne can teach. Seek him out east of the Inn in Dolanaar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7115, 'Leatherworking', 'Leatherworking', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7116, 'The Maneweavers practice their professions out of their home on the road to Darnassus. Nadyia is an excellent teacher when it comes to Leatherworking. You should see what she can teach you.', 'The Maneweavers practice their professions out of their home on the road to Darnassus. Nadyia is an excellent teacher when it comes to Leatherworking. You should see what she can teach you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7117, 'Nadyia has made quite the leatherworker in her years of practice. Travel west on the road to Darnassus to pay her a visit and maybe she can bestow some of her knowledge upon you.', 'Nadyia has made quite the leatherworker in her years of practice. Travel west on the road to Darnassus to pay her a visit and maybe she can bestow some of her knowledge upon you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7118, 'Skinning', 'Skinning', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7119, 'Radnaal provides Nadyia with a good supply of leather from the animals he skins. I recommend you speak with him if you wish to learn the profession.', 'Radnaal provides Nadyia with a good supply of leather from the animals he skins. I recommend you speak with him if you wish to learn the profession.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7120, 'You can find Radnaal practicing his craft outside his home on the road to Darnassus. You should stop by and see what he can teach you.', 'You can find Radnaal practicing his craft outside his home on the road to Darnassus. You should stop by and see what he can teach you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7121, 'Tailoring', 'Tailoring', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7122, 'I am certain there is a Tailor that could provide you training in Darnassus. When you arrive, ask one of the Sentinels in Darnassus about seeing a Tailor.', 'I am certain there is a Tailor that could provide you training in Darnassus. When you arrive, ask one of the Sentinels in Darnassus about seeing a Tailor.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7123, 'Many of the supplies the tailors need come by way of Darnassus. It is not surprising that they all moved there to work. If you wish to be a tailor then you best head to Darnassus.', 'Many of the supplies the tailors need come by way of Darnassus. It is not surprising that they all moved there to work. If you wish to be a tailor then you best head to Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7124, 'Help! Please, you must help me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7125, 'Hello, $c. I\'ve set up camp here at the Bulwark to make my preparations for my journey into the Plaguelands. I\'ve heard rumor that a great human wizard took up residency in Hearthglen not terribly long ago. He was commissioned by the Scarlet Crusade to fashion their warriors and hunters new bows and guns. It\'s said that he completed a remarkable weapon before the Crusade killed him.$B$BI guess their mistrust of the arcane has grown.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7126, '', 'Greetings, $c. Don\'t have too much time to talk--I\'m just using Chillwind as a camp to get some rest before heading to Hearthglen. I\'ve heard word that before they went crazy, the Scarlet Crusade had a mage working for them who was trying to create powerful bows and guns for their soldiers. I\'m not sure if he succeeded or not... the Crusade killed him before any of his finished product made it out. Maybe they killed him to keep it a secret... or maybe they distrust the arcane that much.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7127, '', 'He... hello, $n. Sorry, I\'m recovering from my wounds.$B$BThose Scarlet Crusaders are tougher than I had given them credit for. They almost did me in.$B$BYou should be careful too. I\'d hate to see them get the jump on you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7128, '', 'My family worked  the Northridge Lumber Mill in what\'s now Western Plaguelands. They were dedicated to the Dumah family, and now the Scarlet Crusade have taken it over along with all of Hearthglen!$B$BI will not rest until those zealots are wiped from Azeroth.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7129, 'Long ago, before the last great war, my father ran the Northridge Lumber Mill. It was his life, and he was committed to it. Then the war came, and as much as it pained him, we had to flee to Stormwind.$B$BWe lost a number of friends and family in the those days, and it pains me to know that the Scarlet Crusade has taken the mill for their own ends.$B$BThat\'s why I\'ve returned.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7130, 'Greetings, $c.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7131, 'Greetings, $c.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7132, 'Greetings, $c.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7133, 'I see those fools at the Abbey sent some fresh meat for us.', 'I see those fools at the Abbey sent some fresh meat for us.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7134, 'This land belongs to the Defias Brotherhood now!', 'This land belongs to the Defias Brotherhood now!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7135, 'We are the last of the furbolg race... The Timbermaw tribe remains uncorrupted, yet we face the same hostility from outsiders; most believe that it is not possible... that there could actually be furbolg that escaped the corruption...$B$BAngry at those that strive to make enemies too quickly, my brethren now attack any that draw near; they trust no one.$B$BBut perhaps, if you strive to make your presence peaceful with them, they might look upon you as an ally.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7136, 'How can I prove myself to the Timbermaw furbolg?', 'How can I prove myself to the Timbermaw furbolg?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7137, 'You must find a way to show that you both are fighting for the same side. Although the Timbermaw may attack you now, if you are diligent, you will find a way.$B$BYou must remember that any attacks on the Timbermaw furbolg directly will greatly hinder your chances of gaining them as an ally.$B$BA good place to start would be the Deadwood furbolg found in many parts of Felwood. These furbolg are the enemy of the Timbermaw tribe, and slaying them would greatly improve your standing with the Timbermaw.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7138, 'We Timbermaw can grant you safe passage through the hold, however, you must prove yourself worthy of our aid.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7139, 'What about the Winterfall furbolg?', 'What about the Winterfall furbolg?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7140, 'The Winterfall furbolg that reside here in Winterspring were once our allies... But one day, that all changed.$B$BDriven by the strange demands of their high chief, they began to attack all others that they came in contact with, including our tribe. They keep to themselves now...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7141, 'Please, do not bother me... Your kind is all the same...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7142, 'I would like to buy from you.', 'I would like to buy from you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7143, 'I trust very few of your kind, $n. But I can tell that you are a friend.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7144, 'Yes, yes. I see you.$B$BBe on your way. You may have earned the trust of a few, but it will take more than that to prove it to me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7145, 'School is in session!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7146, 'I have no time for you, $r. Continue on your way.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7147, 'If you prove that you are a true ally, I might consider training you...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7148, 'Welcome, $n! Are you interested in training in tailoring?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7149, 'I\'d like to train.', 'I\'d like to train.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7150, 'I don\'t get paid enough for this!', 'I don\'t get paid enough for this!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7151, 'Time to take out the trash!', 'Time to take out the trash!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7152, 'I\'m going to dust you into oblivion!', 'I\'m going to dust you into oblivion!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7153, 'How dare you?!', 'How dare you?!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7154, 'Spot of tea, gov\'na?', 'Spot of tea, gov\'na?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7155, 'Do not turn your back on the Light, $c, it may be the one thing that saves you some day.', 'Do not turn your back on the Light, $c, it may be the one thing that saves you some day.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7156, 'I trust the Light is with you, $n. Is there something that I can I help you with?', 'I trust the Light is with you, $n. Is there something that I can I help you with?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7157, 'I seek more training in the priestly ways.', 'I seek more training in the priestly ways.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7158, 'The Light protect you, $c.', 'The Light protect you, $c.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7159, 'I hope the Light is with you, $n. Is there anything I can do for you today?', 'I hope the Light is with you, $n. Is there anything I can do for you today?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7160, 'I seek more training in the priestly ways.', 'I seek more training in the priestly ways.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7161, 'You be watchin\' yerself, $c. Respect our ways, or a curse be on you and yours.', 'You be watchin\' yerself, $c. Respect our ways, or a curse be on you and yours.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7162, 'I do what me can to train you in the priestly ways, if that what you be here for.', 'I do what me can to train you in the priestly ways, if that what you be here for.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7163, 'Yes, $c?', 'Yes, $c?', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7164, 'What can this insignificant servant do for you, $n? Training perhaps?', 'What can this insignificant servant do for you, $n? Training perhaps?', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7165, 'Elune be with you, $c.', 'Elune be with you, $c.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7166, 'My wisdom is yours, as is my knowledge. What can I do for you, $n?', 'My wisdom is yours, as is my knowledge. What can I do for you, $n?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7167, 'I seek more training in the priestly ways.', 'I seek more training in the priestly ways.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7168, 'I seek more training in the priestly ways.', 'I seek more training in the priestly ways.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7169, 'I seek more training in the priestly ways.', 'I seek more training in the priestly ways.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7170, 'I be seeking someone, $c. Please, leave me be... it is important I find them.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7171, 'I be a messenger from the Eastern Kingdoms, $n. I be sent here to make sure da priests who travel here be given proper direction.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7172, 'Be quiet! Can\'t you see the master is speaking?', 'Be quiet! Can\'t you see the master is speaking?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7173, 'Spend less time rattling and more time listening.', 'Spend less time rattling and more time listening.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7174, 'We must focus if we are to spawn our dragonflight!', 'We must focus if we are to spawn our dragonflight!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7175, 'Be silent! I cannot afford to miss even a moment of this lecture!', 'Be silent! I cannot afford to miss even a moment of this lecture!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7176, 'Did you forget your student\'s attire? Do you think fashion is more vital than the Scourge\'s domination?', 'Did you forget your student\'s attire? Do you think fashion is more vital than the Scourge\'s domination?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7177, 'Did you finish yesterday\'s assignment? If so, then sit by me!', 'Did you finish yesterday\'s assignment? If so, then sit by me!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7178, 'Watch your step in here, $gboy:girl;. The master sees all...', 'Watch your step in here, $gboy:girl;. The master sees all...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7179, 'Did you bring your text books? Can I look on with you?', 'Did you bring your text books? Can I look on with you?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7180, 'What, may I ask, are you doing here? Put on your student\'s attire, and study!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7181, 'Be silent! Do you think our plagued dragons will hatch and train themselves?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7182, 'Have you come from the Burning Steppes? Do you have my black dragon eggs?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7183, 'Your mind is unfocused. Are you hiding something from me?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7185, 'I sense a disturbance, lurking around you...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7186, 'Your future is clouded. Concentrate, and ask again.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7187, 'You don\'t look like a student...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7188, 'Now if you turn to page 34 in your text, you see an illustration of a dwarf undergoing dissection...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7189, 'Take note of the delicacy of the plagued dragon\'s diet. Only terrified flesh will suffice.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7190, 'We await a batch of black dragon eggs from the Burning Steppes. We believe that, through their study, we will advance our knowledge dramatically.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7191, 'Our oldest clutch of dragons are still far from maturity, but with patience and study, we are confident the dragonflight will soon be ready.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7192, 'From yesterday\'s field trip, Marduk showed us that the dragons will tolerate the meat of recently killed humanoids, but only if they died slowly and painfully.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7193, 'Tomorrow we will begin training of our promising dragons, so don\'t forget your chew toys.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7194, 'The Lich King\'s forces are building. It is imperative that our timetable supports his plans.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7195, 'When preparing the dragon\'s meal, be sure to torture the prisoner in view of the dragon. It responds well to pre-meal entertainment.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7196, 'This kodo sure looks nothing like the beast I originally lured! I wonder if the kombobulator can be used on me.', 'This kodo sure looks nothing like the beast I originally lured! I wonder if the kombobulator can be used on me.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7197, '', 'The Plaguelands are an excellent place to strike against the Scourge!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7198, 'May I have another Dawn\'s Gambit, Betina? I want to test it again...', 'May I have another Dawn\'s Gambit, Betina? I want to test it again...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7199, '', 'I\'ve run out of arrows! I\'m afraid if any more come you will need to take them on by yourself my friend.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7200, '', 'Wait... did you hear that? Something approaches from the west!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7201, '', 'Praise Elune! I don\'t know if I could have survived the day without you, friend.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7202, '', 'My leg feels much better now, the remedy must be working. If you will excuse me, I must go report to my superiors about what has transpired here.\n', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7203, 'Who dares to challenge me in my domain?!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7204, '', 'I just fired an arrow.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7205, 'End our suffering!', 'End our suffering!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7206, 'You must save him!', 'You must save him!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7207, 'Oh, Darrowshire! I would give a thousand lives for you!', 'Oh, Darrowshire! I would give a thousand lives for you!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7208, 'Do not fail us!', 'Do not fail us!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7209, 'The Light must prevail!', 'The Light must prevail!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7210, 'Beware Marduk! Beware, or your strength will wither.', 'Beware Marduk! Beware, or your strength will wither.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7211, 'I was devoured by Horgus! I can still feel his teeth upon me!', 'I was devoured by Horgus! I can still feel his teeth upon me!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7212, 'Captain Redpath! How could you forsake us!', 'Captain Redpath! How could you forsake us!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7213, '%s takes in a deep breath...', '%s takes in a deep breath...', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7214, 'Well met, $c. You may speak to me unless you have direct business with my mistress Magatha.$B$BI deal with most of her tasks for her so she can concentrate on more important matters. Perhaps there are some things we could discuss before you seek audience with her?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7215, 'Hello, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7216, 'Please, feel free to speak to me anytime, $n. You have served Magatha well. I am sure she will call upon you in the future.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7217, 'Do not presume to speak to me as though you are my equal, $c. Sylvanas is my queen, and I will do as she bids.$B$BIf you do not show the proper respect, I assure you, we can find a more than suitable place for you in the Nether.$B$BBut there is no reason we cannot have words if you wish to aid the Dark Lady...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7218, 'All members of the Horde are equal in my eyes, $n. We have all suffered many burdens, and if it were not for wisdom and honor, then we would be no better than the Scourge or my people while our blood was tainted by the demon Mannoroth.$B$BIt is your duty to aid the Horde and to defend our way of life. But it is also your duty to know when quarter and compassion should be given to friend and foe alike.$B$BUnderstand this well. This is the new Horde, not some demon-spawned army who lack freewill.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7219, 'I am ready to hear your tale, Tirion.', 'I am ready to hear your tale, Tirion.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7220, 'I must apologize for not being entirely truthful with you in regards to my identity. I had to be certain that you could be trusted - few people would have selflessly lent assistance to an old and forgotten hermit.$B$BYou have proven yourself as one of those people: A person of integrity and honor.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7221, 'Thank you, Tirion. What of your identity?', 'Thank you, Tirion. What of your identity?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7222, 'My son, Taelan, was only a child when I was found guilty of treason and banished from the Alliance to live a life in exile. I chose to remain here so that I could watch over him; to somehow insure that he would grow up an honorable man.$B$BAs I had hoped, he took my place as lord of Mardenholde; but in a cruel twist of fate, joined the ranks of the Scarlet Crusade. He is now their Highlord, $r.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7223, 'That is terrible.', 'That is terrible.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7224, 'Indeed. The Scarlet Crusade are an aberration of the Order of the Silver Hand.$B$BYou must believe me, $r; Taelan is a good man. He needs guidance. He needs to remember... remember what it is to be noble and honorable. I know that in his heart, he knows what he does is wrong. Will you help him? Will you help him remember?', '', 0, 0, 0, 1, 1, 6, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7225, 'I will, Tirion.', 'I will, Tirion.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7226, 'Why is it that small creatures such as goblins are always fascinated with larger creatures?', 'Why is it that small creatures such as goblins are always fascinated with larger creatures?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7227, 'Ah! My wounds are too severe. Defenders, fight on without me!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7228, 'Listen closely to what you are told and do not quickly trust, or distrust. For even friends serve their own ends, $c.$B$BOur great Warchief knows this, and to prove that there are warlocks within the Horde who do not wish to destroy it from within or gain control over it, I have volunteered to aid our young leader in anyway I can.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7229, 'Prepare for a severe beating.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7230, 'I shall wear your entrails as a necklace.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7231, 'I can smell your fear, $r.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7232, 'If you run now, you may live.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7233, 'How dare you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7234, 'Pathetic.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7235, 'The reckoning is upon you, weakling!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7236, 'Disappointing.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7237, 'Is that the best you can do?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7238, 'You weren\'t worth the energy expenditure. <Nathanos spits.>', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7239, 'Next time, bring friends.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7240, 'I am now corrupting you...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7241, 'Weapons Trainer', 'Weapons Trainer', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7242, 'Woo Ping\'s stamina is simply astounding. Watching him practice his weapon techniques for hours on end, I felt sorry for the practice dummy afterward. I can\'t think of anyone better suited to train you in armed combat.', 'Woo Ping\'s stamina is simply astounding. Watching him practice his weapon techniques for hours on end, I felt sorry for the practice dummy afterward. I can\'t think of anyone better suited to train you in armed combat.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7243, 'Master Woo Ping has mastered nearly every weapon known to man. He is especially deadly with his sword techniques. He spends a good deal of time over in the Weller\'s Arsenal waiting on new shipments of weapons to arrive if you need some training.', 'Master Woo Ping has mastered nearly every weapon known to man. He is especially deadly with his sword techniques. He spends a good deal of time over in the Weller\'s Arsenal waiting on new shipments of weapons to arrive if you need some training.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7244, 'Weapons Trainer', 'Weapons Trainer', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7245, 'Through the milennia, Ilyenia Moonfire has mastered numerous weapons and now offers to train others in their use. You can find her at the Warrior\'s Terrace if you desire training.', 'Through the milennia, Ilyenia Moonfire has mastered numerous weapons and now offers to train others in their use. You can find her at the Warrior\'s Terrace if you desire training.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7246, 'I received the majority of my weapons training from Ilyenia Moonfire in the Warrior\'s Terrace. You would do well to seek her out if you desire to learn the use of a new weapon. ', 'I received the majority of my weapons training from Ilyenia Moonfire in the Warrior\'s Terrace. You would do well to seek her out if you desire to learn the use of a new weapon. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7247, 'Weapons Trainer', 'Weapons Trainer', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7248, 'Between Bixi and Buliwyf, there ain\'t many weapons they haven\'t had the pleasure of slashing or smashing an orc with. You can find them hanging out at the Timberline Arms weapon shop if ye need some training.', 'Between Bixi and Buliwyf, there ain\'t many weapons they haven\'t had the pleasure of slashing or smashing an orc with. You can find them hanging out at the Timberline Arms weapon shop if ye need some training.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7249, 'Yer looking to learn how to use a new weapon are ye? Well, ye should be talking to Bixi or Buliwyf over at the Timberline Arms weapon shop then $glad : lass;. They know their weapons from axes to... uh... zweihanders.', 'Yer looking to learn how to use a new weapon are ye? Well, ye should be talking to Bixi or Buliwyf over at the Timberline Arms weapon shop then $glad : lass;. They know their weapons from axes to... uh... zweihanders.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7251, 'Looking to learn how to use a new weapon? Find Sayoc and Hanashi inside Arms of Legend in the Valley of Honor.', 'Looking to learn how to use a new weapon? Find Sayoc and Hanashi inside Arms of Legend in the Valley of Honor.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7252, 'Look for Sayoc and Hanashi at the Valley of Honor inside Arms of Legend. Watch your tongue around them or Sayoc may decide to give a piece of you as a snack to Grinning Dog.', 'Look for Sayoc and Hanashi at the Valley of Honor inside Arms of Legend. Watch your tongue around them or Sayoc may decide to give a piece of you as a snack to Grinning Dog.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7253, 'The weapon master', 'The weapon master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7254, 'Ah, you seek the wisdom of Ansekhwa. Look for him on the lower rise at Thunder Bluff Weapons.', 'Ah, you seek the wisdom of Ansekhwa. Look for him on the lower rise at Thunder Bluff Weapons.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7255, 'None are as skilled in the use of weapons as Ansekhwa. It is said that he is guided by the spirits of the finest warriors of our honored ancestors. Seek him out on the lower rise and he will teach you what you want to know.', 'None are as skilled in the use of weapons as Ansekhwa. It is said that he is guided by the spirits of the finest warriors of our honored ancestors. Seek him out on the lower rise and he will teach you what you want to know.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7256, 'The weapon master', 'The weapon master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7257, 'Archibald who you look for. He outside weapon merchant shop on inner ring of War Quarter.', 'Archibald who you look for. He outside weapon merchant shop on inner ring of War Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7258, 'Little forsaken know all weapons. Him Archibald. You find on inner ring of War Quarter.', 'Little forsaken know all weapons. Him Archibald. You find on inner ring of War Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7259, 'The weapon master', 'The weapon master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7260, 'It would be wise to explain to you the depths at which our enemies will go to to hide themselves under our noses.$B$BKnow this first and foremost: our enemy is the Shadow Council. No matter what task I give you, or who I ask you to act against, know that one simple fact.$B$BMany cults exist within the Council, but only to hide its actions against the foolish.$B$BGroups like the Burning Blade, the Argus Wake, and the Searing Blade are all tools of the Council. Consider them one and the same.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7261, 'Well, well. Looks like we got ourselves some competition. Get \'em boys!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7262, 'Ah, one of our own. It is good to see our numbers are growing so rapidly that I can no longer keep track of the faces of our members.$B$BThat fool Thrall thinks I am working hard to cleanse the image of warlocks and prove we are his servants. Idiot! The Burning Legion will make ash of this entire world after it drains it completely.', '', 0, 0, 0, 2, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7263, 'Naive fool. The Burning Blade IS the Shadow Council! There are many cults and organizations under the Council\'s rule, but all of them are just extensions of their will. I may lead the Burning Blade, but I am still just as much a pawn to the Council as you are to me!$B$BLook at the Searing Blade--most of them think they are becoming the next rising group of powerful warlocks that they will replace the Shadow Council.', '', 0, 0, 0, 14, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7264, 'Haha! Save for their leaders--the satyr, Bazzalan, and the warlock Jergosh--yes, all of them. That is why I constantly send Thrall\'s worshippers at them. It gives Thrall the impression that I am weeding out the evil in the bowels of his great city. The victory is in reality two-fold: kill Thrall\'s faithful and test the strength of those in the Searing Blade to see if they are truly worthy of joining me.', '', 0, 0, 0, 11, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7265, 'I will make it known to you. You are obviously already proving yourself in our ranks to become a lieutenant so quickly. Perhaps you can return to me later and I will have tasks for you. The council adores those skilled in the arcane, but servants of all kinds are always welcome, no matter their profession.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7266, 'You may speak frankly, Neeru...', 'You may speak frankly, Neeru...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7267, 'It is good to see the Burning Blade is taking over where the Shadow Council once failed.', 'It is good to see the Burning Blade is taking over where the Shadow Council once failed.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7268, 'So the Searing Blade is expendable?', 'So the Searing Blade is expendable?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7269, 'If there is anything you would have of me...', 'If there is anything you would have of me...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7270, 'I am now corrupted.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7271, 'I\'m saved.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7272, '', 'So dark...', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7273, '', 'Is someone there? Tirion my old friend, is that you? Have you come to save us?', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7274, '', 'Are they all... dead?', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7275, '', 'The Sarkhoff\'s... sometimes the Sarkhoff\'s visit. They are good people.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7276, 'Being part of the Explorers\' League, I\'ve been hunting for artifacts in the area. Winterspring is said to contain very magical items... Most are highly guarded.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7277, '%s searches for a soul to swallow!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7278, 'The solution is ready in my cauldron. It\'s just the matter of treating both the unfinished key and the scarab.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7279, 'It\'s done $n, and I think you\'ll be satisfied with the results.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7280, 'The solution is ready in my cauldron. It\'s just the matter of treating both the unfinished key and the scarab.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7281, 'It\'s done $n, and I think you\'ll be satisfied with the results.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7282, 'Captain Ballinda Stonehearth will soon arrive at her garrison. A new battle will soon be joined!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7283, 'Fresh troops flood to Iceblood Garrison! Captain Galvangar will soon arrive, and a new battle will soon begin!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7284, 'This is the Alterac Valley battlefield. Both sides are still fighting.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7285, 'Let\'s start a new battle, and beat the Horde this time!', 'Let\'s start a new battle, and beat the Horde this time!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7286, 'This is the Alterac Valley battlefield. Currently both sides are at odds.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7287, 'Let\'s start a new battle, and beat the Alliance this time!', 'Let\'s start a new battle, and beat the Alliance this time!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7288, 'Our forces are ready and eager. Soon, Balinda Stonehearth will enter her outpost and the battle will begin anew!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7289, 'Our general, Vanndar Stormpike, has fallen in battle! The battle is lost!$B$BSpeak with our quartermaster and help rebuild our forces.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7290, 'The horde general is defeated!$B$BThe Alliance is the victor, and the Horde now scrambles to rebuild its weakened forces.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7291, 'Neither generals is spawned.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7293, 'Drek\'Thar is defeated! The Alliance is victorious... for now.$B$BSpeak with our quartermaster and aid in our reconstruction.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7294, 'I\'m here! Come and get me you alliance pansies!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7295, 'Vanndar is here. Come on, hordelings! Show me what you got!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7296, 'The Frostwolf General is dead!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7297, 'The Stormpike General is dead!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7301, 'Hey, look out with that kodo! You had better inspect that beast before I give you credit!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7302, 'That kodo sure is a beauty. Wait a minute, where are my bifocals? Perhaps you should inspect the beast for me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7303, 'Ah...the wondrous sound of kodos. I love the way they make the ground shake... inspect the beast for me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7304, 'How do the goblins ever manage to secure packs to these beasts? Kodos are ten times the size!', 'How do the goblins ever manage to secure packs to these beasts? Kodos are ten times the size!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7305, '', 'These are dangerous times, adventurer. Walk lightly.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7306, 'I am ready for the illusion, Myranda.', 'I am ready for the illusion, Myranda.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7307, '', 'Hello! What brings you to Winterspring? These cold and vast lands may seem dismal, but this is a place of many secrets...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7308, '', 'Such sssorrow... The passst cannot be undone...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7309, '', 'I will not forrrget what you have done...$B$BPleassse, leave meee...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7310, 'Hey, you call yourself a body guard? Get to work and protect us...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7311, 'Mister body guard, are you going to earn your money or what?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7312, 'You\'re fired! <Cough...Cork clears throat.> I mean, help!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7313, 'I will lead us through Heartghlen to the forest\'s edge. From there, you will take me to my father.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7314, 'Remove your disguise, lest you feel the bite of my blade when the fury has taken control.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7315, 'Halt.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7316, '%s calls for his mount.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7317, '%s whinnies.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7318, 'NOOOOOOOOOOOO!', '', 1, 0, 0, 15, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7319, 'Sir?', 'Sir?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7320, 'Something is wrong with the Highlord. Do something!', 'Something is wrong with the Highlord. Do something!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7321, 'What the...', 'What the...', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7322, 'Hey, what\'s going on over there? Sir, are you alright? Sir??', 'Hey, what\'s going on over there? Sir, are you alright? Sir??', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7323, 'Highlord Fordring, are you alright?', 'Highlord Fordring, are you alright?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7324, 'Run!', 'Run!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7325, 'It\'s Proudmoore! Get out of here!', 'It\'s Proudmoore! Get out of here!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7326, 'Here I am, alliance! Take me down... if you can!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7327, '', 'For the Alliance!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7328, '', 'Please speak with Sentinel Onaeya at Maestra\'s Post in Ashenvale, she will see to it that you are properly rewarded for your bravery this day.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7329, 'It\'s not much further. The main road is just up ahead.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7330, 'Eeck! Demons appear hungry for the kodos!', '', 1, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7331, 'What am I paying you for? The kodos are nearly dead!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7332, 'Only if I were about five feet taller, then I would show these blasphemous demons a thing or two! Help!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7333, 'Wow! We did it... not sure why we thought we needed the likes of you. Nevertheless, speak with Smeed Scrablescrew; he will give you your earnings!\n', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7334, 'Blast those stupid centaurs! Sigh - well, it seems you kept your bargain. Up the road you shall find Smeed Scrabblescrew, he has your money.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7335, 'The Alliance wins!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7336, 'The Horde wins!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7337, 'You disappoint me, Taelan. I had plans for you... grand plans. Alas, it was only a matter of time before your filthy bloodline would catch up with you.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7338, 'It is as they say: Like father, like son. You are as weak of will as Tirion... perhaps more so. I can only hope my assassins finally succeeded in ending his pitiful life.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7339, 'TEST - restart battlefield.', 'TEST - restart battlefield.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7340, 'Get out of here!', 'Get out of here!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7341, 'Don\'t let them catch you!', 'Don\'t let them catch you!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7342, 'She\'ll have our heads if we\'re caught!', 'She\'ll have our heads if we\'re caught!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7343, 'Do not lose hope, Darrowshire! We will not fall!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7344, 'The Light burns bright in you, Davil. I will enjoy snuffing it out!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7345, 'Now is the time, Davil. Now is the time of your death!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7346, 'Horgus, your nightmare ends! Now!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7347, 'For Darrowshire!', 'For Darrowshire!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7348, 'Our captain will save us!', 'Our captain will save us!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7349, 'Fight! Fight for your families!', 'Fight! Fight for your families!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7350, 'The Scourge will not prevail!', 'The Scourge will not prevail!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7351, 'Do not give up! Shed your blood for Darrowshire!', 'Do not give up! Shed your blood for Darrowshire!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7352, 'Fight, and we will win this day!', 'Fight, and we will win this day!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7353, 'Fight to your last breath, brothers and sisters!', 'Fight to your last breath, brothers and sisters!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7354, 'Go back to your Lich King, foul creatures!', 'Go back to your Lich King, foul creatures!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7355, 'Defenders of Darrowshire! Rally! We must prevail!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7356, 'Darrowshire! You are doomed!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7357, 'Darrowshire is defeated! Soon, all of Lordaeron will fall!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7358, 'Darrowshire, to arms! The Scourge approach!', 'Darrowshire, to arms! The Scourge approach!', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7359, 'The Grand Crusader has charged me with destroying you and your newfound friends, Taelan, but know this: I do this for pleasure, not of obligation or duty.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7360, '%s calls for his guardsmen.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7361, 'The end is now, Fordring.', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7362, 'Sit and listen to my story, $r.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7363, '', 'Too much time has passed. We must try again later.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7364, '', 'Ok, let\'s get going!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7365, '', 'Remember, I need your help to properly channel. I will ask you to aid me several times on our path, so please be ready.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7366, 'Davil Lightfire is defeated! Darrowshire is lost!', 'Davil Lightfire is defeated! Darrowshire is lost!', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7367, 'Davil Lightfire has succumbed to his wounds!', 'Davil Lightfire has succumbed to his wounds!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7368, 'Horgus is slain! Take heart, defenders of Darrowshire!', 'Horgus is slain! Take heart, defenders of Darrowshire!', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7369, 'Captain Redpath is slain!', 'Captain Redpath is slain!', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7370, 'Isillien!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7371, 'This is not your fight, stranger. Protect yourself from the attacks of the Crimson Elite. I shall battle the Grand Inquisitor.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7372, 'What have you done, Isillien? You once fought with honor, for the good of our people... and now... you have murdered my boy... ', '', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7373, 'May your soul burn in anguish, Isillien! Light give me strength to battle this fiend.', '', 0, 0, 0, 15, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7374, 'Face me, coward. Face the faith and strength that you once embodied.', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7375, '', 'This blue light... It\'s strange, $N. What do you think it means?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7376, '$g Hey there cutie, you looking for a ride to Grom\'gol? I hear it gets mighty steamy down in those jungles, maybe I could join you for a short vacation, show you a real jungle cat. : Come, take a trip to the wonderful, gorgeous, tropical jungles of Stranglethorn. That\'s right, you too can be spending your time sunbathing by the crystal blue waters while I stand here in this unbearable heat with nothing to look at but my brother Frezza all day! Enjoy your trip!;', '$g Hey there cutie, you looking for a ride to Grom\'gol? I hear it gets mighty steamy down in those jungles, maybe I could join you for a short vacation, show you a real jungle cat. : Come, take a trip to the wonderful, gorgeous, tropical jungles of Stranglethorn. That\'s right, you too can be spending your time sunbathing by the crystal blue waters while I stand here in this unbearable heat with nothing to look at but my brother Frezza all day! Enjoy your trip!;', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7377, 'Slip a little coin into the boss\'s hand and look what it gets ya. I love my job. Though, if I could find a way to get that little firecracker Snurk reassigned to this port....well, I can\'t imagine it any better. But I\'m betting that you aren\'t here about my business practices. You\'re looking for the Zeppelin to Undercity, am I right? Well that\'s great, because it docks right here behind me. Oh and hey, if you see Snurk, put a good word in for me, would ya?', 'Slip a little coin into the boss\'s hand and look what it gets ya. I love my job. Though, if I could find a way to get that little firecracker Snurk reassigned to this port....well, I can\'t imagine it any better. But I\'m betting that you aren\'t here about my business practices. You\'re looking for the Zeppelin to Undercity, am I right? Well that\'s great, because it docks right here behind me. Oh and hey, if you see Snurk, put a good word in for me, would ya?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7378, '', 'Please, light this while I am channeling.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7379, '', 'This is the place. Let\'s light it.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7380, '', 'We found it, $N.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7381, 'Enough!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7382, '%s laughs.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7383, 'Did you really believe that you could defeat me? Your friends are soon to join you, Taelan.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7384, '%s turns his attention towards you.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7385, '', '$N, it is time for the final step; we must activate the altar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7386, '', 'I will read the words carved into the stone, and you must find a way to light it.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7387, '', 'It... it is working!', 0, 0, 0, 273, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7388, '', 'What is happening? Look!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7389, '', 'It has been many years...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7390, '', 'Who has disturbed the altar of the goddess?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7391, '', 'Please, priestesses, forgive us for our intrusion. We do not wish any harm here.', 0, 0, 0, 68, 0, 0, 0, 400, 0);
INSERT INTO `broadcast_text` VALUES (7392, '', 'We only wish to know why the wildkin guard this area...', 0, 0, 0, 30, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7393, '', 'Enu thora\'serador. This is a sacred place.\n', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7394, '', 'We will show you...\n', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7395, 'You will not make it to the forest\'s edge, Fordring.', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7396, 'The Nightmare is finally over! Darrowshire, forgive me!', '', 0, 0, 0, 15, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7397, 'Pamela? Are you there, honey?', '', 0, 0, 0, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7398, 'Hahah!', '', 0, 0, 0, 11, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7399, '', 'Daddy!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7400, '', 'Daddy!  You\'re back!', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7401, '', 'Let\'s go play!  No, tell me a story, Daddy!  No... let\'s go pick flowers!  And play tea time!  I found my dollie, did I tell you?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7402, '', 'I missed you so much, Daddy!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7403, 'I missed you too, honey. And I\'m finally home...', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7404, 'The slain spirit of Lunaclaw stares at you intently, as though wanting something from you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7405, 'You have fought well, spirit. I ask you to grant me the strength of your body and the stength of your heart.', 'You have fought well, spirit. I ask you to grant me the strength of your body and the stength of your heart.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7406, 'The spirit looks intently into your eyes and grasps your hand. You feel a warmth shoot through your body, almost knocking you to your knees.$B$BAs the warmth passes, you feel the slight presence of an ursine spirit within your being... bolstering your resolve.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7407, 'The Scourge are defeated! Darrowshire is saved!', 'The Scourge are defeated! Darrowshire is saved!', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7408, 'We have all been witness to terrible tragedies, but we must not let them drag us to despair.$B$BInstead, hold those memories close so that one day, we might find a reckoning.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7409, 'I need another Relic Bundle!', 'I need another Relic Bundle!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7410, '', 'Let\'s find the next one, $N.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7411, '', 'We must continue on now.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7412, '', 'The altar is glowing! We have done it!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7413, '', 'Look above you; thara dormil dorah... ', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7414, '', 'This gem once allowed direct communication with Elune, herself.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7415, '', 'Through the gem, Elune channeled her infinite wisdom...', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7416, '', 'Anu\'dorini Talah. Ru shallora enudoril.\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7417, '', 'Realizing that the gem needed to be protected, we turned to the goddess herself.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7418, '', 'Soon after, we began to have visions of a creature... A creature with the feathers of an owl, but the will and might of a bear...', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7419, '', 'It was on that day that the wildkin were given to us. Fierce guardians, the goddess assigned the wildkin to protect all of her sacred places.\n', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7420, 'A thousand more like him exist. Ten thousand. Should one fall, another will rise to take the seat of power.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7421, '%s falls to one knee.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7422, '', 'But now, many years later, the wildkin have grown more feral, and without the guidance of the goddess, they are confused...\n', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7423, 'Look what they did to my boy.', '', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7424, '', 'Without a purpose, they wander... But many find their way back to the sacred areas that they once were sworn to protect.\n', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7425, '%s holds the limp body of Taelan Fordring and softly sobs.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7426, 'Your death will not have been in vain, Taelan. A new Order is born on this day... an Order which will dedicate itself to extinguishing the evil that plagues this world. An evil that cannot hide behind politics and pleasantries.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7427, '', 'Wildkin are inherently magical; this power was bestowed upon them by the goddess.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7428, '', 'Know that wherever you might find them in the world, they are protecting something of importance, as they were entrusted to do so long ago.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7429, 'Too long have I sat idle, gripped in this haze... this malaise, lamenting what could have been... what should have been.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7430, 'This I promise... This I vow...', '', 0, 0, 0, 15, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7431, '', 'Please, remember what we have shown you...', 0, 0, 0, 30, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7432, '', 'Farewell.\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7433, 'Tragic. The elder Fordring lives on... You are too late, old man. Retreat back to your cave, hermit, unless you wish to join your son in the Twisting Nether.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7434, '', 'Thank you for your help, $n. I wish you well in your adventures. ', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7435, '', 'I want to stay here and reflect on what we have seen. Please see Erelas and tell him what we have learned.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7436, 'Then come, hermit!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7437, 'The bear looks at you calmy and patiently, but says nothing.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7438, 'The bear spirit looks upon you with a calm and peaceful gaze. You hear a voice speak to you as though it was coming from the bear, but the creature makes no indication that it is speaking.$B$B\"Greetings, my young friend. If you have come to me seeking guidance, then perhaps I can help you find what you seek.\"', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7439, 'What do you represent, spirit?', 'What do you represent, spirit?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7440, 'I represent the strength which bolsters you as a young druid. I am the strength of the body, and the strength of the heart. You have already come to know this in your training, and you draw upon this when you take on the aspect of the bear.$B$BContinue to keep the balance in all things, young one, and keep your perspective clear and true. When you need me... I will be there for you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7441, 'I represent the strength which bolsters you as a young druid. You have come to me to learn this strength, young one, and I will teach this to you. I will teach you all that is the spirit bear, provided you will listen and understand.$B$BIn order to know what it means to draw upon my spirit, you need to understand the importance of the strength of the body, as well as the strength of the heart.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7442, 'I seek to understand the importance of strength of the body.', 'I seek to understand the importance of strength of the body.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7443, 'Strength of the body is the power and swiftness of action. You must keep yourself fit at all times, both physically and mentally. The bear\'s girth highlights its strength, as it is a ferocious foe in combat. The bear\'s girth, however, belies its lithe agility and sharp mind. These are surprises you will use to your advantage.$B$BYou must rely on the strength of the bear\'s body in order to master the way of the Claw.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7444, 'I seek to understand the importance of strength of the heart.', 'I seek to understand the importance of strength of the heart.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7445, 'Strength of the heart is what gives you the resolve to take action... action that is rooted in intents that are pure and forthright. To keep the balance is not to be complacent or banal.$B$BYou must show resolve for that which you believe in, and you must be willing to fight for it. The mother bear shows endless conviction in protecting her young, as does an elder bear protecting his den. It is this resolve, this strength of heart, which you must come to know if you are to master the way of the Claw.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7446, 'I have heard your words, Great Bear Spirit, and I understand. I now seek your blessings to fully learn the way of the Claw.', 'I have heard your words, Great Bear Spirit, and I understand. I now seek your blessings to fully learn the way of the Claw.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7447, 'It remains to be seen if you are ready, young one. Even the wisest and oldest of druids are never truly ready when their ultimate time of testing comes.$B$BYou have heard my words, and now you must move on. Heed what I have taught you. There will be a time when you will have your strength tested. You must face your foe as the bear would - with strength of body and with strength of heart. Learn from the fight, young one. Go... with my blessings.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7448, 'Greetings, $c. You are welcome in Nighthaven, as well as all of Moonglade. The Cenarion Circle welcomes all beings who respect the authority and command of nature.$B$BWhile you are here, please be sure to preserve the balance in all things. Disturbances are not welcome here, and our wardens are trained to cease all hostilities without question.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7449, '', 'Do you require an illusion?$B$BShould your illusion falter in the midst of your quest, return to me and I shall reapply the guise.$B$BShould you fail in your endeavors, do not give up! Return to me. I shall assist you until you have completed your objectives.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7450, 'Strive to keep the balance in all things, $c. This is where true harmony lies.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7451, 'Welcome, my $g brother : sister;. If you are here to train, then I would be delighted to teach you.$B$BIt never hurts to reaffirm one\'s place within what I like to call the Great Balance. As preservers of this balance, we must strive to continually keep our minds clear and our hearts strong. Training helps us in this regard.', '', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7452, 'I seek training as a druid.', 'I seek training as a druid.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7453, 'The battle is over, and the people of Darrowshire are saved.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7454, 'The battle is over, and the people of Darrowshire are saved.$B$BThey are saved, $n, because of you. You are truly a hero of Darrowshire.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7455, 'A darkness had fallen over my eyes, but you tore me from my unholy reverie. You saved me, $n.$B$BAnd now I beg you... to forgive me.', '', 0, 0, 0, 20, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7456, 'Welcome, my $g brother : sister;. If you are here to train, then I would be delighted to train you.$B$BWe are the preservers of the balance, now and forever, as Malfurion lies in the Dreaming. Never forget this.', '', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7457, 'Our master Malfurion remains lost in the Dreaming, $c. His is the light of a beacon that shows the way for us, and we must do everything in our power to preserve that light.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7458, 'The blessings of Cenarius be yours, $c. Our leader, Arch Druid Staghelm, guides us and all of Teldrassil to a renewed life!', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7459, 'Welcome, my $g brother : sister;. If you are here to train, then I would be delighted to train you.$B$BThere are rumors of strife within the Cenarion Circle, but you should pay such loose talk no heed. Our Arch Druid, Fandral Staghelm, leads us and all of Teldrassil to glory!', 'Welcome, my $g brother : sister;. If you are here to train, then I would be delighted to train you.$B$BThere are rumors of strife within the Cenarion Circle, but you should pay such loose talk no heed. Our Arch Druid, Fandral Staghelm, leads us and all of Teldrassil to glory!', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7460, 'Even within cold, stone walls such as the ones that shelter this town, one may find tranquility and peace. In fact, you may find them everywhere you are... simply look inside yourself for such balance.', 'Even within cold, stone walls such as the ones that shelter this town, one may find tranquility and peace. In fact, you may find them everywhere you are... simply look inside yourself for such balance.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7461, 'Welcome, my $g brother : sister;. If you are here to train, then I would be delighted to train you.$B$BThere will be a time soon where the call of the Emerald Dream will take hold in you. You will hibernate for many years and walk amongst the purest of forms. It is there and then that your ultimate training will take place.', '', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7462, 'You merely prolong the inevitable, $r. Succumb. She will free your misguided soul.', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7463, '', 'We work to preserve the balance as Cenarius teaches, $c.  His is the light of a beacon that shows the way for us, and we must do everything in our power to preserve that light.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7464, '', 'Welcome, my $g brother : sister;.  If you are here to train, then I would be delighted to train you.$B$BWe are the preservers of the balance, now and forever, as Cenarius has taught us.  Never forget this.', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7465, '', 'Thanks again, $n! I\'m very pleased with what we found.$B$BGood luck to you!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7466, '', 'I\'m getting ready to travel up the ridge, $n. I could use some help -- would you like to come with me?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7467, '', '%s begins chanting a strange spell...', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7468, 'Are you ready to fetch, boy?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7469, '%s barks in anticipation.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7470, 'Go get it!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7471, 'Redpath! Your life ends, here and now!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7472, 'I am unable to train you.', 'I am unable to train you.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7473, 'Good boy! Now put it back on the bone pile.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7474, 'I am looking for some bodyguards that would like to protect the Gizelton Caravan. We are stopped on the road east of Kormek\'s Hut, north of Kolkar Centaur Village.', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7475, 'This is Rigger Gizelton asking for assistance escorting my caravan past Mannoroc Coven. I\'m on the road east of Shadowprey village.', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7476, 'I don\'t train your kind $c.', 'I don\'t train your kind $c.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7477, 'We are but darkness and shadows. Eternal. Invisible.', 'We are but darkness and shadows. Eternal. Invisible.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7478, 'I seek training.', 'I seek training.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7479, 'Greetings young rogue. What do you seek?', 'Greetings young rogue. What do you seek?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7480, 'I can not train you. You need to talk to your class trainer.', 'I can not train you. You need to talk to your class trainer.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7481, 'Can\'t help ya, $n. I only train rogues.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7482, 'Can\'t help ya, $n. I only train rogues.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7483, '', 'Sorry, but I only train rogues.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7484, 'Ah... a customer! Talk to my greatest invention, the Vendor-Tron 1000. He has all your needs... some of the rarest items you will ever find on the market anywhere in Kalimdor... not to mention that his programming will not allow any bargaining, so the price you see is the price you pay.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7486, 'No doubt you are another inept simpleton sent from the Dark Lady to train under her champion.$B$B<Nathanos sighs.>$B$BI\'ve sent every one of you back to her in pieces, I don\'t see why it will be any different this time.$B$BMine, however, is not to question why, but instead to do as the Dark Lady bids.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7487, 'Let me take a look at what you have to offer.', 'Let me take a look at what you have to offer.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7488, 'This just happens to be your lucky day; I just completed Super-Seller 680\'s maintenance. There are tons of items for sell at great low-low prices. Go ahead and see for yourself... my brother Cork thinks his Vendor-Tron 1000 will out sell my bot.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7489, 'What kin I do for ye, $n?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7490, 'I\'m lookin\' for rogue trainin\'.', 'I\'m lookin\' for rogue trainin\'.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7491, 'Can you train me how to use rogue skills?', 'Can you train me how to use rogue skills?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7492, 'You look like the type I should watch my coin purse around. Something I can help you with?', 'You look like the type I should watch my coin purse around. Something I can help you with?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7493, 'I don\'t think you want to learn anything I can teach. Might want to find a $c trainer to help you out.', 'I don\'t think you want to learn anything I can teach. Might want to find a $c trainer to help you out.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7494, 'Come to learn from the master eh?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7495, 'Yes, I have. Teach me.', 'Yes, I have. Teach me.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7496, 'Do I look like a $c to you? Now run along before I feel the need to oil my blade... in your blood.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7497, 'Elune be with you, $c.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7498, 'Greetings, my friend. May the Dawn keep you well in these troubled times.', '', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7499, 'Not so fast, $g guy : lady;... I\'ll not be handing out the reigns of a mighty ram steed to someone that\'s not a proven ally of the dwarves of Ironforge. Once you\'ve been recognized as friendly to Ironforge, I\'ll gladly show you what varieties of rams I have for sale.', 'Not so fast, $g guy : lady;... I\'ll not be handing out the reigns of a mighty ram steed to someone that\'s not a proven ally of the dwarves of Ironforge. Once you\'ve been recognized as friendly to Ironforge, I\'ll gladly show you what varieties of rams I have for sale.', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7500, 'My programming will not allow me to lower the cost of any items.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7501, 'I am curious to see what a bucket of bolts has to offer.', 'I am curious to see what a bucket of bolts has to offer.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7503, 'You\'ve not had a steed until you\'ve taken the reigns of a finely bred Barak Tor\'ol mountain ram, my friend. Please, feel free to inspect these fine companions to any adventurer. They\'ll serve you well, believe me.', 'You\'ve not had a steed until you\'ve taken the reigns of a finely bred Barak Tor\'ol mountain ram, my friend. Please, feel free to inspect these fine companions to any adventurer. They\'ll serve you well, believe me.', 0, 0, 0, 4, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7504, 'I am the latest incarnation of automated vendors... we will rule the world one day... don\'t tell my boss that I am skimming money off his merchandise...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7505, 'So sorry to leave a customer but we have places to go and people to swindle. We will be back sometime later today. Good-bye!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7506, 'Time for the Gizleton Caravan to head on out! We\'ll be back soon but if you cannot wait, head north to Kormek\'s Hut. We open shop in about an hour.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7507, 'Sorry, but I am not disposed to sell horses to any individual who is not considered to be at least friendly to the Kingdom of Stormwind. These steeds are the finest you\'ll find anywhere, and we certainly wouldn\'t want them in the hands of our enemies.$B$BNot saying you\'re an enemy, of course... just earn the trust of Stormwind and then we can do business.', 'Sorry, but I am not disposed to sell horses to any individual who is not considered to be at least friendly to the Kingdom of Stormwind. These steeds are the finest you\'ll find anywhere, and we certainly wouldn\'t want them in the hands of our enemies.$B$BNot saying you\'re an enemy, of course... just earn the trust of Stormwind and then we can do business.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7508, 'Years of quality breeding techniques and plenty of care allow me the privilege of showing you these horses! Please, take your time as you consider them for your mount. A horse is a companion for the ages, friend. Don\'t sell yourself short - get your steed here today!', 'Years of quality breeding techniques and plenty of care allow me the privilege of showing you these horses! Please, take your time as you consider them for your mount. A horse is a companion for the ages, friend. Don\'t sell yourself short - get your steed here today!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7509, 'Let me browse your goods.', 'Let me browse your goods.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7510, 'You are not known to my people. A kodo is a mighty steed, and it is the product of love and care by my people; such is not given onto strangers. Until the elders of Thunder Bluff consider you to be a friend, I cannot offer you the sale of a kodo.', 'You are not known to my people. A kodo is a mighty steed, and it is the product of love and care by my people; such is not given onto strangers. Until the elders of Thunder Bluff consider you to be a friend, I cannot offer you the sale of a kodo.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7511, 'Before beginning a hunt, it is wise to ask someone what you are looking for before you begin looking for it.', 'Before beginning a hunt, it is wise to ask someone what you are looking for before you begin looking for it.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7512, 'Kodo beasts, my friend! Trained from a young age and raised on the Golden Plains of Mulgore, these fine animals will make even finer steeds! Please, browse my selection, and see for yourself why we taurens have come to rely on these magnificant creatures!', 'Kodo beasts, my friend! Trained from a young age and raised on the Golden Plains of Mulgore, these fine animals will make even finer steeds! Please, browse my selection, and see for yourself why we taurens have come to rely on these magnificant creatures!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7513, '', 'Welcome to the Cenarion Circle, $c.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7514, 'I\'d like to train.', 'I\'d like to train.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7515, 'I am here for training.', 'I am here for training.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7516, 'Let me train you in the ways of the hunter.', 'Let me train you in the ways of the hunter.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7517, 'I am in need of training.', 'I am in need of training.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7518, 'I have much knowledge to impress upon you, $c. You must learn to hunt with honor.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7519, 'I have come for training.', 'I have come for training.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7520, '', 'I train hunters in the ways and traditions of our ancestors.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7521, 'I am in need of training.', 'I am in need of training.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7522, 'Tauren have a passion for hunting; it is something that runs deep with our kind.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7523, 'I am a hunter and wish to train.', 'I am a hunter and wish to train.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7524, 'The Royal Apothecary Society has little patience for fools, as do I. Perhaps the next time you and I speak of my steeds, you will be considered a friend of the Undercity. Until then...', 'The Royal Apothecary Society has little patience for fools, as do I. Perhaps the next time you and I speak of my steeds, you will be considered a friend of the Undercity. Until then...', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7525, 'On behalf of the Royal Apothecary Society, I am pleased to offer you the finest steeds available for the Forsaken. These beasts have endured trials and tribulations as we all have, and now they too have emerged stronger and more resilient than mere mortal flesh would have had them destined to be! Please, inspect them for yourself and see.', 'On behalf of the Royal Apothecary Society, I am pleased to offer you the finest steeds available for the Forsaken. These beasts have endured trials and tribulations as we all have, and now they too have emerged stronger and more resilient than mere mortal flesh would have had them destined to be! Please, inspect them for yourself and see.', 0, 0, 0, 4, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7526, '', 'Come learn the ways of the hunter. Pride, honor, and tradition.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7527, 'I can teach you the finer points of Ram Riding, provided you have enough gold to cover the lesson!', 'I can teach you the finer points of Ram Riding, provided you have enough gold to cover the lesson!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7528, 'I\'d like to train.', 'I\'d like to train.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7529, 'I train and guide young hunters in the traditions passed down many generations.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7530, 'I can teach you the finer points of Mechanostrider Piloting, provided you have enough gold to cover the lesson!', 'I can teach you the finer points of Mechanostrider Piloting, provided you have enough gold to cover the lesson!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7531, 'I can teach you the finer points of Horse Riding, provided you have enough gold to cover the lesson!', 'I can teach you the finer points of Horse Riding, provided you have enough gold to cover the lesson!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7532, 'I can teach you the finer points of Tiger Riding, provided you have enough gold to cover the lesson!', 'I can teach you the finer points of Tiger Riding, provided you have enough gold to cover the lesson!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7533, 'I can teach you the finer points of Kodo Riding, provided you have enough gold to cover the lesson!', 'I can teach you the finer points of Kodo Riding, provided you have enough gold to cover the lesson!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7534, 'I can teach you the finer points of Raptor Riding, provided you have enough gold to cover the lesson!', 'I can teach you the finer points of Raptor Riding, provided you have enough gold to cover the lesson!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7535, 'I can teach you the finer points of Skeletal Horse Riding, provided you have enough gold to cover the lesson!', 'I can teach you the finer points of Skeletal Horse Riding, provided you have enough gold to cover the lesson!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7536, 'I can teach you the finer points of Wolf Riding, provided you have enough gold to cover the lesson!', 'I can teach you the finer points of Wolf Riding, provided you have enough gold to cover the lesson!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7537, 'You need to be friendly with the dwarves of Ironforge before I will teach you a riding skill, $c.', 'You need to be friendly with the dwarves of Ironforge before I will teach you a riding skill, $c.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7538, 'You need to be friendly with the orcs of Orgrimmar before I will teach you a riding skill, $c.', 'You need to be friendly with the orcs of Orgrimmar before I will teach you a riding skill, $c.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7539, 'You need to be friendly with the trolls of the Darkspear tribe before I will teach you a riding skill, $c.', 'You need to be friendly with the trolls of the Darkspear tribe before I will teach you a riding skill, $c.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7540, '', 'Thanks $n.  Now let\'s get out of here!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7541, 'You need to be friendly with the tauren of Thunder Bluff before I will teach you a riding skill, $c.', 'You need to be friendly with the tauren of Thunder Bluff before I will teach you a riding skill, $c.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7542, 'You need to be friendly with the humans of Stormwind before I will teach you a riding skill, $c.', 'You need to be friendly with the humans of Stormwind before I will teach you a riding skill, $c.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7543, 'You need to be friendly with the night elves of Darnassus before I will teach you a riding skill, $c.', 'You need to be friendly with the night elves of Darnassus before I will teach you a riding skill, $c.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7544, '', 'We made it!  Thanks again!  I\'m going to run ahead!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7545, 'You need to be friendly with the gnomes of the Gnomeregan Exiles before I will teach you how to pilot a Mechanostrider, $c.', 'You need to be friendly with the gnomes of the Gnomeregan Exiles before I will teach you how to pilot a Mechanostrider, $c.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7546, 'You need to be friendly with the Forsaken of the Undercity before I will teach you a riding skill, $c.', 'You need to be friendly with the Forsaken of the Undercity before I will teach you a riding skill, $c.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7547, 'I\'d be wasting both your gold and my time if I were to teach you how to pilot a Mechanostrider, $c. You\'re just too large!', 'I\'d be wasting both your gold and my time if I were to teach you how to pilot a Mechanostrider, $c. You\'re just too large!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7548, 'I seek training to ride a steed.', 'I seek training to ride a steed.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7549, 'Please train me.', 'Please train me.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7550, '', 'Hey Hornizz!  I\'m back!  And there are some people behind me who helped me out of a jam.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7551, '', 'We\'re going to have to scratch the Maraudines off our list.  Too hard to work with...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7552, '', 'Well, I\'m off to the Gelkis.  They\'re not as dumb as the Maraudines, but they\'re more reasonable.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7553, 'Bye! Better luck next time!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7554, 'Always on the go, that girl.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7555, 'Remember, all things are connected. If the beasts were gone from the land, our kind would die from a great loneliness of spirit, for whatever happens to the beasts also happens to the Tauren. Whatever befalls Azeroth befalls all those that inhabit it. \n', '', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7556, 'I wish to train.\n', 'I wish to train.\n', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7557, 'If you\'re here to be trained in the tactics and strategy of hunting, you\'ve come to the right place!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7558, 'I am here for training.', 'I am here for training.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7559, 'Hey mon! You\'ve ever been diving deep in the ocean? I\'ll let you in on a little secret, eh. Deep in the ocean you can find bubbly fissures; they\'ll allow you to breathe air while underwater. Let\'s keep this secret between you and me though, eh. I don\'t need no competition for me shellfish traps elsewhere... if you get my drift!\n', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7561, 'Impossible! Stay your attack, mortals... I submit! I submit!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7562, '', 'The flutter of the wings of a hawk, the breath of a ram in the snow, the pace of a bear through the forest... Such is life for the hunter.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7563, 'I would like to train.', 'I would like to train.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7564, '', 'Look at the wisdom and knowledge of the beast, and you will see yourself. Observe as the owl does, learn cleverness from the sabercat, embody the mighty spirit of the wolf.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7565, 'Please train me.', 'Please train me.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7566, 'The runes of warding have been destroyed! Hunt down the infidels, my brethren!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7567, 'Brashly, you have come to wrest the secrets of the Living Flame! You will soon regret the recklessness of your quest.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7568, 'I go now to summon the lord whose house this is. Should you seek an audience with him, your paltry lives will surely be forfeit! Nevertheless, seek out his lair, if you dare!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7569, '[PH] Ok, I\'m outta here... see you in Ragnaros\' Lair... l8', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7570, '%s splits into two new Lava Spawns!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7571, 'I just finished wandering.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7572, '', 'You\'re not a druid!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7573, 'I\'d like to fly to Rut\'theran Village.', 'I\'d like to fly to Rut\'theran Village.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7574, '', 'Would you like a flight to Rut\'theran Village, courtesy of the Cenarion Circle?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7575, '', 'If you\'re looking for a flight back to Thunder Bluff, then you should talk to my tauren counterpart, Bunthen Plainswind.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7576, 'You\'re not a druid!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7577, 'Would you like a flight to Thunder Bluff, courtesy of the Cenarion Circle?', '', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7578, 'If you\'re looking for a flight back to Rut\'theran Village, then you should talk to my night elven counterpart, Silva Fil\'naveth.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7579, 'You\'ve interfered with our plans for the last time, foolish $c!', 'You\'ve interfered with our plans for the last time, foolish $c!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7580, '%s begins countdown.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7581, '%s disabled ghost region.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7582, '', 'You\'ve interfered with our plans for the last time, foolish $c!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7583, '%s is angered!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7584, 'The times we live in are as fickle as the shifting winds, $c. It is important to remember what is true and lasting in this world, and to preserve that. Everything else is a waste.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7585, '%s is drawn to the ghost magnet...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7586, 'No other race on Azeroth has suffered as much as our people, $c. To laugh in the face of death has become second nature for all of us.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7587, 'Greetings, $N, and welcome to Camp Narache. News of your arrival spread quickly among the tribe.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7588, 'Ah, the beauty of Shadowglen never ceases to delight my senses!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7589, 'Ah, well aren\'t you a sturdy-looking one? Perhaps you can assist me with a thing or two. Not much help around here except for green apprentices, and they\'ve other things to worry about.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7590, 'Hey, citizen! You look like a stout one. We guards are spread a little thin out here, and I could use your help...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7591, 'The land of Azshara.', 'The land of Azshara.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7592, 'My knowledge of night elf lore is limited, but it is said long ago--back when the night elves still held sacred the art of arcane spell casting--that Azshara was the greatest of their kind. A beautiful woman they named queen. Sadly, it was her addiction to the arcane that would one day be the catalyst for the most devastating events in this world\'s history.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7593, 'The Ruins of Eldarath.', 'The Ruins of Eldarath.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7594, 'The mage tower to the north.', 'The mage tower to the north.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7595, '', 'Assassins! Guards! Guards!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7596, '', 'I sense danger up ahead.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7597, '', 'My back is killing me.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7598, '', 'These orders must get to High General Abbendis!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7599, '', 'If I should fall, take my pouch. Make certain that it gets to the High General.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7600, 'The Timbermaw furbolgs.', 'The Timbermaw furbolgs.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7601, 'The presence of blue dragons.', 'The presence of blue dragons.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7602, 'Because you have aided me, I will tell you what I know of this land and of the things there are to see. My knowledge is not vast, but perhaps I can point out some things that might interest one such as yourself.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7603, 'Once a great city of the night elves, the Highborne once ruled there, practicing great and powerful arcane magics. They are cursed now, haunting the lands, and their ruined city has been victimized by the Spitelash naga. I understand that the naga search the ruins for powerful Highborne artifacts.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7604, 'A powerful human mage is rumored to live to the north; he keeps watch over the land for some unknown reason. I have not traveled there myself, but it is said that visitors are welcome and can even earn favor with him if they are willing to do errands for him.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7605, 'I\'ve seen terrible things happen to the furbolg over the last few years--they were victims on many fronts. But it\'s unclear to me what\'s become of this tribe. Are they corrupted by the same plague that\'s touched Felwood? Or perhaps they are simply angry at what\'s happened to their once peaceful people and now seek to defend themselves. Either way, most of them appear hostile to those who come into the area.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7606, 'The blue dragons themselves aren\'t here, but followers of Malygos are. I do not know why, but I can tell you this: wherever the blue dragons are, expect to find arcane magic. I wouldn\'t be surprised if the dragonspawn are here protecting something... perhaps something the Highborne left behind.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7607, '', 'I should find some hired help; I don\'t like sitting here all day selling fish supplies. I\'d much rather go fishing, and then sell supplies.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7608, 'Let\'s take a look at your fishing supplies.', 'Let\'s take a look at your fishing supplies.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7610, '', 'Throm-Ka, $c. My mood is poor this day, so please, unless you can help me, I would prefer to be alone.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7611, '', 'Good work, $n!  Complete your trial by speaking with me.  Time continues to slip away until you do so!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7612, 'Reckless mortals! None may challenge the Sons of the Living flame!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7613, 'I can not train you, $c.', 'I can not train you, $c.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7614, 'I am Ulysses Rackmore, captain of the Gallant. And this is the log of the last few days of my doomed ship.$B$BWe were sailing to Feathermoon Stronghold with a shipment of supplies when naga stormed the sides of the Gallant. We drove them back but we knew they\'d return. Fearing the loss of my most precious belongings, I steered the Gallant to an island off the coast of Desolace, one with old, night elf ruins. And there I left my treasure chest.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7615, 'Continue reading...', 'Continue reading...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7616, 'My chest is one of ingenious design, and requires two keys to unlock, one gold and one silver. The first key, the silver key, I gave to my first mate, and I kept the gold.$B$BBut we were doomed to lose them. My key was lost during the next naga attack, and my first mate\'s was lost a day later when drysnap makrura assailed us and pulled him into the deep.$B$BI am sure the naga and makrura have the keys. I am glad the two are enemies and will not share them.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7617, 'After the makrura attack, we could see that the Gallant would not survive another. So I and the few surviving sailors abandoned my ship.$B$BWe are now ashore and will soon strike out for the town of Nijel\'s Point, but much danger lies between us and sanctuary, and I fear Desolace will consume us.$B$BI write this now in hopes that someone will know our plight, and some day avenge us against the watery fiends who were our ruin.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7618, '', 'The Shrine of Remulos is a sacred place for us in the Cenarion Circle here in Moonglade... so please conduct yourself in a manner befitting a visitor.$B$BA visitor who doesn\'t want my scimitar buried in them, that is.', 0, 0, 0, 1, 25, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7619, '', 'May Cenarius watch over you, $g brother : sister;.$B$BI am Tajarri, and I am a sworn protector of this shrine.  It is my honor to serve Keeper Remulos as a warden of this sacred land.', 0, 0, 0, 2, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7620, 'Before the Scourge came, I had a great business. I bought and sold everything. Everything!$B$BAnd my prices were INSANE!$B$BJust ask anyone. Anyone who\'s not dead.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7621, 'Hi $n! Are you here to buy something? Or maybe sell something?$B$BYou won\'t find better prices anywhere, or my name isn\'t Augustus the Touched!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7622, 'Commerce! I crave it!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7623, 'Yes, Augustus. I would like to do business.', 'Yes, Augustus. I would like to do business.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7624, 'This is a dangerous place, $n. Be careful around here.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7625, 'Greetings, $c. Please be brief in how the Kirin Tor may be of service to you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7626, 'DIE, INSECT!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7627, 'You won\'t find better prices anywhere!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7628, 'I\'m Augustus, and my prices are INSANE!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7629, 'I\'m Augustus! You want it, I got it! You wanna sell it, then I want it!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7630, 'Do not waste my time, $c.', 'Do not waste my time, $c.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7631, 'I don\'t train your kind $c. Find your own trainer before you anger me.', 'I don\'t train your kind $c. Find your own trainer before you anger me.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7632, 'Take it easy, mon. I only train da warriors.', 'Take it easy, mon. I only train da warriors.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7633, 'What kin I do for ya, mon?', 'What kin I do for ya, mon?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7634, 'Due to incompatibility on your class specification, I am unable to help you rectify any situation you have with lack of ability.', 'Due to incompatibility on your class specification, I am unable to help you rectify any situation you have with lack of ability.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7635, 'Excellent! You meet the required parameters and specifications. What can I do for you?', 'Excellent! You meet the required parameters and specifications. What can I do for you?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7636, 'TOO SOON! YOU HAVE AWAKENED ME TOO SOON, EXECUTUS! WHAT IS THE MEANING OF THIS INTRUSION???', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7637, 'Hegnar Rumbleshot, at your service!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7638, 'While Theramore is reknown for it\'s exceptional training of warriors, I am afraid that I can not help you.', 'While Theramore is reknown for it\'s exceptional training of warriors, I am afraid that I can not help you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7639, 'Come to train with the Theramore guard have you?', 'Come to train with the Theramore guard have you?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7640, 'Yes. I have.', 'Yes. I have.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7641, 'What can I do for you my child?', 'What can I do for you my child?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7642, 'I cannot train a $c such as yourself.', 'I cannot train a $c such as yourself.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7643, 'I seek training in the ways of the Hunter.', 'I seek training in the ways of the Hunter.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7644, 'I can not train you, young one.\n', '', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7645, 'The Firelord and his brethren once held sway over this entire world, mortal. As a servant of the Old Gods, he fought against the Titans for domination of this planet. The victorious Titans banished my master and his brethren to the Elemental Plane - there to remain imprisoned until the end of time.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7646, 'Tell me more.', 'Tell me more.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7647, 'Do you prefer guns or bows?', 'Do you prefer guns or bows?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7648, 'I do not have the knowledge you seek.', 'I do not have the knowledge you seek.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7649, 'Very well, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7650, 'Wot kin I do fer ye?', 'Wot kin I do fer ye?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7651, 'I kinnae help ye with trainin\'. Ye should look fer a $c trainer.', 'I kinnae help ye with trainin\'. Ye should look fer a $c trainer.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7652, 'What?', 'What?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7653, 'Don\'t waste my time.', 'Don\'t waste my time.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7654, 'I can not train you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7655, 'Impudent whelps! You\'ve rushed headlong to your own deaths! See now, the master stirs!\n', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7656, 'Hey mon, you be needin\' to head on over to da $c trainer.', 'Hey mon, you be needin\' to head on over to da $c trainer.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7657, 'Behold Ragnaros - the Firelord! He who was ancient when this world was young! Bow before him, mortals! Bow before your ending!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7658, 'Teach me the ways of the spirits.', 'Teach me the ways of the spirits.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7659, 'The spirits have guided you to me. How may I be of service?', 'The spirits have guided you to me. How may I be of service?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7660, 'May the spirits be with you.', 'May the spirits be with you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7661, 'These mortal infidels, my lord! They have invaded your sanctum and seek to steal your secrets!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7662, 'FOOL! YOU ALLOWED THESE INSECTS TO RUN RAMPANT THROUGH THE HALLOWED CORE? AND NOW YOU LEAD THEM TO MY VERY LAIR? YOU HAVE FAILED ME, EXECUTUS! JUSTICE SHALL BE MET, INDEED!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7663, 'Da spirits be strong wit you, mon. What kin dis troll do for ya?', 'Da spirits be strong wit you, mon. What kin dis troll do for ya?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7664, 'May da spirits protect you mon.', 'May da spirits protect you mon.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7665, 'Where are you going? Well if you\'re looking to get there quickly, then look no further!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7666, 'I try to keep Sentinel Hill stocked, and that\'s not easy with all the action we see.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7667, 'I need a ride, fast! I have an important message to deliver!', 'I need a ride, fast! I have an important message to deliver!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7668, 'Ah, $gpriest:priestess;, you came along just in time. I appreciate it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7669, 'Argh, the pain. Will it ever leave me?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7670, 'Thank you! Thank you, $gpriest:priestess;. Now I can take on those murlocs with the Light on my side!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7671, 'Farewell to you, and may the Light be with you always.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7672, 'A mere three hundred years ago, a reckless dwarf named Thaurissan summoned the master from his fiery realm. His return to this paltry world devastated the surrounding lands and created this volcanic Core. Mighty Ragnaros has slept under this mountain ever since. \n', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7673, 'What else do you have to say?', 'What else do you have to say?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7674, 'We, his sworn servants, do his bidding and enforce his iron-rule. Cut off from the energies of his fiery realm, Ragnaros is but a shadow of his true self. However, he has more than enough strength to be your end.   \n', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7675, 'You challenged us and we have come. Where is this master you speak of?\n', 'You challenged us and we have come. Where is this master you speak of?\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7676, 'Good day, $gsir:ma\'am;. I\'d shake your hand, but as you see, I\'m in the middle of something. May I be of assistance?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7677, 'You\'ve made a good start, but you still have a great deal to learn, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7678, 'You\'ve already learned everything I can teach you, $n. Talk to the night elf Lilyssia Nightbreeze in the Mage Quarter of Stormwind if you want to continue your studies. I hear she\'s a good teacher.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7679, '', 'Shut inside this little house, I dream of joining the battles that rage outside the city walls. Still, duty compels me to share my knowledge with others who fight in my stead... for now.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7682, 'Ishnu-alah, $gbrother:sister;. Have you any tidings from abroad? My heart yearns for news of Teldrassil.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7683, 'I am honored to share my knowledge with you, $n. See that you use it well.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7684, 'You must choose a more experienced teacher, for you have surpassed my abilities. I advise you to speak with Lilyssia Nightbreeze here: she has much knowledge to offer.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7685, 'NOW FOR YOU, INSECTS! BOLDLY, YOU SOUGHT THE POWER OF RAGNAROS. NOW YOU SHALL SEE IT FIRSTHAND!\n', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7686, '', 'The druids say that it is only a matter of time until nature itself drives the Horde from Kalimdor. Yet corruption continues to spread throughout our lands, even here on Teldrassil.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7687, '', 'Well done! Your understanding of potions is coming along nicely.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7688, '', 'Unfortunately, there is little I can teach someone with your experience, my friend. Seek out the night elf Sylvanna Forestmoon, who resides in the Craftsmen\'s Terrace of Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7689, '', 'Elune-adore, my $gbrother:sister;. You are welcome here. Speak, and I will do what I can to aid you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7690, '', 'Is it not astonishing what one can create from the plants of the forest? Truly, Elune has blessed us with her bounty.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7691, '', 'You are kind to seek me out, but your skills are beyond my own. Have a word with Sylvanna Forestmoon, just over there. She\'s been looking for another student.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7692, 'Did my sweet lass Kali send you down to me? The apple of my eye, she is.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7693, 'I don\'t suppose you\'re here to talk about my lovely Kali. Fine, then. Potions it is. Anyone ever tell ye you\'ve got a one-track mind?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7694, 'Ye\'re beyond my skill to teach, $n. Go pester Tally Berryfizz; she\'s a gnome living in Tinker Town of Ironforge. Oh, and since you\'re headed up anyway, give Kali my love.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7695, 'Don\'t let those big, innocent eyes fool ye. Tally\'s an expert, all right: I learn something new every day. Why d\'you think I\'m here in Tinker Town? Do I look like an engineer to you? Aye, I didn\'t think so.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7696, 'Put your back into it, $glad:lass;. Hard work and determination--that\'s the way! Don\'t let anyone tell you book learnin\' is all ye need.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7697, 'Och, there\'s nothing I can teach ye, $n. Have a word with Tally Berryfizz over there. She can hardly reach the tables, but when she starts brewing a potion, watch out!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7698, 'You got the look of someone wantin\' adventure. Me, I had enough of that by the time we trolls came here. Now I take each day as it comes, an\' I train other folks to handle adventures for me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7699, 'Ready for another lesson? All right, then. First, relax. You gotta stay loose to stir a potion just right.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7700, 'You got questions, but they be too big for me to answer, $n. You be needin\' to have words with the orc Yelmak in the Drag of Orgrimmar. He\'ll help you out.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7701, 'All the bottles on this table are potions I brewed today. And they say a watched pot never boils. Ha!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7702, 'They called me stupid, but I\'ll show them. I\'m going to watch every pot I have and prove that they can all boil stuff anyway. Yeah, I\'m on a roll! Who\'s stupid now, huh? Huh!?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7703, 'Can\'t help you. You know more about potions than I do. Besides, I have pots to watch. Talk to Yelmak over there. He knows a lot about potions.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7704, 'Bena works like one possessed. Day and night I have seen her bent over these potions, often going without sleep or food. I know our undead allies need help, but I begin to fear she will do herself an injury.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7705, 'I must have broken hundreds of vials when I first began brewing potions. The glass was so fragile in my clumsy hands. I hope you are more cautious than I was.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7706, 'Your capacity for learning has outstripped mine. You will need to talk to Bena Winterhoof over there for further instruction. Please don\'t take too much of her time, though. She needs her rest.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7707, '', 'Nice to see a friendly face. I don\'t get much chitchat from Apothecary Johaan unless he wants something. He\'s been out of the grave too long. Someday I\'ll be like that, I hear. Creepy. Maybe by then I won\'t care.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7708, '', 'Here to learn about potions? I\'ve got plenty to teach you. I might be dead, but I haven\'t lost my brains. Yet.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7709, '', 'I appreciate the company, but let\'s face it: you have more experience with potions than I do. Talk to Doctor Marsh instead if you want training. He\'s a Forsaken in the Apothecarium of the Undercity.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7710, 'Once, it is true, I was human. What might otherwise be a shortcoming works to our advantage, $n, for I know human weaknesses intimately.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7711, 'Very well. Cut another lump of flesh from one of these specimens. Let us test what you\'ve learned so far, and we can progress from there.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7712, 'Your knowledge of potions has exceeded mine, but you still have much to learn. Go up the stairs to the southeast and see Doctor Marsh. He will teach you true sufferi--ahem. He is a Forsaken who will train you further.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7713, 'Stop giving me that look. I may not be an adventurer like you, but that doesn\'t mean I can\'t have a few muscles. All that stirring makes your arms strong.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7714, 'There\'s nothing more I can teach you. I\'ve heard of a night elf who might be able to, though. Her name is Ainethil, and she lives in the Craftsmen\'s Terrace of Darnassus.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7715, 'I\'m sorry, $n, but I have no time to spend on novices. You want someone closer to your own skill level. Try the night elf Milla Fairancora, in the Craftsmen\'s Terrace of Darnassus.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7716, 'You\'ve put in your time studying the basics. Now it\'s time to up the ante.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7717, 'You want my help? You got it. What do you need?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7718, '', 'Don\'t let the beady eyes and bushy beard fool you. Vosur\'s a talented apprentice and a good friend, even if I do get a crick in my neck when I talk to him.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7719, '', 'Um, you already know everything I could teach you. Maybe it\'s time for you to talk to someone else. Try the night elf Ainethil in the Craftsmen\'s Terrace of Darnassus. She\'s got some real talent!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7720, '', 'Hey, that\'s actually not so bad. Alrighty, then. How\'d you like to run an errand for me?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7721, '', 'That\'s nice, $n. Go show whatever it is to Vosur over there. He\'ll answer any questions you have.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7722, '', 'Who are you and why are you bothering me? Oh, right--potions student. Right. What can I do for you?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7723, '', 'Ishnu-dal-dieb, $n. Have you come here in search of knowledge?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7724, '', 'Teaching is not a one-sided trade, $n. As I teach you, I learn more myself, and my understanding grows.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7725, '', 'My apologies, $n, but I do not offer instruction to novices. Instead, talk to Milla Fairancora in the Craftsmen\'s Terrace of Darnassus. She can help you make up for lost time.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7726, '', 'You have learned the fundamentals of making potions, but further study requires greater commitment. Prove your dedication by carrying out a task for me.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7727, '', 'Although I am flattered by your confidence in my abilities, teaching you is beyond my skill. You must go to the Craftsmen\'s Terrace in Darnassus, and speak with the night elf Ainethil. She can help you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7728, '', 'Even slight alterations to a potion\'s ingredients can change it fundamentally. Patience and precision, young one: they will serve you well in all walks of life.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7729, '', 'My skill at creating potions is inferior to yours. You are ready to speak with Ainethil, just over there. She will understand that you are ready for further instruction.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7730, '', 'Your skill is not inconsiderable. Since you appear to have sufficient experience, there is a matter in which I require your aid.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7731, '', 'Ah, to be young again, and to have discovered potions for the first time. Thank you for refreshing my memory, $n. Now go share that youthful enthusiasm with Milla Fairancora over there.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7732, '', 'I have learned much of potions over the long years, $n, and that knowledge is at your disposal. You have only to ask.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7733, 'I\'m busy, $n. Tell Whuut to handle whatever it is. That young pup has been stewing all day long. He spends much more time like that, and he\'s going to hurt something.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7734, 'I\'m honored to meet you, $n, but your skill at potions is greater than my own. Go see Doctor Herbert Halsey, an undead in the Apothecarium of the Undercity. He can help you from now on.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7735, 'I\'ve been looking for a little help. Since you\'ve got the skill, I guess you\'ll do, provided you\'ve also got the guts.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7736, 'You\'re talking to the wrong orc. I don\'t train novices. Talk to young Whuut over there... and use small words.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7737, 'Another student, eh? All right. I\'ll see what I can do to help you out.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7738, 'Glyx and I have an understanding: he keeps me supplied, and I keep him rich. Seriously! What that pirate charges me for a measly pouch of crumpled kingsblood... it\'s enough to make a grown goblin cry.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7739, 'I\'ve got plenty of great recipes for you to take a look at, and at discounted prices, too!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7740, 'What is this? You trying to ruin my reputation? I don\'t teach novices, $gbuddy:lady;. Try talking to the orc Whuut in the Drag of Orgrimmar. He\'s a lot closer to your level of skill. And brains.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7741, 'Herbs are so pricey! I could always use a little something extra, if you know what I mean. Help a goblin out?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7742, 'You\'re better at this gig than I am! You want someone with even more experience than I\'ve got. That\'d be Ainethil, a night elf lady who lives in the Craftsmen\'s Terrace of Darnassus.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7743, '', 'When I first saw the undead, I wept, for I could not imagine their horror and loneliness. The Earth Mother spoke to me then; she sang in my blood. And she told me, \"Save them.\"', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7744, '', 'Can you not sense the anguish of the Earth Mother for her lost children, the undead? Speak quickly, $n. Every second costs me dearly. We must help them find their way back to her!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7745, '', 'I cannot in good conscience spend time training novices while our allies, the undead, are suffering so terribly. Please speak with Kray over there instead; he can help you for now.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7746, '', 'You have some skill, it is true, but every moment I spend teaching you is one more moment the undead will spend in torment. Prove to me that you are worth my time.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7747, '', 'Your potion-making skills are beyond mine. Seek out the undead Doctor Herbert Halsey in the Apothecarium of the Undercity. He has the skill to train you further, and you can witness firsthand his people\'s plight.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7748, 'Another interruption. How am I supposed to get any work done like this? Don\'t answer that; do I look as if I care what you have to say? Why don\'t you go bother Mr. High-And-Mighty instead?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7749, 'Me, train you? Come on--we both know I can\'t. It\'s this teaching, you see. My own skills suffer. You want training, go to Doctor Herbert Halsey, a Forsaken in the Apothecarium of the Undercity.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7750, 'If you\'re going to be taking up my time, you\'d better prove you\'re worth it. I ought to get something out of this arrangement, after all.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7751, 'Don\'t waste my time. I have research projects older than you. Go talk to Doctor Martin Felben in the Apothecarium of the Undercity. He\'s a Forsaken who can probably manage to teach you the basics.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7752, 'Another student. Wonderful. This is why Lydon belongs to the Royal Society of Snobs and gets to do research, and I\'m stuck being an instructor. If I win another teaching award, I\'ll never live it down!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7753, 'My powers have returned and you have squandered your chance, foolish mortals. Till we meet again.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7754, 'With Algernon to keep me supplied, I can do all my work here without hunting for a single herb. If I never have to see the wonders of nature again, it won\'t be too soon.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7755, 'I have no more knowledge to bestow upon you. Go through the archway and down the ramp to the southeast, and talk to Doctor Herbert Halsey. He is a Forsaken who can teach you more advanced techniques.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7756, 'If you seek to become my student, you\'ll need to complete an assignment for me. Prove yourself worthy, and I might even consider rewarding you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7757, 'I\'m not amused by your audacity in coming to me for training. Go through the archway and down the ramp to the southeast, and talk to Doctor Martin Felben. He is a Forsaken who can teach you the basics.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7758, 'Here to learn? Very well. I suppose you can\'t cause our allies much harm if I teach you. Just as long as you actually do manage to harm our enemies.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7759, '', 'This potion seems to heal less damage every time I brew it, though I use the same ingredients. There can be only one conclusion: the plants are changing. I cannot say why or into what.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7760, '', 'It is time you moved on to a more advanced teacher. In the Feathermoon Stronghold of Feralas dwells the night elf Kylanna Windwhisper. She has a brilliant mind; doubtless she can train you further.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7761, '', 'This is excellent work. Are you ready to undertake a mission on my behalf? I know of no one more qualified.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7762, '', 'Your skill at brewing potions needs to be greater before you can become one of my students. In the meantime, perhaps Sylvanna Forestmoon or Milla Fairancora can help you, just over there.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7763, '', 'Your education is progressing well. You should be proud of your accomplishments.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7764, 'My colleagues and I are gathering the deadliest substances we know and testing their effects on our enemies. Once I have everything I need, I shall brew such plagues as this world has never seen.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7765, 'Incredible. Your potions display a knowledge that rivals my own. I cannot train you further, but talk to the orc Rogvar of Stonard in the Swamp of Sorrows. He\'s known for his cutting-edge experiments.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7766, 'You\'ve got real talent and a passion for making potions. There is one little matter I\'d like you to take care of, though.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7767, 'You don\'t yet have the skill to study with me. Go talk to Doctor Martin Felben over there, or go up the ramp to the southeast and see Doctor Marsh. They are Forsaken who can give you the training you need.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7768, 'Ready for training? Then try to keep up, $n. We\'ve got a lot of ground to cover.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7769, '', 'Once I welcomed darkness, for it was the sister of night. Now it is dark all the time, even during the day, and the forests cry out for sustenance. Unless we act soon, this spreading blight will cover the world.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7770, '', 'Have you come here for instruction in the art of making potions? You were mistaken if you thought that I would offer such a privilege to you, unearned. Go, and do not return to me until you have greater skill.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7771, '', 'Your skill displays an enduring affection for our art, but I would see further evidence of your commitment. I shall charge you with a task. Fulfill it, and I will be content.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7772, '', 'Elune has seen fit to gift me with skill and experience. Now I shall pass them on to you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7773, 'Kalaran, I have misplaced my torch. I require another.', 'Kalaran, I have misplaced my torch. I require another.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7774, 'Warchief Thrall himself asked me to come out here. I was honored to obey, and it\'s no hardship. I couldn\'t have asked for a better posting than Stonard. Just about any herb I might need grows in the muck here.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7775, 'Don\'t stir the potion like that, you ham-handed farmer! No, don\'t stop stirring it, either! Forget it--just give me that ladle before you start a fire. I\'ll salvage what I can. Now, pay attention.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7776, 'Hmm, well, I can see that you have a little skill at making potions. Whether you\'re worthy to be my student remains to be seen, however. I have a job for you. Impress me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7777, 'How dare you ask me for training, maggot? What were you hoping for, you slack-jawed yokel, a lecture on the proper uses of silverleaf? Out! You\'re lucky I don\'t thrash you senseless for wasting my time.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7778, '', 'Ah, $gpriest:priestess;, you came along just in time. I appreciate it.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7779, '', 'Argh, the pain. Will it ever leave me?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7780, '', 'Thank you! Thank you, $gpriest:priestess;. Now I can take on those corrupt timberlings with Elune\'s power behind me!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7781, '', 'Farewell to you, and may Elune be with you always.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7782, '', 'Ah, $gpriest:priestess;, you came along just in time. I appreciate it.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7783, '', 'Argh, the pain. Will it ever leave me?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7784, '', 'Thank you! Thank you, $gpriest:priestess;. Now I can take on those humans with your power to back me!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7785, '', 'Farewell to you, and may our ancestors be with you always!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7786, 'Ah, $gpriest:priestess;, you came along just in time. I appreciate it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7787, 'Argh, the pain. Will it ever leave me?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7788, 'Thank you! Thank you, $gpriest:priestess;. Now I can take on those wendigo with the Light on my side!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7789, 'Farewell to you, and may the Light be with you always.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7790, 'Ah, $gpriest:priestess;, you came along just in time. I appreciate it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7791, 'Argh, the pain. Will it ever leave me?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7792, 'Thank you! Thank you, $gpriest:priestess;. Now I can take on those gnolls with your power to back me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7793, 'Farewell to you, and may shadow always protect you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7794, 'Just a minor adjustment here... ah there we go!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7795, 'I would train ye if I could, but ye need the services of someone a bit more... what\'s the word? Brainy! Look for Trixie Quickswitch in Ironforge. Tell \'er Bronk sent ye!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7796, 'Lookin\' to be an engineer, eh? Well, by Muradin, ye\'ve come to the right place!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7797, '%s goes into a killing frenzy!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7798, '%s becomes enraged!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7799, 'Where\'s my spinner? I just had it here somewhere...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7800, 'Your current aptitude exceeds my instructional capabilities. You should enquire further with my fellow Gnome Trixie Quickswitch in Ironforge!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7801, 'I would be delighted to aid you in the commencement of your pursuit! Let me grab my spinner and we\'ll get started...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7802, '', 'I heard the new Henweigh 6000 will be rolling off the assembly line soon. I can barely contain my excitement!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7803, '', 'Well... I hate to admit it, but my sister\'s the one you\'ll have to talk to if you want to continue training. I don\'t know as much as she does... yet! She\'s standing over by the rocket car.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7804, '', 'I\'d love to teach you! Show my sister she\'s not the only one who knows a thing or two about engineering...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7805, '', 'Hello there! My name\'s Jenna! And you? ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7806, '', 'Oh, you\'ve got oodles of things to learn still. I would teach you everything if I could, but I\'m still learning too! Why don\'t you talk to Trixie Quickswitch in Ironforge, she\'ll help you further your education!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7807, '', 'You want to train? With me? Fantastic! The only thing better than assembling bullets and explosive devices is to share that knowledge with someone else! Let\'s get started!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7808, '', 'Ah, what a beautiful day to blow something up!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7809, '', 'You need to start learning things that are beyond my expertise! Talk to Lilliam Sparkspindle in front of the tent there. He\'ll set you on the right course!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7810, '', 'This kind of work isn\'t for the faint of heart. I have an uncle who lost a hand! Poor old Stumpy... anyway, if you\'re still interested I might be able to show you a few things.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7811, 'What? You want something?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7812, 'I can\'t help ya\'! You\'re ready to see someone who\'s been at it longer than I have! Let\'s see... you\'re an Alliance type, so you need to talk to Lilliam Sparkspindle in Stormwind.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7813, 'Yeah, I\'ll show you a few things. We\'ll see how you do, maybe you\'ll surprise me!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7814, '(Sniff, sniff) You smell that? Don\'t you just LOVE the smell of grease?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7815, 'I\'m afraid you\'re out of my league, friend! You should go see Nogg in Orgrimmar, he\'ll take you to the next level!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7816, 'A fellow enthusiast, huh? Well, you just wait, we\'ll make a gearhead out of you in no time!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7817, 'I like to build stuff. Of course, I like to blow stuff up even more!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7818, 'You know who can really show you some things? Nogg! You go find him in Orgrimmar.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7819, 'I know a few things. You want me to show you?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7820, 'Who says orcs aren\'t smart? It takes a lot of brains to be an engineer!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7821, 'You know who\'s really smart? Nogg! Smarter than me, even. You go tell Nogg Thund sent you! You can find him by the control panel over there.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7822, 'You think you\'re smart enough? You think you got what it takes? Let\'s find out!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7823, 'The Dark Lady has said that knowledge will be our greatest weapon in this war.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7824, 'You are ready for greater things! See Franklin Lloyd, to my right. He will guide you from now on.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7825, 'Engineering will provide you with the skills and knowledge to dismantle your opponents! Let me show you how...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7826, 'I\'m never too busy to speak to a stranger! Almost never, anyway...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7827, 'You\'re working your way up... higher than me, at this point! You should talk to Springspindle Fizzlegear in Ironforge! (He\'ll? She\'ll?) show you more than I ever could!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7828, 'I can show you a few tricks... if you don\'t mind doing a little something first, that is.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7829, 'Hmm... well, I can\'t help you quite yet! You have to learn the basics first. Why don\'t you go see Sprite Jumpsprocket in Stormwind!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7830, 'Fantastic! Aren\'t you the industrious one? Well, let\'s get started, shall we?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7831, 'Let\'s see... fourteen plus seven, carry the two... oh, yes?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7832, 'Well, you\'re just zooming right along! I can\'t teach you anything you don\'t already know, but Springspindle Fizzlegear sure can! You can find Springspindle in Ironforge. Good luck!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7833, 'Quid pro quo! I\'ll teach you what I know, but first I want you to perform a small task...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7834, 'You can learn from me in time... but not yet! Why don\'t you start out by talking to Sprite Jumpsprocket to my right here. You can check in with me after you\'ve gotten your feet wet!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7835, 'You certainly did not disappoint! Now that we\'ve gotten that out of the way, your instruction may begin!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7836, '', 'The plans for my twenty-foot tall turkey baster are nearly complete! Now all I need is a twenty-foot tall turkey...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7837, '', 'Don\'t tell anyone else I said this, but even I don\'t know everything! If you want to keep learning, you should talk to the owner of the shop, Springspindle Fizzlegear.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7838, '', 'You\'ll need to prove your worthiness before I go giving away my secrets! I\'ve got just the test for you...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7839, '', 'Close, but no cigar! I\'m afraid you\'ll have to talk to my sis, Jemma first. After she\'s gone over the basics with you, come see me and I\'ll show you the juicy stuff!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7840, '', 'Not bad, for a beginner! I guess it couldn\'t hurt for me to show you a few things...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7841, 'If there were just a few more hours in a day...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7842, 'You have earned the privilege of training with a higher instructor. Go see my pal Roxxik there.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7843, 'Every day is a test, and this day is no different. If you pass this test, I will share some of my knowledge...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7844, 'You have not yet earned the right to train with me. See Thund first, over by the table. Come back when he says you\'re ready!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7845, 'You have passed your test, well done! Your training may now continue.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7846, 'I don\'t have time for idle gossip, I\'m a professional!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7847, 'What do you think I could teach you? Roxxik would do a much better job. You can find him in Orgrimmar. Now be off!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7848, 'Let\'s see how competent you are, shall we? If you perform this task to my liking, I\'ll spend the time to teach you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7849, 'This is a waste of time, mine and yours! Go see Graham Van Talen if you want to begin your instruction! He\'s standing to my left over there.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7850, 'Well... you almost impressed me. I suppose I could spare the time for a few lessons...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7851, 'If my calculations are correct, the wheel will soon be obsolete!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7852, 'You have certainly come a long way in your training. There is only one instructor who can train you further. Find Buzzek Bracketswing in Gadgetzan!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7853, 'Not that I doubt your abilites, but I would like you to perform a task for me. Call it a demonstration of good faith...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7854, 'The things I would teach you would turn your brain to jelly! Come back when you\'ve learned sufficiently!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7855, 'Excellent, you have proven yourself worthy! Now for the next phase of your instruction...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7856, 'Sometimes I surprise even myself!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7857, 'I can\'t teach what I don\'t know! If only there was someone else who knew more than I do... wait, there is! Go see Buzzek Bracketswing in Gadgetzan!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7858, 'I think you\'re more than qualified for this little assignment... let\'s see if I\'m right!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7859, 'Nice try, but you\'ll need some more experience before training with me! Back to the trenches! ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7860, 'Nice going! I knew I was right about you, no matter what those other people said. Ha! I\'m kidding! Okay, on with your lessons...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7861, 'I\'ve been working on a way to transport oil underground between major cities, but everyone tells me it\'s a pipe dream.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7862, 'Well my friend, you\'ve reached heights some people only dream of. Congrats! And now, without further ceremony, it is time for your final lesson!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7863, 'Show me what you got! You won\'t be sorry, I promise!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7864, 'I would never turn away someone who\'s ready to train with me. Unfortunately, you\'re not! You\'ve still got a ways to go kid!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7865, '%s refuses to die while its master is endangered!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7866, '%s collapses and begins to smolder.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7867, '%s reignites from the heat of another Core Hound!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7868, '', 'Hail, traveler! What brings you to my doorstep?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7869, '', 'I\'m afraid I have nothing to teach to someone with your experience. But you haven\'t reached the end of the road yet. Speak to Lucan Cordell, behind the counter there. He\'ll guide you through the next phase of your journey.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7870, '', 'Hmm... well you seem capable enough. If you truly desire to learn, then nothing will stand in your way! I will guide you through your first steps on what I hope will be a long and fulfilling path.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7871, 'There are ways to strengthen steel beyond the hammer and forge, young wanderer. Care to learn more?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7872, 'Ye\'re a fine enchanter indeed! Now it\'s time fer ye to move on. I want ye to talk to Gimble Thistlefuzz, upstairs. He\'s a bit of a character, but he\'ll steer ye in the right direction from here on out!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7873, 'Ah, ye\'re an inquisitive one, are ye\'? Excellent! Pay close attention, this just might save yer skin when the goin\' gets rough!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7874, '', 'The Goddess has granted me wisdom, stranger. If it is her will, I shall pass it on to you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7875, '', 'Your skills are beyond my reach. It is time for you to face new challenges. Taladan will help you prepare. He is standing by the table there.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7876, '', 'I will teach you what I can. Each lesson is a gift from the Goddess herself. Use her gifts wisely, and you shall not falter.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7877, '', 'Elune\'s secrets are many, but they are not to be shared with just anyone. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7878, 'Greetings to you traveler.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (91711, 'Go on, I\'m listening.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (91712, 'This better not be a waste of time.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7879, '', 'Open your mind, wanderer, and we shall begin.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7880, 'I do not have time to waste, stranger. State your business or be off!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7881, 'Eh, well you\'ve made it this far... go see Lavinia Crowe by the other bookshelf there. If you don\'t mess things up with her, maybe you can learn some more.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7882, 'You don\'t look like much to me, but who knows, maybe there\'s an enchanter hidden in there somewhere!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7883, 'What I wouldn\'t give to be out on the open plains on a day like this... but, my duty binds me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7884, 'I cannot teach you anything you don\'t already know. However, the knowledge of my father far surpasses my own. Go inside and speak to him. Good luck!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7885, 'There are many secrets that may be unlocked with patience and determination. Now, let us begin...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7886, 'Throm-ka! What you looking for, stranger?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7887, 'You come to wrong place. Talk to the expert Godan over there. You learn more from him.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7888, 'To die in battle is glorious! But, with what I teach you, maybe you not die so quickly, hm? ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7889, 'Who are you? What do you want from me?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7890, 'What do I look like, the Grand Poobah of enchanters? Your skills require the guidance of an Artisan! Go find Kitta Firewind at the Tower of Azora.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7891, 'Great, let\'s get started... ha! Fooled you! Surely you didn\'t think it would be that simple. You\'ll need to prove your mettle just like everyone else!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7892, 'You\'re joking, right? I believe you\'re looking for the OTHER short fellow, Thonys Pillarstone. He\'s downstairs. Now skedaddle!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7893, 'Okay, well... don\'t get cocky! Since you have demonstrated your competence though, we may now begin!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7894, 'If you have what it takes, I will be happy to show you the way.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7895, 'A true professional know his limitations, and I\'m not ashamed to admit that your skills exceed mine. I want you to pay a visit to Kitta Firewind at the Tower of Azora. She is well-renowned, and with good reason.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7896, 'You seem confident. Surely you wouldn\'t mind a test of your skills?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7897, 'You are not ready. Speak to Betty Quinn, she\'s standing by the stairs over there. Return to me when you have achieved a higher enlightenment.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7898, 'Your confidence is well-founded, traveler. Now your true education may begin!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7899, 'If you seek to hunt naga, then you will need all the help you can get. Deep in the water you can find bubbly fissures that produce enough air bubbles that will allow you to breath underwater. Swim up close so that you can use the bubbles.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7900, '', 'That one, Malagan. I know $g him:her;. Dependable, brave, and trustworthy. $n is $g his:her; name.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7901, '$n, eh? Oy! Citizen $n, come \'ere. Highlord Bolvar Fordragon has requested that all able bodied heroes o\' the Alliance meet with him at Stormwind Keep. You just been recruited, mate! Get a move on!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7902, 'It is true I have skills to teach to those who are worthy. Would that be you?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7903, 'You are an exceptional enchanter. Beyond my talents as a teacher, I fear. Seek out Kitta Firewind at the Tower of Azora. Only she can qualify as your instructor now.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7904, 'I believe you are up to this task... will you accept the challenge?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7905, 'You have not yet learned the basics, wanderer. Have you spoken to my assistant, Lalina Summermoon? Perhaps you should.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7906, 'Fine work! You are truly deserving of the knowledge I shall bestow upon you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7907, '', 'If you are seeking a truly rewarding pursuit, then a life of enchanting is for you!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7908, '', 'I know many things, stranger, but your knowledge rivals my own. Enchanters who wish to hone their skills further are known to travel to the Tower of Azora, where an artisan named Kitta Firewind resides.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7909, '', 'Why don\'t we see just what you\'re capable of? If you complete the task I shall be happy to further your training! ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7910, '', 'I would be happy to teach you when you are ready, but that time has not yet come! I\'m sure Lalina Summermoon will guide you until then. You may find her in the Craftsmen\'s Terrace in Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7911, '', 'You have gone to great lengths, stranger, and you have my appreciation. I promised to further your training, and now I shall keep that promise.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7912, 'Enchanting has run in our family for generations. I learned at an early age, just as my son is doing now.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7913, 'There is one whose knowledge exceeds my own. You are ready to seek her out. Go to Sun Rock Retreat in Stonetalon Mountains. Find Hgarth, and continue your training.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7914, 'I will teach you... soon. I require a demonstration of your loyalty, first.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7915, 'Surely Mot didn\'t send you to me? He knows better. You are not yet capable of understanding what I would teach you. See my son Mot, just outside there, and finish training with him. ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7916, 'Ah, very good. I am satisfied that you are a worthy student. Now, your training with me may begin...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7917, '', 'I don\'t get many visitors.  You\'re looking for something specific, yes?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7918, '', 'If I wasn\'t stuck here day in and day out, I would have attained the mastery required to train the likes of you! Unfortunately that\'s not the case. Go talk to Hgarth at the Sun Rock Retreat in the Stonetalon Mountains.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7919, '', 'Hmm, you scratch my back, I\'ll scratch yours. If you do what I ask, and do it well, maybe we can discuss your training.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7920, '', 'No, no, you\'ll need FAR more training before you\'re ready to learn from me! Go talk to that uptight dabbler Malcomb Wynn. He\'s the one buried in books over there.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7921, '', 'Well, I\'ve seen better. But, you have potential, I won\'t deny it. Pay attention and you just might learn something!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7922, 'Greetings, traveler! Care to delve into the mysteries of the arcane? Hm?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7927, 'Some adventurers think enchanting doesn\'t work. I find that when I challenge them to battle they change their tune!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7928, 'Your skills are out of my league, stranger.Talk to Hgarth at the Sun Rock Retreat in the Stonetalon Mountains. He will provide you with more knowledge than I can. ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7929, 'You may have what it takes. Let\'s find out! ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7930, 'You are unfit to train with me, stranger. Spend some time with my assistant Jhag over there. ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7931, 'Well done! You have that spark in your eye... I know it well, it\'s the same spark I had when I was but a pup! Now, let\'s see if I can\'t teach you something.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7932, '', 'What brings you to the Tower?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7933, '', 'You have nearly reached the pinnacle of your training. To become the greatest enchanter you can possibly be, you have one more instructor to seek out. Her name is Annora, and you may find her in Uldaman.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7934, '', 'To come this far has not been easy, I know. But how boring life would be without challenges, don\'t you think? Good, because I have a challenge for you...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7935, '', 'You are eager, that is good. However, you are not yet ready to train with the likes of me. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7936, '', 'Your skills are truly becoming extraordinary. Now, let us continue your training...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7937, 'Have you come to seek the knowledge of Hgarth?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7938, 'There is only one whose skills exceed my own. You are ready to receive her knowledge. She is Annora, and you may find her... in Uldaman.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7939, 'True mastery must be earned! It is up to you to earn it now...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7940, 'Train hard, train every day. And when you have perfected your skills, return to me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7941, 'You have reached new heights in your path to mastery. Let us see if we can take you even further...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7942, '', 'Greetings traveler, you have my undying gratitude for dispensing with those dreadful creatures! I was beginning to think I might have to hide out forever!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7943, '', 'I am proud. You are truly a gifted student. Now, for your final lesson...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7944, '', 'Your education is nearly complete. One final task is all that stands between you and the culmination of all your efforts...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7945, '', 'There are still lessons you must learn from others before my knowledge may be shared with you. Be diligent, and do not give up hope.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7946, '', 'Since my husband left for the front, Helene here has been my strength. Her kind heart and sharp sword have helped me through a lot of tough times. I don\'t know what I would\'ve done without her.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7947, '', 'Gosh, I\'m sorry. I don\'t have anything new to teach you. How about you go talk to Simon Tanner in the Old Town section of Stormwind? He\'s a human with big muscles and dreamy red hair.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7948, '', 'How\'s it going? I love the smell of leather; don\'t you?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7949, 'I already know a lot about style. Now it\'s just a matter of mastering the techniques for working with leather. Good thing Simon\'s a real sport about helping me learn them.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7950, 'There\'s nothing I can teach you, other than maybe a better eye for color and detail. Talk to Simon Tanner over there; he knows a lot of advanced techniques for working with leather.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7951, 'This outfit really brings out the blue in my eyes, don\'t you think? It should be obvious I have plenty of skill to go around, so I can afford to share a few of my tricks of the trade. Listen up, okay?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7952, '', 'Welcome to our home, $n. Radnaal and I have little to offer, but you are welcome to what hospitality we can provide.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7953, '', 'My apologies, but you have already learned everything I know. The night elf Faldron could offer you further training, however. Look for him in the Craftsmen\'s Terrace of Darnassus.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7954, '', 'You need to make your stitches smaller, $n. Fewer items will slip out of your quivers and ammo pouches. Besides, no one likes a lumpy tunic.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7955, '', 'If you\'re interested in sewing with leather, you\'ve certainly come to the right place. Faldron and Telonis are true artists, and I hope to learn much from them.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7956, '', 'Oh, I\'m sorry, but you know more about leather than I do at this point. Talk to Faldron over there if you wish to learn more.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7957, '', 'It\'s so exciting to be here in Darnassus, surrounded by talented people! I just know I\'m going to make my family proud. Maybe if I\'m really lucky, the Sentinels will ask me to join their forces on the front!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7958, '', 'This hide isn\'t ready to work with yet. I wish it didn\'t take so long to make leather. There has to be some way to cut down on all this waiting. It can\'t be good for my health, you know?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7959, '', 'Aww, what rotten luck! My skills aren\'t good enough to be of any use to you. Oh well. Talk to Fimble Finespindle over there by the loom. He\'s been working with leather for longer than I have.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7960, '', 'Oh, goody, something else to do while I wait! You\'re a real life-saver, $n!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7961, '', 'You like the rug? I killed and skinned the beast myself. May all the centaur fall as easily.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7962, '', 'I have nothing more to teach you. Talk to Karolek if you wish to further your leatherworking skills.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7963, '', 'Too bad we can\'t skin the humans and their allies. All that skin is just going to waste. Still, who\'d want to wear the flimsy leather you could make out of it?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7964, '', 'Skin, glorious skin. It holds your insides... in. You might\'ve noticed my skin\'s not doing so well. To think I used to worry about my pores. Hardly any skin left to worry about these days. In a way, it\'s very freeing.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7965, '', 'You\'ve got the hang of it. I can\'t teach you any more, but talk to Arthur Moore, a Forsaken who works in the Rogues\' Quarter of the Undercity. Tell him I sent you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7966, '', 'Before the plague came, I knew nothing about leather. Now I know enough to teach others. Death\'s left me with a lot of time on my hands.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7967, 'Meat, skin, bones... a good hunter lets nothing go to waste. Every scrap of leather can be salvaged, perhaps to reinforce an ally\'s armor. Thus, each of us is strong alone, but together we become invincible.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7968, 'You have outgrown any teachings I could offer. Go with the blessing of the Earth Mother. Seek out the tauren Tarn in the northwestern shops of Thunder Bluff\'s central plateau. Be guided by his wisdom.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7969, 'We live and die, and the land remains, eternal, unmoved by our passage. Thus, land cannot belong to us, $n. Instead, we belong to it. Therefore we protect the land, for without it we cannot hope to survive.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7970, 'The caverns below us are a source of strange and wondrous hides, but you must take care if you venture into their depths, $n. Remember that your own skin is far more valuable.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7971, 'Your understanding of leather is deeper than my own. Perhaps you should visit the tauren Krulmoo Fullmoon in Camp Taurajo of the Barrens. He has much knowledge to offer a student of your talents.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7972, 'Even here in the wilds, you seek out learning. Your drive for knowledge is an admirable trait.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7973, 'Cairne himself lives here, they say. Once I have finished crafting a fitting gift, I will seek him out. Perhaps he will have words of wisdom for me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7974, 'Your talent is beyond my own, friend. Speak with Tarn here; his knowledge of leather is much greater than mine. I\'m sure he would welcome another student.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7975, 'Cleanse your hands before you begin any project. Staining the leather with blood or dirt would take away from its natural beauty.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7976, 'When I clawed my way out of the grave, I thought my family would welcome me with open arms. Instead, they drove me from the village, screaming in a language I could no longer understand.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7977, 'I can\'t help you; your work is beyond my own. Talk to Arthur Moore, over there by the coffins. He\'ll be able to train you further.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7978, 'Take every word I say and commit it to memory. Until our enemies have been eradicated, we must never shirk a chance to further our knowledge. Reinforce your armor, and never relax your guard.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7979, 'HELP! ASSASSINS HAVE COME TO KILL ME!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7980, 'I\'m free! That device shall never torment me again!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7983, '', 'You are beyond my limited abilities. Seek out the night elf Ainethil in the Craftsmen\'s Terrace of Darnassus. She can teach you more. I only wish that I could go with you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7984, '', 'You were wise to seek me out. I would ask a small favor. In return, there is much I can teach you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7985, '', 'My teachings would be too advanced for you at this stage. If you wish to learn more of potions, speak with Tel\'Athir here instead.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7986, '', 'Listen carefully, $n, for I have much knowledge to impart.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7987, 'Even I have my limitations! Go talk to Lavinia Crowe in Undercity if you wish to further your training.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7988, 'Not bad... I suppose training you is not out of the question. Very well! Let\'s begin...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7989, 'Randal\'s a bit of a dandy, but he\'s dependable, which is more than I can say for a lot of the students I take on. Still, I guess I can\'t blame them for wanting a bit of adventure.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7990, 'Sorry, but you\'ve reached the limit of what I can teach. If you want to learn more, go see Telonis in the Craftsmen\'s Terrace of Darnassus. He\'s pretty talented, so I hear.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7991, 'Okay, I\'ll teach you, but I want you to do something for me. This is important, so pay close attention.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7992, 'Come back to me when you\'ve had more training. Randal Worth over there can help you with that.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7993, 'Hmm, I guess I can take on another student. Well, don\'t just stand there. Tell me what you\'re looking to learn.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7994, 'Darianna shows promise, but she is so full of energy that it is hard for her to stay still and listen. With her strength and agility, I fear it won\'t be long before she is called to fight. We must teach her quickly.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7995, 'If you are here for further training, you have overestimated my abilities. Have a word with Telonis over there; he can help you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7996, 'This war has been an enormous drain upon our people and resources. Even here on Teldrassil, we cannot escape its repercussions. Forgive me for asking it of you, but would you perform a task for me?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7997, 'In time you will be ready for what I have to teach, $n. Until then, please ask Darianna over there to help you with your training.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7998, 'Many animals lost their lives so that we could gain these materials. Do not take their sacrifice lightly, $n. Handle the leather with the respect it deserves.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (7999, 'Lots to do! Haven\'t been this busy since my cousin accidentally vaporized all the cloth and leather in Gnomeregan. My, it was drafty for a while! Ah, $n, those were the days....', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8000, 'Nope, can\'t help you, I\'m afraid. Your skills have mine beat! If you want to learn more, talk to the night elf Telonis in the Craftsmen\'s Terrace of Darnassus. He\'s amazing!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8001, 'I can see that you\'ll be a quick learner. That\'s good for me, because I\'m always busy these days. In fact, that reminds me: could you run a quick errand for me?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8002, 'Well, I declare! It\'s a new student! I\'ll gladly teach you everything I know once you have a little more skill. Go talk to Gretta Finespindle over there, would you? She\'ll have you up to speed in no time.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8003, 'Your skills are coming along nicely. Soon you\'ll be running a leather shop of your own. I\'ll be glad for the help, $n. My hands are only so fast, you know? There\'s so much work to do!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8004, 'This blue leather does draw the eye, I know. You see, we have a great many eager recruits running around here, hunting and slaying with more enthusiasm than wisdom. One must take precautions.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8005, 'I cannot aid you with further lessons: your skills have surpassed mine. Go and see the tauren Una in the northwestern shops of Thunder Bluff\'s central plateau. She offers more advanced training.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8006, 'A task presses upon me, but I do not have the time to take care of it myself. Can I entrust you with it?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8007, 'I do not offer apprentice training. Look for the tauren Waldor in the Wailing Caverns, here in the Barrens. He can assist you with whatever you need.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8008, 'Have you come for further training? Shake the dust of the road from your garments, then, and let us get to work.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8009, 'My axe has seen many battles, and I like to keep it handy. Enemy forces have fought their way into our capital several times now, but have no fear: we have always driven them back.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8010, 'I cannot help you with further training: your skill at working with leather rivals my own. You should speak with Una, over there. I will vouch for your skill if need be.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8011, 'You have come to me for instruction, and in return I have a favor to ask of you. See it done, and you will be rewarded.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8012, 'Your skills are insufficient to learn what I teach. Try talking to Mak over there instead. He can help you out until you\'re ready for more advanced lessons.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8013, 'I am pleased with your progress so far. Surely the Earth Mother has inspired your hands.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8014, 'We have only a small foothold on this continent for now, but the humans can\'t fight us off forever. Then they\'ll pay for enslaving us. We\'ll cut their lying tongues out for their treachery.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8015, 'I\'d train you if I could, but you\'re too much for me. Tell you what: go talk to the tauren Una in the northwestern shops of Thunder Bluff\'s central plateau. She\'s got some real talent.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8016, 'There\'s enough work here for an army of orcs, and most of it, only I can do. Still, there is a job I could use a hand with.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8017, 'You\'ve got more guts than brains, coming to me without the proper training. Go talk to somebody closer to your skill level, like the orc Kamari, who lives in the Drag of Orgrimmar.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8018, 'We\'re pretty far from anything I\'d call civilization here, at least until the Horde can send us more reinforcements. You must really like working with leather.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8019, 'Am I busy? What\'s that supposed to mean? Do you think I just stand here all day, twiddling my thumbs? If you\'re here to buy something, talk to Handor.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8020, 'You won\'t learn anything new from me; you know too much already. If you want more training, talk to the tauren Una in the northwestern shops of Thunder Bluff\'s central plateau. Send her my regards, eh?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8021, 'That\'s decent work, I admit. But I\'d be more impressed if you\'d handle something for me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8022, 'You can\'t study my techniques until you\'ve learned the basics. Speak with Kamari if you are interested in training.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8023, 'The twists and turns of Orgrimmar are designed to thwart invaders. Can you imagine how quickly one of those puny gnomes would get lost in here? Ha! If it was lucky, I\'d track it down and keep it as a pet.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8024, 'Before my death, I was a librarian; I had nothing to do with wars and plagues. I never asked to become this. On the other hand, now that my mind is my own again, I\'m not about to surrender without a fight.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8025, 'I\'m pleased to say that your talents have outpaced my own. Go away now. Err, try the tauren Una in the northwestern shops of Thunder Bluff\'s central plateau. She\'s very skilled, I assure you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8026, 'Yes, yes, I\'ll train you. There\'s no need to raise your voice.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8027, 'If I taught novices, there\'d be a lot more students visiting me, and things would get noisy. Go talk to Dan Golthas over there. He\'s a little intense, but he means well, and he\'s a decent teacher.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8028, 'I first set up shop in the Magic Quarter, but it was pretty noisy there. This is a definite improvement, barring a few persistent students who still track me down, despite the fact that I\'ve relocated.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8029, '', 'Elune has granted the night elves many gifts. In return, it is our responsibility to safeguard these forests from the demonic taint that the Horde always brings with it.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8030, '', 'Training you further is beyond my abilities. Speak to Telonis in the Craftsmen\'s Terrace of Darnassus. He is always glad to help talented students.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8031, '', 'I cannot leave my post here, $n, but there is a task that must be taken care of. Can I trust you to see it done?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8032, '', 'I only teach students who have already mastered the basics. You must seek out the night elf Darianna in the Craftsmen\'s Terrace of Darnassus. Her skills will be a perfect complement to yours.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8033, '', 'Always double-check your measurements before you begin to cut the leather. We cannot afford to waste materials.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8034, 'I need a ride, Dungar! I have important supplies to deliver!', 'I need a ride, Dungar! I have important supplies to deliver!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8035, 'Do you know where I may find the Half Pendant of Aquatic Agility?', 'Do you know where I may find the Half Pendant of Aquatic Agility?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8036, 'Do you know where I may find the Half Pendant of Aquatic Endurance?', 'Do you know where I may find the Half Pendant of Aquatic Endurance?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8037, 'The waters of Westfall are where you must search, due west-northwest of where the Gold Coast Quarry meets with the shore. An anchor shows where a boat has sunk beyond it, deep within the water. The underwater pressure is fierce here; swimming too far will crush even the mightiest swimmer from fatigue. $B$BLook for a natural formation to aid you, $n - a bubbly fissure is near here, offering deep-water swimmers a chance for renewed breath.\n', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8038, 'In the Silverpine Forest along the edges of the North Tide\'s Run, a derelict boat rests on the shore. Out to the west of this lies what you seek; swim too far and the ocean will crush you from fatigue. It resides on the ocean floor in a lockbox, waiting for you to claim it.$B$BYou\'ll be hard pressed to survive a dive to such depths without aid, so take heart; a bubbly fissure offers renewed breath to divers seeking this half of the pendant. Use it wisely.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8039, '', 'In the northern part of Darkshore runs the Cliffspring River, flowing down from the lands now known as Felwood.  As the river flows out and empties out into the Mist\'s Edge, travel north to where the deepest parts of the ocean begin.  A stone formation that some call the Stone Claw due to its shape is where you may find what you seek.$B$BBe warned - threshers gather by the Stone Claw, and will attack those who trespass on their territory.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8040, '', 'In the northeastern Barrens lies a pool of water sickened by pollution.  The locals call it the Sludge Fen; the Venture Company has turned the once crystal-blue waters into a mire of industrial byproducts and waste.  In the heart of this fen lies what you seek, $n.$B$BBe warned - the Venture Company does not tolerate trespassers, and they consider anyone who does not work for them as such.  You\'ll especially be a target as a protector of nature, something they vehemently oppose.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8041, 'The grass is soft and lush. The air is fresh, full of light and song. Everything I\'ve seen suggests that Teldrassil is a dream come true. Why, then, is my heart full of foreboding?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8042, 'You have advanced beyond my skill to teach you. Journey now to Aerie Peak in the Hinterlands and speak with the dwarf Drakk Stonehand. His work is magnificent, far better than my own.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8043, 'You have demonstrated skill in learning our craft. Do you also possess the resolve to complete a mission on my behalf?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8044, 'I regret to say that I cannot aid you, $gnoble sir:gentle lady;, until you learn more of working with leather. Speak with Faldron or Darianna here instead. They can teach you the skill you require.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8045, 'Do not try to force your will upon the leather. Rather, let its natural tendencies dictate what form and function it will take. This is not a battle to be won. Work with the leather, not against it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8046, '', 'The needle is small, but the thread is strong, and the stitches are fine. Our pact with Thrall and the others will hold. The Horde will not be driven from these lands.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8047, '', 'Even my talents have a limit, and you have reached it. Congratulations, $n. Your next stop should be Camp Mojache in Feralas. The tauren Hahrana Ironhide lives there; she is renowned for her skill.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8048, '', 'As members of the Horde, we must contribute to our community to ensure that it continues to thrive. Thus, I am certain you will gladly aid us by undertaking a small task I have for you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8049, '', 'It\'s been so long since I was a novice myself that I don\'t think I\'d be the best teacher for you. Try talking to Tarn or Mak, right over there. One of them should be able to answer your questions.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8050, '', 'Cairne has brought us to a land rich with possibilities for the future. We have been fortunate, and so in turn we pass on our good fortune to others. That is why I began teaching, $n.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8051, 'I can make amazing things with the materials I find out here. By Muradin\'s beard, there are fantastic creatures in these hills! Go have a whack at them, $n, and bring me back some tall tales.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8052, 'Och, \'twas a sad day when I realized my obsession. I woke up and found myself tannin\' before I had my first tankard of ale. Hardly even a respectable hangover! I knew then--aye, I knew!--how it was goin\' to be.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8053, 'I love a good challenge; don\'t you? Aye, of course ye do. An\' that\'s why you\'re about to do exactly what I say.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8054, 'I\'m not here to teach babes fresh out of the cradle. Ye\'ve nowhere near the amount of skill I require in my students. In fact, ye\'re a sad figure of a $glad:lass;, an\' I\'ll thank ye not to stand there jibber-jabberin\' at me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8055, '', 'All requests for my work must be made in person by the buyer. Orwin often asks me to reconsider, but my priorities lie here. Look around you: many of these fighters rely upon me for their armor.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8056, '', 'I only wish I had more students like you. Not many will venture so far into enemy territory. I salute your courage.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8057, '', 'We cannot afford to give an inch in this war. Day and night, I create new armor, and I patch whatever can be salvaged from the old. I have no time for other tasks. Would you take care of something for me?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8058, '', 'You have wasted your time and mine by coming to me for training, $n. I have no time to teach basic skills. We\'re fighting a war here, and this is one of the front lines. Don\'t return to me until you are more prepared.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8059, 'I can tell that your wardrobe is in dire need of care.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8060, 'I\'m a simple man who knows a few things about tailoring, but you need more assistance than I can provide. I have heard of a Night Elf who is quite an accomplished tailor. He resides in Stormwind, in the Magic District. ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8061, 'So you would like to learn a few things? I would be happy to get you started!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8062, 'Life is like a carefully woven tapestry.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8063, 'Hmm... the things you need to learn are a little above my head. Have a talk with Sellandus over at Larson\'s Clothiers just up the path to the right. He taught me everything I know!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8064, 'Very well, let your journey of tailoring begin.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8065, 'Needlework requires a steady hand. And patience... above all, patience.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8066, 'It is time for you to journey to Darnassus and speak to Me\'lynn. She will guide you from now on.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8067, 'I would be happy to take you under my wing. Let us begin.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8068, 'Welcome to me shop. What can I do for ye\'?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8069, 'Hold on there, I\'m just an assistant. If ye want to start learnin\' the fancy stuff, talk to old Stonebrew over there.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8070, 'Excellent! I\'ve been hopin\' for a new student. Well, let\'s get started right away then!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8071, 'We use many animal pelts and skins in our creations. ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8072, 'I know a few things, but I\'m no expert. Lucky for you, Magar is. He\'s standing near the back of the shop. See if he can help you, but be careful not to rile him. He can be irritable.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8073, 'I will teach you what I can.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8074, 'No part of the animals we hunt is wasted. ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8075, 'You seek a higher knowledge. Tepa is blessed with such knowledge. She should be standing somewhere nearby...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8076, 'With the Earth Mother\'s blessing, we shall begin.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8077, 'Pleased to make you acquaintance. May I help you?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8078, 'I am knowledgable, but I am not an expert. You know who you should talk to? Rhiannon! She\'s not far. Ask around for her in Undercity', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8079, 'So... you want to be a tailor? Very well, we\'ll see if you have what it takes.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8080, 'We don\'t get many visitors out here. May I interest you in something?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8081, 'I\'m just a student myself, really. Rhiannon\'s the expert. She\'s standing by the crates over there. You can learn a lot from her.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8082, 'Yes, I think you have potential. Maybe I can show you a thing or two.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8083, '', 'There is no greater way to clear the mind than to weave.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8084, '', 'My education is ongoing, and your knowledge surpasses mine. If you wish to learn more you must speak to Me\'lynn. She will guide you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8085, '', 'You will not be disappointed, traveler. Tailoring is a joyous endeavor.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8086, 'Hey mon, what can I be doin\' for you?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8087, 'At dis point you know as much as I do, mon. Maybe you go see Magar in Orgrimmar. He not be da happiest tailor around, but he help you anyway.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8088, 'Dat\'s da attitude! Grab a needle and thread and let\'s get jammin\'!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8089, 'I am afraid that I am no longer gentle or humble. Age and countless battles have hardened my skin and soul. Perhaps that is why Cairne assigned me to Sun Rock Retreat... the gentle winds and warm sun does ease the pain.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8090, 'I knew you would come, $n. It is good to see you again, friend.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8091, 'Yawww!', '', 0, 0, 0, 35, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8092, '%s loses power and shuts off!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8093, '%s nearly shatters!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8094, 'Greetings. Our bats are well trained and fed. You\'ll not find faster.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8095, 'How may I be of service?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8096, 'Greetings, and welcome. I am not very good at smiling, but trust me; I am happy to serve you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8097, 'Let me browse your goods.', 'Let me browse your goods.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8098, '', 'The Hippogryph is a noble beast, proud and swift.  They are an honor to train, and can quickly take their rider a great distance.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8099, 'To maintain our link to the mainland, we have hippogryphs constantly flying between Rut\'theran Village and Darkshore.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8100, '', 'The fish off the coast of Teldrassil are quite large and robust.  Rumors say it is the speedy growth of our new world tree that is the cause.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8101, 'Although the coast of Darkshore is more dangerous than that of Teldrassil, many fishers find profit here.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8102, 'It is my honor to provide for adventurers, for they are heroes in the making.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8103, 'I\'m sorry but I can not let you ride my gryphons. You should go talk to Gringer if you need a ride somewhere.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8104, 'Fastest gryphons this side of Aerie Peak. Want to take a ride?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8105, 'Sure! Let\'s go for a ride.', 'Sure! Let\'s go for a ride.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8106, 'On the northern dock, you can board a ship that will carry you to Rut\'theran Village and Darnassus.\n\nFrom the southern dock, you can find passage across the Great Sea to the human city of Stormwind in the Eastern Kingdoms.\n\nSafe journeys to you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8107, 'On guard, friend. The lady dragon will not give in without a fight.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8108, 'This dock here? Well you can catch the ship that goes to that Night Elf place. What\'s it\'s name? Uhh Auberdine I think. I don\'t really know for sure, I just load and unload stuff.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8109, 'As was fated a lifetime ago in Karazhan, monster - I come - and with me I bring justice.', '', 1, 0, 0, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8110, 'Are you here to help with the delivery? Miran\'s just about ready.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8111, 'My wind riders are trained to fly quickly through the hot Barrens air.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8112, 'The Barrens, with its hot sun and hostile denizens, is the perfect place to test your strength and fortitude.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8113, 'Delivering these barrels to the excavation site is hard work, but there are so many depending on us to get the blast powder there.$B$BWe must do what we can against the Dark Irons!', '', 0, 0, 7, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8114, 'Greetings. If you are here for leather goods, then you will find my wares well crafted.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8115, 'Thanks for your help, $n. Because of you, another shipment arrived safely at the excavation.', '', 0, 0, 7, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8116, 'When mounted on the back of a wind rider, one sees that its reputation for strength and speed is well earned.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8117, 'You\'re better at this gig than I am! You want someone with even more experience than I\'ve got. That\'d be Doctor Herbert Halsey, an undead who works in the Apothecarium of the Undercity.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8118, 'What is this? You trying to ruin my reputation? I don\'t teach novices, $gbuddy:lady;. Go talk to Milla Fairancora in the Craftsmen\'s Terrace of Darnassus.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8119, '', 'Seize him! Seize the worthless criminal and his allies!', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8120, 'Have a look at my meats, friend. You won\'t want to go out into the Barrens without a good stock of provisions.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8121, 'Reginald, you know that I cannot let you pass.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8122, 'You haven\'t lived until you\'ve looked down on the world from the back of a wind rider.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8123, 'You must do what you think is right, Marcus. We served together under Turalyon. He made us both the men that we are today. Did he err with me? Do you truly believe my intent is to cause harm to our alliance? Would I shame our heroes?', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8124, '%s appears lost in contemplation.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8125, 'I am ashamed, old friend. I know not what I do anymore. It is not you that would dare bring shame to the heroes of legend - it is I. It is I and the rest of these corrupt politicians. They fill our lives with empty promises, unending lies.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8126, 'Dear friend, you honor them with your vigilant watch. You are steadfast in your allegiance. I do not doubt for a moment that you would not give as great a sacrifice for your people as any of the heroes you stand under.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8127, 'Stand down! Can you not see that heroes walk among us?', '', 0, 0, 0, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8128, 'Move aside! Let them pass!', '', 0, 0, 0, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8129, 'Reginald Windsor is not to be harmed! He shall pass through untouched!', '', 1, 0, 0, 22, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8130, 'Go, Reginald. May the light guide your hand.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8131, 'You can find passage to Theramore from this dock. Heed my words, those lands are rarely safe to travel for people of the Alliance. Watch your step and your back.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8132, 'We shame our ancestors. We shame those lost to us... forgive me, Reginald.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8133, 'Holding me here is not the right decision, Marcus.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8134, 'Now, it is time to bring her reign to an end, Marcus. Stand down, friend.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8135, 'Your understanding of leather is deeper than my own. Perhaps you should visit Aayndia Floralwind in the town of Astranaar in Ashenvale Forest. She has much knowledge to offer a student of your talents.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8136, 'Good day to you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8137, 'You are beyond my level of expertise. Georgio Bolero, the master of Duncan\'s Textiles around the corner could help you advance your skills.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8138, 'I will teach you, but first you must prove your worth...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8139, 'Welcome, looking to get some premium wares?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (65013, 'This place is quite puzzling, but it has great business!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8140, 'A fine display of worthiness. Your instruction may now continue.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8141, 'Ah, this Darnassian fabric is the finest!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8142, 'Well, aren\'t you the smarty-pants? Ye know more than I do, I can tell ye that! Don\'t go getting\' a big head, but if ye want to keep learnin\', have a chat with Georgio Bolero in Stormwind. They say he knows his tailorin\'!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8143, 'I don\'t train just anyone! You\'ll need to perform a task first!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8144, 'I don\'t have time to babysit! Go talk to me assistant, Uthrar! ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8145, 'Hmm... you show a little promise, I\'ll give you that. Okay then, where were we?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8146, '', 'Do you hear the call of the hunt, $c?  You must listen closely, but the call comes from deep within you.  It comes from deep within all of us.', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8147, 'I made a lot of the things you see in the store. What do you think? Answer carefully, your life may depend on it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8148, 'You need to learn from someone who knows more than I do. Go talk to Josef Gregorian in Undercity. Well, what are you still doing here?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8149, 'You think you just walk up to me and I start teaching you? Ha! You need to pay your dues like everyone else!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8150, 'What? Why are you bothering me? Why, I bet you can\'t even create a high quality robe! Go talk to Snang and come back when you\'re worthy of my talents!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8151, 'About time you showed me some results! Now I\'ll show you results!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8152, 'They say a stitch in time saves... what, eleven? Ah, I can\'t remember. Stupid saying anyway.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8153, 'Not bad... for an amateur! Still, you know about as much as I do. If you wanna\' learn more you\'re gonna\' have to talk to Josef Gregorian in Undercity.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8154, 'Maybe you got it, maybe you don\'t. I think I got a way to find out...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8155, 'Ha! You ain\'t got what it takes... yet! Come back to me after you\'ve learned a few things!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8156, 'Well well, not too bad. Maybe I CAN show you a few things...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8157, '', 'What a fine day! It inspires me to create a new design.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8158, '', 'Your talents excel! You are truly inspired. To continue your pursuit of knowledge, speak with Georgio Bolero in Stormwind. He is held in high regard, and will be able to further your instruction.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8159, '', 'There are many obstacles one must overcome on the path to excellence. Your path is no exception...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8160, '', 'There are basics that must be learned. Trianna is standing nearby, speak with her and return to me later.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8161, '', 'You have done well, traveler. I am proud to call you my student!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8162, '', 'Welcome to our tent. May I assist you?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8163, '', 'Your knowledge is great, traveler. You must learn from one who is wiser than I. Many tales have been told of the wisdom of Josef Gregorian. You may find him in Undercity.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8164, '', 'There is a task I would like you to perform before we go any further...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8165, '', 'You still have lessons to learn before I may begin your training. In the meantime, perhaps Vhan, in Thunder Bluff can help you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8166, '', 'Expertly accomplished! Now, let us continue your education...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8167, 'Light be with you, sir.', 'Light be with you, sir.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8168, '', 'Aren\'t the patterns beautiful? Each one speaks with its own voice.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8169, '', 'My knowledge is limited, though I am still learning. It is time for you to move on, traveler. Find Josef Gregorian in Undercity and tell him you are ready.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8170, 'You are an inspiration to us all, sir.', 'You are an inspiration to us all, sir.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8171, '', 'I will require a demonstration of your determination before I share my gifts with you...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8172, 'There walks a hero.', 'There walks a hero.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8173, '', 'There are fundamental things you must learn before I can aid you. Speak to my apprentice, Vhan. He is kind and patient and will teach you what you need to know.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8174, '', 'You are truly a determined student. I will be happy to provide you with instruction!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8175, 'Make way!', 'Make way!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8176, 'Blacksmithing is a refined art, I spent quite some time in Hammerfall before I was assigned here working on practicing it.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8177, 'We are but dirt beneath your feet, sir.', 'We are but dirt beneath your feet, sir.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8178, '', 'You have worked your way up... time to see the boss. Go to Josef and tell him you are ready.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8179, '', 'Okay... so you wanna\' be a tailor. How about you prove it?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8180, 'A moment I shall remember for always.', 'A moment I shall remember for always.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8181, '', 'How can I share my secrets with a beginner? Try speaking with Victor Ward first.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8182, '', 'Good, you\'ve got some grit! I like that. Well then, let\'s get going!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8183, '...nerves of thorium.', '...nerves of thorium.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8184, 'A living legend... ', 'A living legend... ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8185, 'Yes, I\'ve been running this shop for some time now. Business has been pretty good lately. A lot of new adventurers coming through...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8186, 'There\'s only one person I know of who is more learned in the art of tailoring than myself. Last I heard he could be found in Theramore Isle. He is the person you should seek.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8187, 'I require all my students to prove their dedication. Now it\'s your turn...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8188, 'You\'ll need more training before I can share my knowledge with you, I\'m afraid. ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8189, 'Good! I can tell that my efforts will not be wasted!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8190, 'Cloth is okay. Personally I prefer to work with human skin.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8191, 'You require the instruction of a true master! Go to Daryl Stack in Tarren Mill and embrace your destiny!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8192, 'Before your learning may begin, I must ask of you a favor...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8193, 'I will instruct you when the time is right... that time is not now. You have much more to learn first!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8194, 'Good! I can tell that my efforts will not be wasted!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8195, 'I have outfitted kings. With the proper training, you can too.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8196, 'You have truly become one of the finest tailors in the business! Congratulations, I am proud to have been your instructor!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8197, 'You have come a long way, traveler. There is one more task for you to perform...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8198, 'I cannot help you until you have learned more of the essentials. Come back to me when you are ready.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8199, 'I am not worthy, sir.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8200, 'The place is a bit drafty... but what do I care? I\'m dead.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8201, 'Well done. You have now earned tailoring\'s highest honor. Go forth, and do terrible things with this knowledge!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8202, 'You\'re doing very well in the art of tailoring, I must admit. Now, let\'s see how good you REALLY are...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8203, 'Who sent you here? You\'re not ready for my instruction yet! You\'ll be told when you\'re ready.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8204, '', 'I do so enjoy sitting out here, listening to the sounds of the Great Sea.  Oh, what?  Yes, there is a ship that will take you to Menethil Harbor.  It docks here from time to time.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8205, 'Thank you, old friend. You have done the right thing.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8206, 'Follow me, friends. To Stormwind Keep!', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8207, 'Be brave, friends. The reptile will thrash wildly. It is an act of desperation. When you are ready, give me the word.', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8208, 'Onward!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8209, 'Mark my words! You won\'t find faster gryphons anywhere in the Eastern Kingdoms than the ones right here in Thelsamar!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8210, 'Majesty, run while you still can. She is not what you think her to be...', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8211, 'The masquerade is over, Lady Prestor. Or should I call you by your true name... Onyxia...', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8212, 'To the safe hall, your majesty.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8213, 'If you ask me, there\'s no greater profession than a miner. And if you ask me, there\'s no greater miner than a dwarf!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8214, '', '%s laughs.', 2, 0, 0, 11, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8215, '', 'You will be incarcerated and tried for treason, Windsor. I shall watch with glee as they hand down a guilty verdict and sentence you to death by hanging...', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8216, '', 'And as your limp body dangles from the rafters, I shall take pleasure in knowing that a mad man has been put to death. After all, what proof do you have? Did you expect to come in here and point your fingers at royalty and leave unscathed? ', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8217, 'The Wildhammer dwarves might have fast steeds, but can those Aerie Peak gryphons stand the heat of The Great Forge? I think not!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8218, 'You will not escape your fate, Onyxia. It has been prophesied - a vision resonating from the great halls of Karazhan. It ends now...', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8219, 'Listen, dragon. Let the truth resonate throughout these halls.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8220, 'The best miner has a firm grip on $ghis:her; pick, a stout heart in $ghis:her; chest, and thick, black dust in $ghis:her; hair.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8221, 'I would like to train.', 'I would like to train.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8222, 'The sturdy crate sits in the middle of the lumber mill surrounded by debris and very zealous followers of the Scarlet Crusade.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8223, 'Place Termite Barrel on the crate.', 'Place Termite Barrel on the crate.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8224, '%s, too injured, gives up the chase.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8225, '%s obviously beaten, tries to run.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8226, '%s reaches into his pack and pulls out the encoded tablets.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8227, 'The Dark Irons thought these tablets to be encoded. This is not any form of coding, it is the tongue of ancient dragon.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8228, '%s reads from the tablets. Unknown, unheard sounds flow through your consciousness.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8229, 'What can you tell me about the bear - Ursangous?\n', 'What can you tell me about the bear - Ursangous?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8230, 'What can you tell me about the nightsaber cat - Shadumbra?\n', 'What can you tell me about the nightsaber cat - Shadumbra?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8231, 'What can you tell me about the hippogryph - Sharptalon?\n', 'What can you tell me about the hippogryph - Sharptalon?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8232, '', 'This bear wanders a path close to where the Talondeep Path lies, due west of Mystral Lake and the Alliance outpost of Silverwind Refuge.  While he is the easiest of the three creatures you will face on the Ashenvale Hunt, he should not be trifled with.  His presence is a constant threat to travelers who sojourn between Ashenvale and the Stonetalon Mountains.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8233, '', 'This nightsaber is rumored to be docile in the presence of night elves and their allies, which makes her an even more worthy opponent.  Shadumbra\'s hunting path takes her from the Raynewood Retreat to our west, ultimately to lands due west of there.  Her fur is the color of pitch, and her speed is not to be underestimated.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8234, '', 'The mightiest of all the beasts that make up the Ashenvale Hunt, Sharptalon is let to wander freely by the self-proclaimed guardians of this land, the night elves.  Sharptalon patrols for prey to the south of here, taking delight in rending the unsuspecting and unaware traveler limb from limb.$B$BTo bring down such a beast would be the mightiest of hauls, $n.  I wish you luck in the hunt for this creature!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8235, '', 'Curious... Windsor, in this vision, did you survive? I only ask because one thing that I can and will assure is your death. Here and now.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8236, '%s gasps.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8237, 'Dragon filth! Guards! Guards! Seize this monster!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8238, '', '%s laughs.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8239, '', 'Yesss... Guards, come to your lord\'s aid!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8240, 'Get your seats, folks! The race is starting in 2 minutes!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8241, 'The Drag race will start in 1 minute!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8242, 'Look at what $n brought us! Let\'s drink up!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8243, 'Greeting $c - welcome to the front lines in our conquest of Ashenvale! If you are looking for something to do, then you\'ve come to the right place. I\'m always looking for able-bodied individuals to help bring glory to the Horde!', '', 0, 0, 0, 66, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8244, '$n brought us booze! Let\'s party!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8245, '%s hisses.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8246, '', 'Was this fated, Windsor? If it was death that you came for, then the prophecy has been fulfilled. May your consciousness rot in the Twisting Nether. Finish the rest of these meddlesome insects, children. Bolvar, you have been a pleasurable puppet.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8247, 'DO NOT LET HER ESCAPE!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8248, '', 'You have failed him, mortalsss... Farewell!', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8249, 'Reginald... I... I am sorry.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8250, 'Bol... Bolvar... the medallion... use...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8251, '%s dies.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8252, '%s becomes exhausted!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8253, '%s performs one last service for Ragnaros...', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8254, '%s forces one of his Firesworn minions to erupt!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8255, 'The dragon hides behind a royal facade. The tablets will reveal her for what she is... If what I have seen holds true, I will not survive this battle, friend. It is you that must finish what I have started. Are your forces ready? Are you ready? We will begin on your command.', '', 0, 0, 0, 1, 6, 25, 1000, 2000, 3000);
INSERT INTO `broadcast_text` VALUES (8256, 'I am ready, as are my forces. Let us end this masquerade!', 'I am ready, as are my forces. Let us end this masquerade!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8257, 'If you want to know about the comings and goings of caravans and merchants here in Splintertree Post, then I am fortunately the orc you are looking for. Unfortunately for you though, I don\'t like people.$B$BIf you want something, make it quick.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8258, 'The elements speak to us, $c. They share knowledge that we must learn, and they share secrets that must be kept.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8259, '', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8260, 'Through rigorous retraining I have had to break many students of all they had previously learned so that I might teach them anew. The service I offer becomes increasingly difficult to perform each time it is done. It is for that reason that it becomes increasingly expensive with each retraining. Do you desire that I break you of the talents you have learned?', 'Through rigorous retraining I have had to break many students of all they had previously learned so that I might teach them anew. The service I offer becomes increasingly difficult to perform each time it is done. It is for that reason that it becomes increasingly expensive with each retraining. Do you desire that I break you of the talents you have learned?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8261, 'I wish to unlearn my talents.', 'I wish to unlearn my talents.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8262, 'Yes. I do.', 'Yes. I do.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8263, 'If you desire, in addition to training, I have it within my power to erase the knowledge of your talents from your mind. Be warned that every time you undergo this procedure the more difficult it is to perform, and therefore the more expensive it becomes.', 'If you desire, in addition to training, I have it within my power to erase the knowledge of your talents from your mind. Be warned that every time you undergo this procedure the more difficult it is to perform, and therefore the more expensive it becomes.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8264, 'Look at the wisdom and knowledge of the beast, and you will see yourself. Observe as the owl does, learn cleverness from the sabercat, embody the mighty spirit of the wolf.', 'Look at the wisdom and knowledge of the beast, and you will see yourself. Observe as the owl does, learn cleverness from the sabercat, embody the mighty spirit of the wolf.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8265, 'Thank you for freeing me. Speak with my sister in Splintertree. I... I must find a place to rest...', '', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8266, '%s\'s medallion shatters.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8267, 'The arcane only corrupts those who are weak. Keep up on your training, or you may find a similar fate.', 'The arcane only corrupts those who are weak. Keep up on your training, or you may find a similar fate.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8268, 'Welcome, my $g brother : sister;. If you are here to train, then I would be delighted to train you.$B$BThere will be a time soon where the call of the Emerald Dream will take hold in you. You will hibernate for many years and walk amongst the purest of forms. It is there and then that your ultimate training will take place.', 'Welcome, my $g brother : sister;. If you are here to train, then I would be delighted to train you.$B$BThere will be a time soon where the call of the Emerald Dream will take hold in you. You will hibernate for many years and walk amongst the purest of forms. It is there and then that your ultimate training will take place.', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8269, 'Welcome, my $g brother : sister;. If you are here to train, then I would be delighted to train you.$B$BThere will be a time soon where the call of the Emerald Dream will take hold in you. You will hibernate for many years and walk amongst the purest of forms. It is there and then that your ultimate training will take place.', 'Welcome, my $g brother : sister;. If you are here to train, then I would be delighted to train you.$B$BThere will be a time soon where the call of the Emerald Dream will take hold in you. You will hibernate for many years and walk amongst the purest of forms. It is there and then that your ultimate training will take place.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8270, 'Welcome, my $g brother : sister;. If you are here to train, then I would be delighted to train you.$B$BThere will be a time soon where the call of the Emerald Dream will take hold in you. You will hibernate for many years and walk amongst the purest of forms. It is there and then that your ultimate training will take place.', 'Welcome, my $g brother : sister;. If you are here to train, then I would be delighted to train you.$B$BThere will be a time soon where the call of the Emerald Dream will take hold in you. You will hibernate for many years and walk amongst the purest of forms. It is there and then that your ultimate training will take place.', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8271, 'I wish to unlearn my talents.', 'I wish to unlearn my talents.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8272, 'Yes. I do.', 'Yes. I do.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8273, 'I wish to unlearn my talents.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8274, '', 'I wish to unlearn my talents.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8275, 'What can I do for ya?', 'What can I do for ya?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8276, 'I got nothin\' more to teach! Not for somebody who knows as much as you do, anyway! You Horde types need to talk to Nogg in Orgrimmar!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8277, 'Ragers, Fetch!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8278, 'Ok, let\'s move out!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8279, '', 'Who dares attack the Silverwing Outpost?!?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8280, 'Silverwing is ours!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8281, 'Go report that the outpost is taken. We will remain here.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8282, 'Prepare yourselves. Silverwing is just around the bend.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8283, 'For the Horde!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8284, 'Everyone ready?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8285, '', 'I mean you no harm. I am scouting the quilboar and taking notes of their activities. My people have noticed something odd--an emanation or darkness--coming from this area. I\'ve come to investigate, and what I\'ve found is very disturbing.$B$BThere seems to be an undead diplomat who is powerful in the ways of the arcane. I don\'t believe he\'s one of the Forsaken... I think it\'s worse.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8286, '', 'How fortuitous. Usually, I must leave my lair in order to feed.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8287, '', 'Learn your place, mortal!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8288, '', 'This meaningless exertion bores me. I\'ll incinerate you all from above!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8289, 'You know enough to be dangerous, but you still look like you need training to me $n.', 'You know enough to be dangerous, but you still look like you need training to me $n.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8290, '', 'It seems you\'ll need another lesson, mortals!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8291, 'Your will is not strong enough to manipulate the forces we control $c.', 'Your will is not strong enough to manipulate the forces we control $c.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8292, 'It is a greater knowledge of the ways of the warlock that I crave.', 'It is a greater knowledge of the ways of the warlock that I crave.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8293, '$n, how may I further your training in the eyes of the Earth Mother?', '$n, how may I further your training in the eyes of the Earth Mother?', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8294, 'Many are the paths of the Earth Mother. May your ancestors watch over you $c.', 'Many are the paths of the Earth Mother. May your ancestors watch over you $c.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8295, 'I seek further druidic training to have a closer understanding of the Earth Mother\'s will.', 'I seek further druidic training to have a closer understanding of the Earth Mother\'s will.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8296, 'It is wise that you humble yourself before me $N in your desire for more warlock knowledge.', 'It is wise that you humble yourself before me $N in your desire for more warlock knowledge.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8297, 'I have no time to waste on the likes of you $c.', 'I have no time to waste on the likes of you $c.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8298, 'I submit myself for further training my master.', 'I submit myself for further training my master.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8299, 'I can tell just by the way you\'re standing that you\'re no warrior. $B$BWhy don\'t you go do something safe, like read a book.', 'I can tell just by the way you\'re standing that you\'re no warrior. $B$BWhy don\'t you go do something safe, like read a book.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8300, 'The darkness does not embrace you, $c. Cease your prattle and remove yourself from my sight! Be gone!', 'The darkness does not embrace you, $c. Cease your prattle and remove yourself from my sight! Be gone!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8301, 'I\'m afraid your darkness quotient isn\'t sufficient enough for me to bother talking to you any further. Move along please.', 'I\'m afraid your darkness quotient isn\'t sufficient enough for me to bother talking to you any further. Move along please.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8302, 'Yer no warrior... Why ye wouldn\'t even last a day under my training regimen! $B$BA $c who thinks $ghe\'s : she\'s; a warrior. Ha!', 'Yer no warrior... Why ye wouldn\'t even last a day under my training regimen! $B$BA $c who thinks $ghe\'s : she\'s; a warrior. Ha!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8303, 'I do not see the warrior\'s spirit within you, child. I have no teachings which would benefit a $c such as yourself.', 'I do not see the warrior\'s spirit within you, child. I have no teachings which would benefit a $c such as yourself.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8304, '%s emerges to protect the Fathom Stone!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8305, 'Pathetic. You two do not deserve the training you are getting here if that was the best you can do.', '', 0, 0, 33, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8306, 'Lysta, summon in undead captives.', '', 0, 0, 33, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8307, 'Lysta, summon forth... the abomination!', '', 0, 0, 33, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8308, 'Please share your wisdom with me, Warchief.', 'Please share your wisdom with me, Warchief.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8309, 'The Shattered Hand has been able to place spies throughout the upper citadel of Blackrock. Though the information gathering process has been slow, we have made discoveries that cannot be ignored.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8310, 'What discoveries?', 'What discoveries?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8311, 'It would appear as if the influence of the Black Dragonflight had extended beyond the borders of the Burning Steppes and into the kingdom of Stormwind itself! The usurper has since been unmasked and ousted.', '', 0, 0, 0, 1, 5, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8312, 'Usurper?', 'Usurper?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8313, 'Indeed. The brood mother of the Black Dragonflight had used illusion and trickery to disguise herself as a member of the royal court. Worse yet, she had assumed control of the current Highlord of Stormwind, Bolvar Fordragon.$B$BIt is no wonder that such foolish military decisions had been made. The movement of the Alliance forces as of late had left us scratching our heads. We now understand that the Black Flight was attempting to divide the empire - destroy it from within.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8314, 'With all due respect, Warchief - why not allow them to be destroyed? Does this not strengthen our position?\n', 'With all due respect, Warchief - why not allow them to be destroyed? Does this not strengthen our position?\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8315, '<Thrall laughs.>$B$BSo much to learn still, $r.$B$BYou are too young to remember the wars and bloodshed of ages past. Too young to remember the burden of the curse carried by our people.$B$BOur position with the Alliance right now is a tenuous one. Skirmishes and smaller battles do take place, but there is peace - something our people have not known for millennia. Would you give this up and plunge us into the maw of chaos again but against a new foe? One that will not rest until we are all destroyed?', '', 0, 0, 0, 1, 6, 6, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8316, 'I... I did not think of it that way, Warchief.', 'I... I did not think of it that way, Warchief.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8317, 'No, $n, you did not. I do not fault you, however, as yours is a mind much like mine was as a youth: Impetuous, rebellious, and often without a single coherent thought! Traits I can no longer afford to express.$B$BEnough lamenting. $R, I have a task for you; one that you may not survive. ', '', 0, 0, 0, 1, 5, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8318, 'I live only to serve, Warchief! My life is empty and meaningless without your guidance.', 'I live only to serve, Warchief! My life is empty and meaningless without your guidance.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8319, '<Thrall laughs.>$B$BNoted... Now pay attention, $n. The Black Dragonflight brood mother, Onyxia, has been flushed out from her position of power in Stormwind. She has escaped, fleeing to her lair in Dustwallow Marsh.$B$BShe cannot be allowed to live in such close proximity to our capital cities. We must strike before being struck!$B$BI want her destroyed, $n. ', '', 0, 0, 0, 11, 1, 5, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8320, 'Of course, Warchief!', 'Of course, Warchief!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8321, '%s collapses upon seeing his master die!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8322, 'Welcome, my $g brother : sister;. If you are here to train, then I would be delighted to train you.$B$BWe are the preservers of the balance, now and forever, as Malfurion lies in the Dreaming. Never forget this.', '', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8323, 'Welcome, my $g brother : sister;. If you are here to train, then I would be delighted to train you.$B$BWe are the preservers of the balance, now and forever, as Malfurion lies in the Dreaming. Never forget this.', '', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8324, 'Myranda, do you have enough eyeballs to make another amulet? I have lost the old one.', 'Myranda, do you have enough eyeballs to make another amulet? I have lost the old one.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8325, 'I see some interesting characters come and go here at the port. Watch yourself, $n. It\'s never clear who to trust, what with all of the shady activity that goes on here...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8326, 'So you\'re here for the Warsong axes. Well, it\'s about time, $n. That crate has been sitting out there for days, now.$B$BFeel free to haul it away whenever you are ready.', '', 0, 0, 0, 1, 25, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8327, '', 'Let\'s go before they find out I\'m free!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8328, '', 'Look out! We\'re under attack!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8329, '', 'Thank you for helping me. I know my way back from here.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8330, 'Stonetalon Mountain has many perilous caves through out this region. Exploring these caves can hold many adventures for those willing to risk life for glory.$B$B$C, perhaps you seek riches that only following your heart and soul can garnish.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8331, '%s goes into a rage after seeing Misha fall in battle!', '%s goes into a rage after seeing Misha fall in battle!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8332, '', 'Please wait while I attempt to bind the shards together so that they will be whole once again.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8333, '', 'Ishnu... Oshalan thara dormil... Anu\'dorin...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8334, 'Fish so delicious. You come learn fish? Eat fish? Mmmmm...', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8335, 'I require training, Lumak.', 'I require training, Lumak.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8336, 'Yap, I can teach ye to fish.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8337, 'I require training, Grimnur.', 'I require training, Grimnur.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8338, '', 'Yes, I was able to reseal the relic, $n.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8339, '', 'Please, leave this place, $n. You know nothing of what troubles us...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8340, 'Where can I get Enchanted Thorium?', 'Where can I get Enchanted Thorium?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8341, 'Where can I find Crystal Restore?', 'Where can I find Crystal Restore?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8342, '', 'Enchanted thorium is a highly sought-after metal. You best chance of finding some of it is to speak with an artisan blacksmith or miner.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8343, '', 'Crystal Restore is a difficult item to acquire.$B$BI believe that it comes from Un\'Goro Crater, so you\'d best head there.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8344, 'We may have been suffering a drought but there\'s no shortage of meat! Care to browse my goods?', '', 0, 0, 0, 1, 6, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8345, 'I wish to browse your goods, Dirge.', 'I wish to browse your goods, Dirge.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8346, '', 'Can Zamja help you, $r?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8347, 'I require training, Zamja.', 'I require training, Zamja.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8348, 'I keep \'em fed, kid. Yep, Daryl Riknussun, at yer service.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8349, 'I require training, Daryl.', 'I require training, Daryl.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8350, 'You are a little gnat to the Foulweald! Die!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8351, 'No! You cannot be stronger than the Foulweald! No!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8352, 'Heeellp meee... ri... ribss... bro... broken.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8353, 'Dying... See the light... gloriouss...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8354, 'I... I think I see Blue...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8355, 'I\'m saved! Thank you, doctor!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8356, 'My entrails are leaking out! HELP!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8357, 'I\'m not gonna make it... death comes... ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8358, 'Goodbye, cruel world... I\'m leavin\' you today... goodbye... goodbye... goodbye...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8359, 'HOORAY! I AM SAVED!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8360, 'Brain encaved... mortar... bas... hed... leaking... dyin... g...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8361, 'Sweet, sweet embrace... take me... ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8362, 'The good doctor saves the day! HOORAY!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8363, 'I regret to inform you that you have failed your examination, doctor.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8364, 'I am an orc of medicine, $r. If you have come here to learn, I shall do my best to teach.', '', 0, 0, 0, 1, 2, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8365, 'I require training, Arnok.', 'I require training, Arnok.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8366, '', 'May this evil aura be banished from this artifact!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8367, '', 'I can feel the presence of evil leaving...', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8368, '', 'Are you a student of the medical sciences?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8369, 'I require training, Nissa.', 'I require training, Nissa.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8370, '', 'Why have you come here, $c?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8371, '', 'My people, they are as shattered as the relic you seek to repair.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8372, '', 'While nothing can change the past, time can heal all wounds. Thank you for showing me that it is possible, $n. I wish you well.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8373, 'Kel\'Thuzad? If you know that name at all, then perhaps you did read the book I sent you to get.$B$BUnfortunately, you\'re answer is incorrect.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8374, 'Gul\'dan. Not a name many have heard on Azeroth.$B$BUnfortunately, not the correct answer.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8375, 'An interesting response to my question... and the wrong one.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8376, 'Ah, Ner\'zhul. Not a name that should be uttered lightly, but I did ask you for an answer.$B$BAnd Ner\'zhul is the correct one.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8377, 'Kel\'Thuzad is my answer.', 'Kel\'Thuzad is my answer.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8378, 'Gul\'dan is my answer.', 'Gul\'dan is my answer.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8379, 'Kil\'jaeden is my answer.', 'Kil\'jaeden is my answer.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8380, 'Ner\'zhul is my answer.', 'Ner\'zhul is my answer.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8381, 'Ysera is my answer.', 'Ysera is my answer.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8382, 'Ah, Ysera the Dreamer.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8383, 'Neltharion is my answer.', 'Neltharion is my answer.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8384, 'Alexstrasza is my answer.', 'Alexstrasza is my answer.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8385, 'Malygos is my answer.', 'Malygos is my answer.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8386, 'Neltharion, hmm? As good an answer as any, yes?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8387, 'Alexstrazsa, the Life-Binder. A magnificent creature.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8388, 'Malygos, is it?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8389, 'Greetings, $c. Is there something I can help you with?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8390, 'My question to you is this, $c:$BWhich of the Aspects created by the titans became the nefarious creature known as Deathwing?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8391, 'It\'s always nice when someone wishes to speak to me. How are things with you, $c?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8392, 'Now, answer me this, $n:$BWhich creature became the Lich King before spreading the plague across Lordaeron?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8393, 'That is the wrong answer, $n. Be off with you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8394, 'I\'m afraid not, $n. Now, feel the shame you have brought upon yourself.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8395, '', 'In position!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8396, '', 'For the Great Mother!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8397, '', 'Warriors of the night... Assemble!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8398, '%s attacks Karang\'s Banner!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8399, 'Yeah, you know a few things! If you wanna\' learn more you\'re gonna\' have to talk to Georgio Bolero in Stormwind.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8400, 'The Thorium Brotherhood possesses the knowledge required to make the sacred arms and armaments of the Fire Lord, but we lack the manpower to obtain the materials required for the crafting.$B$BI, Lokhtos Darkbargainer, have been empowered by the Brotherhood to exchange the exalted recipes for materials gathered from the Molten Core.$B$BPresent your offer, $r.', '', 0, 0, 0, 1, 1, 25, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8401, 'Show me what I have access to, Lokhtos.', 'Show me what I have access to, Lokhtos.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8402, 'Want to make your demons more powerful? It\'ll cost you, but you\'ve come to the right place.', 'Want to make your demons more powerful? It\'ll cost you, but you\'ve come to the right place.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8403, 'You\'re no warlock and I\'ve no time for you. Begone!', 'You\'re no warlock and I\'ve no time for you. Begone!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8404, 'Show me your selection of grimoires.', 'Show me your selection of grimoires.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8405, '', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8406, 'You\'ve come for training in order to pass it on to your pets?', 'You\'ve come for training in order to pass it on to your pets?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8407, 'Ah friend, I only help hunters and their pets.', 'Ah friend, I only help hunters and their pets.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8408, 'Train me in the ways of the beast.', 'Train me in the ways of the beast.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8409, 'Fear lies in your heart, $N. When you truly are ready to face Vorsha the Lasher, come speak with me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8410, 'Now we must wait, $N. It won\'t be long before the naga realize what we have done.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8411, 'The Royal Apothecary Society has little patience for fools, as do I. Perhaps the next time you and I speak of my steeds, you will be considered exalted by the Undercity. Until then...', 'The Royal Apothecary Society has little patience for fools, as do I. Perhaps the next time you and I speak of my steeds, you will be considered exalted by the Undercity. Until then...', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8412, 'Be on your guard, $n!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8413, 'Perhaps we will get a moment to rest. But I will not give up until we have faced off against Vorsha!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8414, 'I only make my mounts available to those who are considered exalted to Orgrimmar and the Orcish race. Go prove yourself to us, and I\'ll make my mighty wolves available for your inspection.', 'I only make my mounts available to those who are considered exalted to Orgrimmar and the Orcish race. Go prove yourself to us, and I\'ll make my mighty wolves available for your inspection.', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8415, 'I\'m sorry $g mon : sis;, but I won\'t be sellin\' my raptors to someone who hasn\'t proven themselves to be exalted to the Darkspear tribe. When ya make a stand and show yourself to be no less than exalted to us, then I\'ll make my raptors available to ya.', 'I\'m sorry $g mon : sis;, but I won\'t be sellin\' my raptors to someone who hasn\'t proven themselves to be exalted to the Darkspear tribe. When ya make a stand and show yourself to be no less than exalted to us, then I\'ll make my raptors available to ya.', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8416, 'You are not known to my people. A kodo is a mighty steed, and it is the product of love and care by my people; such is not given onto strangers. Until the elders of Thunder Bluff consider you to be exalted, I cannot offer you the sale of a kodo.', 'You are not known to my people. A kodo is a mighty steed, and it is the product of love and care by my people; such is not given onto strangers. Until the elders of Thunder Bluff consider you to be exalted, I cannot offer you the sale of a kodo.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8417, 'Alas $c, you are not considered to be exalted to Darnassus and the night elves of Teldrassil. Only when you prove yourself to us through deeds - not just mere words - will I then make available my selection of mounts for your perusal.', 'Alas $c, you are not considered to be exalted to Darnassus and the night elves of Teldrassil. Only when you prove yourself to us through deeds - not just mere words - will I then make available my selection of mounts for your perusal.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8418, 'Stay back! Children should not be here! Be gone, child!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8419, 'Intruders! Attack the intruders!', 'Intruders! Attack the intruders!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8420, 'Steady, people. We\'ll raid soon enough.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8421, 'With luck, we\'ll gain some good scars today.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8422, 'These woods make my tusks itch...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8423, 'They say Duriel is a fierce night elf. It\'ll be a good fight!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8424, 'We\'ll be notching our axes after this.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8425, 'Stay calm, people. We don\'t want to give away our position.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8426, 'Plucky? I heard race master Kronkrider up on the the tower knows where he is.', 'Plucky? I heard race master Kronkrider up on the the tower knows where he is.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8427, 'You looking for plucky? Well, you didn\'t hear me say this, but... maybe if you ask Kronkrider up in the tower, he\'ll tell you...', 'You looking for plucky? Well, you didn\'t hear me say this, but... maybe if you ask Kronkrider up in the tower, he\'ll tell you...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8428, 'Sorry, but I am not disposed to sell horses to any individual who is not considered to be at least exalted to the Kingdom of Stormwind. These steeds are the finest you\'ll find anywhere, and we certainly wouldn\'t want them in the hands of our enemies.$B$BNot saying you\'re an enemy, of course... just earn the trust of Stormwind and then we can do business.', 'Sorry, but I am not disposed to sell horses to any individual who is not considered to be at least exalted to the Kingdom of Stormwind. These steeds are the finest you\'ll find anywhere, and we certainly wouldn\'t want them in the hands of our enemies.$B$BNot saying you\'re an enemy, of course... just earn the trust of Stormwind and then we can do business.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8429, 'Not so fast, $g guy : lady;... I\'ll not be handing out the reigns of a mighty ram steed to someone that\'s not a proven ally of the dwarves of Ironforge. Once you\'ve been recognized as exalted to Ironforge, I\'ll gladly show you what varieties of rams I have for sale.', 'Not so fast, $g guy : lady;... I\'ll not be handing out the reigns of a mighty ram steed to someone that\'s not a proven ally of the dwarves of Ironforge. Once you\'ve been recognized as exalted to Ironforge, I\'ll gladly show you what varieties of rams I have for sale.', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8430, 'Erm... I really don\'t know you that well, sorry. I mean, as an authorized Mechano-strider dealer, I really only can sell my mounts to those people who are considered exalted amongst the gnomes. If you ever are recognized as exalted to Gnomeregan, I\'d be happy to show you what I have to offer.', 'Erm... I really don\'t know you that well, sorry. I mean, as an authorized Mechano-strider dealer, I really only can sell my mounts to those people who are considered exalted amongst the gnomes. If you ever are recognized as exalted to Gnomeregan, I\'d be happy to show you what I have to offer.', 0, 0, 0, 24, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8431, 'You need to be exalted with the dwarves of Ironforge before I will teach you a riding skill, $c.', 'You need to be exalted with the dwarves of Ironforge before I will teach you a riding skill, $c.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8432, 'You need to be exalted with the Forsaken of the Undercity before I will teach you a riding skill, $c.', 'You need to be exalted with the Forsaken of the Undercity before I will teach you a riding skill, $c.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8433, 'You need to be exalted with the gnomes of the Gnomeregan Exiles before I will teach you how to pilot a Mechanostrider, $c.', 'You need to be exalted with the gnomes of the Gnomeregan Exiles before I will teach you how to pilot a Mechanostrider, $c.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8434, 'You need to be exalted with the humans of Stormwind before I will teach you a riding skill, $c.', 'You need to be exalted with the humans of Stormwind before I will teach you a riding skill, $c.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8435, 'You need to be exalted with the night elves of Darnassus before I will teach you a riding skill, $c.', 'You need to be exalted with the night elves of Darnassus before I will teach you a riding skill, $c.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
