INSERT INTO `broadcast_text` VALUES (8458, 'Overflowin\' with rats! Help a pirate out, matey!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8459, 'Do yer part in keepin\' the Deeprun Tram clean!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8460, 'Don\'t crowd me, people!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8461, 'Your own strength feeds me, $n!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8462, 'The little feller be hidin\' in one o\' them boxes, matey. G\'luck!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8463, 'Monty be me name an\' rats be me game.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8464, 'I\'m ready to find the squirrel, Monty.', 'I\'m ready to find the squirrel, Monty.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8465, 'Into the box me pretties! Thas it. One by one ye go.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8466, 'Winner! We be havin\' a winner here!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8467, 'The strongest hides come from those kodo that have laid themselves to rest in this wasteland.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8468, 'Your skills have surpassed mine, young one. Go see Una who has a shop in Thunder Bluff\'s central plateau. She can give you the guidance you seek.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8470, 'I am sorry but I can not train any more apprentices. Seek out Mak in Thunder Bluff. He will be able to teach you the basics.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8471, 'First you must prove yourself to me, then I will train you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8472, 'Now you are ready to learn the finer points of kodo hide tanning. Let us begin...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8473, '', 'Between the light and the dark, there is shadow... and those shadows have powers you have yet to see.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8474, '', 'Between the light and the dark, there is shadow... and those shadows have powers you have yet to see.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8475, 'You\'ll find no finer rat kabob this side of Khaz Modan!', '', 0, 0, 0, 1, 5, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8476, 'I wish to make a purchase, Nipsy.', 'I wish to make a purchase, Nipsy.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8477, 'You look like you could use some work, $g fella:lady;. You should board the tram and visit my brother, Monty, at the Ironforge Deeprun Tram depot. I\'m sure he has something you could do.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8478, 'Watch your back, stranger.', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8479, 'Ravenholdt Manor does not recognize you, $c.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8480, 'Brash. Master Ravenholdt does not like brash.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8481, 'It is good to see you again, $n.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8482, 'Greetings, $c. Have you spoken with Fahrad yet?', '', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8483, 'The Syndicate is made up of a bunch of hacks - disorganized, unskilled, and lazy... ', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8484, 'You have truly grown more cunning, $n.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8485, 'An honor to see you again, $n.', '', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8486, 'I believe Simone was looking for you earlier, $n. Have you spoken with her yet?', '', 0, 0, 0, 1, 6, 0, 1, 2, 0);
INSERT INTO `broadcast_text` VALUES (8487, '$G Master:Mistress; $n! Master Kang has been asking about you! Perhaps it is your time!', '', 0, 0, 0, 5, 25, 0, 1, 2, 0);
INSERT INTO `broadcast_text` VALUES (8488, 'Your presence steels our nerves, $G Master:Mistress; $n.', '', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8489, 'Tales of your heroics are heard throughout Ravenholdt Manor, $G Master:Mistress; $n.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8490, 'I am unworthy of this conversation, $G Master:Mistress;.', '', 0, 0, 0, 16, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8491, 'It is my dream to one day serve under your command, $G Master:Mistress; $n. That is, should you ever deem me worthy of such things...', '', 0, 0, 0, 1, 16, 0, 1, 2, 0);
INSERT INTO `broadcast_text` VALUES (8492, 'The Manor has been abuzz with talk of your arrival, $G Master:Mistress; $n. We are all honored and graced by your presence.', '', 0, 0, 0, 1, 16, 0, 1, 2, 0);
INSERT INTO `broadcast_text` VALUES (8493, 'You dare set foot inside Ravenholdt Manor, worm?', 'You dare set foot inside Ravenholdt Manor, worm?', 0, 0, 0, 14, 0, 0, 1, 0, 0);
INSERT INTO `broadcast_text` VALUES (8494, 'Making it out of here without a blade in your back or poison in your veins would be an accomplishment.', 'Making it out of here without a blade in your back or poison in your veins would be an accomplishment.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8495, 'You\'re shaping up nicely, $n. I\'ve heard good things...', 'You\'re shaping up nicely, $n. I\'ve heard good things...', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8496, 'You should talk to Winstone. I hear he\'s looking for thieves with your skill.', 'You should talk to Winstone. I hear he\'s looking for thieves with your skill.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8497, 'Marvelous. Simply marvelous. You are an inspiration to us all, $n.', 'Marvelous. Simply marvelous. You are an inspiration to us all, $n.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8498, 'I really envy you, $n. You do amazing work.', 'I really envy you, $n. You do amazing work.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8499, 'Your time has come, $n. Master Kang awaits...', 'Your time has come, $n. Master Kang awaits...', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8500, 'And to think, one day you might be the one that the stories of legend are about!', 'And to think, one day you might be the one that the stories of legend are about!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8501, 'I wept like a babe when I heard of your heroics. Master Ravenholdt would do well to have an apprentice like you.', 'I wept like a babe when I heard of your heroics. Master Ravenholdt would do well to have an apprentice like you.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8502, 'If I was not bound by duty to Master Ravenholdt, I would follow you to the ends of the world, $G Master:Mistress; $n. You are grace and cunning personified.', 'If I was not bound by duty to Master Ravenholdt, I would follow you to the ends of the world, $G Master:Mistress; $n. You are grace and cunning personified.', 0, 0, 0, 1, 16, 0, 1, 2, 0);
INSERT INTO `broadcast_text` VALUES (8503, '', 'May Elune walk with you, $c.  The Temple of the Moon has come to help the troubled citizens of Auberdine.', 0, 0, 0, 2, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8504, 'I\'m a test spirit healer.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8505, 'Why did you kill me, $n?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8506, 'Shoja\'my is over in front of the inn. Now move along, I\'m busy.', 'Shoja\'my is over in front of the inn. Now move along, I\'m busy.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8507, 'Pets to take care of? Just make sure you clean up after it! Look for Shoja\'my in front of the inn.', 'Pets to take care of? Just make sure you clean up after it! Look for Shoja\'my in front of the inn.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8508, 'The stable master', 'The stable master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8509, 'Go to the Valley of Honor and seek out Xon\'cha near the stables.', 'Go to the Valley of Honor and seek out Xon\'cha near the stables.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8510, 'Xon\'cha will stable your extra pets. Find him in the Valley of Honor at the stables between the arena and the Hunter\'s Hall. Now begone!', 'Xon\'cha will stable your extra pets. Find him in the Valley of Honor at the stables between the arena and the Hunter\'s Hall. Now begone!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8511, 'The stable master', 'The stable master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8512, 'Only a fool walks into a den of thieves unaware. That fool rarely walks out...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8513, 'Ah yes, it is Seikwa that you must find. I believe I saw her last standing out in front of the inn.', 'Ah yes, it is Seikwa that you must find. I believe I saw her last standing out in front of the inn.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8514, 'Seikwa can take care of your animal friends when you have the need. Seek her out in front of the inn.', 'Seikwa can take care of your animal friends when you have the need. Seek her out in front of the inn.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8515, 'The stable master', 'The stable master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8516, 'It is the assistance of Bulrug that you need. You will find him outside on the lower rise standing between the inn and the Thunder Bluff Bank.', 'It is the assistance of Bulrug that you need. You will find him outside on the lower rise standing between the inn and the Thunder Bluff Bank.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8517, 'The concern that you show for the welfare of your animal companions is commendable. Bulrug, on the lower rise, can help you with that. Seek him out between the bank and the inn.', 'The concern that you show for the welfare of your animal companions is commendable. Bulrug, on the lower rise, can help you with that. Seek him out between the bank and the inn.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8518, 'The stable master', 'The stable master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8519, 'Morganus the stable master can be found out in front of the stables strangely enough.', 'Morganus the stable master can be found out in front of the stables strangely enough.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8520, 'Not too clever are you? Hmm, if I were a stable master perhaps I might be in the vicinity of say, the stable? Look for Morganus over there.', 'Not too clever are you? Hmm, if I were a stable master perhaps I might be in the vicinity of say, the stable? Look for Morganus over there.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8521, 'The stable master', 'The stable master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8522, 'Anya Maulray at inn on upper ring of Trade Quarter.', 'Anya Maulray at inn on upper ring of Trade Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8523, 'Us want lots of pets. Anya Maulray help you take care of them. You find her at inn, upper ring of Trade Quarter.', 'Us want lots of pets. Anya Maulray help you take care of them. You find her at inn, upper ring of Trade Quarter.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8524, 'The stable master', 'The stable master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8525, 'Stable Master', 'Stable Master', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8526, 'You can trust any pets you have to be well taken care of by Alassin. He resides in the Cenarion Enclave where the hunters gather.', 'You can trust any pets you have to be well taken care of by Alassin. He resides in the Cenarion Enclave where the hunters gather.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8527, 'For many hundreds of years Alassin has cared for the animals that our hunters befriend. His care of animals is highly sought for by the hunters around here. If you seek him as well, then look for him in the Cenarion Enclave.', 'For many hundreds of years Alassin has cared for the animals that our hunters befriend. His care of animals is highly sought for by the hunters around here. If you seek him as well, then look for him in the Cenarion Enclave.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8528, 'We are all pieces of a much larger puzzle, $n. Just as you guide the piece of a puzzle to its destination, so too are we guided by another.$B$BAnd perhaps they who guide us are themselves guided by an even greater force.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8529, 'Stable Master', 'Stable Master', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8530, 'Ah, gentle Seriadne. All animals are safe while in her care. You can find her just outside the Inn in Dolanaar.', 'Ah, gentle Seriadne. All animals are safe while in her care. You can find her just outside the Inn in Dolanaar.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8531, 'You seek someone who cares for animals? Then you should look for Seriadne outside the Inn in Dolanaar. She is the one that you seek.', 'You seek someone who cares for animals? Then you should look for Seriadne outside the Inn in Dolanaar. She is the one that you seek.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8532, 'Good ol\' Erma, she\'s been taking care of the local animals for years now. I\'m sure she\'d be happy to take care of any you\'d like to put up. She can be found right outside of the Lion\'s Pride Inn.', 'Good ol\' Erma, she\'s been taking care of the local animals for years now. I\'m sure she\'d be happy to take care of any you\'d like to put up. She can be found right outside of the Lion\'s Pride Inn.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8533, 'Erma does all the caring for animals around here. She\'s right outside the Lion\'s Pride Inn if you need to speak with her.', 'Erma does all the caring for animals around here. She\'s right outside the Lion\'s Pride Inn if you need to speak with her.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8534, 'Stable Master', 'Stable Master', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8535, 'Why did you kill me, $n?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8536, 'Stable Master', 'Stable Master', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8537, 'Jenova Stoneshield over in the Dwarven District is the one you\'re looking for. She tends to hunters\' pets while they seek training from her father.', 'Jenova Stoneshield over in the Dwarven District is the one you\'re looking for. She tends to hunters\' pets while they seek training from her father.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8538, 'If you need to house your pet while you\'re training another, then you should talk to Jenova Stoneshield over in the Dwarven District.', 'If you need to house your pet while you\'re training another, then you should talk to Jenova Stoneshield over in the Dwarven District.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8539, 'Stable Master', 'Stable Master', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8540, 'Well $glad : lass;, ye can find Shelby Stoneflint right outside the Thunderbrew Distillery in Kharanos. She\'ll take good care of any pets ye might have.', 'Well $glad : lass;, ye can find Shelby Stoneflint right outside the Thunderbrew Distillery in Kharanos. She\'ll take good care of any pets ye might have.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8541, 'Shelby Stoneflint houses a good deal of pets for the local hunters. She can be found over by the Thunderbrew Distillery if ye need her to look after a pet.', 'Shelby Stoneflint houses a good deal of pets for the local hunters. She can be found over by the Thunderbrew Distillery if ye need her to look after a pet.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8542, 'Stable Master', 'Stable Master', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8543, 'Taking care of animals is something that Ulbrek Firehand takes quite seriously. He usually hangs out with the hunters at the Hall of Arms if ye want him to look after a pet or two.', 'Taking care of animals is something that Ulbrek Firehand takes quite seriously. He usually hangs out with the hunters at the Hall of Arms if ye want him to look after a pet or two.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8544, 'Over in the Hall of Arms you can find our resident stable master, Ulbrek Firehand. He\'ll make sure any pets ye have are well fed and rightly cared for.', 'Over in the Hall of Arms you can find our resident stable master, Ulbrek Firehand. He\'ll make sure any pets ye have are well fed and rightly cared for.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8545, 'You think you\'ve won already? Perhaps you\'ll need another lesson in pain!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8546, 'Help! Guards! It\'s going to step on me!', 'Help! Guards! It\'s going to step on me!', 0, 0, 13, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8547, 'I got something for you, $n. Looks important.', 'I got something for you, $n. Looks important.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8548, 'May I have it, please.', 'May I have it, please.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8549, 'Welcome to SI:7 headquarters, $n.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8550, '$n, this came for you this morning.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8551, 'Messengers from Ravenholdt delivered this letter to me this morning. It is addressed to you, $n.', 'Messengers from Ravenholdt delivered this letter to me this morning. It is addressed to you, $n.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8552, 'Take heart! Pain goes away and $gladies love men with scars:men love women with scars;!', 'Take heart! Pain goes away and $gladies love men with scars:men love women with scars;!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8553, 'I require aid.', 'I require aid.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8554, 'Show strength to your enemy, and we will prevail!', 'Show strength to your enemy, and we will prevail!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8555, 'This will be a tough fight, $n. Follow me closely!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8556, 'This is the brazier, $n. Put it out. Vorsha is a beast, worthy of praise from no one!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8557, 'Bah, it\'s not here. I wonder where I left it last...', '', 0, 0, 0, 5, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8558, 'You have my deepest gratitude, $N. I thank you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8559, 'Now where did I put that companion document?', '', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8560, 'Quite the riveting tale. Now I know why there was so much dust on that book.', '', 0, 0, 0, 274, 11, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8561, 'So many bookshelves, so little time...', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8562, 'I should talk to Dewey about some sort of notation system to these keep books better organized.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8563, 'A book by a goblin on gnomish military victories... and all the pages are blank. Figures.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8564, 'I am going to patrol the area for a while longer and ensure that things are truly safe.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8565, 'Please return to Zoram\'gar and report our success to the Warsong Runner.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8566, 'Are you sure that you are ready? If we do not have a group of your allies to aid us, we will surely fail.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8567, '$N, I sense that the great beast draws near...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8568, 'Look, just at the water\'s surface!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8569, 'We have done it, $N!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8570, '', 'You seek to lure me from my clutch? You shall pay for your insolence!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8571, 'Yes, $c? Are you reporting in?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8572, 'COME FORTH, MY SERVANTS! DEFEND YOUR MASTER!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8573, 'YOU CANNOT DEFEAT THE LIVING FLAME! COME YOU MINIONS OF FIRE! COME FORTH, YOU CREATURES OF HATE! YOUR MASTER CALLS!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8574, 'Crush them!!!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8575, 'RUN THEM THROUGH, SOLDIERS!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8576, 'Push them back to their base! Let not a single Alliance soldier remain living on this battlefield!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8577, 'Rally around your Lieutenants and Commanders! Protect our bases!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8578, 'Major Domo Enabled.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8579, 'Who controls the Coldtooth Mine?', 'Who controls the Coldtooth Mine?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8580, 'The Coldtooth Mine is owned by the Kobolds.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8581, 'The Coldtooth Mine is owned by the Alliance.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8582, 'The Coldtooth Mine is owned by the Horde.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8583, 'The Coldtooth Mine lies in Horde territory, in the mountains north of the Frostwolf Graveyard.$B$BIt is controlled by the Alliance.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8584, 'The Coldtooth Mine lies in Horde territory, in the mountains north of the Frostwolf Graveyard.$B$BIt is controlled by the Horde.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8585, 'The Coldtooth Mine lies in Horde territory, in the mountains north of the Frostwolf Graveyard.$B$BIt is controlled by the Kobolds.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8586, 'Who controls the Irondeep Mine?', 'Who controls the Irondeep Mine?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8587, 'The Irondeep Mine is in Alliance territory, north of the Stormpike Graveyard.$B$BIt is controlled by the Alliance.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8588, 'The Irondeep Mine is in Alliance territory, north of the Stormpike Graveyard.$B$BIt is controlled by the Horde.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8589, 'The Irondeep Mine is in Alliance territory, north of the Stormpike Graveyard.$B$BIt is controlled by the Troggs.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8590, 'This monument is dedicated to all those who have fallen in the protection of Stormwind. Our people have weathered unbelievable hardships to retain their freedoms and to control their own destinies. It is here we remember every sacrifice our citizens have made during the first war that riddled our people.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8591, 'The Irondeep Mine is owned by the Alliance.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8592, 'The Irondeep Mine is owned by the Horde.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8593, 'The Irondeep Mine is owned by the Troggs.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8594, 'The Alliance has taken the Coldtooth Mine!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8595, 'The Alliance has taken the Irondeep Mine!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8596, 'The Horde has taken the Coldtooth Mine!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8597, 'The Horde has taken the Irondeep Mine!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8599, 'The troggs are retaking the Irondeep Mine!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8600, 'The kobolds are retaking the Coldtooth Mine!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8601, 'In Alterac Valley, battles rage and equipment is in short supply.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8602, 'I\'ve reached my supply needs for now.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8603, 'Hook me up!', 'Hook me up!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8604, '', 'Cover me, soldier! I have to make it back to our base and get the fleet prepped for an aerial assault!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8605, '', 'WOOT! Made it!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8606, '', 'There\'s no time to waste, trooper. I must get back to base immediately! Cover me!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8607, '', 'Let\'s get down to business!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8608, 'Guse? Jeztor? Are they... flat spin?? Seas of Southshore??', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8609, 'I need to get back to my ride. Search and destroy imminent! Cover me! On my six! GO GO GO!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8610, 'I come for you, puny Alliance!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8611, 'Good work, soldier. Now we light the fires and spite their ire!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8612, '%s let\'s loose a menacing hoot.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8613, '', 'Soldiers of Frostwolf, come to my aid! The time has come to unleash the fury of the elements upon them!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8614, 'I\'ve reached my supply needs for now.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8615, 'This expedition is a terrible drain on military supplies.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8616, 'WHO DARES SUMMON LOKHOLAR?', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8617, 'The blood of a thousand Alliance soldiers I shall spill... None shall stand against the might of the Ice Lord!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8618, 'I drink in your suffering, mortal. Let your essence congeal with Lokholar!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8619, '%s\'s death wail has stirred the nearby silithid hive!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8620, '', '%s\'s death wail has stirred the nearby silithid hive!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8621, '', 'The blood of our enemies shall be spilt upon the battlefield. The great elementals shall answer our call and crush all that stand in our way.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8622, '', '%s cackles.', 2, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8623, '', 'The Ice Lord will consume the entire Stormpike army!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8624, 'Answer cannot be seen through the twisting nether.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8625, '', 'Watch as the blood seeps into the snow, children. The elemental will be bound to us!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8626, '', 'It is done! The Ice Lord has arrived! Bow to the might of the Horde, fools!', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8627, 'Forget about it!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8628, 'It is not worth answering.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8629, 'Is that some kind of a joke?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8630, 'Do not summon me again unless you have a real question to ask, ok?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8631, '%s is enraged!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8632, '', 'Channel your energies into the Altar!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8633, 'It\'s just so horrible. Our three top guns were shot down over Alliance territory during an air assault Nobody knows if they\'re dead or alive!$B$BThis is a terrible blow to our air strike capabilities.', '', 0, 0, 0, 1, 1, 5, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8634, 'I need a ride out of here, Martin.', 'I need a ride out of here, Martin.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8635, '', 'Good work, soldier. I need to get back to base camp! Will you cover me?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8636, 'I got your back, Guse.', 'I got your back, Guse.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8637, '', 'About damn time someone came. What took you so long? Ah, nevermind. Cover me, soldier. I gotta make it back to base!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8638, 'You can count on me, Jeztor.', 'You can count on me, Jeztor.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8639, '', 'You do not belong in these gardens.  Your body shall nourish our lovely creations!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8640, '', 'Nothing must befoul the gardens! You must be destroyed!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8641, 'How much more blood is needed, Commander?', 'How much more blood is needed, Commander?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8642, '', 'There is much work left to be done, $n! Continue to strike down our enemies!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8643, 'We must be close, Commander. I can feel the elements stirring.', 'We must be close, Commander. I can feel the elements stirring.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8644, '', 'Yes, $n. The elements are stirring. Soon, we will unleash their might upon our enemies. They shall reel from the blows!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8645, 'I sense a great power among us, Commander. Is the spell ready?', 'I sense a great power among us, Commander. Is the spell ready?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8646, '', 'I have made contact, $n. It is Lokholar the Ice Lord. He has deemed our cause worthy - our earth bound sacrifice sufficient. He comes soon... prepare yourself.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8647, 'I need a few good wing men to get me out of here safely and back to base.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8648, 'I will be your wingman!', 'I will be your wingman!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8649, 'Leave this sacred place or perish!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8650, 'For the Legion! For Kil\'Jaeden!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8651, 'Kazzak is supreme!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8652, 'Holds mistletoe over $g his : her; head', 'Holds mistletoe over $g his : her; head', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8653, 'The pages of the journal seem to be filled with information about trades, buyers and other miscellaneous business related events.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8654, '', 'Never look the Commander directly in the eyes.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8655, '', 'We are under direct orders from Cairne, $r. Do not interfere with the Commander\'s task.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8656, '', 'The Shaman Commander is not to be trifled with, $r.', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8657, '', 'Soon, Lokholar shall rise... Soon... Very, very soon...', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8658, 'You continue to thumb through the journal looking for any information that might help in your search for the stolen chest.$B$BTowards the final pages of the book, in red pencil, you see a notation from James along the side of the page. It says, \"Old home near Stone Cairn Lake... gnolls will work perfectly as bodyguards.\"$B$BThere is no reason to suggest this is where the chest is, but there doesn\'t seem to be any other clues.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8659, '', 'The fleet is ready, $n. You must take this beacon and place it at the eastern crater of the central battlezone. Defend the beacon from attackers until the fleet is able to lock on to its coordinates!', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8660, '** CONFIRMS TARGET LOCK. WAR RIDER UNIT INCOMING. AERIAL ASSAULT IMMINENT. **', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8661, '** PLANTED. CONFIRMING WAR RIDER LOCK. STAND BY. T-MINUS 60 SECONDS. **', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8662, '** SERVOS DESTROYED. UNIT TARGETTING CORRUPT. SHUT DOWN IMMINENT. **', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8663, '%s lets loose a shrill cry.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8664, 'I am Hydraxis, Duke of the Waterlords. You are not an elemental and so cannot understand our struggle, but perhaps we can strike a deal that will benefit us both...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8665, '$n, it is good to see you. Our war with the other elemental lords yet rages, but your efforts help ensure our victory.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8666, 'I require a vial of aqual quintessence, Hydraxis, for I go to the Molten Core to extinguish a rune of the Firelords.', 'I require a vial of aqual quintessence, Hydraxis, for I go to the Molten Core to extinguish a rune of the Firelords.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8667, 'I\'ll get that beacon to the crater safely, Guse. You can count on me!', 'I\'ll get that beacon to the crater safely, Guse. You can count on me!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8668, '', 'The fleet is ready, $n. You must take this beacon and place it at the western crater of the central battlezone. Defend the beacon from attackers until the fleet is able to lock on to its coordinates!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8669, 'Consider it done, Jeztor. Give me a beacon.', 'Consider it done, Jeztor. Give me a beacon.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8670, 'The time has come to prove your worth as a wing man, $n. Take this beacon and plant it dead center of the Snowfall graveyard. Defend it until my fleet arrives.$B$B<Mulverick salutes.>', '', 0, 0, 0, 1, 1, 113, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8671, 'I\'m ready to plant the beacon, Mulv. Give me a beacon.', 'I\'m ready to plant the beacon, Mulv. Give me a beacon.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8672, 'Yeee hawww! Mulverick\'s fleet is ready to rock. All able bodied Horde soldiers need request a beacon and try their damndest to plant it so that my War Riders can home in for the aerial assault.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8673, '', 'War Riders are now ready! Soldiers of the Horde, I need your assistance.', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8674, '', 'Jeztor\'s Riders are ready! Soldiers of the Horde, report for duty!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8675, '%s springs forth to defend its hive!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8676, '%s springs forth to defend its hive!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8677, 'The Earth Mother watch over us; under cover of the night as in the day. Let this fire illuminate both our bodies and spirits and remind us of our honored ancestors, who are ever present and guiding our way.\n', '', 0, 0, 3, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8678, 'Nothing better then tinkering! If you need supplies I got it!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8679, 'Show these savages no mercy, soldiers! They\'ll give none to you!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8680, 'Withdraw your troops, brother, and I promise you a swift second death!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8681, 'Rally around your Lieutenants and Commanders, soldiers! Keep the savages at bay!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8682, 'Show our enemies your resolve, soldiers! Crush them underfoot! Under steel!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8683, 'FER THE KING! Push \'em back, soldiers!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8684, 'Surrender an\' we\'ll let ye live, heathens! Jus\' put down yer arms an\' march!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8685, 'Sending ground assaults takes huge amounts of supplies!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8686, '$n, what are you waiting for? Find Field Marshal Teravaine in the Field of Strife and give him the assault orders. Once he gets the orders, he and his troops will charge into the Horde and take no prisoners!', '$n, what are you waiting for? Find Field Marshal in the Field of Strife and give him the assault orders. Once he gets the orders, he and his troops will charge into the Horde and take no prisoners!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8687, 'Give me the orders! I will deliver them to Teravaine in the Field of Strife!', 'Give me the orders! I will deliver them to Teravaine in the Field of Strife!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8688, 'The Eye sees all within its sphere of influence, $c. What is it that you wish to see?', '', 0, 0, 0, 1, 6, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8689, 'The orders are given! Move out, and show them what we\'re made of!', 'The orders are given! Move out, and show them what we\'re made of!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8691, 'Thank you, $n. I\'ll mobilize my people right now!', 'Thank you, $n. I\'ll mobilize my people right now!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8692, 'I wish to see my enemies, Ryson. Show them to me.', 'I wish to see my enemies, Ryson. Show them to me.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8693, 'Take it! Return to me should the Eye\'s power falter.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8694, 'If you\'re looking for information that the Cenarion Circle might have archived somewhere, then I\'m the one to talk to. Then again, you need to know what information you\'re looking for first before I can really help you.$B$BSee how that works? Brilliant!', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8695, 'Don\'t listen to the hearts of the world - the answer lies within. You know the truth.', '', 0, 0, 0, 274, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8696, 'The past lives in these wastes, $c. We must be careful to not awaken it.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8697, 'The power of the Eye is unquestionable, Ryson, but what else can it do?', 'The power of the Eye is unquestionable, Ryson, but what else can it do?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8698, 'The power of the Eye is unquestionable, Ryson, but what else can it do?', 'The power of the Eye is unquestionable, Ryson, but what else can it do?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8699, 'You have only just begun to see the Eye\'s power, $c. If you wish to facilitate the abilities of the Eye, then there is a task that you must complete.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8700, 'What is this task?', 'What is this task?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8701, 'Take this beacon and advance upon the enemy\'s base. Make your way to the outside of their main stronghold and place the beacon in the center of the courtyard. Defend the beacon from attackers until the Eye has locked on the beacon\'s coordinates.$B$BOnce this is done, all activity outside the enemy\'s base will be viewable from here.$B$BWill you take on this task?', '', 0, 0, 0, 1, 1, 6, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8702, 'Absolutely. Give me a beacon, Ryson.', 'Absolutely. Give me a beacon, Ryson.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8703, '$n, you are ready for the ultimate task. The fiery runes etched on the floor of the molten core are conduits to the realm of fire, and their presence is both an affront to us and a source of power for the Firelords. They must be extinguished!$B$BTo extinguish a rune, take a vial of aqual quintessence. Pour it on the rune and its flame will go out, rendering it useless and weakening our enemy!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8704, 'Welcome to the Undercity Guild Registry. What do you want?', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8705, 'Welcome to the Cenarion Circle, $c. How may I assist you?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8706, 'I need a Scrying Scope to view the Globe of Scrying, Ryson.', 'I need a Scrying Scope to view the Globe of Scrying, Ryson.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8707, 'Speak with Hydraxis.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8708, 'You have no business with me, little one.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8709, 'Speak not your vulgar language to me! Only Hydraxis can stand it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8710, 'Greetings, little one. You have business with the Waterlords?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8711, 'Tales of your exploits spread. Your name is not unknown to us, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8712, 'You have made our enemies your own. I will gladly share waters with you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8713, 'Perhaps you will tell me of your deeds, $n. I would be honored to know them.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8714, 'Our lord Hydraxis speaks highly of you. My water is your water, friend.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8715, 'When you next go to the Molten Core, kill a Firelord for me, will you?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8716, 'I don\'t think you want to learn anything I can teach. Might want to find a $c trainer to help you out.', 'I don\'t think you want to learn anything I can teach. Might want to find a $c trainer to help you out.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8717, 'You look like the type I should watch my coin purse around. Something I can help you with?', 'You look like the type I should watch my coin purse around. Something I can help you with?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8718, 'Upgrade to seasoned units!', 'Upgrade to seasoned units!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8719, 'Upgrade to veteran units!', 'Upgrade to veteran units!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8720, 'I see that you have found the eye.$B$B<Master Ryson grins.>$B$BWell, give it to me!', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8721, 'Take it! This thing is probably more trouble than its worth.', 'Take it! This thing is probably more trouble than its worth.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8722, 'The All Seeing Eye must be recovered!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8723, 'Upgrade to champion units!', 'Upgrade to champion units!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8724, 'Lets get back to your base!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8725, 'I am greatly indebted to you, $c. Perhaps I can help your cause.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8726, 'I was searching for materials to build my latest and greatest invention, the portable remote control shredder unit, when these trolls clubbed me and dragged me into the depths of their cave.$B$BReturn me to safety and I will lend my aid to your cause, $r.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8727, 'Let\'s go, goblin!', 'Let\'s go, goblin!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8728, 'Upgrade to seasoned units!', 'Upgrade to seasoned units!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8729, 'Upgrade to veteran units!', 'Upgrade to veteran units!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8730, 'Upgrade to champion units!', 'Upgrade to champion units!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8731, '', 'This land is tainted. Ivus must be called!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8732, '', 'Stormpike soldiers, aid and protect us! The Forest Lord has granted us his protection. The gateway must now be opened!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8733, '', 'Come, brothers.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8734, '', 'Onward!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8735, '', 'We must focus our thoughts upon the Circle of Calling if the Forest Lord is to come! We must hurry! Concentrate your energies!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8736, 'Wicked, wicked, mortals! The forest weeps. The elements recoil at the destruction. Ivus must purge you from this world!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8737, 'I come to raze your bases, Frost Wolf clan. Ivus punishes you for your treachery!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8738, 'Ancients, rise!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8739, 'Your base is forfeit, mortals! From its ashes shall rise life!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8740, 'Your base is forfeit, puny mortals!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8741, 'Our seasoned units are leaving the field. We need more supplies!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8742, 'Our veteran units are leaving the field. We need more supplies!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8743, 'Our champion units are leaving the field. We need more supplies!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8744, 'Seasoned units are entering the battle!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8745, 'Veteran units are entering the battle!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8746, 'Champion units are entering the battle!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8747, 'We have been sent here from Darnassus to aid the Stormpike in their time of need.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8748, 'The Arch Druid believes that Ivus the Forest Lord will come once he is made aware of the destructive forces within this valley.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8749, 'It is not for us to judge these atrocities. Ivus will pass judgement once he arrives.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8750, '', 'My faith guides my actions, $r. I know that Ivus will come.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8751, 'Our seasoned units are leaving the field. We need more supplies!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8752, 'Our veterans are leaving the field. We need more supplies!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8753, 'Our champion units are leaving the field. We need more supplies!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8754, 'Seasoned units are taking to the field!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8755, 'Our veterans are entering the field!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8756, 'Our champion units are equipped! Beware, alliance!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8757, 'Have you made contact with Ivus, Arch Druid?', 'Have you made contact with Ivus, Arch Druid?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8758, '', 'I have been lost in the Dream. I sense his lifeforce but my powers have not yet grown strong enough to make contact.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8759, 'The air is abuzz with energy, Arch Druid. What is the status of the Forest Lord?', 'The air is abuzz with energy, Arch Druid. What is the status of the Forest Lord?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8760, '', 'Yes. $n. I have located the Forest Lord. We must redouble our efforts! More crystals are needed!', 0, 0, 0, 1, 5, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8761, 'The forest shakes and shudders! The winds howl! Does the Forest Lord approach?', 'The forest shakes and shudders! The winds howl! Does the Forest Lord approach?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8762, '', 'The Forest Lord is aware of the destruction in this valley, $n. Soon we must make our way to the Circle of Calling. Prepare yourself!', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8763, 'I was shot down by that reckless fool, Mulverick. I fear that if I do not make it back to base, all will be lost! Mulverick and his squad of War Riders must be stopped!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8764, 'I got your back, Ichman. GO GO GO!', 'I got your back, Ichman. GO GO GO!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8765, 'I can\'t believe it... I was doing a routine recon mission over the central DMZ when all hell broke loose. I saw the Horde Wing Commander, Mulverick, take out both Ichman and Slidore! Anger took hold of me and I broke from my wing man and drove that bastard Mulverick down. Unfortunately, he clipped my gryphon before he crashed, forcing me down as well. I ended up captured by these savages.$B$BI have to get back to base! Help!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8766, 'I got you covered, Vipore. Move out!', 'I got you covered, Vipore. Move out!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8767, 'I heard their mocking laughter as my gryphon crashed and burned, $c.$B$B\"Slidore, you stink!\" They shouted.$B$BNobody talks to Slidore that way! Help me get out of here so that I can show them who the real stinker is!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8768, 'I got your back, Slidore, but to be honest, you do stink. Take a shower, man.', 'I got your back, Slidore, but to be honest, you do stink. Take a shower, man.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8769, 'Cover me, I\'m making a break for it!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8770, 'We\'ve got no time to waste. This is a really long run! Cover me!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8771, 'Just get me close enough to our base and I can channel a teleport!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8772, 'This is far enough! Meet me back at base!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8773, 'You\'re going down, Mulv!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8774, '', 'I have orders here for an attack on alliance forces in the middle fields.  I need someone to get them to the assault leader.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8775, '', 'It\'s a cold, bloody battle we wage in Alterac Valley.  It\'s perfect.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8776, 'Yes, I will deliver the orders to Warmaster Garrick!', 'Yes, I will deliver the orders to Warmaster Garrick!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8777, '', 'This forest can be confusing! Be careful out there.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8778, 'Season\'s savings to you and yours!', 'Season\'s savings to you and yours!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8779, 'Fresh from our farm to your plate - it\'s Smokywood Pastures wholesome goodness!', 'Fresh from our farm to your plate - it\'s Smokywood Pastures wholesome goodness!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8780, '\'Tis the season for great bargains! Fah la la la la!', '\'Tis the season for great bargains! Fah la la la la!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8781, 'Have a wondrous Feast of Winter Veil! Start it off right with treats from Smokywood Pastures!', 'Have a wondrous Feast of Winter Veil! Start it off right with treats from Smokywood Pastures!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8782, 'Be sure to tell Greatfather Winter what secret surprise you\'d like to get this Winter Veil!', 'Be sure to tell Greatfather Winter what secret surprise you\'d like to get this Winter Veil!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8783, 'Don\'t let the jingle of the bells take away from the jingle in your pocket! Buy Smokywood Pastures treats for your Winter Veil festivities!', 'Don\'t let the jingle of the bells take away from the jingle in your pocket! Buy Smokywood Pastures treats for your Winter Veil festivities!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8784, 'If I had some chestnuts, you\'d better believe I\'d have them roasting over an open fire. Mmmm!', 'If I had some chestnuts, you\'d better believe I\'d have them roasting over an open fire. Mmmm!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8785, 'Don\'t forget to stock up on wrapping paper this holiday season! Nothing says \"Wondrous Winter Veil\" like wrapped armors and weapons for your friends and family.', 'Don\'t forget to stock up on wrapping paper this holiday season! Nothing says \"Wondrous Winter Veil\" like wrapped armors and weapons for your friends and family.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8786, 'Let me browse your seasonal fare.', 'Let me browse your seasonal fare.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8787, 'Double time!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8788, '%s arming. T-minus 60 seconds.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8789, '%s armed. Aerie Gryphon en route.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8790, '%s is destroyed. Aerial assault unable to confirm strike point. Mission aborted.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8791, 'Soldiers of Stormpike, come to my aid! The beacon must be planted.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8792, 'Pay attention, soldier! You need to take this beacon to the Snowfall graveyard and plant it! Protect it long enough so that my gryphon can lock on.$B$BIf my gryphon gets a solid strike location, Mulv and his crew will be humiliated!', '', 0, 0, 0, 1, 1, 11, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8793, 'I\'m your man, Ich. Give me a beacon!', 'I\'m your man, Ich. Give me a beacon!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8794, 'My flight is ready! Soldiers come to my aid!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8795, '$n! Finally!$B$BYou must take this beacon and plant it in the eastern crater of the central battle grounds. My flight will attack when the beacon beams its coordinates!', '', 0, 0, 0, 1, 5, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8796, 'Give me the beacon, Slidore! Also, you still stink!', 'Give me the beacon, Slidore! Also, you still stink!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8797, 'Their spirits are restored! The gryphons are ready to strike! Come, soldiers! Come to my side!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8798, 'The gryphons are ready to strike, $n! You must take this beacon and plant it in the western crater of the central battlegrounds.$B$BDo this and the battle is ours!', '', 0, 0, 0, 1, 5, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8799, 'I am ready to rock, Vipore! Give me a beacon.', 'I am ready to rock, Vipore! Give me a beacon.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8800, 'We now hold the wisdom of the Winterax, $n. This power can be yours for a short time should you so desire.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8801, 'Grant me the wisdom of Winterax!', 'Grant me the wisdom of Winterax!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8802, 'Our units are not upgraded, and I don\'t have enough supplies to upgrade them.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
