INSERT INTO `broadcast_text` VALUES (8808, 'Our units are upgraded to Champions, but I can still collect more supplies.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8809, 'I cannot store any more supplies. I have all I can handle!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8810, 'Here to learn how to wield a new weapon, or just to waste my time?', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8811, 'I\'d like some weapon training', 'I\'d like some weapon training', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8812, 'What can other weapon masters teach?', 'What can other weapon masters teach?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8813, 'Ansekhwa, the tauren weapon master, is on Thunder Bluff\'s lower rise. He can train one and two-handed maces, staves and guns.\n\nHanashi, standing next to me here, can instruct in the use of bows, one and two-handed axes, staves and all manner of thrown weapons.\n\nArchibald, the undead weapon master in the War Quarter of the Undercity, can teach the use of crossbows, daggers, one and two-handed swords and polearms.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8814, 'Who controls the Stormpike Graveyard?', 'Who controls the Stormpike Graveyard?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8817, 'The Stormpike Graveyard is cut into the mountains, across the bridge east of Dun Baldar.$B$BThe Alliance currently controls it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8818, 'The Stormpike Graveyard is cut into the mountains, across the bridge east of Dun Baldar.$B$BThe Horde currently controls it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8820, 'Who controls the Snowfall Graveyard?', 'Who controls the Snowfall Graveyard?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8821, 'The Snowfall Graveyard lies high atop a hill in the Field of Strife, east of Winterax Hold.$B$BThe Alliance currently controls it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8823, 'The Snowfall Graveyard lies high atop a hill in the Field of Strife, east of Winterax Hold.$B$BThe Horde currently controls it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8824, 'Who controls the Frostwolf Graveyard?', 'Who controls the Frostwolf Graveyard?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8825, 'The Frostwolf Graveyard is along the side of the road, not far north of Frostwolf Village.$B$BThe Alliance currently controls it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8826, 'The Frostwolf Graveyard is along the side of the road, not far north of Frostwolf Village.$B$BThe Horde currently controls it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8830, 'The Fostwolf Graveyard is owned by the Alliance.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8831, 'The Fostwolf Graveyard is owned by the Horde.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8832, 'The Snowfall Graveyard is owned by the Alliance.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8833, 'The Snowfall Graveyard is owned by the Horde.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8834, 'A wondrous Winter\'s Veil to you... uh... little $G boy : girl;. Good children get special, um, discounts at any local Smokywood Pastures vendor. Yum.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8835, 'I have a pretty good idea what ol\' Greatfather Winter\'s gonna be doing when this shift is over. I got a bottle of Winter Veil cheer right here in my bag, and I intend to get to the bottom of it.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8836, 'No... you can\'t sit on my lap.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8837, 'Yeah, sure, go ahead and tell ol\' Greatfather Winter what you\'d like for this Winter\'s Veil.$B$BHo ho hurry up.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8838, 'The Stormpike clan bows to no one, especially the horde!', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8839, 'Your attacks are weak! Go practice on some rabbits and come back when you\'re stronger.', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8840, 'Is that the best you can do?', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8841, 'It\'ll take more than you rabble to bring me down!', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8842, 'We will not be swayed from our mission!', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8843, 'We, the Alliance, will prevail!', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8844, 'You cannot defeat the Frostwolf clan!', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8845, 'Your spirits are weak, and your blows are weaker!', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8846, 'Your attacks are slowed by the cold, I think!', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8847, 'If you will not leave Alterac Valley on your own, then the Frostwolves will force you out!', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8848, 'You are no match for the strength of the Horde!', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8849, 'Today, you will meet your ancestors!', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8850, '%s\'s wings thunder in the distance.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8851, 'Tell me about the Glowing Shard.', 'Tell me about the Glowing Shard.', 0, 0, 3, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8852, 'Mine! Mine! Mine! Gizlock is the ruler of this domain! You shall never reveal my presence!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8853, 'Dun Baldar took a real beating in the last Horde attack! We need more supplies in order to rebuild and make repairs!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8854, 'They say an army travels on its stomach, and that\'s the truth. For any offensive to succeed, your troops have to be well-fed and well supplied.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8855, 'Some say that strength and bravery are most needed for an army\'s success. And it\'s true! But an army also needs supplies to keep itself in good shape, and it\'s my job to make sure they have what they need.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8857, 'Frostwolf Keep is razed! We must have supplies in order to repair and rebuild!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8858, '%s whimpers obediently.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8859, '', 'Greetings, soldier. Are you here to help the cause?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8860, '', 'Well done, soldier. A few more and our stables will be overflowing with mounts for the cavalry to ride into combat!', 0, 0, 0, 1, 5, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8861, 'Thank you, Stable Master. Please take the animal.', 'Thank you, Stable Master. Please take the animal.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8862, 'Heya mon. You come right place learn how ta wield a new weapon.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8863, 'Ah, $n! You\'re just the person I was hoping to see! We have enough supplies to send a ground assault against the Horde! Field Marshal Teravaine is waiting in the Field of Strife for orders...$B$BYou\'re a brave veteran who\'s proven himself time and again. Do you want to deliver Teravaine the orders?', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8864, 'Ansekhwa, da tauren weapon master, is on da Thunder Bluff lower rise. He teach one and two-handed maces, staves and guns.\n\nArchibald, da undead weapon master in da War Quarter of da Undercity, train good with crossbows, daggers, one and two-handed swords and polearms.\n\nSayoc, da ugly orc right here, can teach da ways of bows, daggers, fist weapons, one and two-handed axes and thrown weapons mon.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8865, '$n! What are you doing talking to me? You should be out fighting the Horde!$B$BI have assault orders for one of our field marshals in the Field of Strife, but I need someone I can count on.$B$BPerhaps if your standing with the Stormpike militia were higher, I could count on you...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8866, 'I would be honored to train you in the use of the weapons that I have mastered should you have the ability to learn within you.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8867, '$n! Our supplies are dwindling and until we get more we can\'t launch large ground assaults into the Field of Strife!$B$BSpeak with the Stormpike Quartermaster. He\'ll tell you what you can do to resupply our assault forces.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8868, 'Sayoc is the orc weapon master and is located in Orgrimmar\'s Valley of Honor. He instructs in the use of bows, daggers, fist weapons, one and two-handed axes and thrown weapons.\n\nHanashi, the troll weapon master in the Valley of Honor of Orgrimmar, can teach the use of bows, one and two-handed axes, staves and thrown weapons.\n\nArchibald, the undead weapon master in the War Quarter of the Undercity, can train the use of crossbows, daggers, one and two-handed swords and polearms.\n', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8869, 'Death to Stormpike!', '', 0, 0, 0, 15, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8870, 'Unleash the cavalry!', 'Unleash the cavalry!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8871, 'You have done an exceptional job, soldier! The cavalry is ready to ride on your command.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8872, 'Awaiting orders from my superiors.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8873, 'I suppose you are here to be trained in the use of a new weapon? Make it fast, I\'m very busy!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8874, 'Sayoc is the orc weapon master. He is located in Orgrimmar\'s Valley of Honor. He can train the use of bows, daggers, fist weapons, one and two-handed axes and thrown weapons.\n\nHanashi, the troll weapon master in the Valley of Honor of Orgrimmar, can instruct in the use of bows, one and two-handed axes, staves and all thrown weapons.\n\nAnsekhwa, the tauren weapon master, is on Thunder Bluff\'s lower rise. He teaches the use of one and two-handed maces, staves and guns.\n', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8875, 'I wish to stable my pet, Stable Master.', 'I wish to stable my pet, Stable Master.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8876, '$n, it\'s good to see veterans like you in the field! I\'m sorry to report that our supplies are currently too low to launch any large ground assaults against the Horde.$B$BSpeak with our quartermaster; he has supply missions available.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8877, 'Have you come seeking training in the ways of armed combat?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8878, 'Buliwyf, the dwarven weapon master at the Timberline Arms in Ironforge, teaches fist weapons, guns, one and two-handed axes and one and two-handed maces.\n\nBixi Wobblebonk, the gnome weapon master at the Timberline Arms in Ironforge, teaches crossbow, daggers and throwing weapons.\n\nIlyenia Moonfire, the night elf weapon master, resides at the Warrior\'s Terrace in Darnassus where she teaches bow, daggers, fist weapons, staff and throwing weapons.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8879, '', 'The grace with which I fight is not something I can teach you, but I can teach you the use of various weapons.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8880, '', 'Buliwyf, the dwarven weapon master at the Timberline Arms in Ironforge, teaches fist weapons, guns, one and two-handed axes and one and two-handed maces.\n\nBixi Wobblebonk, the gnome weapon master at the Timberline Arms in Ironforge, teaches crossbow, daggers and throwing weapons.\n\nWoo Ping, the human weapon master at Weller\'s Arsenal in Stormwind, teaches crossbow, daggers, one and two-handed swords, polearms, and staff.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8881, 'Ye want to be trained in the use of a weapon, do ye? Well don\'t stand there slack-jawed, speak up $gladdie : lassie;!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8882, 'Bixi Wobblebonk, the gnome weapon master, is wandering about in this shop. She can teach crossbow, daggers and throwing weapons.\n\nWoo Ping, the human weapon master at Weller\'s Arsenal in Stormwind, teaches crossbow, daggers, one and two-handed swords, polearms, and staff.\n\nIlyenia Moonfire, the night elf weapon master, resides at the Warrior\'s Terrace in Darnassus where she teaches bow, daggers, fist weapons, staff and throwing weapons.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8883, '', 'Well hi there!  Are you here to learn the proper technique of a new weapon?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8884, '', 'Buliwyf, the dwarven weapon master right here at the Timberline Arms, teaches fist weapons, guns, one and two-handed axes and one and two-handed maces.\n\nWoo Ping, the human weapon master at Weller\'s Arsenal in Stormwind, teaches crossbow, daggers, one and two-handed swords, polearms, and staff.\n\nIlyenia Moonfire, the night elf weapon master, resides at the Warrior\'s Terrace in Darnassus where she teaches bow, daggers, fist weapons, staff and throwing weapons.', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8885, '', 'We need more wolves! Come to the stables and help!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8886, '', 'Well done, Horde! We have enough Frostwolves in our stables to provide mounts to all of our wolf riders!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8887, 'Wolf riders, to Frost Dagger Pass!', '', 1, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8888, 'RARRRRRR!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8889, 'Let me know when it is best to ride. You must be our eyes and ears on the battlefront.', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8890, 'The time has come, brothers. We must not let the enemy advance through Frost Dagger Pass. Some of us will surely perish, but would any of you shy away from this duty? This honor? Onward! For the Horde!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8891, 'For the Warchief!', 'For the Warchief!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8892, 'Myolor, I was once an armorsmith and wish to retake the hammer once more! Teach me the way of the armorsmith.', 'Myolor, I was once an armorsmith and wish to retake the hammer once more! Teach me the way of the armorsmith.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8893, 'Myolor, I was once a weaponsmith and wish to retake the hammer once more! Teach me the way of the weaponsmith!', 'Myolor, I was once a weaponsmith and wish to retake the hammer once more! Teach me the way of the weaponsmith!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8894, 'Krathok, I was once an armorsmith and wish to retake the hammer once more! Teach me the way of the armorsmith.', 'Krathok, I was once an armorsmith and wish to retake the hammer once more! Teach me the way of the armorsmith.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8895, 'Krathok, I was once a weaponsmith and wish to retake the hammer once more! Teach me the way of the weaponsmith.', 'Krathok, I was once a weaponsmith and wish to retake the hammer once more! Teach me the way of the weaponsmith.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8896, 'Patrol Frost Dagger Pass. Let none through! Die with honor...', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8897, 'The Kolkar invaders are retreating!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8898, 'We have gathered enough hides from the wolves. Soon, the cavalry will be ready to ride! Speak to me again when we have stabled enough rams.', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8899, '', 'The stables are full again! Our cavalry is soon to ride!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8900, '', 'The stable is empty! We must provide our cavalry with suitable mounts. Stormpike Stables requests your assistance!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8901, 'Death to Frostwolf!', '', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8902, 'We ride on your command!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8903, 'YAW! Er, to the front lines with you!', 'YAW! Er, to the front lines with you!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8904, 'Awaiting orders from my superiors!', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8905, 'Ram Riders, REGULATE!', '', 1, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8906, 'Lads and ladies. Today can be your greatest day, for today, you fight for the glory of Stormpike! Ride unto the field and grant your enemies no mercy! Strike the savages down and let their Warchief sort em out!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8907, 'Patrol the front line, soldiers!', '', 1, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8908, 'For the King!', 'For the King!', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8909, '', 'The indigenous rams of Alterac are vital to the functionality of our cavalry!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8910, '', 'Just a few more and our stables will be overflowing with mounts. Release it into my custody, soldier. I\'ll take it from here.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8911, 'With pleasure. These things stink!', 'With pleasure. These things stink!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8912, 'I wish to stable my pet.', 'I wish to stable my pet.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8913, 'All right, Reavers! The orders are here! To battle!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8914, 'I\'ll perform these with pleasure, $n!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8915, '%s emerges from the plant, allowing it to heal.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8916, '%s attacks!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8917, 'Well met, $n. We must be brief because there is much to do.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8918, 'May Cenarius guide you, $c.$B$BI am here in this corrupt land with my sisters on a mission of great importance to the Cenarian Circle. But only those who are powerful enough I ask speak to me about such matters--to send a lesser being would be folly.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8919, '', 'My sisters and I have come down from the Stonetalon Mountains to put to rest a tragedy from ages past. Unfortunately, we are not powerful enough to accomplish our goals on our own and now seek mortals such as yourself to aid us.$B$BIf you are powerful enough, then we have much to discuss, $c. If you are not, then, for your safety, I will leave the details of my mission out of our conversation.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8920, '', '$n, please take a look at the sample of the Vylestem vine that I took from the caverns, so that you know what to look for...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8921, 'I greet you, $r.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8922, 'Free! Free from my bonds at last!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8923, '$n, please take a look at the sample of the Vylestem vine that I took from the caverns, so that you know what to look for...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8924, 'I need no home to know I still have a place in this world, $c. The tribes will see... all of them. They fight amongst one another, and for what? Scraps left over by vultures... lands infested by the Legion... bah!$B$BThe centaur should be proud of their heritage--it is just as noble as any Night elf\'s or orc\'s--it is time we started acting like it!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8925, 'At last, I am free of the corruption that once bound me to this place...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8926, 'Thank you for what you have done, $n.$B$BKeep the scepter safe; may it aid you in your toil against the evils that have invaded this place...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8927, 'How has this come to be, $n? Do you think that an item such as the Scepter of Celebras is trivial?$B$BI can help you this time, but do not be careless with this valuable item again...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8928, 'Celebras, I lost my Scepter...', 'Celebras, I lost my Scepter...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8929, 'I carry both parts of the scepter.', 'I carry both parts of the scepter.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8930, 'My scepter... After so many years... You have recovered it! You shall truly be the savior of this place...$B$BPlease listen to me carefully, $n. The head and the rod of the scepter may only be reunited into one at the Stone of Maraudon.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8931, 'Please show me the Stone of Maraudon.', 'Please show me the Stone of Maraudon.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8932, 'Please explain how the scepter works.', 'Please explain how the scepter works.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8933, 'Between the colors, a path.$B$BBlink across in light.$B$BFrom the water, you emerge.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8934, 'I don\'t quite understand...', 'I don\'t quite understand...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8935, 'Between the orange and the purple passageways near the doors to Maraudon, you will find a third path.$B$BThe scepter you hold in your hands is the key.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8936, 'No, really, I still don\'t understand...', 'No, really, I still don\'t understand...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8937, 'I grow weary...$B$BFrom here, you can see the waterfalls, $n...The scepter grants you the power to travel to this very place, should you ever desire to enter through the front gates of Maraudon again.$B$BNear the entrance to Maraudon, you will find a passage that goes down beneath the caverns. Use the scepter at the ancient ritual site to create a portal, and you will find yourself here once more.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8938, 'Please tell me more about Maraudon.', 'Please tell me more about Maraudon.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8939, 'Long ago, my dryad sisters and I entered these caves to free the tortured spirit of my uncle, Zaetar. However, the corruption that had already spread through these caverns soon overcame us, and we too fell.$B$BWe were destined to wander these passages; the toxins poisoning our minds...$B$BAlthough my spirit may now be at rest, I know that I have failed in my mission. It is a great sadness I must bear...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8940, 'Please tell me more about Zaetar.', 'Please tell me more about Zaetar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8941, 'Ah, Zaetar... <Celebras looks away.>$B$BMy father, Remulos, tried to stop him...$B$BZaetar was headstrong, and when he fell for an earthen elemental princess, my father knew that their union was cursed, but Zaetar refused to listen.$B$BThe centaur are the result of his mistake. They were born misshapen and hateful; my uncle Zaetar was killed by his own kin...$B$BThe evil princess, Theradras, still guards his remains here in the crystal caverns.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8942, 'Those goblins should be ashamed of themselves. Bah!', '', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8943, 'Spending coin is NOT the reason for the Feast of Winter Veil!', '', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8944, 'I cannot believe those greedy goblins are allowed to hawk their wares while spoiling the spirit of this season.', '', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8945, 'Vile goblins... anything for a gold coin.', '', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8946, 'Could I have a copy of \"The Feast of Winter Veil\" to read?', 'Could I have a copy of \"The Feast of Winter Veil\" to read?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8947, 'Perhaps we can attempt this later. Please speak to me again if you wish to do so.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8948, 'Together, the two parts shall become one, once again.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8949, 'Shal myrinan ishnu daldorah...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8950, 'Read this tome I have placed before you, and speak the words aloud.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8951, '%s begins to channel his energy, focusing on the stone.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8952, 'You wish to learn of the stone? Follow me.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8953, 'For so long I have drifted in my cursed form. You have freed me... Your hard work shall be repaid.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8954, 'Please do as I instruct you, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8955, 'My scepter will once again become whole!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8956, 'Welcome to the Explorer\'s League. If you need information, and it is in book form, then I might be able to help you out.', '', 0, 0, 0, 3, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8957, 'How can I find the two parts of your scepter?', 'How can I find the two parts of your scepter?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8958, 'As the toxins lurking within Maraudon seeped into my mind, I began to lose my grip on reality. Worried that it would be used for evil, my dryad sisters secretly took the scepter from me, breaking it into two parts and hiding it within the caverns.$B$BSoon they fell victim to the madness as well, and the two parts of the scepter, left unguarded, came to be in the possession of Vyletongue, himself. The satyr entrusted one part to Noxxion, an evil elemental, and the other he still has to this day.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8959, 'Why didn\'t Lord Vyletongue unite both parts of the scepter into one?', 'Why didn\'t Lord Vyletongue unite both parts of the scepter into one?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8960, 'Without knowledge of the incantations required to unite the two parts, Lord Vyletongue was unable to use the scepter. However, now that I am free of the curse of Vyletongue\'s corruption, I will be able to assist you, if you\'d like to bring me the two parts -- both the head and the rod of the scepter.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8961, 'Tell me more about the history of Remulos and Zaetar.', 'Tell me more about the history of Remulos and Zaetar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8962, 'You ask of the Sons of Cenarius, Remulos and Zaetar.$B$BMy father, Remulos, was strong and beautiful, and my uncle, Zaetar, very cunning and slight of build. While they were respected quite equally, Zaetar always felt as if he could never quite compare to the glory and attention he thought Remulos had.$B$BAfter my sisters and I were born, Zaetar, blinded by jealousy, set out to outdo his brother... He was the creator of his own end. The sadness it brings me has never lessened, even as time passes...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8963, 'Long have my spirit and remains been kept captive here within Maraudon, young $c. The time has given me nothing but ample opportunities to reflect on my mistakes and realize that my jealousy of Remulos was foolish. He attempted to warn and persuade me that my relations with Theradras would bear nothing pure--that only destruction would come from chaos. My brother was right.$B$BBut that does not mean since you have freed me, I am free of penance.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8964, 'Please, go on.', 'Please, go on.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8965, 'Though exiled as a heretic, my beliefs still ring true--that alone drives me onward. But for my destiny to be complete, I need the aid of an outsider... one not of any centaur tribe.$B$BThe task requires someone to enter the holy halls of Maraudon--a temple my people protect, and only the most devout are ever allowed to enter.$B$BIf you agree to aid me, then you need only nod. I will pass you a parchment with my instructions on it. Read it over and consider my words.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8966, 'I will aid you. Give me the instructions.', 'I will aid you. Give me the instructions.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8967, '%s bows low before you.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8968, 'Thank you, good $c.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8969, 'Greetings, $n. I am Doctor Gregory Victor, head trauma surgeon of Horde Trauma.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8970, 'It is good to see you again, Doctor $n. I have been following your progress and I dare say that it is time you learned some of the more advanced bandaging techniques.$B$BReturn to me from time to time, as your skill advances, and I shall teach you all that I know.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8971, 'Teach me how to create and apply a Heavy Mageweave Bandage, Doctor.', 'Teach me how to create and apply a Heavy Mageweave Bandage, Doctor.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8972, 'Teach me how to create and apply a Runecloth Bandage, Doctor.\n', 'Teach me how to create and apply a Runecloth Bandage, Doctor.\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8973, 'Teach me how to create and apply a Heavy Runecloth Bandage, Doctor.\n', 'Teach me how to create and apply a Heavy Runecloth Bandage, Doctor.\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8974, 'Greetings, $n. I am Doctor Gustaf VanHowzen, head trauma surgeon of Alliance Trauma.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8975, 'Great! Let\'s keep those supplies coming, people!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8976, 'Thanks for the supplies, $n!', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8977, 'Thanks a lot, $n!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8978, 'We\'re making progress! Bring more supplies to repair Dun Baldar!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8979, 'We have enough supplies! Our captain has retaken position in the Field of Strife!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8980, 'Now we\'re making progress!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8981, 'Thank you for the armor scraps, $n!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8982, 'Our cache of equipment grows!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8983, 'Thank you for the supplies, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8984, 'Very good. Our supply cache grows...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8985, 'Keep them coming! We need more supplies in order to rebuild...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8986, 'We can now rebuild! For the Horde!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8987, '', 'Hail, $n!  It is good you came to see me.  We are prepared to launch a ground assuault through the Field of Strife!  Once Warmaster Garrick gets his orders, he and his reavers will cut a swath through the Alliance!$B$BDo you want to deliver those orders, $n?  Garrick is waiting in the Field of Strife...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8988, '', 'Why are you here, $n?  Is there lead in your boots?$B$BYou have the assault orders - Warmaster Garrick needs them in the Field of Strife before he can make his assault!  Go!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8989, '', 'It\'s good to have veterans like you in Alterac Valley, $n.  Our supplies are too low to launch a ground assault through Alterac Valley... speak with our quartermaster - he\'ll tell you how to increase our supply base.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8990, '', 'Hail, $c!  Our supply level is high, and we are ready to launch a ground assault through the Field of Strife.  Perhaps if your renown was greater, I would trust you to deliver the assault orders.$B$BSo get out there and fight the Alliance!  Show me you\'re a trusted and capable member of the Horde!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8991, '', 'You!  $n!  What are you doing wandering around the village like this?  There are Alliance foes in Alterac!  We must drive them out!$B$BOur supplies are too low to launch a large ground assault, so speak with our quatermaster.  He\'ll tell you how you can increase our supply base.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8993, '', 'Even the bravest warrior must be well supplied if he is to face his foes with strength!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8994, 'I just gave kill credit to $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8995, 'Click here to remove res effects and repair all gear', 'Click here to remove res effects and repair all gear', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8996, 'Hi! Clear your res effects here!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8997, 'Click here to repair your gear', 'Click here to repair your gear', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8998, 'We have enough supplies to launch a ground assault. If you want to send the order, and you think you qualify, then speak Sergeant Yazra Bloodsnarl.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (8999, 'We have enough supplies to launch a ground assault. If you want to deliver those orders, and you think you qualify, then speak with Corporal Noreg Stormpike.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9000, 'I am in charge of recruitment for the Frostwolf Clan. As a soldier of Frostwolf, it is your responsibility to check in with me as your rank among the clan rises.$B$BHonor and respect are earned on this frigid field.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9001, 'Warmaster, I have lost my insignia. Could you supply me with a replacement?', 'Warmaster, I have lost my insignia. Could you supply me with a replacement?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9002, 'Hail, $r! I am Lieutenant Haggerdin, officer in charge of recruitment for the Stormpike Guard.$B$BAs you gain prestige and notoriety among the Stormpike, return to me and I shall see about upgrading your insignia. Also, should you ever lose your insignia, I am able to supply a replacement.', '', 0, 0, 0, 1, 1, 113, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9003, 'Lieutenant, I have lost my insignia. Could you please supply me with a replacement?', 'Lieutenant, I have lost my insignia. Could you please supply me with a replacement?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9004, 'I have found both parts of your scepter.', 'I have found both parts of your scepter.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9005, 'Argh, I need help here!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9006, 'Watch out for the $n!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9007, 'Incoming $n - look sharp, friends!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9008, 'You\'ll rue the day you crossed me, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9009, 'Our ancestors teach us that while the individual must be strong, there is safety in numbers. Do you wish to defend the honor of Thunder Bluff by organizing a guild?', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9010, 'Aye, you look the type to be a leader. Here to organize a guild to fight for the Alliance?', '', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9011, 'I will not be lured away!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9012, 'Shenthul, I have lost my whistle and I have no more flares. Please help!', 'Shenthul, I have lost my whistle and I have no more flares. Please help!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9013, 'Greetings, $c. I am Rexxar, champion of the Horde, servant of Thrall, master to the beasts of the wilds.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9014, 'Rexxar, I have lost the Drakefire Amulet. Could another be created?', 'Rexxar, I have lost the Drakefire Amulet. Could another be created?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9015, '', 'What is it you ask of the Blue Flight?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9016, 'Haleh, I have lost the Drakefire Amulet. Could you fashion another?', 'Haleh, I have lost the Drakefire Amulet. Could you fashion another?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9017, 'Master Nesingwary has a big hunt ahead of him. I find helping him prepare for a day in the jungle is much more relaxing than helping him ready for combat. Although I do miss some of those glorious moments on the battlefield.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9018, 'Barnil, I seem to have misplaced Chapter I.', 'Barnil, I seem to have misplaced Chapter I.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9019, 'Barnil, I seem to have misplaced Chapter II.', 'Barnil, I seem to have misplaced Chapter II.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9020, 'Barnil, I seem to have misplaced Chapter III.', 'Barnil, I seem to have misplaced Chapter III.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9021, 'Barnil, I seem to have misplaced Chapter IV.', 'Barnil, I seem to have misplaced Chapter IV.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9022, 'I see you misplaced that chapter you worked so hard to put together, $n. Well, you left it right here!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9023, 'Kalaran, please enchant the torch.', 'Kalaran, please enchant the torch.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9024, '', 'I know these forests as if they were a part of me... but know that there is still much to be learned from them, and much danger still hidden.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9025, 'Raene, I lost Dartol\'s Rod of Transformation.', 'Raene, I lost Dartol\'s Rod of Transformation.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9026, '', 'Yes, I expected that you would be back. I sent Korra out earlier and she came across Dartol\'s Rod, abandoned in the forest.$B$BWe worked so hard to recover it, $n. Please take care to see that you do not handle it so carelessly in the future.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9027, '', 'You have completed Teronis\' mission, and I appreciate all you have done, $n.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9028, '', 'Hiya! This workshop isn\'t mine, no! But one day after I finish training I am going to be an inventor, just like my hero, Marvon Rivetseeker.$B$BYou\'ve never heard of him? Well it just so happens that he left quite some time ago to study some ancient troll ruins he kept talking about. He left in a zeppelin... not quite sure about where exactly he went...$B$BTanarasin? Tenkeris? Taneranisany?$B$B<Liv shakes her head.>$B$BNo, that\'s not it...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9029, '', 'Hi there. No, Marvon sure isn\'t here, and he won\'t be back for quite some time. Actually, I am getting a little worried about him...$B$BI hope he gets back soon. A large chest arrived for him and it\'s too heavy for me to lug it inside here! I had to leave it out by the door...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9030, 'You have learned all that I am able to teach you at this time, Doctor $n. Return to me when your practice has expanded and I shall train you in the ways of the more advanced medicinal arts.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9031, 'Run! They are coming!', 'Run! They are coming!', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9032, '', 'This place... it\'s cursed.$B$BI barely managed to escape, and I cannot bear to go down through the tunnels again.$B$BMy brother, and sisters though... They were not as fortunate. Trapped inside, they are feverish with the corrupted haze that flows from the evil garden growing there.$B$BPlease, you must help.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9033, '', 'Oh, I am so glad you have returned, $n.$B$BMy brother\'s spirit has now be freed from the corruption, but I choose to remain here, in my mourning.$B$BThank you so much for your help, $n.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9034, 'Interlopers! Invaders! The All Seeing Eye has been stolen! The miserable dog, $n, has it in $g his:her; possession!', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9036, '', 'Need some supplies?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9037, 'Show me what\'s for sale.', 'Show me what\'s for sale.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9038, 'Korrak has come, weaklings! Snowfall belongs to Winterax!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9039, 'How many more supplies do we need?', 'How many more supplies do we need?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9040, 'Alterac Valley - Alliance Quatmaster Greeting Option Default', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9041, 'The Commander has been slain! Charge their lines! Die with honor!', 'The Commander has been slain! Charge their lines! Die with honor!', 0, 0, 1, 0, 0, 0, 0, 0, 0);
