INSERT INTO `broadcast_text` VALUES (9299, 'In the time before time, what the usurpers would later call the First Age of Creation, my master and his siblings ruled over this world. It was they, the Old Gods, who created all.$B$BAnd it was they who would ultimately be cast down by those that would call themselves Shapers.\n', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9300, 'Continue, please.', 'Continue, please.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9301, 'Their manifest forms weak from eons of battle, imprisoned beneath the world - the world that they themselves created - the corruption of the Titan Shapers would permeate through the surface and befoul the Old Gods.$B$BIt was then and there that the brothers did turn. For five millennia a battle was fought at the core of this world.\n', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9302, 'Both the Horde and Alliance captains have fallen! Defeat Drek\'Thar before it\'s too late!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9303, 'Both the Horde and Alliance captains have taken to the field. Our Captain Balinda Stonehearth directs the Stormpike forces from the Stonehearth Outpost, and the enemy Captain Galvangar barks orders from within Iceblood Garrison.$B$BTo battle!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9304, 'NO!!! It puts the tannin in the basket, or it gets the mallet again!', '', 1, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9305, 'Both the Horde and Alliance captains have taken to the field. Our Captain Galvangar directs the Frostwolves from Iceblood Garrison, and his rival, Captain Balinda Stonehearth, hides in her Stonehearth Outpost.$B$BThe battle has begun!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9306, 'The Stormpike general has fallen! The battle is won!$B$BNow laugh as the Alliance scurries to recover its lost holdings.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9307, 'The battle rages, and both our captain and the captain of our enemy are slain!$B$BDrek\'Thar now leads us from Frostwolf Keep, and the dwarf Vanndar Stormpike hides deep in Dun Baldar. Defeat Vanndar and claim victory for the Horde!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9308, 'Captain Balinda Stonehearth is dead! Defeat Vanndar and the battle will be ours!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
