INSERT INTO `broadcast_text` VALUES (9310, 'Tell me about Alterac Valley.', 'Tell me about Alterac Valley.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9311, 'Alterac Valley has been home to the Frostwolf Clan for years; we will not let the dwarves and their Alliance trespass! And so we battle!$B$BTo win back Alterac, defeat their general Vanndar Stormpike, in Dun Baldar.$B$BFor the Horde!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9312, 'The Stormpikes uncovered ancient artifacts within Alterac Valley, but the Frostwolf orcs don\'t take kindly to explorers. If the dwarves have to fight for knowledge of their ancestors, then so be it!$B$BTo win this battle, defeat the orc, Drek\'Thar, in Frostwolf Keep. Fortune be with you, $c!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9313, 'Captain Balinda Stonehearth leads the front line Alliance forces. She is based in the Stonehearth Outpost, in the northern section of the Field of Strife.$B$BBalinda has not yet reentered the battle.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9314, 'Captain Galvangar is the trusted aid to Drek\'Thar. He leads the Horde forces from the Iceblood Garrison, in the southern section of the the Field of Strife.$B$BCaptain Galvangar has not yet entered the battle.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9315, 'Vanndar Stormpike is the general of the Alliance military in Alterac, and directs his forces from Dun Baldar.$B$BIf he is defeated then the Alliance will surely lose the battle for Alterac.$B$BVanndar is not currently in Alterac Valley.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9316, 'Drek\'Thar is the military and spiritual leader of the Frostwolf Clan in Alterac, leading his brethren from Frostwolf Keep.$B$BIf he is defeated then the Horde forces in Alterac will crumble.$B$BDrek\'Thar is not currently in Alterac Valley.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
