INSERT INTO `broadcast_text` VALUES (9319, 'Tell me of Vanndar Stormpike.', 'Tell me of Vanndar Stormpike.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9320, 'Tell me of Drek\'Thar.', 'Tell me of Drek\'Thar.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9321, 'Tell me again about Alterac Valley...', 'Tell me again about Alterac Valley...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
