INSERT INTO `broadcast_text` VALUES (9806, 'The Horde Flag was dropped by $n!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9807, 'The Horde Flag was picked up by $n!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9808, 'The Alliance Flag was returned to its base by $n!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9809, 'The Horde Flag was returned to its base by $n!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9810, '', 'Thieves! Scallywags! Rapscallions! Come face me gigantic pillas!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9811, '', 'We chose to leave the protection and comfort of the Depths to exploit... er, I mean... hrm, yes that\'s right, EXPLORE the world.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9812, 'Run if you like fools! Your fate is sealed anyways!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9813, 'Flee while you can mortal!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9814, 'Generic Text', 'Generic Text', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9815, 'Only a fool would remain in this battle. Farewell, coward.', 'Only a fool would remain in this battle. Farewell, coward.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9816, 'Maybe this wasn\'t the best place for a shop after all...', '', 0, 0, 0, 274, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9817, 'Got anything interesting for sale, Zorbin?', 'Got anything interesting for sale, Zorbin?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9818, 'I wish to browse your wares.', 'I wish to browse your wares.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9819, 'Burninate\'s the name, smithin\'s my game... and, erm... I sell a little food and water on the side. Care for some meat?', '', 0, 0, 0, 14, 6, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9820, 'Well hello again, $n! I don\'t know how much longer I\'m going to keep on trying to set up a shop out in the middle of nowhere. Still, since you\'ve helped me out in the past, I\'ll show you some stuff you may find interesting!', '', 0, 0, 0, 3, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9821, 'When Lolo doesn\'t wanna spill any o\' his precious ale or get his lil\' dress dirty, I\'m called in. Yep. Ol\' Hansel is the dwarf they use to mete out a lil\' bit o\' justice an\' a whole lotta pain.', '', 0, 0, 0, 1, 23, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9822, 'Now that the Stormpike general is defeated, we can spare a few troops to guard Winterax Hold. But we must be ever vigilant, $c, for the Alliance will return.$B$BWhen you find them skulking about, teach them another lesson!', 'Now that the Stormpike general is defeated, we can spare a few troops to guard Winterax Hold. But we must be ever vigilant, $c, for the Alliance will return.$B$BWhen you find them skulking about, teach them another lesson!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9823, '', 'Now that the Frostwolf general is defeated, we can spare some troops to guard the entrance to Winterax Hold.$B$BBut stay vigilant!  We must always be on the lookout for Horde interlopers..', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9824, 'As acting Taskmaster of this here compound, it is my duty to make sure that there is a certain level of tedium involved with all forms of business, work, rest, and relaxation that may take place here.$B$BIf you find anything that you are asked to do to be too easy and hassle free, report to me immediately.', '', 0, 0, 0, 1, 1, 273, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9825, 'You\'ve done all that I\'ve asked you to do, $n. There\'s really nothing more here for you. If you are interested in continuing your career as an agent of the Thorium Brotherhood, speak with Lokhtos Darkbargainer at the Grim Guzzler in Blackrock Depths.$B$BFarewell!', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9826, 'I swear... if Quint tries to plant one more fishing grub on me, I\'m going to hack down his tree with my not-so-rusty sword.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9827, '', 'Beware!  The $r $n attacks us at Winterax Hold!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9828, '', 'Thorium Brotherhood or not, if one of them Dark Iron gets near to me they\'ll taste my mattock up the side of their head!  Now, where can I send ya $G lad : lass;?', 0, 0, 7, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (9829, 'Hey Pratt - can you teach me how to make Wild Leather Boots again?', 'Hey Pratt - can you teach me how to make Wild Leather Boots again?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
