INSERT INTO `broadcast_text` VALUES (10267, 'Right away sir.', '', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10268, 'If you have come to save the world then you have come to the right place. The faceless one - the Blood God, Hakkar - has been brought back into our world!', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10269, 'The strength of your resolve shines, $n. You continue to prove that you are an asset to our people. Someday the world will realize the extent of your great deeds.', '', 0, 0, 0, 4, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10270, 'The burgeoning hero returns! It is good to see you again, $n. You are always welcome at our village.', '', 0, 0, 0, 66, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10271, 'I knew of another like you once... His name was Vol\'jin. You are a rare breed, my friend.', '', 0, 0, 0, 1, 2, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10272, 'Long is the way, and hard, the road you have traveled. Few reach such status amongst our ranks. You are a great friend of the Zandalar empire, $n.$B$BLegendary perhaps... Yes... Perhaps.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10273, 'Right away sir.', '', 0, 0, 1, 1, 0, 0, 0, 0, 0);
