INSERT INTO `broadcast_text` VALUES (10278, '', 'Hmm... That rock is nice, but too small.', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10279, '', 'This is a shiney rock, I\'ll put it in my bucket. Ok, I just need a few more...', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10280, '', 'I bet my friend Gruk would like this rock for the spear he is making! It\'s very sharp... I hope he likes it.', 0, 0, 1, 1, 0, 0, 0, 0, 0);
