INSERT INTO `broadcast_text` VALUES (10292, '', 'Yummy. I love roasted Plainstrider.', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10293, ' I am going to practice and become a warrior like my father!', '', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10294, 'Now to find an unsuspecting Harpy!', '', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10295, 'There\'s one! For the Horde!', '', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10296, 'Take that you mangy Harpy! Hyaa!', '', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10297, 'You can\'t get away from a warrior of The Horde! Take that! And that!', '', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10298, 'Now go back home where you belong! ', '', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10299, 'Aaahhh... Another glorious victory for The Horde! ', '', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (10300, '', 'Mogg, get inside. It is not safe out there while your father is gone.', 0, 0, 1, 1, 0, 0, 0, 0, 0);
