INSERT INTO `broadcast_text` VALUES (11423, '%s lets loose a massive attack.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11424, '%s hears the call to battle and rises to serve its master.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11425, '%s casts its massive shadow upon the whole of Silithus.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11426, '%s breaks free of its cocoon beneath Hive\'Ashi.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11427, '$n, Champion of the Bronze Dragonflight, has rung the Scarab Gong. The ancient gates of Ahn\'Qiraj open, revealing the horrors of a forgotten war...', '$n, Champion of the Bronze Dragonflight, has rung the Scarab Gong. The ancient gates of Ahn\'Qiraj open, revealing the horrors of a forgotten war...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11428, 'Slay them all. Leave their husks for the master to consume...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11429, 'Soon you will know the price of your meddling, mortals... The master is nearly whole... And when he rises, your world will cease.', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11430, 'Presents for everyone! Father Winter\'s put gifts under the tree for all.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11431, 'Do you think you\'ve won? So easily? The legion has only just begun its march. The armies of C\'Thun will raze this world.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11432, 'Massive Qiraji resonating crystals break through the earthen crust of Kalimdor. Colossal creatures made of obsidian stone breach the freshly broken land.', 'Massive Qiraji resonating crystals break through the earthen crust of Kalimdor. Colossal creatures made of obsidian stone breach the freshly broken land.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11433, 'Breathe your last!', '', 6, 8639, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11434, 'Warriors, Captains, continue the fight! ', '', 6, 8640, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11435, 'Impudent fool! I will kill you myself!', '', 6, 8638, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11436, 'Fandral! Your time has come! Go and hide in the Emerald Dream and pray we never find you!\n', '', 6, 8641, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11437, 'Staghelm will whimper and beg for his life, just as his whelp of a son did! One thousand years of injustice will end this day!\n', '', 6, 8642, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11438, 'Fear is for the enemy! Fear and death!', '', 6, 8643, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11439, 'No longer will we wait behind barred doors and walls of stone! No longer will our vengeance be denied! The dragons themselves will tremble before our wrath!\n', '', 6, 8644, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11440, 'The time of our retribution is at hand! Let darkness reign in the hearts of our enemies!', '', 6, 8645, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11441, '%s senses your fear.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11442, 'You will be judged for defiling these sacred grounds! The laws of the Ancients will not be challenged! Trespassers will be annihilated!\n', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11443, 'I sentence you to death!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11444, 'I serve to the last!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11445, 'Are you so eager to die? I will be happy to accommodate you...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11446, 'Let your death serve as an example!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11447, 'You only delay the inevitable!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11448, 'senses your fear.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11449, 'Sands of the desert, rise and block out the sun!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11450, 'You are terminated.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11451, 'I...have...failed. ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11452, 'My brother, no!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11453, 'You will not escape death!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11454, 'Vek\'lor, I feel your pain!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11455, 'Your fate is sealed!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11456, 'Starting at 6PM, they will show fireworks from the highest bluff!', 'Starting at 6PM, they will show fireworks from the highest bluff!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11457, 'The kegs are filled with strong brew! Drink, friend, and celebrate!', 'The kegs are filled with strong brew! Drink, friend, and celebrate!', 0, 0, 0, 1, 153, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11458, 'I heard that, after the fireworks show, goblets will be brought to the tables. Fine toasts we shall raise!', 'I heard that, after the fireworks show, goblets will be brought to the tables. Fine toasts we shall raise!', 0, 0, 0, 71, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11459, 'Hail, $c! Welcome to the celebration!', 'Hail, $c! Welcome to the celebration!', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11460, 'They\'re going to show fireworks all night long. From 6PM until dawn, the sky will blaze over Booty Bay!', 'They\'re going to show fireworks all night long. From 6PM until dawn, the sky will blaze over Booty Bay!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11461, 'Wow! These kegs are filled with powerful stuff! They lift me right out of my boots!', 'Wow! These kegs are filled with powerful stuff! They lift me right out of my boots!', 0, 0, 0, 1, 153, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11462, 'Stick around after the firework show is over. Find a goblet and raise it to toast your hardy friends!', 'Stick around after the firework show is over. Find a goblet and raise it to toast your hardy friends!', 0, 0, 0, 71, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11463, 'All right! Party time!', 'All right! Party time!', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11464, 'Times like these get me all sentimental...', 'Times like these get me all sentimental...', 0, 0, 0, 24, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11465, 'I sure could use a kiss about now. Know where I can find one?', 'I sure could use a kiss about now. Know where I can find one?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11466, 'Hah! This tauren wants a kiss!', 'Hah! This tauren wants a kiss!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11467, 'A happy Winter\'s Vale to you, friend!', 'A happy Winter\'s Vale to you, friend!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11468, 'Season\'s greetings, and peace to you!', 'Season\'s greetings, and peace to you!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11469, 'I wish you a happy holiday, friend!', 'I wish you a happy holiday, friend!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11470, 'Ah, mistletoe...', 'Ah, mistletoe...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11471, 'Kneel before me, mortal! Kneel before Zod!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11472, 'Why ... won\'t you ... just dieeeee?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11473, '%s bristles with energy.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11474, '%s drains your mana and turns to stone.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11475, 'Burn in hate, $r.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11476, '%s is weakened!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11477, 'I lied...', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11478, 'Come get some!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11479, '', 'Mighty fine weather we\'re having here, wouldn\'t you say?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11480, 'Oh Umi, please teach me again how to create a Tranquil Yeti. I miss it so.', 'Oh Umi, please teach me again how to create a Tranquil Yeti. I miss it so.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11481, '', 'Well, alright then $n. I can tell that you\'ve seen the error of your ways and rejoined the engineering fold.$B$BHave fun with your yeti!!!', 0, 0, 0, 4, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11482, '', 'Why is it that there\'s never a gyrochronatom lying around when you need one?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11483, '', 'I\'ll get the last laugh... they\'ll see! Oh, hi $c, I didn\'t see you there.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11484, '', 'Invention, my dear $c, is two percent bottled inspiration, four percent solar exposure, six percent chance of thunderstorms clearing by mid-afternoon, and ninety-three percent Rumplesnicker.$B$BWait, did that add to one hundred? I\'ve got my notes around here somewhere.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11485, 'We\'ve gotta find out what the Qiraji are feeding those monsters! Look at the size of \'em!', '', 0, 0, 0, 5, 5, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11486, 'Studying and understanding the Colossus will give us a needed advantage over the Qiraji.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11487, '', 'The secrets behind the power of the Colossus could lead to all sorts of innovations in science!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11488, 'Where is Elder Graveborn?', 'Where is Elder Graveborn?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11489, 'The inner gates of Ahn\'Qiraj crash open.', 'The inner gates of Ahn\'Qiraj crash open.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11490, 'Elder Graveborn spends time near Brill in Tirisfal Glades.', 'Elder Graveborn spends time near Brill in Tirisfal Glades.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11491, 'Where is Elder Moonstrike?', 'Where is Elder Moonstrike?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11492, 'Elder Moonstrike can be found in the Scholomance in the Western Plaguelands.', 'Elder Moonstrike can be found in the Scholomance in the Western Plaguelands.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11493, 'Where is Elder Darkcore?', 'Where is Elder Darkcore?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11494, 'Elder Darkcore can be found in the Undercity.', 'Elder Darkcore can be found in the Undercity.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11495, '', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11496, 'Show them what you\'re made of! Attack!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11497, 'Where is Elder Obsidian?', 'Where is Elder Obsidian?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11498, 'Elder Obsidian wanders near the Sepulcher in Silverpine Forest.', 'Elder Obsidian wanders near the Sepulcher in Silverpine Forest.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11499, 'Where is Elder Snowcrown?', 'Where is Elder Snowcrown?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11500, 'Welcome, are you here to ride the Windstrider? She is Thunder Bluff\'s first airship made in collaboration with the Durotar Labor Union.\n\nI have had a lot of influence in the design, being a former Shaman I ensured that she received the blessing of the spirits, and that the material and methods used for her construction was not dangerous or harmful to the land.\n\nMy goblin colleagues wanted to cut corners but the Elders had very strict terms to allow this airship. Connecting our fair city to our Orcish allies is a great boon I am sure.\n\nThey all laughed at me, telling me a Tauren cannot be an Engineer, well look at me now!\n\nIn any case, I am sure she\'s due to arrive at any time. Unless you need something else I must be excused.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11501, 'Where is Elder Highpeak?', 'Where is Elder Highpeak?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11502, 'Elder Highpeak watches over the Hinterlands from a hill near its center.', 'Elder Highpeak watches over the Hinterlands from a hill near its center.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11503, 'Where is Elder Windrun?', 'Where is Elder Windrun?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11504, 'Elder Windrun resides in the Crown Guard Tower in the Eastern Plaguelands.', 'Elder Windrun resides in the Crown Guard Tower in the Eastern Plaguelands.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11505, 'Where is Elder Farwhisper?', 'Where is Elder Farwhisper?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11506, 'Elder Farwhisper can be found wandering the streets of Stratholme.', 'Elder Farwhisper can be found wandering the streets of Stratholme.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11507, 'Where is Elder Ironband?', 'Where is Elder Ironband?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11508, 'Elder Ironband lives in Blackchar Cave in Searing Gorge.', 'Elder Ironband lives in Blackchar Cave in Searing Gorge.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11509, 'Where is Elder Goldwell?', 'Where is Elder Goldwell?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11510, 'Elder Goldwell spends his time in the village of Kharanos in Dun Morogh.', 'Elder Goldwell spends his time in the village of Kharanos in Dun Morogh.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11511, '', 'Begone foul creatures!  Your kind has no place in this world.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11512, 'Where is Elder Bronzebeard?', 'Where is Elder Bronzebeard?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11513, 'Show your courage, men and women of Azeroth! Drive back the Qiraji invaders!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11514, 'Elder Bronzebeard can be found in the ancestral home of the Bronzebeards, Ironforge.', 'Elder Bronzebeard can be found in the ancestral home of the Bronzebeards, Ironforge.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11515, '', 'Show no weakness!  Crush these foul creatures!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11516, 'Where is Elder Silvervein?', 'Where is Elder Silvervein?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11517, '', 'Elune be with you, $c.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11518, '', 'The High Priestess could not leave Darnassus. Fandral is in a very foul mood.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11519, '', 'Tyrande sends her prayers. The Mother Moon blesses us all.', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11520, 'It is settled. I command the Might of Kalimdor. Be certain that your soldiers understand and accept this as fact. Bloodshed is already plentiful. Let there be none between our factions.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11521, 'Soon, young ones. Soon the armies of the Horde will arrive. We march. March to the cursed gates and crush all in our way. Let them feel the might of Kalimdor!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11522, '', 'You have a way with words, Overlord Saurfang. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11523, '', 'As to your warning - worry not. Our troops are all aware of the chain of command. We fight united. Let us hope your own troops remember that...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11524, 'Elder Silvervein can be found near Thelsamar in Loch Modan.', 'Elder Silvervein can be found near Thelsamar in Loch Modan.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11525, '%s goes into a frenzy at the sight of his fallen troops.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11526, 'You will pay for that...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11527, 'FOR THE MIGHT OF KALIMDOR!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
