INSERT INTO `broadcast_text` VALUES (11560, 'My work here is done. I\'ll be sure to let Mar\'alith and the others at Cenarion Hold know that you helped me out a little. Be careful in here, you don\'t have me holdin\' your hand past this point.', '', 6, 0, 0, 5, 0, 0, 0, 0, 0);
