INSERT INTO `broadcast_text` VALUES (11593, 'Elder Mistwalker enjoys watching the struggles at the Maul in Feralas.', 'Elder Mistwalker enjoys watching the struggles at the Maul in Feralas.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11594, 'Where is Elder Skyseer?', 'Where is Elder Skyseer?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
