INSERT INTO `broadcast_text` VALUES (11767, 'Hail! You look familiar... have we met before?$B$BWell if we have met, then... surely you were given a secret code. Tell me your code and I will give you a gift!', 'Hail, $c. You look familiar... have we met before?$B$BWell if we have met, then... surely you were given a secret code. Tell me your code and I will give you a gift!', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11768, 'Just because it\'s not my heart doesn\'t mean it can\'t be broken.', 'Just because it\'s not my heart doesn\'t mean it can\'t be broken.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
