INSERT INTO `broadcast_text` VALUES (11800, 'What?! How did you sneak past my jail...er, minion out there? Well, I suppose it\'s for the best. Sales have been a little slow lately.$B$BHow may I fulfill your infernal needs, $c?', '', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11801, 'You look like you\'ve had your heart broken. Come back when you\'re not so sad.', 'You look like you\'ve had your heart broken. Come back when you\'re not so sad.', 0, 0, 0, 25, 153, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11802, 'I\'d like you better if you would apply some perfume.', 'I\'d like you better if you would apply some perfume.', 0, 0, 0, 24, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11803, 'What, you don\'t have a token to give me? Don\'t you love me?', 'What, you don\'t have a token to give me? Don\'t you love me?', 0, 0, 0, 18, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11804, 'It looks like you\'ve already found love. You can give me a love token, but I\'m not giving you anything nice. I\'m waiting for someone special.', 'It looks like you\'ve already found love. You can give me a love token, but I\'m not giving you anything nice. I\'m waiting for someone special.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11805, 'You look like you\'ve had your heart broken. Come back when you\'re not so sad.', 'You look like you\'ve had your heart broken. Come back when you\'re not so sad.', 0, 0, 0, 25, 153, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11806, 'I\'d like you better if you would apply some perfume.', 'I\'d like you better if you would apply some perfume.', 0, 0, 0, 24, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11807, 'What, you don\'t have a token to give me? Don\'t you love me?', 'What, you don\'t have a token to give me? Don\'t you love me?', 0, 0, 0, 18, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11808, 'You look like you\'ve had your heart broken. Come back when you\'re not so sad.', '', 0, 0, 0, 25, 153, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11809, 'I\'d like you better if you would apply some perfume.', '', 0, 0, 0, 24, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11810, 'What, you don\'t have a token to give me? Don\'t you love me?', '', 0, 0, 0, 18, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11811, 'I\'d talk to you if you smelled better. How about cologne? Maybe perfume?', 'I\'d talk to you if you smelled better. How about cologne? Maybe perfume?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11812, 'Intruders! More pawns of the Argent Dawn, no doubt. I already count one of their number among my prisoners. Withdraw from my domain before she is executed!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11813, 'You\'re still here? Your foolishness is amusing! The Argent Dawn wench needn\'t suffer in vain. Leave at once and she shall be spared!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11814, 'May this prisoner\'s death serve as a warning. None shall defy the scourge and live!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11815, 'I shall take great pleasure in taking this poor wretch\'s life! It\'s not too late, she needn\'t suffer in vain. Turn back and her death shall be merciful!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11816, '', 'Don\'t worry about me!  Slay this dreadful beast and cleanse this world of his foul taint!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11817, '', 'My death means nothing.... light... will... prevail!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11818, '', 'I am free! Let\'s work together now to save my brother, Sothos!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11819, '', 'Thank you for freeing me, my brother, and all of you! We can finally rest in peace now.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11820, 'Let\'s see what you have.', 'Let\'s see what you have.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11821, 'My mission is complete. I may have some spare supplies if you need any.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11822, 'No, I\'m not, but lots of others are looking for love. You should buy my love tokens, put on some cologne or perfume, and then pass them around!', 'No, I\'m not, but lots of others are looking for love. You should buy my love tokens, put on some cologne or perfume, and then pass them around!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11823, 'Does that heart mean you\'re looking for love?', 'Does that heart mean you\'re looking for love?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11824, 'What are love tokens?', 'What are love tokens?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11825, 'You\'re selling cologne and perfume?', 'You\'re selling cologne and perfume?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11826, 'Love tokens are small messages of affection you can give to amorous town and city folk. Such people will reward you with a gift of adoration.$B$BOr, if you are already adored by another, then at the least they\'ll give you a gift of friendship.', 'Love tokens are small messages of affection you can give to amorous town and city folk. Such people will reward you with a gift of adoration.$B$BOr, if you are already adored by another, then at the least they\'ll give you a gift of friendship.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11827, 'If you put on cologne or perfume, then you\'ll see who is amorous. Those are the ones who accept love tokens... and offer a gift in return.$B$BBut remember: to give someone a love token, you have to smell just right! Men like the scent of perfume, while women prefer cologne.', 'If you put on cologne or perfume, then you\'ll see who is amorous. Those are the ones who accept love tokens... and offer a gift in return.$B$BBut remember: to give someone a love token, you have to smell just right! Men like the scent of perfume, while women prefer cologne.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11828, 'What can I do with these gifts?', 'What can I do with these gifts?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11829, 'When you give love tokens to townsfolk, you\'ll receive different gifts in return. Some of those you\'ll want to keep and use, but others can be bundled together and given to your favorite hero! Visit Kwee Q. Peddlefeet to turn them in.$B$BKwee can be found near Bolvar, Magni, or Tyrande.', 'When you give love tokens to townsfolk, you\'ll receive different gifts in return. Some of those you\'ll want to keep and use, but others can be bundled together and given to your favorite hero! Visit Kwee Q. Peddlefeet to turn them in.$B$BKwee can be found near Bolvar, Magni, or Tyrande.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11830, '$n! You\'ve returned just to talk to me? That\'s very thoughtful of you, and you look pretty sweet in that outfit, too!$B$BYou don\'t know how good it makes me feel to see you. It can get pretty lonely up here, what with most of these yahoos not being able to see me, and all. It\'s a great thing that you did for all of us, and I\'ll never forget it!$B$BThanks for dropping in and saying hi, and don\'t be such a stranger.', '', 0, 0, 0, 71, 1, 0, 0, 1000, 0);
INSERT INTO `broadcast_text` VALUES (11831, 'Welcome back, $n. Have I mentioned how good it is to have someone to talk to?$B$BAlright then, let\'s get down to business. What\'s next that we have to do?', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11832, 'Hey, $n. Over here, I want to talk to you.', '', 0, 0, 0, 70, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11833, 'Who\'s the most popular hero of the realm, you ask? Well here are the current standings!$B$BThrall: $2200w$BCairne: $2201w$BSylvanas: $2202w$BTotal Horde: $2207w$B$BBolvar: $2203w$BMagni: $2204w$BTyrande: $2205w$BTotal Alliance: $2206w$B$BIf you want to cast your vote, be sure turn in your gift collections!', 'Who\'s the most popular hero of the realm, you ask? Well here are the current standings!$B$BThrall: $2200w$BCairne: $2201w$BSylvanas: $2202w$BTotal Horde: $2207w$B$BBolvar: $2203w$BMagni: $2204w$BTyrande: $2205w$BTotal Alliance: $2206w$B$BIf you want to cast your vote, be sure turn in your gift collections!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11834, 'Hi! I\'m Kwee Q. Peddlefeet! I\'m here to find out who is the most popular of all the heroes in Azeroth!$B$BIt sure warms my heart to see so many adoring fans greet their leaders, but... do you know what would warm me even more?$B$BA kiss!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11835, 'How are the gift standings? Who\'s the most popular?', 'How are the gift standings? Who\'s the most popular?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11836, 'Annalise? Is that you?', '', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11837, '', 'My dear Staffron, have you forgotten what it is to love? The love that we once shared?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11838, '', 'Staffron...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11839, 'Have I forgotten? Of course I have... I have forgotten about love, happiness... of life itself.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11840, 'But I do know this - love makes the heart and body weak. It can be exploited. Without your love, I have only my work, Annalise.', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11841, '', 'But of course I love you, Staffron. And it pains me to see you suffer so.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11842, '', 'I pledge my love to you forever. Death cannot erase that, even as I am sure that you still remember your love for me.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11843, '', 'And if you do not, then I am truly lost.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11844, 'How could you love me, Annalise? Look at me. I am not the man you once knew.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11845, 'Annalise? Annalise! No, don\'t leave me!', '', 0, 0, 0, 20, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11846, 'Annalise... You\'re right. I-I can\'t do this. I must find another way.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11847, 'You there. You\'re welcome to take the contents of my cauldron. It was to be the second stage of my plan - amorous clothing.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11848, 'You two are surely star-crossed lovers who have found each other. There is much happiness in your future.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11850, 'Yikes! Two fiery personalities. You\'ll have many arguments, but your love will burn just as hot!', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11851, 'While you may try to make it work, in the end, you will go your separate ways.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11852, 'You two will have many ups and downs, but if you can hold on, the ride will be worth it.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11853, 'I hear wedding bells in your future.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11854, 'The future is cloudy for you two. If you work at your relationship, I\'m sure you can find some measure of happiness.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11856, 'Here are the final standings!$B$BThrall: $2200w$BCairne: $2201w$BSylvanas: $2202w$BTotal Horde: $2207w$B$BBolvar: $2203w$BMagni: $2204w$BTyrande: $2205w$BTotal Alliance: $2206w$B$BThe Alliance has the most popular hero!', 'Here are the final standings!$B$BThrall: $2200w$BCairne: $2201w$BSylvanas: $2202w$BTotal Horde: $2207w$B$BBolvar: $2203w$BMagni: $2204w$BTyrande: $2205w$BTotal Alliance: $2206w$B$BThe Alliance has the most popular hero!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11857, 'Here are the final standings!$B$BThrall: $2200w$BCairne: $2201w$BSylvanas: $2202w$BTotal Horde: $2207w$B$BBolvar: $2203w$BMagni: $2204w$BTyrande: $2205w$BTotal Alliance: $2206w$B$BThe Horde has the most popular hero!', 'Here are the final standings!$B$BThrall: $2200w$BCairne: $2201w$BSylvanas: $2202w$BTotal Horde: $2207w$B$BBolvar: $2203w$BMagni: $2204w$BTyrande: $2205w$BTotal Alliance: $2206w$B$BThe Horde has the most popular hero!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11858, 'I am free! Let\'s work together now to save my sister, Jarien!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11859, 'Thank you for freeing me, my sister, and all of you! We can finally rest in peace now.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11860, '', 'My torture is ended and now I can join the Goddess. Thank you so very much!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11861, 'Though I move on in peace, you all have my undying gratitude. Thank you for freeing me from the curse of my folly and greed.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11862, 'Very clever how you got in here like that, $n.$B$BWhat? Surprised that I know your name? I\'m a demon, that\'s what we do. I also know your true name, so you\'d better buy something expensive.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11863, 'Here for the rod? Or, maybe you\'re simply stocking up on runes?$B$BWait a minute! Are you just trying to offload a whole bunch of junk on me?! Curse my never-ending compulsion to buy, buy, buy!', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11864, 'What? No, I don\'t do armor repairs. Everlook is about a mile to the north, $g brother : sister;.$B$BNow buy something, or move along before I summon my warlock to deal with you!', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11865, 'Can I show you what I have for sale?\n\n', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11866, 'If I make a gift collection, what do I do with it?', 'If I make a gift collection, what do I do with it?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11868, 'If it is your desire to visit the rise of the Defiler, you have only to ask. I am able to transport you and your comrades.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11869, 'We wish to face the Defiler.', 'We wish to face the Defiler.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11870, 'When you give love tokens to townsfolk, you\'ll receive different gifts in return. Some of those you\'ll want to keep and use, but others can be bundled together and given to your favorite hero! Visit Kwee Q. Peddlefeet to turn them in.$B$BKwee can be found near Cairne, Sylvanas and Thrall.', 'When you give love tokens to townsfolk, you\'ll receive different gifts in return. Some of those you\'ll want to keep and use, but others can be bundled together and given to your favorite hero! Visit Kwee Q. Peddlefeet to turn them in.$B$BKwee can be found near Cairne, Sylvanas and Thrall.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11871, '<PH> Incoming Mages <PH>', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11872, 'Here are the final standings!$B$BThrall: $2200w$B$BCairne: $2201w$B$BSylvanas: $2202w$B$BTotal Horde: $2207w$B$B$BBolvar: $2203w$B$BMagni: $2204w$B$BTyrande: $2205w$B$BTotal Alliance: $2206w$B$BThe Horde and the Alliance tied in total votes!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11873, 'We am free! Thank you little, puny ones.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11874, 'The content on the pages of this tome appear to be in constant flux. You feel as if your thoughts are being bled from your mind and consumed by the book.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11875, 'Hundreds of various diagrams and schematics begin to take shape on the pages of the book. You recognize some of the diagrams while others remain foreign but familiar.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11876, 'I am 100% confident that I wish to learn in the ways of goblin engineering.', 'I am 100% confident that I wish to learn in the ways of goblin engineering.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11877, '', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11878, 'I am 100% confident that I wish to learn in the ways of gnomish engineering.', 'I am 100% confident that I wish to learn in the ways of gnomish engineering.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11879, 'Your fate is sealed, Deliana! You will pay for your trespasses against Lord Valthalak!', 'Your fate is sealed, Deliana! You will pay for your trespasses against Lord Valthalak!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11880, 'So you\'re looking to relearn what you forgot, eh? Or maybe learn what you were never taught? I know just the thing, $n. Over by Steamwheedle port, to the east, you\'ll find a magical book in Narain\'s hut called \"Soothsaying for Dummies.\" Give it a read.$B$B<Nixx winks.>$B$BYou\'ll find all that you need to know and then some...', '', 0, 0, 0, 6, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11881, '', 'You\'re never going to stop coming after me, are you?  Fine, but you won\'t take me down without a fight!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11882, 'Teach you the ways of gnomish engineering? Not going to happen! Not this gnome, anyway. You have a stink on you, $n. Maybe this is the pity talking but I will offer some advice.$B$BIn the Tanaris desert you\'ll find a hut that belongs to Narain Soothfancy. In that hut is a magical book that has been known to do wonderous things. Find that book and you may yet learn the gnomish way.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11883, '', 'I didn\'t mean to drag you into this, $n.  Ever since we took on a job to steal Lord Valthalak\'s spellbook, everyone involved has wound up dead or missing.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11884, 'Teach you gnomish engineering? I don\'t have time for this nonsense. You only get one chance with Oglethorpe and you blew that, didn\'t you?$B$BYou\'ll have to head to Tanaris and find Narain Soothfancy\'s hut. In that hut is a magical book that might be able to jostle whatever is left of your brain and reconnect you with what you\'ve lost.$B$BNow get lost!', '', 0, 0, 0, 1, 1, 5, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11885, '', 'Trying to find out more about my predicament has been nearly impossible.  The smuggler that hired us has vanished and Anthion Harmon, our mercenary company\'s leader, is dead.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11888, '', 'Come to think about it, most everyone involved is dead.  I\'ll understand if you don\'t want to be involved in any of this, $n.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11889, 'I am absolutely certain that I want to learn dragonscale leatherworking.', 'I am absolutely certain that I want to learn dragonscale leatherworking.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11890, 'I am absolutely certain that I want to learn elemental leatherworking.', 'I am absolutely certain that I want to learn elemental leatherworking.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11891, 'I am absolutely certain that I want to learn tribal leatherworking.', 'I am absolutely certain that I want to learn tribal leatherworking.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11892, 'The assortment of images, shapes, and markings come together before your eyes. The book seems to know your skill with leatherworking and offers you a choice - but be warned - you may only select one and once you have done so, may not change your mind!:', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11893, 'Your fate is sealed, Mokvar! You will pay for your trespasses against Lord Valthalak!', 'Your fate is sealed, Mokvar! You will pay for your trespasses against Lord Valthalak!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11894, 'I cannot teach you dragonscale leatherworking, $n. You must go to Tanaris and find the home of Narain Soothfancy. Once there, consult the book \"Soothsaying for Dummies.\"', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11895, 'Begone foul creatures! Go back to the void your master shaped you from!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11896, 'It was not my intention to get you involved in this, $n. I\'ve spent every waking hour looking over my shoulder ever since I helped an old friend in a daring raid on Blackrock Spire.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11897, 'Trying to find out more about these creatures hasn\'t been easy. My friend and the person who hired his mercenaries to steal Lord Valthalak\'s spellbook have both been feared dead for a while.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11898, 'One by one, nearly everyone involved in this matter has fallen prey to these spectres. I\'ll understand if you decide to walk away from this, $n.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11899, '', 'I cannot teach you elemental leatherworking, $n. You must go to Tanaris and find the home of Narain Soothfancy. Once there, consult the book \"Soothsaying for Dummies.\"', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11900, 'I cannot teach you elemental leatherworking, $n. You must go to Tanaris and find the home of Narain Soothfancy. Once there, consult the book \"Soothsaying for Dummies.\"', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11901, '', 'I cannot teach you tribal leatherworking, $n. You must go to Tanaris and find the home of Narain Soothfancy. Once there, consult the book \"Soothsaying for Dummies.\"', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11902, 'I cannot teach you tribal leatherworking, $n. You must go to Tanaris and find the home of Narain Soothfancy. Once there, consult the book \"Soothsaying for Dummies.\"', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11903, 'Thrall has shown great kindness in granting me sanctuary within his domain. I do not wish to overstay my welcome, at any rate. It would be a great dishonor if I brought any trouble to our leader.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11904, '', 'Try not to draw too much attention, I\'m waiting for the King to grant me audience.  Not that I mind waiting, I feel pretty safe around all these burly guards.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11905, 'Losing my life was a danger I had come to terms with. Losing Ysida is something I cannot endure, even after my own death.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11906, 'Greetings, friend! Can I interest you in some ectoplasmic capacitors? Or perhaps a nice omni-directional spectral assimilator? ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11907, 'What now? Can\'t you see I\'m rather busy?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11908, 'I seem to have misplaced the distiller, Mux. Did I happen to leave it here?', 'I seem to have misplaced the distiller, Mux. Did I happen to leave it here?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11909, 'About that ghost revealer. Did I happen to leave it here? I seem to be missing it.', 'About that ghost revealer. Did I happen to leave it here? I seem to be missing it.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11910, 'I believe I misplaced that banner you enchanted for me, Falrin.', 'I believe I misplaced that banner you enchanted for me, Falrin.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11911, 'Anthion, I seem to have misplaced the banner you created for me. ', 'Anthion, I seem to have misplaced the banner you created for me. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11912, 'I guess it\'s all just about over, right $n? I\'m going to miss you... it\'ll be pretty lonely up here without someone to talk to.$B$BBut that doesn\'t mean that you can\'t come back to see me in the future!', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11913, 'Hey Vi\'el, show me your wares!', 'Hey Vi\'el, show me your wares!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11914, 'Return the amulet! Return to me what was stolen!!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11915, 'Bodley, tell me what\'s going on.', 'Bodley, tell me what\'s going on.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11916, 'It\'s quite simple, really, $n. My old merc company, The Veiled Blade, took the wrong mission at the wrong time.$B$BWe were hired by a goblin smuggler out of Booty Bay, one Gremnik Rizzlesprang, who was covetous of Lord Valthalak\'s spellbook. Or, maybe there was some old rivalry there? I suppose we\'ll never know now, because Gremnik was killed not too long ago by a group of Valthalak\'s spectral assassins. They really can be very deadly when a large number of them gather.$B$BWhere was I?', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11917, 'You were telling me about The Veiled Blade and how you took the wrong mission.', 'You were telling me about The Veiled Blade and how you took the wrong mission.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11918, 'Oh yes, that\'s right.$B$BSo, here we are invading deep into the upper reaches of Blackrock Spire, against their lord and leader; this was before Drakkisath. Not too smart if you ask me, but our leader, Anthion Harmon, you met him, was always one who loved a challenge.$B$BSurprisingly, everything went according to plan, right up to the point where we were prying Valthalak\'s spellbook out of his stiff fingers. That\'s when it all went south.$B$BShould I continue? You look like your eyes are glazing over.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11919, 'No, no, please continue... it\'s just that the air up here is so, um, smoky.', 'No, no, please continue... it\'s just that the air up here is so, um, smoky.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11920, '<Bodley looks at you dubiously, but continues...>$B$BThere we are, spellbook in hand, and Kormok, our two-headed ogre necromancer, gets this strange look in all four of his eyes. It was really creepy, because that second set of eyes always kind of looked dead, like a cow\'s eyes, you know? So, all of a sudden he just reaches out and rips this amulet off that Valthalak was wearing.$B$BThe rest of the company went crazy; some of us telling them to leave it alone, and others who wanted it for themselves.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11921, 'Please continue, Bodley.', 'Please continue, Bodley.', 0, 0, 0, 20, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11922, 'Sheesh, you don\'t have to beg! Anyways, I\'m almost done with the story.$B$BSo, the whole company is in the middle of that massive chamber fighting over this amulet, and you know how it echoes in there! In the end it was broken into three pieces, and we rolled the dice, with it going to various people or groups.$B$BThe rest is a bit of a blur, though, because it was at about that time that old Valthalak\'s spirit decided to make an entrance and start summoning forth all of these spectral assassins.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11923, 'I thought you were almost done?', 'I thought you were almost done?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11924, 'Cool your heels!$B$B<Bodley grumbles something under his breath about the impatience of the living>$B$BAs I was saying, Lord Valthalak\'s spirit and spectral assassins, summoned forth through his necromantic dabbling, started to assail us. Let me tell you, at that point we weren\'t in any condition to fight back, so we ran. I can still remember Mor Grayhoof, one of our druids, slip and fall at one point into the lower reaches of the spire.$B$BThe rest as they say, is history.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11925, 'What happened to your mercenary company, The Veiled Blade?', 'What happened to your mercenary company, The Veiled Blade?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11926, 'We broke up afterward, that\'s what happened. Too much bad blood, I guess...it had been building for a while. And the thing with the amulet was the straw that broke the kodo\'s back.$B$BEver since then Valthalak\'s servants have been picking us off one by one. Most of the amulet piece holders are dead, their souls corrupted by the portion of Valthalak\'s spirit contained within it.$B$BThat\'s why I\'m glad that you\'re here, $n. With your help we\'ll be able to recombine the amulet and return it to Lord Valthalak!', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11927, 'Your meddling ends here, $n! Lord Valthalak shall have his revenge!', 'Your meddling ends here, $n! Lord Valthalak shall have his revenge!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11928, 'Friends of yours?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11929, 'Use your tonk action bar above your standard action bar to attack other tonks.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11930, '', 'I\'ll be fine for now, don\'t worry about me.  I\'ll find a way out of here once I\'ve performed some cleansing rituals in this room.  Foul magic taints this place, $n.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11931, '', 'You did it... you\'ve slain Baron Rivendare!  The Argent Dawn shall hear of your valiant deeds!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11932, 'Talk about making the wrong investment!$B$BLord Valthalak\'s dark book of spells fetched a pretty penny in the black market. It\'s too bad he didn\'t take too kindly to getting his possessions stolen. That stinker got us even after he\'d been dead for months!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11933, 'Ysida lives thanks to your courage, $n. I am deeply in your debt and shall assist you as much as it is in my power.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11934, 'What are these Tonk Control Consoles?', 'What are these Tonk Control Consoles?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11935, '', 'Our newest attraction, and at no cost to you I might add, these miniature tanks, or Tonks as we\'ve cleverly named them, can be controlled from any of the Tonk Control Consoles spread throughout the faire.$B$BAll that you have to do is activate an available control console. Each tonk will come equipped with a stock set of devices, and a random special power to unleash upon your enemy!$B$BHold epic battles with your friends, cruise around, or just cause wild mayhem... the choice is yours!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11936, 'Tell me about the cannon.', 'Tell me about the cannon.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11937, '', 'The Blastenheimer 5000 Ultra Cannon is a state-of-the-art single person projectile device. Delivering its payload at near the speed of a dragon, the cannon is the pride and joy of the Blastenheimer family, world renown for their aeronautical and combustible feats!$B$BIf you\'d like to be launched through the skies to fly free as a bird, seek out Maxima Blastenheimer on the east end of the faire. I hear that she\'s placed a target ring way over on Crystal Lake for you to aim for as you sail through the air!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11938, '', 'The Blastenheimer 5000 Ultra Cannon is a state-of-the-art single person projectile device. Delivering its payload at near the speed of a dragon, the cannon is the pride and joy of the Blastenheimer family, world renown for their aeronautical and combustible feats!$B$BIf you\'d like to be launched through the skies to fly free as a bird, seek out Maxima Blastenheimer on the south edge of Thunder Bluff\'s Hunter Rise. She\'ll aim you at the target far to south on Stonebull Lake!', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11939, '%s goes into a rage after seeing $n fall in battle!', '%s goes into a rage after seeing $n fall in battle!', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11940, '', 'It is said that Kel\'Thuzad seeks the legendary Atiesh, greatstaff of the Guardian.$B$BYes, the very staff once held by Medivh.', 0, 0, 0, 1, 273, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11941, 'Watch your step, $r. We of the Brotherhood of the Light are not as lenient as our brethren when it comes to meting justice to those that would do this world wrong.$B$BWoe unto those that would anger Korfax.$B$BNow if you have no business with me or the other members of the Brotherhood, please leave so that we may continue our meeting. \n', '', 0, 0, 0, 25, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11942, 'The Lich King is attempting to make his presence known in our world. This must not be allowed to occur.\n', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11943, 'What is the Brotherhood of the Light? Well... We are all members of the Argent Dawn at the core - members of the Argent Dawn that aren\'t held in check by morals, guilt and useless human emotion. Consider us Scarlet Crusade minus the stupidity, lack of leadership, and blind zealotry.$B$BDon\'t let the Scarlet Commander tell you otherwise.$B$BAnd don\'t let Tyrosus know I said that... It would be a terrible shame if I had to brutally maim him should a brawl break out.\n', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11944, '', 'The Argent Dawn might be impotent idealists but their delusional leader knows that they are outmatched. I have been sent here as an emissary of the Scarlet Crusade. An ambassador, if you will... It is doubtful that we could even dent the armies of Kel\'Thuzad - united or not - but we might be able to contain them long enough for some sort of miracle to happen.$B$BAs for me? I\'ll die in battle...$B$B<Marjhan shrugs.>\n', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11945, '', 'That is wonderous, Rimblat! Even this foul, sundered earth is not beyond healing.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11946, 'Watch, druid. The earth still breathes. It yearns to be cleansed.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11947, 'The title is misleading. I\'m really a very nice person once you get to know me.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11948, 'They almost sent my brother, Loksey, to this gathering. He cannot even take care of his hounds, let alone represent the Scarlet Crusade in a competent manner.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11949, 'I\'ve been expec...$B$B<Rimblat coughs.>$B$BExcuse me, old habit. I am Rimblat of the Earthen Ring. ', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11950, '', 'Hello, $c. Please excuse me as I am having a conversation with Rimblat. He believes that even this land is not beyond saving.', 0, 0, 0, 3, 1, 0, 0, 1, 0);
INSERT INTO `broadcast_text` VALUES (11951, 'What is it that you want, lemming? Get out of my sight before you find out why I am known as the Wrathcaster.', '', 0, 0, 0, 6, 1, 25, 0, 0, 2);
INSERT INTO `broadcast_text` VALUES (11952, 'You want a fight? A fight you\'ll get!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11953, 'I\'m going to kick yer ugly head in!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11954, 'I crush weaklings like you every day! Bring it on!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11955, 'Your insolence is appalling, fool! I shall feast on your soul!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11956, 'Who dares to disturb my eternal rest? Your deaths will be slow and painful!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11957, 'You\'re but worms beneath my feet. You dare challenge me?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11958, 'First Kormok crush your bones! Then Kormok burn your soul!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11959, 'You so little and puny... you no make good servants for Kormok!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11960, 'What is Dreadnaught armor?', 'What is Dreadnaught armor?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11961, 'The first suit of Dreadnaught armor was made exclusively for the Scarlet Highlord Mograine. Whilst donning the full suit, the Highlord looked himself to be a demon from the depths of the Nether.$B$BAye, \'twas quite a sight to behold as Mograine would wade into a hundred Scourge, Ashbringer held high, and come out on the other side in a cloud of dust and flames - enemies left in ashes beneath his feet.', '', 0, 0, 0, 1, 1, 1, 0, 1, 1);
INSERT INTO `broadcast_text` VALUES (11962, 'Continue please.', 'Continue please.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11963, 'It was not until after his controversial death that the knights and warriors of Lordaeron would wear similarly fashioned suits in honor of their fallen hero. While the death of Mograine was a great victory for Kel\'Thuzad, the resulting martyrdom would be felt until the end of the war.$B$BWhere one did fall, many would rise...', '', 0, 0, 0, 1, 1, 1, 1, 1, 1);
INSERT INTO `broadcast_text` VALUES (11964, 'Anything else?', 'Anything else?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11965, 'During your battles with the Scourge in Naxxramas, should you come across desecrated battlements and wartorn plate scraps, return them to me and I shall craft for you a piece of this armor worn by heroes.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11966, 'All of Nature\'s might is at my disposal, puny mortals. Let us see which of my many forms it will be that ends your suffering!', '', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11967, 'Muahahahaha! What is this pathetic spectacle I see before me? Well, what are you waiting for? I hunger, and you look as if you may make for a passable appetizer!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11968, 'You think you can summon us? We are the ones that summon, not you! We bash you good for this and suck the marrow from your bones!', '', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11969, 'I\'m what you call a capitalist, $r.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11970, '', 'In life, I focused my energies on healing others. Let us see in death how much pain I can cause!', 1, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11971, '', 'Hello, brother.', 0, 0, 0, 3, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11972, 'Hello, sister. What have we here?', '', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11973, '', 'Would-be interlopers, I\'m afraid.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11974, 'Shall we slay them for the impertinence of disturbing our sleep?', '', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11975, '', 'Yes, we shall!', 0, 0, 0, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11976, 'What is Redemption armor?', 'What is Redemption armor?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11977, 'The armor of Redemption was worn by our most pious knights - armor worthy of the Lightbringer himself!$B$BThousands fell in the last war; their vestments stripped from their corpses and taken to the dread citadel of Naxxramas.$B$BIt is said that the death knights of Naxxramas corrupt the Redemption plate mail for their own use.$B$BSuch tragedy... ', '', 0, 0, 0, 1, 1, 274, 1, 1, 1);
INSERT INTO `broadcast_text` VALUES (11978, 'What is Cryptstalker armor?', 'What is Cryptstalker armor?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11979, 'The preternatural ability of the crypt fiend to hunt and kill its foe is one studied and vaunted by men in my profession. I, myself, have slain thousands of the beasts in my crusade to rid this world of Scourge.$B$BWhile I loathe the crypt fiend for its merciless nature, its abilities must be admired. It has a job and it does it - very well.', '', 0, 0, 0, 1, 1, 1, 1, 1, 1);
INSERT INTO `broadcast_text` VALUES (11980, 'Continue please.', 'Continue please.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11981, 'I have discovered a use for the carapace of the crypt fiend and its various body parts. I am able to fashion an extremely light and very deadly set of armor by combining the fragments with some of the desecrated magical armors lost in Naxxramas.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11982, 'I am unable to teach a Tauren to ride a Skeletal Mount. I am sorry but there is nothing I can do for you.', 'I am unable to teach a Tauren to ride a Skeletal Mount. I am sorry but there is nothing I can do for you.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11983, 'What brings a tauren to the Plaguelands?', 'What brings a tauren to the Plaguelands?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11984, 'In the last war, tauren fought. Shaman were sent in defense of this world against the Scourge invasion. Most lost their lives. Many were otherwise ripped asunder, their body parts used for foul experiments in that cursed citadel. I mourn their passing but my heart swells with pride at their courage.', '', 0, 0, 0, 1, 1, 1, 0, 1, 1);
INSERT INTO `broadcast_text` VALUES (11985, 'Continue please.', 'Continue please.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11986, 'I was a young tauren, barely having reached adolescence at the time, but I knew that those that would brave the battles for our freedom were well protected in my namesake\'s vestments.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (11987, 'The name of Earthshatter has been one held by my family for hundreds of years. And like my father and his father before him, I too have inherited the right to create the vestments of my namesake. ', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12003, 'What is it that you do exactly, Rohan?', 'What is it that you do exactly, Rohan?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12004, 'Ah, as the title states I am an assassin for the Scarlet Crusade. I spend my days exterminating high priority targets and nights at the Gadgetzan discotheque. Dancing is my true calling. Assassination just pays the bills.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12005, 'So what brings you to Light\'s Hope?', 'So what brings you to Light\'s Hope?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12006, 'Good question, $r. In my younger days I was footloose and fancy free without a care in the world. In those days I would sing in a barber shop quartet out of Stormwind known as the Four Dons. That\'s where I learned how to craft the Bonescythe digs.', '', 0, 0, 0, 273, 1, 1, 1, 1, 1);
INSERT INTO `broadcast_text` VALUES (12007, 'What? Bonescythe?', 'What? Bonescythe?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12008, 'Yes, Bonescythe. You see, it was our tenor, an ornery fellow known as Don Julio, who got me started on the whole thing. In his travels throughout Lordaeron, Don Julio picked up many interesting skills, one of them being the ability to take the bones of fallen Scourge and fashion them into a lethal set of leather armor - perfectly suited for an assassin.', '', 0, 0, 0, 1, 1, 25, 1, 1, 1);
INSERT INTO `broadcast_text` VALUES (12009, 'Wow, you\'re insane, aren\'t you?', 'Wow, you\'re insane, aren\'t you?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12010, 'I\'m as sane as the next guy, $g fella:lady;. Believe what you will, but I AM capable of fashioning this armor. If you\'re interested, maybe we can make a deal. The sooner I can get out of the assassination business, the better.', '', 0, 0, 0, 1, 1, 0, 1, 1, 0);
INSERT INTO `broadcast_text` VALUES (12011, 'Hey wait, Gadgetzan has a disco?', 'Hey wait, Gadgetzan has a disco?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12012, 'Members only, scrub!\n', '', 0, 0, 0, 273, 25, 0, 1, 1, 0);
INSERT INTO `broadcast_text` VALUES (12015, 'Hello, Rayne. What brings the Cenarion Circle to the Plaguelands?', 'Hello, Rayne. What brings the Cenarion Circle to the Plaguelands?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12016, '', 'We are all drawn here for different reasons, $c.$B$B<Rayne smiles.>$B$BI am here to meet with Rimblat of the Earthen Ring. He has some interesting theories on cleansing these lands.', 0, 0, 0, 1, 1, 0, 1, 1, 0);
INSERT INTO `broadcast_text` VALUES (12017, 'Are these lands not beyond healing? They look pretty beat up.', 'Are these lands not beyond healing? They look pretty beat up.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12018, '', 'I think not. Rimblat has already proven that new life can grow from the sundered earth. Now we must find a way to reproduce and, more importantly, sustain the growth.$B$BWhile I am here, I am also offering my services to adventurers entering Naxxramas.', 0, 0, 0, 1, 1, 0, 1, 1, 0);
INSERT INTO `broadcast_text` VALUES (12019, 'What kind of services?', 'What kind of services?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12020, '', 'I am a crafter of some renown, $c. With the proper materials, I am able to craft an armor known as Dreamwalker. Druids - and even some shaman - are drawn to this armor due to its magical properties. Are you interested?', 0, 0, 0, 1, 1, 6, 1, 1, 1);
INSERT INTO `broadcast_text` VALUES (12021, 'What is Atiesh?\n', 'What is Atiesh?\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12022, '', 'Like Kel\'Thuzad, I too was once a mage of the Kirin Tor. And like Kel\'Thuzad, I seek the legendary Atiesh, greatstaff of the Guardian. Our fates are intertwined as such, $c.', 0, 0, 0, 1, 1, 0, 1, 1, 0);
INSERT INTO `broadcast_text` VALUES (12023, 'What Guardian? I don\'t understand any of this.', 'What Guardian? I don\'t understand any of this.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12024, '', 'Progeny of Aegwynn, he that would be the last Guardian of Tirisfal... Medivh.', 0, 0, 0, 1, 0, 0, 1, 0, 0);
INSERT INTO `broadcast_text` VALUES (12025, 'Please continue.', 'Please continue.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12026, '', 'It is a story of legend and one that I do not have the time nor the inclination to retell. Just know that Medivh was the last of the Guardians. The greatstaff of Atiesh was a focal point of his power and a focal point of corruption.', 0, 0, 0, 1, 1, 0, 1, 1, 0);
INSERT INTO `broadcast_text` VALUES (12027, 'What corruption?', 'What corruption?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12028, '', 'The corruption of a fallen titan. The corruption of the Destroyer. After all, the staff was forged from the seed of hate. That is, of course, what has brought me here...', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12029, 'Please continue.', 'Please continue.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12030, '', 'After the fall of Karazhan, the Kirin Tor dispatched an assembly of mages to search the tower and seize all items of import... for the purposes of \"research.\" It was then that the fabled staff was discovered and brought back to Dalaran.', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12031, 'Please continue.', 'Please continue.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12032, '', 'What followed were a series of tragic events, leading to the death of every Kirin Tor mage that came into contact with Atiesh.$B$BYou see, it was an artifact with sentience. A sentience granted to it by Sargeras...', 0, 0, 0, 1, 1, 0, 1, 1, 0);
INSERT INTO `broadcast_text` VALUES (12033, 'What?', 'What?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12034, '', '<Angela nods.>$B$BAfter the deaths, the Six decided that the best course of action was to seal the staff behind impenetrable barriers of magic and appoint a watcher: A guardian of the staff... I would be that watcher.', 0, 0, 0, 273, 1, 0, 1, 1, 0);
INSERT INTO `broadcast_text` VALUES (12035, 'This is disturbing. Please continue.', 'This is disturbing. Please continue.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12036, '', 'There is not much more to tell. Dalaran was nearly wholly destroyed by Archimonde, the staff lost, presumably shattered by the barrier\'s fail-safe mechanisms.$B$BThere are those that believe the staff to be the cause of Archimonde\'s reprisal.', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12037, 'Please, tell me more.', 'Please, tell me more.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12038, '', 'After the fall of Dalaran, I took my leave from the Kirin Tor. I spent years watching over Atiesh, only to have my purpose amongst the Kirin Tor taken from me in one fell swoop. I would find the staff, though it might take a lifetime. That is how I came to join the Argent Dawn and why I am here now.', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12039, 'Is there anything else?', 'Is there anything else?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12040, '', 'I have tracked the various fragments of the staff across this world and in doing so, discovered Kel\'Thuzad\'s plans to reform the staff. He has already collected several of the staff\'s fragments - which are being held by his most trusted lieutenants.$B$BI cannot emphasize enough the importance of keeping the greatstaff of the Guardian out of Kel\'Thuzad\'s grasp. Frankly, I do not know what the result would be, other than a catastrophe of epic proportion.', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12041, 'What is Plagueheart armor?', 'What is Plagueheart armor?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12042, 'It is power, imbecile.$B$B<Mataus\'s eyes darken.>$B$BThese fools send idiots like you into Naxxramas to retrieve desecrated armor so that they may purify it and restore the pieces to their former glory.$B$B<Mataus spits.>$B$BJust the thought of such utter stupidity brings the bile up to my throat.$B$BI too seek that armor, but in its most foul and desecrated form.', '', 0, 0, 0, 275, 1, 25, 1, 2, 2);
INSERT INTO `broadcast_text` VALUES (12043, 'I also seek such power.', 'I also seek such power.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12044, 'I make no pretenses, maggot. The darkness such items hold could very well destroy the both of us. That is why the price is so high. If I\'m going to die, it\'s going to be as a rich man.$B$BAnd I don\'t give an ounce of gnoll spit how you die, only that you do as I ask. Bring me what I require and we will both benefit - or die, but I\'ve already explained this...', '', 0, 0, 0, 1, 1, 274, 1, 1, 1);
INSERT INTO `broadcast_text` VALUES (12045, 'Tell me about faith, Father.', 'Tell me about faith, Father.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12046, 'Faith... Some have lost it, others have found it. It cannot be destroyed and it cannot be created.$B$BOr perhaps you speak of the vestments of Faith? Armor worn by our priests and most devout warriors into battle against the Scourge during the last war.$B$BAye, much of it is lost... Stolen by the fiends of Naxxramas.', '', 0, 0, 0, 1, 1, 0, 1, 1, 0);
INSERT INTO `broadcast_text` VALUES (12047, '', 'Your message did reach us, Commander, but by no means is our involvement here sanctioned by the Grand Crusader.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12048, 'Your leader takes residence at the doorstep to Naxxramas. Do you think he does not feel the pressure?\n', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12049, '%s lowers the sound of his voice to a whisper.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12050, 'Even demons are capable of fear...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12051, 'Scourge activity in the Plaguelands has increased ten-fold. We are receiving reports of Scourge attacks from as far away as Kalimdor! Now how do you suppose this has happened? How have the wretched undead done that which was previously thought impossible? Is nowhere safe?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12052, '', 'The heathens will be forced to react. Orcs, bull-men...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12053, 'Tauren.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12054, '', 'Whatever. The revolting, unkempt Horde filth...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12055, 'You lack tact, Commander. This is not surprising. However, you are right in that the Horde will be forced to act. But not just the Horde. They already come - heroes from across the world.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12056, '', 'Too little, too late. The dam has broken. We are a bump in the road at best.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12057, 'So why are you here? What are we to do if there is no hope?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12058, 'There is always... Ashbringer.', 'There is always... Ashbringer.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12062, 'So this is what you have come for? To try and claim that which is the reason for the Dawn\'s existence and the Crusade\'s downfall? You open old wounds with your words, Commander!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12063, 'The Ashbringer is dead and there will never be another like him. The lesser Mograine is a far cry from the man his father was. You know this, Commander! It was, after all, your own leadership that was responsible for the death of the Scarlet Highlord Mograine. ', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12065, 'It was your leadership that manipulated a grieving child to try and recover the sword. A child that has grown into a man with nothing but vengeance and hatred in his heart! And for what? You are no closer to the sword now than you were five years ago.\n', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12066, 'Yet you think that recovering Ashbringer will somehow turn the tide of battle? Let me let you in on a little secret, Commander: The power of the Ashbringer came from the man who would wield it... I was there, Commander. I watched him burn legions of undead in righteous fire before he would even unsheathe the blade. Alas, your grasp on history is ... lacking.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12067, 'Now be silent and note where you are, lest I inform Lord Fordring and his knights that you are no longer here on amicable terms. I am certain he would take great pleasure in seeing to your \'atonement.\'', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12074, 'I cannot be teachin\' a Tauren to ride a Raptor.', 'I cannot be teachin\' a Tauren to ride a Raptor.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12075, 'Where can I find crypt fiend parts, Leopold?', 'Where can I find crypt fiend parts, Leopold?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12076, 'Those filthy beasts are all over the place.$B$BIf you prefer hunting outdoors, you\'ll find most of them around Plaguewood and Terrordale, with a good concentration of them in the Terrorweb tunnel. They are also likely to appear at almost any Scourge encampment in the Eastern Plaguelands.$B$BIf you\'re a hearty dungeon crawler, you\'ll find a good lot of them in Stratholme and a few of the necrofiend variety in Scholomance.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12077, 'Where can I find bone fragments?', 'Where can I find bone fragments?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12078, 'Are you daft? Did you miss the BONY SKELETAL CREATURES littering the landscape on your way over here?$B$B<Rohan sighs.>$B$BIf you\'re the outdoor type, you\'ll find skeletal creatures all over both the Western and Eastern Plaguelands. Trust me, you can\'t miss \'em - they certainly won\'t miss you if they get the chance.$B$BIf you\'re a dungeon type, Stratholme and Scholomance are definitely the place to be for bone fragments.', '', 0, 0, 0, 6, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12086, 'Where can I find core of elements?', 'Where can I find core of elements?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12087, '', 'Ah, a good question.$B$BIf you prefer hunting in the great outdoors, there are elementals here in the Plaguelands, Azshara, Burning Steppes, Felwood, Silithus, Winterspring, and even Un\'Goro Crater.$B$BIf you are the type that laughs in the face of danger, then perhaps you should visit Dire Maul. I have heard that a great many elemental abberations exist within its walls. You would also do well in seeking out the elementals employed by the Dark Iron dwarves of Blackrock Depths.', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12088, 'Korfax, where can I find Dark Iron scraps?', 'Korfax, where can I find Dark Iron scraps?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12089, 'Is this a serious question? You really don\'t know?$B$BIf you are the cowardly sort, afraid to get their hands too dirty, you will find Dark Iron scraps on the creatures in the Burning Steppes and Searing Gorge.$B$BIf you\'re the bloodthirsty adventuring type, the dungeons of Blackrock Spire and Blackrock Depths hold quite a large supply of the scraps.', '', 0, 0, 0, 6, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12090, 'Rayne, where should I look for savage fronds?', 'Rayne, where should I look for savage fronds?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12091, '', 'If you are one that prefers to hunt in the outdoor world, both Felwood\'s treants and Un\'Goro\'s lashers are an excellent source of fronds.$B$BIf you are more of a risk-taker, the eastern and western wings of Dire Maul will provide you with a nearly limitless supply of savage fronds.', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12096, 'I\'m far too busy to be playing \'guess the smell\' with you, $r. State your business or get out of my sight!', '', 0, 0, 0, 25, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12105, 'Alright, everything looks in order. Let\'s get this show on the road.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12109, 'The time has come for Horde and Alliance to look towards the north and the invasion of the Scourge. In recent days, territories across Kalimdor and the Eastern Kingdoms have come under attack. Will you take up arms to save your land from destruction at their hands?', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12110, 'Don\'t be afraid of the sword, nubling. It\'s the mutton that gets ya!', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12111, 'This cultist is in a deep trance...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12112, 'Use 8 necrotic runes and disrupt his ritual.', 'Use 8 necrotic runes and disrupt his ritual.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12113, 'Onward! The front lines await!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12114, 'Whew! That was some hard work, $r. I haven\'t seen the Scourge this aggressive since...$B$B<Guard Didier swallows hard.>$B$BSince the last war...', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12116, 'Terrible thing, what happened to them mules... Metz is gonna have my hide for this failure.', '', 0, 0, 0, 18, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12118, 'Poor little donkey. It was only a wee lad.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12123, 'Name, rank and serial number, soldier.\n', '', 0, 0, 0, 25, 6, 0, 1, 1, 1);
INSERT INTO `broadcast_text` VALUES (12124, 'Yes, sir! Thank you, sir!', 'Yes, sir! Thank you, sir!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12125, '<Field Marshal Chambers growls.>$B$BSo you\'re the riff-raff that assisted Didier in getting the supplies here in one piece? Where are the rest of you? I know there\'s gotta be more. Chickens like you generally roam in huge packs.$B$BBah, just give me the damned writ so I can sign off on it.\n', '', 0, 0, 0, 6, 25, 1, 1, 1, 1);
INSERT INTO `broadcast_text` VALUES (12126, 'Job well done, soldier. Dismissed!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12127, 'Alright, I\'ll sign writs now. Bring \'em to the front, maggots.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12128, 'I think I hear something! Chambers must be coming. Make way! Make way!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12132, '', 'Ok, you had the fish platter, Commander. Enjoy!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12133, '', 'Father, you had the bowl of fruit? Right?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12134, '', 'And for the ladies, roast baby boar. Enjoy!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12135, '', 'Please let me know if you need anything else!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12136, '', 'Welcome to Light\'s Hope Chapel, stranger.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12137, 'My compliments to the chef!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12138, 'The fish is exquisite, Jessica.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12139, 'Delicious!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12140, 'Thank you, Mrs. Chambers.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12141, 'Is this a mango? Stupendous!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12142, 'You\'ve really outdone yourself this time, Mrs. Chambers. Not a spot of mold to be found!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12143, '', 'Thank you, Jessica.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12144, '', 'And might I add, that dress is lovely! You must tell me where you got it from.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12145, '', 'Indeed... lovely dress, Jessica.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12146, '', 'The boar is superb!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12147, '', 'Oh, quite succulent. Many thanks, Mrs. Chambers.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12148, 'Our caravans go out \'bout twice a day, but I\'ll be damned if the Scourge haven\'t gone out of their way to stop Didier and his mules from getting shipments to the front lines. Craziest thing I\'ve ever seen. If only we had the heroic adventurin\' types to help protect the shipments...', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12152, 'I\'ve got too much to do and not enough time to do it in, $r! Please, get out of my way.', 'I\'ve got too much to do and not enough time to do it in, $r! Please, get out of my way.', 0, 0, 0, 5, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12153, 'I\'ve got too much to do and not enough time to do it in, $r! Please, get out of my way.$B$BIf you\'re infected with the maggot slime or ghoul rot, I might be able to help you.', 'I\'ve got too much to do and not enough time to do it in, $r! Please, get out of my way.$B$BIf you\'re infected with the maggot slime or ghoul rot, I might be able to help you.', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12154, 'I am diseased. Please cure me, medic.', 'I am diseased. Please cure me, medic.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12155, '%s is jolted back to life!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12156, '%s loses its link!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12174, '%s rushes to the aid of Feugen!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12176, 'What\'s happening?', 'What\'s happening?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12178, '%s overloads!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12180, 'The tides of war have come again. From the cold north, the Lich King\'s necropolises have laid siege to our lands. He and his cursed general, Kel\'Thuzad, send flying necropolises to invade across the world.', '', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12195, '%s dies.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12197, 'OH NO YOU DIDN\'! ITS ON NOW! <PH>', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12198, 'Is Winterspring currently under attack?', 'Is Winterspring currently under attack?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12199, 'Indeed, the hills of Winterspring are currently attempting to withstand a renewed Scourge assault. Your assistance would help them greatly.$B$BNumber of Necropolises remaining: $2284W', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12200, 'For the time being, that area is free of Scourge influence. I fear it will only be a matter of time before they return.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12201, 'Is Tanaris currently under attack?', 'Is Tanaris currently under attack?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12202, 'It seems the army of the Scourge has come to Tanaris. A significant number of their necropolises and other forces have been dispatched there.$B$BNumber of Necropolises remaining: $2283W', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12203, 'Are the Blasted Lands currently under attack?', 'Are the Blasted Lands currently under attack?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12204, 'The Scourge infestation grows in the Blasted Lands. Every able bodied warrior is needed to stem the tide of their invasion.$B$BNumber of Necropolises remaining: $2280W', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12205, 'Are the Burning Steppes currently under attack?', 'Are the Burning Steppes currently under attack?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12206, 'If additional support is not soon sent to the Burning Steppes, I fear the Scourge will establish a base of operations there. If you can, you should go there to aid the defenders.$B$BNumber of Necropolises remaining: $2281W', '', 0, 0, 0, 25, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12207, 'What can I do?', 'What can I do?', 0, 0, 0, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12209, 'The Scourge are establishing small staging points in the places under attack, receiving communications and other assistance from the necropolises that fly overhead. From what we can tell, the only way to root them out is by killing the ground forces that surround the points.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12226, 'Miranda, could you please tell me the insignia cost of items that you are offering for adventurers with other reputations?', 'Miranda, could you please tell me the insignia cost of items that you are offering for adventurers with other reputations?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12227, '', 'Certainly, $n. You only need to tell me what reputation you would like to know about.\n', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12228, 'Miranda, could you please tell me the insignia cost of items that you are offering for adventurers of honored reputation?', 'Miranda, could you please tell me the insignia cost of items that you are offering for adventurers of honored reputation?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12229, '', 'Certainly, $n.$B$BAdventurers that the Dawn views as honored must submit a combined total of 40 insignias to access superior items. To access items of even greater power, they would be required to submit a total of 150 insignias.$B$BI must admit, the price is high even for those honored amongst the Dawn.\n', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12231, 'Miranda, could you please tell me the insignia cost of items that you are offering for adventurers of revered reputation?', 'Miranda, could you please tell me the insignia cost of items that you are offering for adventurers of revered reputation?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12232, '', 'Certainly, $n.$B$BAdventurers that the Dawn views as revered must submit a combined total of 14 insignias to access superior items. To access items of even greater power, they would be required to submit a total of 90 insignias.$B$BThose that are revered by the Dawn will find the cost of superior items quite reasonable! The cost for the more powerful items is still high.\n', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12234, 'Miranda, could you please tell me the insignia cost of items that you are offering for adventurers of exalted reputation?', 'Miranda, could you please tell me the insignia cost of items that you are offering for adventurers of exalted reputation?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12235, '', 'Certainly, $n.$B$BAdventurers that the Dawn views as exalted must submit a combined total of 12 insignias to access superior items. To access items of even greater power, they would be required to submit a total of 54 insignias.$B$BThose exalted in the eyes of the Dawn receive the greatest discounts.\n', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12236, '', 'Certainly, $n.$B$BAdventurers that the Dawn views as friendly must submit a combined total of 60 insignias to access superior items. To access items of even greater power, they would be required to submit a total of 220 insignias.$B$BI must admit, the price is quite high for adventurers not favored by the Dawn.\n', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12237, 'Friendly.', 'Friendly.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12238, 'Honored.', 'Honored.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12239, 'Revered.', 'Revered.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12240, 'Exalted.', 'Exalted.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12241, 'I have another question.', 'I have another question.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12242, '%s spots a zombie to devour!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12243, 'You will not stop our deepening shadow, $c. Now... join us! Join the ranks of the Chosen!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12246, 'I\'m done for, $N, but you might still be able to stop these monsters!', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12247, 'Is this the best they could do?$B$B<Omarion musters the strength to laugh.>$B$BYou do not possess the resolve to face a monster like Kel\'Thuzad. You will crumble before his minions - just like those that have come before you...$B$BLeave me here to die. I refuse to return to a world that champions the likes of you.\n', '', 0, 0, 0, 6, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12248, 'Could it be? One that passes through these halls with a purpose? Aye, a righteous fire burns in your belly, $r.$B$BMy life may soon end, but the knowledge I hold must be shared with the world. It is, after all, why I am here now.$B$BAre you a master of a trade?\n', '', 0, 0, 0, 6, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12249, 'You honor me with your presence, $r. Alas, it is too late for me. I pass from this world soon, but... I know so much. So much is in this head of mine. So much that could help this world fend off the beasts of this citadel of depravity.$B$BPerhaps if you are a master of a trade... Yes, perhaps you would take the knowledge I hold and use it to save this world.\n', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12251, 'I am a master tailor, Omarion.', 'I am a master tailor, Omarion.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12252, 'A tailor, eh? Very well. What would you like to learn about, tailor?', '', 0, 0, 0, 6, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12253, 'Glacial Vest.', 'Glacial Vest.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12254, 'Glacial Cloak.', 'Glacial Cloak.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12255, 'Glacial Gloves.', 'Glacial Gloves.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12256, 'Glacial Wrists.', 'Glacial Wrists.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12257, 'I am a master leatherworker, Omarion.', 'I am a master leatherworker, Omarion.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12258, 'Perhaps I can teach you something...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12259, 'Icy Scale Breastplate.', 'Icy Scale Breastplate.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12260, 'Icy Scale Gauntlets.', 'Icy Scale Gauntlets.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12261, 'Icy Scale Bracers.', 'Icy Scale Bracers.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12262, 'Polar Tunic.', 'Polar Tunic.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12263, 'Polar Gloves.', 'Polar Gloves.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12264, 'Polar Bracers.', 'Polar Bracers.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12265, 'I have what you need, $c.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12266, 'Icebane Breastplate.', 'Icebane Breastplate.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12267, 'Icebane Gauntlets.', 'Icebane Gauntlets.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12268, 'Icebane Bracers.', 'Icebane Bracers.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12269, 'I am a master blacksmith, Omarion.', 'I am a master blacksmith, Omarion.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12270, 'I need to go. Evil stirs. Die well, Omarion.', 'I need to go. Evil stirs. Die well, Omarion.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12271, 'Show me where I can fly.', 'Show me where I can fly.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12273, 'Master craftsman Omarion just vanished one night. Like a toot in the wind... Poof! Gone! Bah, now I\'m the only one left to craft the goods for the boys on the front lines. ', '', 0, 0, 0, 1, 14, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12274, 'Show me your wares, Wilhelm.', 'Show me your wares, Wilhelm.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12275, 'Your actions will not be tolerated!', 'Your actions will not be tolerated!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12276, 'You will abide by our rules around here, miscreant!', 'You will abide by our rules around here, miscreant!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12277, 'Lay down your weapons, your feuds have no place here!', 'Lay down your weapons, your feuds have no place here!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12279, 'Omarion, I am not a craftsman. Can you still help me?', 'Omarion, I am not a craftsman. Can you still help me?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12280, '<Omarion grumbles something under his breath.>$B$BBut of course I can help you, dear $g boy:girl;. It is my dying wish... my final desire. Please, take this handbook. The information on its pages are a compilation of all that I know and have learned in the past 30 some-odd years of my miserable life. Give the handbook to that good for nothing apprentice of mine that\'s probably sitting on his fat duff at Light\'s Hope Chapel.', '', 0, 0, 0, 14, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12281, 'Thank you, Omarion. You have taken a fatal blow for the team on this day.', 'Thank you, Omarion. You have taken a fatal blow for the team on this day.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12282, '<Tarsis groans.>$B$BThere may still be time... Listen closely, mortal.$B$BThrough time and across the Nether I have searched. Deals made with a host of evil and villainy so wretched...$B$B<Tarsis coughs blood into his hands.>$B$BWould you... would you believe that even Elune has turned her back on me, $r? To die here, in this manner now...', '', 0, 0, 0, 1, 7, 6, 0, 3, 4);
INSERT INTO `broadcast_text` VALUES (12283, 'What are you talking about, mage?', 'What are you talking about, mage?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12284, 'The things I have done... such horrible things...$B$B<Tarsis begins to sob.>\n', '', 0, 0, 0, 1, 18, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12285, 'Why have you done such horrible things?\n', 'Why have you done such horrible things?\n', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12286, 'I am Highborne, $r. It has no meaning now. There was a time, though, when this world was ruled by Highborne. Ten thousand years past...', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12287, 'I still do not understand.', 'I still do not understand.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12288, 'I would have it back, mortal. Atiesh... From it I would create the Highborne anew. I would have it all back. Even the Queen...', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12289, 'You are talking nonsense, mage. ', 'You are talking nonsense, mage. ', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12290, '<Tarsis grabs you weakly by the collar. Even in death you see power unimaginable in his eyes.>$B$BAm I? Then listen closely, for more nonsense is about to escape my lips. Within this citadel you will find my life\'s work: The exact location of every fragment of the splintered staff.', '', 0, 0, 0, 53, 273, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12291, 'What staff?', 'What staff?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12292, 'Atiesh, Greatstaff of the Guardian. The staff handed down through the ages to each of the Guardians of Tirisfal. The staff corrupted by Sargeras. The staff of the last Guardian. The staff of Medivh.\n', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12293, 'You said you would have it back. What does that mean?', 'You said you would have it back. What does that mean?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12294, '<Tarsis\'s eyes light up.>$B$BI held it once... I held it despite the warnings of the Kirin Tor. I held it, mortal... Power unending. Power to do what I desired, as I desired. And just as quickly, it was taken from me. But I had tasted... And that taste was all that it took. ', '', 0, 0, 0, 1, 1, 273, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12295, '%s gets to one knee.', '%s gets to one knee.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12296, '%s sits down.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12297, '<Tarsis is badly injured.>', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12298, '<Wake Tarsis up.>', '<Wake Tarsis up.>', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12299, 'The battle is won. For the time being, the Scourge threat has been pushed back. Our resources can be channeled into aiding you, in thanks and preparation for the future.', 'The battle is won. For the time being, the Scourge threat has been pushed back. Our resources can be channeled into aiding you, in thanks and preparation for the future.', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12300, 'The battle goes well. The Scourge forces seem weakened. I believe it will only be a matter of time before we drive them from our shores. I will turn my magics to you, to aid in this struggle.', 'The battle goes well. The Scourge forces seem weakened. I believe it will only be a matter of time before we drive them from our shores. I will turn my magics to you, to aid in this struggle.', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12301, 'The first steps of our fight against the Scourge go well. We have had some successes, and hopefully my assistance can aid you in bringing future success to the battle.', 'The first steps of our fight against the Scourge go well. We have had some successes, and hopefully my assistance can aid you in bringing future success to the battle.', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12302, 'Give me one of your magic items.', 'Give me one of your magic items.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12303, 'I put at your disposal whatever assistance I can offer.', 'I put at your disposal whatever assistance I can offer.', 0, 0, 0, 2, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12304, 'What happened to the staff?', 'What happened to the staff?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12305, 'It was lost when the Violet Citadel fell... splintered into forty-two pieces.', '', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12306, 'So what has led you to Naxxramas?', 'So what has led you to Naxxramas?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12307, 'Kel\'Thuzad has put forth considerable effort in attempting to reform the staff. The pieces were once scattered across this world, from Kalimdor to the South Seas. I myself held twenty of the pieces at one point in time - until they were stolen from me by Kel\'Thuzad\'s minions. It is why I am here.', '', 0, 0, 0, 1, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12308, 'So Kel\'Thuzad holds all of the pieces?', 'So Kel\'Thuzad holds all of the pieces?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12309, 'Haven\'t you been listening? No, I very much doubt we would be having this conversation had Kel\'Thuzad formed the staff. Kel\'Thuzad holds the head. His underlings hold forty splinters of Atiesh. The last piece - the base - was uncovered by Brann Bronzebeard. He was last seen in the Silithus desert; undoubtedly doing his best to stay as far away as possible from the relentless seekers Kel\'Thuzad has dispatched into the world.', '', 0, 0, 0, 274, 1, 1, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12310, 'To arms! Scourge spotted in the Cathedral of Light!', 'To arms! Scourge spotted in the Cathedral of Light!', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12311, 'Scourge in the Trade District! Have at them!', 'Scourge in the Trade District! Have at them!', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12312, 'You... The frame! How? You must not... It... the Destroyer...', '', 0, 0, 0, 5, 274, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12313, 'What is it Tarsis? I don\'t know what to do!', 'What is it Tarsis? I don\'t know what to do!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12314, '%s scratches at his throat.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12315, 'Light help us... the Scourge are in the Park!', 'Light help us... the Scourge are in the Park!', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12316, 'The Scourge are at the castle entrance! For Stormwind! For King Anduin!', 'The Scourge are at the castle entrance! For Stormwind! For King Anduin!', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12317, 'Hold the line! Protect the King at all costs!', '', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12318, 'Good work, one and all! The Scourge at the castle have been defeated.', '', 0, 0, 7, 71, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12320, '', '%s scratches at her throat.', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12321, '', 'What have you done? FOOL! You brought it... NO!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12325, 'We come destroy puny ones!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12326, 'Kel\'Thuzad say to tell you... DIE!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12327, 'Raaarrrrggghhh! We come for you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12328, 'Hahaha, your guards no match for Scourge!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12329, 'What? This not Naxxramas! We not like this place... destroy!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12330, 'You spare parts! We make more Scourge in necropolis.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12331, '', 'The Scourge attack against my court has been eliminated.  You may go about your business.', 0, 0, 1, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12332, '', 'My Royal Dreadguard, you will deal with this matter as befits your station.  That, or you will wish that you had.', 0, 0, 1, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12333, 'Dreadguard, hold your line. Halt the advance of those Scourge!', '', 0, 0, 1, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12334, 'Scourge in sewers! We need help!', '', 0, 0, 1, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12335, 'Scourge attack Magic Quarter!', '', 0, 0, 1, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12336, 'Rogues\' Quarter attacked by Scourge! Help!', '', 0, 0, 1, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12337, 'The Scourge are at the entrance to the Royal Quarter! Kill them!!', 'The Scourge are at the entrance to the Royal Quarter! Kill them!!', 0, 0, 1, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12338, '%s drops its weapon.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12341, 'Gangway, ye scurvy dog! Revilgaz has spared me from the gallows but my punishment might be nearly as bad.', 'Gangway, ye scurvy dog! Revilgaz has spared me from the gallows but my punishment might be nearly as bad.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12342, 'Why you run away? We make your corpse into Scourge.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12343, 'No worry, we find you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12344, '', 'You\'d better not be causing trouble, $n!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12345, 'What can I do for you, $n? Make it quick, I don\'t have all day!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12346, 'What can I do for you? No, wait! What can you do for me?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12347, '%s belches out a disgusting Bile Sludge!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12348, '%s devours all nearby zombies!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12349, 'The Stormpike forces gain the General\'s Warcry.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12350, 'The Frostwolf forces gain the General\'s Warcry.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12351, 'Take no prisoners! Drive these heathens from our lands!', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12352, 'Leave no survivors!', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12353, 'There Scourge outside Trade Quarter!', '', 0, 0, 1, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12354, 'Scourge inside Trade Quarter! Destroy!', '', 0, 0, 1, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12355, 'Scourge spotted nearby!', 'Scourge spotted nearby!', 0, 0, 1, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12356, 'I just saw a Scourge! Kill it!', 'I just saw a Scourge! Kill it!', 0, 0, 1, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12357, 'Did you see that? There\'s a Scourge over there!', 'Did you see that? There\'s a Scourge over there!', 0, 0, 1, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12358, 'How can I get anything done with the Scourge running amok in here?!', 'How can I get anything done with the Scourge running amok in here?!', 0, 0, 1, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12359, 'There\'s one of the Scourge, right over there!', 'There\'s one of the Scourge, right over there!', 0, 0, 1, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12360, 'This has gone too far. How dare the Scourge attack Undercity! Destroy it before more come!', 'This has gone too far. How dare the Scourge attack Undercity! Destroy it before more come!', 0, 0, 1, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12361, 'Will these unrelenting Scourge attacks never end?', 'Will these unrelenting Scourge attacks never end?', 0, 0, 1, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12362, 'Destroy the Scourge invader now, before it\'s too late!', 'Destroy the Scourge invader now, before it\'s too late!', 0, 0, 1, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12363, 'I desire this eternal quintessence, Duke Hydraxis.', 'I desire this eternal quintessence, Duke Hydraxis.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12364, 'You\'ve served our cause with great loyalty, $n. Allow me to present you with an unending source of our elemental quintessence.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12365, 'Welcome back, $n. You\'ve proven a worthy ally against Ragnaros and your presence is always welcome among us.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12366, 'Scourge spotted nearby!', 'Scourge spotted nearby!', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12367, 'I just saw a Scourge! Kill it!', 'I just saw a Scourge! Kill it!', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12368, 'Did you see that? There\'s a Scourge over there!', 'Did you see that? There\'s a Scourge over there!', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12369, 'How can I get anything done with the Scourge running amok around here?!', 'How can I get anything done with the Scourge running amok around here?!', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12370, 'There\'s one of the Scourge, right over there!', 'There\'s one of the Scourge, right over there!', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12371, 'This has gone too far. How dare the Scourge attack Stormwind! Destroy it before more come!', 'This has gone too far. How dare the Scourge attack Stormwind! Destroy it before more come!', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12372, 'Will these unrelenting Scourge attacks never end?', 'Will these unrelenting Scourge attacks never end?', 0, 0, 7, 6, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12373, 'Destroy the Scourge invader now, before it\'s too late!', 'Destroy the Scourge invader now, before it\'s too late!', 0, 0, 7, 5, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12374, 'The dead walk among us!', 'The dead walk among us!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12375, 'You hold evil in your hands, fool! REPENT!', 'You hold evil in your hands, fool! REPENT!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12376, 'You risk destroying us all, $r! You must be terminated!', 'You risk destroying us all, $r! You must be terminated!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12377, 'This is holy ground, heathen.', 'This is holy ground, heathen.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12378, 'Ashbringer...', 'Ashbringer...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12379, 'Kneel! Kneel before the Ashbringer!', 'Kneel! Kneel before the Ashbringer!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12380, 'I am unworthy, $g sir:maam;.', 'I am unworthy, $g sir:maam;.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12381, 'Have you come to save this world? To cleanse it?', 'Have you come to save this world? To cleanse it?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12382, 'My $g Lord:Lady;, please allow me to live long enough to see you purge this world of the infidels.', 'My $g Lord:Lady;, please allow me to live long enough to see you purge this world of the infidels.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12383, 'And so it begins...', 'And so it begins...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12384, 'Take me with you, $g sir:maam;.', 'Take me with you, $g sir:maam;.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
