INSERT INTO `broadcast_text` VALUES (12848, '', 'With the Emperors slain, the magical protection blocking teleportation within the temple has been greatly weakened. If you desire, I am able to translocate you to the Emperors\' lair.', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12849, 'Teleport me to the lair of the Twin Emperors, please.', 'Teleport me to the lair of the Twin Emperors, please.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12850, '', 'With Ouro slain the way is clear to the final chamber. I am able to transport you there, if you desire.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12851, 'Please teleport me to the final chamber.', 'Please teleport me to the final chamber.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12852, '', 'Your old lives, your mortal desires mean nothing... you are acolytes of the master now, and you will serve the cause without question! The greatest glory is to die in the master\'s service!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12853, '', 'The master will avenge me!!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12854, '', 'You have failed!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12855, '', 'Pathetic wretch!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12856, '', 'Slay them in the master\'s name!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12857, '', 'You cannot hide from me!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12858, '', 'Kneel before me, worm!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12859, '', 'Run while you still can!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12860, 'Enjoying the festival, $c?$b$bWhile the Flamekeeper tends to the fires of the present, I am more of a historian, keeping close the festivals past. I\'m also, of course, documenting this year\'s festivities. Things are going well thus far, don\'t you think?$b$bYou know, $n, there is power inherent in all festival fires burning throughout the holiday. We\'re taking care of ours, but I\'m certain there are sacred flames burning deep within our enemy\'s cities...', 'Enjoying the festival, $c?$b$bWhile the Flamekeeper tends to the fires of the present, I am more of a historian, keeping close the festivals past. I\'m also, of course, documenting this year\'s festivities. Things are going well thus far, don\'t you think?$b$bYou know, $n, there is power inherent in all festival fires burning throughout the holiday. We\'re taking care of ours, but I\'m certain there are sacred flames burning deep within our enemy\'s cities...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12984, 'Who dares violate the sanctity of my domain? Be warned: all who trespass here are doomed!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12985, 'Fools! You think yourselves triumphant? You have only taken one step closer to the abyss!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12986, 'I grow tired of these games. Proceed, and I will banish your souls to oblivion!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12987, 'You have no idea what horrors lie ahead. You have seen nothing! The frozen heart of Naxxramas awaits you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12988, 'It is good that you serve me so faithfully. Soon all will serve the Lich King... and in the end, you shall be rewarded - so long as you do not falter.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12989, 'Your security measures have failed. See to this interruption immediately!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12990, 'Our preparations continue as planned, master.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12991, 'I foresee no complications at this-- Wait! What is this?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12992, 'Yes, master!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12993, 'Come, heroes... By the will of the Lich King, you shall be destroyed.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12994, 'Very well. Warriors of the frozen wastes, rise up! I command you to fight, kill and die for your master! Let none survive!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12995, 'Pray for mercy!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12996, 'Scream your dying breath!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12997, 'The end is upon you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12998, 'Master, I require aid!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (12999, 'Minions, servants, soldiers of the cold dark! Obey the call of Kel\'Thuzad!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13000, 'Just a little taste...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13001, '%s rushes to the aid of Stalagg!', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13002, 'There is no way out.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13003, 'Yes, run! It makes the blood pump faster!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13004, 'Ahh... welcome to my parlor.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13005, 'Shhh... it will all be over soon.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13006, 'I hear little hearts beating. Yes... beating faster now... soon the beating will stop.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13007, 'Where to go? What to do? So many choices that all end in pain, end in death.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13008, 'Which one shall I eat first? So difficult to choose. They all smell so delicious...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13009, 'Closer now. Tasty morsels. I\'ve been too long without food, without blood to drink.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13010, '', 'Defend yourself!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13011, '', 'Touche...', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13012, '', 'Who\'s next?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13013, '', 'Your life is mine!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13014, '', 'Come, Zeliek, do not drive them out. Not before we\'ve had our fun!', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13015, '', 'I do hope they stay alive long enough for me to... introduce myself.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13016, '', 'The first kill goes to me! Anyone care to wager?', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13017, 'Your soul is bound to me, now!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13018, 'There will be no escape!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13019, 'Agghhhh! Do... not... rejoice! Your victory is a hollow one, for I shall return with powers beyond your imagining!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13020, 'I will freeze the blood in your veins!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13021, 'The dark void awaits you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13022, '%s cackles maniacally.\n', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13023, 'Feed you to master!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13024, 'No... more... Feugen.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13025, 'Feugen make master happy.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13026, 'I... am... undone.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13027, 'Death is the only escape!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13028, 'I have waited long enough. Now you face the harvester of souls.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13029, 'Foolishly you have sought your own demise. Brazenly you have disregarded powers beyond your understanding. You have fought hard to invade the realm of the harvester. Now there is only one way out- to walk the lonely path of the damned.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13030, 'Foolishly you have sought your own demise.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13031, 'Brazenly you have disregarded powers beyond your understanding.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13032, 'You have fought hard to invade the realm of the harvester.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13033, 'Now there is only one way out- to walk the lonely path of the damned.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13034, 'C\'mon an\' fight ye wee ninny!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13035, 'What a bloody waste this is!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13036, 'Next time, bring more friends!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13037, 'I like my meat extra crispy!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13038, 'To arms, ye roustabouts! We\'ve got company!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13039, 'I\'ve heard about enough a\' yer snivelin\'! Shut yer flytrap before I shut it for ye\'!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13040, 'I\'m gonna\' enjoy killin\' these slack-jawed daffodils!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13041, 'You are mine now.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13042, 'I see you...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13043, 'You... are next.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13044, '%s takes his last breath.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13045, 'Close your eyes. Sleep.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13046, 'The races of the world will perish. It is only a matter of time.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13047, 'I see endless suffering. I see torment. I see rage. I see everything...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13048, 'Soon the world will tremble.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13049, 'The end is upon you.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13050, 'Hungry worms will feast on your rotting flesh.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13051, 'You seek death?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13052, 'None shall pass!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13053, 'Be still!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13054, 'I... am... released... perhaps it\'s not too late to- No! I need... more... time...', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13055, 'You will find no peace in death.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13056, 'The master\'s will is done.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13057, 'Bow to the might of the Highlord!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13058, 'Enough prattling. Let them come. We shall grind their bones to dust.', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13059, 'Conserve your anger. Harness your rage. You will all have outlets for your frustrations soon enough.', '', 6, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13060, 'Life is meaningless. It is in death that we are truly tested.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13061, 'Glory to the master!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13062, 'Your life is forfeit!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13063, 'Die, trespasser!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13064, 'I will serve the master... in death!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13065, 'My task is done!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13066, 'Breathe no more!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13067, 'Rise, my soldiers! Rise and fight once more!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13068, 'Patchwerk want to play!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13069, 'Kel\'thuzad make Patchwerk his avatar of war!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13070, 'What... happen to-', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
