INSERT INTO `broadcast_text` VALUES (13078, 'The time for practice is over! Show me what you have learned!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13079, 'An honorable... death.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13080, 'Sweep the leg... Do you have a problem with that?', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13081, 'You should have stayed home.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13082, '%s lets loose a triumphant shout.', '', 2, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13083, 'Stalagg crush you!', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
