INSERT INTO `broadcast_text` VALUES (13993, 'Don\'t be afraid; we\'re here to aid you. We\'re grateful for the defense you\'ve granted us within our former home, and as a measure of thanks, we\'ve come to help you return quickly to the Plaguelands should you need it. My compatriot can explain the process further; you\'ll find him where the arcane energies are strongest within this city.', 'Don\'t be afraid; we\'re here to aid you. We\'re grateful for the defense you\'ve granted us within our former home, and as a measure of thanks, we\'ve come to help you return quickly to the Plaguelands should you need it. My compatriot can explain the process further; you\'ll find him where the arcane energies are strongest within this city.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13994, 'I am truly sorry, $c, but as your spirit has never before experienced the horrors that lie in wait within the burning wreck of Stratholme, our magic cannot carry you to the city.', 'I am truly sorry, $c, but as your spirit has never before experienced the horrors that lie in wait within the burning wreck of Stratholme, our magic cannot carry you to the city.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (13995, 'I can see in your eyes you\'re no stranger to the tragedy within Stratholme. If you wish to end the miserable existence of the poor souls trapped therein, step into the circle. It leads directly to the gates of the burning city itself.', 'I can see in your eyes you\'re no stranger to the tragedy within Stratholme. If you wish to end the miserable existence of the poor souls trapped therein, step into the circle. It leads directly to the gates of the burning city itself.', 0, 0, 0, 1, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (14018, 'Be careful out in the desert, $c.', '', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (14364, '', 'The spirits of this place are focused against your kind, $r. Perhaps if you win this tower in the flesh, I will be able to aid you.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (14385, 'Greetings, citizen. I ask that you move along if you have nothing to ask of me.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (14386, 'Marshal Windsor has been expecting you, $n. Unfortunately, he is currently indisposed. He should be returning shortly.', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (14387, 'You made it! Outstanding! Marshal Windsor has been expecting you. Would you like me to signal him now?', '', 0, 0, 0, 1, 1, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (14388, 'Let Marshal Windsor know that I am ready.', 'Let Marshal Windsor know that I am ready.', 0, 0, 0, 0, 0, 0, 0, 0, 0);
