INSERT INTO `broadcast_text` VALUES (66317, 'Blood and thunder!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66318, 'Don\'t die out there!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66319, 'Next time you come back, me want to see scars on you!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66320, 'Always be on your guard.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66321, 'Bring honor to the Horde!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66322, 'Go away and slay some enemys! Don\'t waste your breath.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66323, 'Tell your jokes someone else, i\'m busy!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66324, 'BAHAHA!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66325, 'I got no time for that! I will CRUSH and DESTROY and... uh... oooh... shiny...', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66326, 'You must have fought many battles!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66327, 'There should be more of your kind.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66328, 'Think you strong? Me show you what strong! HAHAHA!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66329, 'A true fighter! Good to have you on our side!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66330, 'Ha! A Draenei has more muscles than you! Bahahahaaa', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66331, 'Trust me... you no wanna start this.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66332, 'Your weapon should break in combat!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66333, 'If i wouldn\'t be on duty i would snap your neck like a branch!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66334, 'A gnome like you should not try to fight.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66335, 'Why don\'t you lead an army instead of touching me!?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66336, 'You no touch me!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66337, 'Are you done?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66338, 'Poke poke poke - is that all you do?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66339, 'Peace, friend.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66340, 'The winds guide you.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66341, 'How may I aid you?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66342, 'Welcome to Thunderbluff, stay away from the cliffs!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66343, 'The wind announced your arrival.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66344, 'Earth Mother protects you.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66345, 'Ish-ne-alo Por-ah', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66346, 'May you find peace in the winds.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66347, 'Relax a bit. We make sure to keep you safe. Well... if you stay away from the cliff.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66348, 'Earth Mother shall keep you safe and cozzy here.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66349, 'Strong as a rock and free as the wind. We protect you, Traveler.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66350, 'Walk with the Earth Mother.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66351, 'Winds be at your back.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66352, 'May the eternal sun shine upon thee.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66353, 'May the eternal sun shine upon thee.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66354, 'Farewell!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66355, 'Well met, Traveler', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66356, 'Stay victorious, Friend', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66357, 'Hohohoho. You should tell that one the others!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66358, 'I shouldn\'t laugh at this but... hm... hmhm.... Hoahahaha!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66359, 'Oh... that was a joke. Haha... ha... it wasn\'t that good.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66360, 'And you took the lift up here? You could CLIMB up here without sweating!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66361, 'By the Earth Mother, i feel sorry for who ever stands against you in battle!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66362, 'You call that strong? I show you what strength looks like!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66363, 'Stop that, you make the other jealous.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66364, 'You know... a fall here is very deep.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66365, 'The wind will remember your conceit!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66366, 'Thunderbluff is a home for everyone. Don\'t make me do something else.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66367, 'Moo\'. Are you happy now?!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66368, 'Ha, you make a fool of yourself.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66369, 'Hohohoho!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66370, 'Your fingers are as fast as the wind. Hahaha!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66371, 'That doesn\'t work on me...', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66372, 'Here we go! Awaken!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66373, 'Initiating...', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66374, 'Processing...', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66375, 'Scanning Unknown beings, threat minimal, universal translator engaged.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66376, 'Systems Online. Analyzer X-51 Online. Memory banks are currently restricted. How can I assist you?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66377, 'It works! It speaks! It somehow scanned us and learned our language! Amazing! ahem... What are you?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66378, 'I am Analyzer X-51, my primary purpose is to analyze systems and mechanisms in order to understand any flaw. Forexample, your purpose has been lost and you have been rendered soft and organic.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66379, 'What? Wait, are you saying that you are...What Gnomes were originally?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66380, 'We share a basal design, correct. However I cannot say whether you are an analyzer or not. In fact judging by your structure I cannot even ascertain if you were ever granted a purpose. I require more information.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66381, 'That\'s... Amazing, what else can you tell us? Where did you work? Were you assigned to the facility here? Are there more like you?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66382, 'My limbs are from different models, and my core was not designed for this body. It is fair to assume that others like me existed or exist. I cannot answer your other questions as I do not know. 97 persent of my memory banks are restricted.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66383, 'Is there any way to unrestrict them?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66384, 'Negative. I require an additional component installed, an activation key.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66385, 'A potion of strength?! Haz\'gorg has learned his place, hah!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66386, 'I stand by for further instructions or inquiries.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66387, 'Hah! Go on then turn on!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66388, 'Scanning Unknown beings, threat minimal, universal translator engaged.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66389, 'Systems Online. Analyzer X-48 Online. Memory banks are currently restricted. How can I assist you?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66390, 'Oh wow! I was afraid I had to get a Titanic translator or somethin\', Yo dude! Tell us where to find gold and rare techologies!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66391, 'I am Analyzer X-48, my primary purpose is to analyze systems and mechanisms in order to understand any flaw in them. Forexample, your unadulterated greed and thirst for technology is errant and can lead to your destruction.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66392, 'What? Are you patronizing me?! I gave you life, ya know? I spent time, effort, and MONEY on makin\' you work, so help me out yeah? Don\'t worry about me, I\'ll be fine even with my greed. Heck if I get enough money maybe it\'ll go away, so yeah, you can help.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66393, 'I doubt your sincerity, however I am in your debt. Would that I could help more, but my memory banks are currently restricted. 98 persent of my memory banks are locked and require a key to function.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66394, 'A key? Huh? Where would we find such a key? Is there another way to unlock your noggin? If we do it will you help?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66395, 'There is no other way to my knowledge. A key to unlock the memories within me are required. Once I have my memories back I will divulge the knowledge you seek in return for rebuilding me.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66396, 'Any idea what this knowledge is? Your analytic abilities might be useful, and could help me win some money through gambling or prospectin\' or something, but I\'d prefer something more substantial. Do you have the blueprints for yourself?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66397, 'I do not know which knowledge is stored in my restricted memory banks. I however, surmise that since the memories are restricted, they would be valuable secrets to my people. So yes it could contain blueprints or technology and other things.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66398, 'Sweet! Hey Partner, come over here and talk to me! Analyzer you may rest or somethin\', we\'ll talk later.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66399, 'Affirmative, standing by.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66400, 'We got company on the ship! Push back these mainlanders!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66401, 'Who are you? You think you can take my ship without a fight?!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66402, 'You wont stand a chance against me scurvy dog!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66403, 'Prepare to die!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66404, 'The Crying Dandelion, eh? Didn\'t take ye for a romantic. So be it, the vessel\'s name will be so from now on, I will let the crew know.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66405, 'I come from a land far away, shrouded in mystery and green mist. I am blessed by those you would not understand, and those that inhabit this mystical land. I am an outcast of my kin, and have only come here to seek new purpose. This purpose was found upon these islands.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66406, 'Slowly, day by day, a darkness came, a power much like myself that dared to challenge the rule I had established. Little by little I would lose this pitiful match of displaying power to attempt to keep the locals swayed to my side.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66407, 'In the end, I could not offer the secrets of my power to those that followed me. Eventually, most of my followers and worshippers stepped aside to bask in the glory of this dark energy. Now, I am seeking revenge, I am seeking to once again reclaim my throne upon these lands, do you understand mortal?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66408, 'Come, %s. It is time to banish this evil.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66409, 'We must travel to the center of Kazon Island, there, I can channel the energies of both Lapidis, and Gillijim.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66410, 'With the energies, and my own, I will be able to draw out the corruption that has plagued the land.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66411, 'Now then - this will take much concentration to bring the entity forward into physical form, it is up to you to weaken it! When it is weak enough, I will be free to join you, until then, protect me!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66412, 'Who are you interlopers? Do you really think you can stop my plans?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66413, 'It is done! The darkness has faded. Can you feel it dissipating before your very eyes? I\'ll be returning to the cave. Meet me there.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66414, 'Who let you in this tower?! Do you know who you are messing with?!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66415, 'You know.. Nothing... I was more powerful then all of you.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66416, 'I see... A radiant city... full of people.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66417, 'They are dying... A great wave swallows them all.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66418, 'Magical energies, waning, blue crystals... A roar.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66419, 'A shadow... Of a dragon?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66420, 'This is all... I need a rest.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66421, 'Cookie looks at Grayson with sadness in his eyes and waves him off.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66422, 'Mrrgl?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66423, 'Cookie, I am sorry! I swear I will make it right. Farewell, my friend.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66424, 'I must apologize for lying to you, and using you for power, you must understand, it was all for the greater good of the region.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66425, 'Leave this place! Leave! It took us, it will take you!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66426, 'Ahoy! Now let\'s get some rum \'n party.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66427, 'Yo-ho-ho!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66428, 'Spirits of Azshara, heed my call. Tell me, what is happening to this land.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66429, 'I was once the High Foreman of the Shadowforge Miners Union. Me and my men helped build many of the halls of the city itself, and worked tirelessly to appease the Senators of their plots and whims. We kept our loyalty, and then worked hard, given benefits for our work.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66430, 'It was then the plans of the Senate failed and the Master became upset that their wrath upon us started growing. The project at Hateforge Quarry put many to their limit, and pulled us away from our home to work in dangerous conditions without any regards for what we did in the past.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66431, 'We were given impossible tasks, which we could not complete, and I was placed to blame. Many of my fellow miners betrayed me, and a new High Foreman was put into my place. Those that remained loyal were enslaved, and the rest of us were exiled. We ended up travelling in exile for days, many of us dying.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66432, 'I was betrayed, backstabbed, and wronged. My heart burns hotter than the Blackrock Mountain, and I demand revenge. Do you see why I ask for help? No doubt you have your reasons to stop the Dark Iron. Let me guide your hand, and we shall both be satisfied.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66433, 'Been an honor working with you.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66434, 'Weird... but clever.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66435, 'Really? Goblins are insane.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66436, 'So it is possible...', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66437, 'Get ready! I will now activate the golem! Draw its attention, and I will cast the Rewiring Spell. Don\'t damage it too much, it needs to remain intact!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66438, 'It worked!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66439, 'Awful handwriting, it\'s like the person writing this was blind. Alright, here it goes. Listen carefully.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66440, 'Tell G. that the people in Darkshire are getting delicious. Sorry, suspicious.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66441, 'The Night Watch is eyeing my shack every day. I need to move out soon if the production of runes is to continue.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66442, 'Send someone. Stormwind shall fall, yadda yadda, F.W. The end.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66443, 'Interesting note, friend. You and Hewen might be in trouble. But that\'s none of my business.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66444, 'The power within these hearts shall aid me, but it is weak, and faded, if I am to truly master my magic, than I need something much stronger.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66445, 'The power flows through my veins! I feel stronger, and mighty! With this, we shall dominate our enemies!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66446, 'In the name of Raz\'gol, a warrior of the Horde, a soldier until his last breath. Go with honor friend, you will not be forgotten.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66447, 'Without you, we surely would have had our plans discovered, for that I am grateful of your service.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66448, 'Your name shall echo with honor for the actions you have taken, walk softly, noble soldier of the Horde.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66449, 'I AM FREE! AT LONG LAST FREE FROM THAT CURSED FORM!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66450, 'Take that ring, I cannot stand to look at it, thank you for everything, I will leave when I have collected myself.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66451, 'O, de powerful Loa! Smite ya enemies!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66452, 'Da ogres of Dunemaul. Curse them!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66453, 'Let dem feel your fury...', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66454, 'Viceclaw my dear. Da feast today!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66455, 'Good boy.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66456, 'When I first prowled these forests I had a pack that followed me, they were my kin, and were loyal. I grew restless of their imperfections, and figured I could do better alone. I hunted, and fought with great glory, but I abandoned those close.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66457, 'I was ambushed by a group of bear, and retreated to this very waterfall, where I was cornered, and alone, slain, only to now walk this world in a much different form. Had I kept my loyalties, and stayed with my pack, perhaps I would not have met an untimely fate. Do you understand what it is I teach you young one? You can be as powerful as the mountains, but if you are alone, you will eventually fall.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66458, 'Theramore was weak, to side with the Horde is betrayal after all who died! We will not be stopped so easily, the Vengeful Mariner will not forget!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66459, 'Oh yes, a sizable force moved here recently... Very recently. They are the Grimtotem Tauren, though. At odds with the horde, as far as I am concerned. Good. Let them fight among themselves.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66460, 'A Tauren? Well, I haven\'t seen any in a long time here. I heard something about Grimtotem in the north? You could have greater success asking closer to the Brackenwall Village. Maybe they know of some Tauren newcomers.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66461, 'Let me think... No, I haven\'t seen any come here recently. Brackenwall Village is a remote outpost, and they barely have anything to trade. On the other hand, I remember one tauren fellow living in Brackenwall. We fought side by side during the siege... Haven\'t seen him in ages. He probably drowned in the swamp.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66462, 'Oh, you will regret doing that, Dear.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66463, 'Thanks for what you\'ve done. The memories I hold in that church will not be forgotten, and perhaps, in time, there can be more made there when the land heals and the crops are sown. If the light is so gracious, we may have such a future, farewell, may your travels be safe now, ya hear?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66464, 'Without your effort we would have had to start fresh all over again, those blasted buzzards ruin everything!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66465, 'You are now blessed, you are the Earthstrider of our kin, the first to walk amongst us as an outsider, we honor you and your aid to the Gelkis cause.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66466, 'You make this old man proud, kid... Not only did you take over those oilfields like it was your own backyard, but you\'ve proudly delivered it into the Union\'s hands, with employees at that. That\'s right, some of the survivors even agreed to join us! You\'ve made our little town just a bit safer, well as safe as it could be!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66467, 'All is well, enjoying some ale, the views of southern lasses, and enjoying the weather on dry land. He should know me not to cause trouble in foreign lands.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66468, 'Check in on me? Haha! I ain\'t causing no trouble, that goody-two-shoes Milldough should learn to have some fun. All I am doing is exploring, this place is much different than Boralus after all.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66469, 'A few weeks ago Kul Tiras was allowed embassy within the city of Stormwind, given by Bolvar Fordragon in an attempt to improve diplomacy between the two nations.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66470, 'While things seem to be going fairly well, it is quite obvious to many that Kul Tiras is still very stubborn on their ideals and traditions, their sanctity of honor, and respect for the fallen is on par with many of the most noble paladin.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66471, 'As of current, there has been no consensus, Stormwind is not looking to be dragged into a war without good reason, I am under good authority and good information to inform of no cooperation between Kul Tiras, and the Alliance in this matter. This \'invasion\' you are facing is, by our eyes, up to Thrall to handle, and deal with as he seeks fit.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66472, 'The SI:7 has given me clear authority to pass along this information for the better interest of both parties. Bring this to your leader, and let him know.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66473, 'Baha! A true soldier of the Horde! One of grit and steel  those Kul Tirans are no match for you!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66474, 'Durotar stands strong, no human shall break the might of the Horde!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66475, 'I can feel it, yes, through my body! I... Hmm... I feel funny, and good. Tell Haz\'gorg that I thank him for the potion, I have thought over what he said earlier, maybe Maul\'ogg rest and think about strategy to expand, rather then smash enemy.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66476, 'Gowlfang strongest, no one dare challenge Gowlfang!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66477, 'You have kept Theramore safe, may honor follow you.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66478, 'I am Falgran Hastil, scout of Theramore. Why did you call for this meeting?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66479, 'We saw you investigate the inn. We wanted to assure you it is not the Horde\'s doing.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66480, 'We found this shield strapped to the wall of the inn. Do you have any recollection of this object?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66481, 'Let me see.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66482, 'Kagoro investigates the shield thoroughly.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66483, 'By the ancestors. Yes, I know this shield. It belonged to Wattapo, a Tauren warrior who once lived in the Brackenwall Village. He died a long time ago.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66484, 'Do you know how it could\'ve ended up in the inn? We found his grave. It was recently disturbed.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66485, 'To disturb the grave of a fallen warrior I assure you, I know nothing of this.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66486, 'It seems like someone wanted to make us blame you for the burning of the inn.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66487, 'Indeed. Have you found anything else in the investigation?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66488, 'Yes, quillboar footprints...', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66489, 'False lead.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66490, 'I see you did some work too.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66491, 'Kagoro nods.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66492, 'And the badge of one of the deserters. Paval Reethe. We couldn\'t find Reethe, though. Have you seen him, by any chance?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66493, 'This is... a delicate matter, and the actual reason I called for the meeting. Paval Reethe is dead.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66494, 'He was a deserter. While I still would\'ve preferred to arrest him and allow for a just trial, I understand that you killed him. We do not blame you.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66495, 'If it only was that simple. We knew he was connected to the burning of the inn, and we wanted to investigate him. Before he could talk... we were attacked. By Theramore soldiers. One of them bore the name Caldwell.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66496, 'Attacked? Unprovoked, by Theramore soldiers? I swear to the Light, orc. If you are lying...', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66497, 'I swear upon my ancestors and my Warchief. They killed him in cold blood. Quick arrow pierced his heart. We also barely made it out alive.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66498, 'Theramore soldiers would never... We shall investigate this. But if we find out that your words aren\'t true...', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66499, 'I am sorry for the loss of your comrades. While you investigate this, we shall follow another lead we have. Black Dragonflight.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66500, 'And we will investigate in Theramore. But be warned, orc. Your story doesn\'t add up.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66501, 'Let\'s meet here once we know more. I hope you will find truth in your city of stones.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66502, 'Kagoro! We are ready.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66503, 'Falgran.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66504, 'Kagoro, on the behalf of the Alliance and the City of Theramore, I would like to apologize for not believing you and my false accusations. We are now certain that the deserters burned the inn.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66505, 'I am relieved to hear it. Our investigation of Black Dragons yielded no results. But how did you learn of the truth?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66506, 'Through the power of magic, of course. Arts of Divination. We communed with the spirit of Paval Reethe.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66507, 'Extraordinary. Incredible sorcery, as always. But... why? Why did they burn the inn?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66508, 'Well. This is where it gets a bit complicated. The deserters were regular patrons of the inn. In their drunken state, they confessed... something to the innkeeper. In fear of the information reaching Theramore, they preferred to kill him and leave false evidence.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66509, 'The shield... Yes, it makes sense.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66510, 'Paval Reethe wanted to stop them. He may have been a deserter, but he had some of his honor still left in his heart.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66511, 'That\'s why they left him in the swamp. But, what was the information that was so precious to the deserters?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66512, 'About this, we are not sure. Something about the Vengeful Mariner, their rumored true leader. Have you heard about him?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66513, 'Vengeful Mariner... The Ogres speak of a haunted sailor terrorizing the shores, but I doubt it\'s connected.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66514, 'We will soon bring justice to the deserters for their crimes, thank you for your information Kagoro, the horde has helped plenty in these lands.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66515, 'We believe in justice the same way you alliance do, go with honor friend.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66516, 'We should report to Captain Garran Vimes and let him know our discoveries.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66517, 'My brother Darnir was a powerful warrior, and well respected amongst those he served with. He traveled to these far lands with our family axe, and made a name for himself in the battles with the Horde during the Third War.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66518, 'A truce was established between Horde and Alliance as both factions set to head toward Hyjal. My brother would be one of the last few casualties in this conflict, as a foul orc named \'Scartusk\' ambushed him, and took our family axe as his own...', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66519, 'This orc breathes today, pardoned by the Horde, and still wielding the axe my brother carried, an heirloom to our family. I desire revenge, above all else.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66520, 'UP UP AND AWAY!', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66521, 'Corruption within Stonetalon? It does not surprise me, even here in tranquil Ashenvale, many of the native species have begun to show signs of aggression. The furbolg are the most obvious of this.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66522, 'Once our ally, the furbolg have become an enemy, attacking at sight and not hesitating to start conflict. Many druids say that nature itself is adapting to the conflict, becoming more hostile and secluded as a means to protect themselves. This could be the case in Stonetalon.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66523, 'Though here in Ashenvale, there is something else rooted deep, clawing at the foundations of all we hold sacred. I do not know what, but I can sense it...', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66524, 'That is all I know, I hope it may be of use to Maloran, we defenders of nature must keep vigilant.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66525, 'I thank you for traveling as you have. The information you have brought will prove valueable. It is up to me to find the root of the troubles now.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66526, 'Your armies have been defeated, and now you stand before the King of Corthan, if you desire to save the lives of the survivors, and yourself, you must swear loyalty to me, as King.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66527, 'I swear my fealty to you, King of Corthan.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66528, 'I will remain loyal.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66529, 'I am Kagoro. We saw you investigate the inn. We wanted to assure you it is not the Horde\'s doing.', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
INSERT INTO `broadcast_text` VALUES (66530, 'We found this shield strapped to the wall of the inn. Do you have any recollection of this object?', NULL, 0, 0, 0, 0, 0, 0, 0, 0, 0);
