INSERT INTO `locales_page_text` VALUES (2661, '<HTML>\r\n<BODY>\r\n<H1 align=\"center\">MILITARY RANKS OF THE HORDE &amp; ALLIANCE</H1>\r\n<BR/>\r\n<BR/>\r\n<IMG src=\"Interface\\PvPRankBadges\\PvPRankHorde\" align=\"left\"/>\r\n<BR/>\r\n<P align=\"right\">Throm\'ka!  In this</P>\r\n<P align=\"right\">book you will find</P>\r\n<P align=\"right\">many listings of</P>\r\n<P align=\"right\">the military ranks</P>\r\n<P align=\"right\">of the Horde and</P>\r\n<P align=\"right\">the puny Alliance.</P>\r\n<P align=\"right\">First will come the</P>\r\n<P align=\"right\">Horde, then the Alliance,</P>\r\n<P align=\"right\">each with listings of officers and enlisted ranks.  As is fitting, the strongest are listed at the top, with the weaker listed below them.</P>\r\n</BODY>\r\n</HTML>', NULL, '<HTML>\n<BODY>\n<H1 align=\"center\"> GRADES MILITAIRES DE LA HORDE &amp; DE L\'ALLIANCE </H1>\n<BR/>\n<BR/>\n<IMG src=\"Interface\\PvPRankBadges\\PvPRankHorde\" align=\"left\"/>\n<BR/>\n<P align=\"right\">Throm\'ka! Vous</P>\n<P align=\"right\">trouverez dans ce </P>\n<P align=\"right\"> livre la liste détaillée </P>\n<P align=\"right\">des grades militaires</P>\n<P align=\"right\">de la Horde et de la</P>\n<P align=\"right\">misérable Alliance.</P>\n<P align=\"right\">Nous traiterons d\'abord</P>\n<P align=\"right\">de la Horde, puis de</P>\n<P align=\"right\"> l\'Alliance, en indiquant chaque fois les grades d\'officiers et de les grades d\'hommes de troupe. Comme il se doit, les plus forts sont en haut, et les faibles en bas.</P>\n</BODY>\n</HTML>', '<HTML>\n<BODY>\n<H1 align=\"center\">MILIT?RISCHE R?NGE DER HORDE &amp; ALLIANZ</H1>\n<BR/>\n<BR/>\n<IMG src=\"Interface\\PvPRankBadgesPvPRankHorde\" align=\"left\"/>\n<BR/>\n<P align=\"right\">Throm\'ka!  In diesem</P>\n<P align=\"right\">buch findet Ihr</P>\n<P align=\"right\">zahlreiche Auflistungen</P>\n<P align=\"right\">der milit?rischen R?nge</P>\n<P align=\"right\">der Horde und</P>\n<P align=\"right\">der erb?rmlichen Allianz.</P>\n<P align=\"right\">Wir beginnen mit der</P>\n<P align=\"right\">Horde, dann folgt die Allianz,</P>\n<P align=\"right\">jeweils mit einer Liste der Offiziers- und Soldatenr?nge. Gebührenderweise werden die St?rksten zu Beginn und die Schw?cheren darunter aufgelistet.</P>\n</BODY>\n</HTML>', '<HTML><BR/> <BODY><BR/> <H1 align=\"center\">联盟与部落的军阶</H1><BR/> <BR/><BR/> <BR/><BR/> <IMG src=\"InterfacePvPRankBadgesPvPRankHorde\"/>align=\"left\"/><BR/> <BR/><BR/> <P align=\"right\">(兽人语)你好!在这本</P><BR/> <P align=\"right\">书中</P><BR/> <P align=\"right\">列出了</P><BR/> <P align=\"righ\">部落和</P><BR/> <P align=\"right\">弱小的联盟的</P><BR/> <P align=\"right\">军阶列表。</P><BR/> <P align=\"right\">首先</P><BR/> <P align=\"right\">是部落军阶，接下来是联盟的，</P><BR/> <P align=\"right\">其中囊括了军官和军阶列表。按等级的高低排列，等级最高的排在首位，等级低的列于下位。</P>', NULL, NULL, NULL, '<HTML>\r\n<BODY>\r\n<H1 align=\"center\">ВОИНСКИЕ ЗВАНИЯ ОРДЫ И АЛЬЯНСА</H1>\r\n<BR/>\r\n<BR/>\r\n<IMG src=\"Interface\\PvPRankBadges\\PvPRankHorde.blp\" align=\"left\"/>\r\n<BR/>\r\n<P align=\"right\">Тром\'ка! В этой</P>\r\n<P align=\"right\">книге ты найдешь</P>\r\n<P align=\"right\">подробный перечень</P>\r\n<P align=\"right\">воинских званий</P>\r\n<P align=\"right\">Орды и</P>\r\n<P align=\"right\">жалкого Альянса.</P>\r\n<P align=\"right\">Сначала в списках идет</P>\r\n<P align=\"right\">Орда, а затем Альянс,</P>\r\n<P align=\"right\">и для обеих сторон перечислены офицерские и рядовые звания. Как и подобает, вверху списка располагаются старшие чины, а младшие – ниже.</P>\r\n</BODY>\r\n</HTML>');
