INSERT INTO `script_texts` VALUES (-1000270, 'I\'ve done it! I have harnessed the power of the stone into the goggles! Let\'s get out of here!', NULL, 'J\'ai rÃ©ussi ! J\'ai aspirÃ© la puissance de la pierre dans les lunettes ! Sortons d\'ici !', NULL, '我已经做到了！我把石头的力量运用到了护目镜里！我们离开这里吧！', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'phizzlethorpe SAY_PROGRESS_6');
