INSERT INTO `script_texts` VALUES (-1780135, 'Krug suddenly halts, his eyes caught by a motion down in the Hive.', NULL, 'Krug s\'interrompt soudainement, le regard attirÃ© par un mouvement en contrebas.', NULL, '克鲁格突然停了下来，他的目光被蜂巢下面的一个动作吸引住了。', NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'Silithus Field Duty Event - 5 (Horde)');
INSERT INTO `script_texts` VALUES (-1780134, 'Be ready!  If I am to sign those papers, you\'re gonna have to...', NULL, 'Soyez sur vos gardes ! Si vous voulez que je signe ces papiers, il va falloir...', NULL, '准备好！如果我要签署这些文件，你就必须...', NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'Silithus Field Duty Event - 4 (Horde)');
INSERT INTO `script_texts` VALUES (-1780133, 'Our scouts have reported that a Silithid attempt to take out this outpost is likely to occur very soon.', NULL, 'Les derniers rapports des Ã©claireurs nous laissent penser qu\'un assaut des Silithides sur cet avant-poste est imminent.', NULL, '我们的侦察兵报告说，异种蝎可能很快就会尝试摧毁这个前哨基地。', NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'Silithus Field Duty Event - 3 (Horde)');
INSERT INTO `script_texts` VALUES (-1780132, 'Get into the ranks ! Fighting alongside the Orgrimmar Legion is no easy task. The recruits these elves from the Cenarion Circle keep sending us never last long.', NULL, 'Rentrez dans les rangs ! Se battre aux cÃ´tÃ©s de la LÃ©gion d\'Orgrimmar n\'est pas chose aisÃ©e. Les recrues que nous envoient ces elfes du Cercle CÃ©narien font rarement long feu.', NULL, '快加入行列吧！与奥格瑞玛军团并肩作战绝非易事。这些来自塞纳里奥议会的精灵不断向我们派遣的新兵永远不会持续太久。', NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'Silithus Field Duty Event - 2 (Horde)');
INSERT INTO `script_texts` VALUES (-1780131, 'Lok\'tar Ogar !', NULL, 'Lok\'tar Ogar !', NULL, '洛克塔奥加尔！', NULL, NULL, NULL, NULL, 0, 0, 1, 22, 'Silithus Field Duty Event - 1 (Horde)');
INSERT INTO `script_texts` VALUES (-1780190, 'Ok, let\'s get started.', NULL, NULL, NULL, '好的，让我们开始吧。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'quest1222and1270pnj4880');
INSERT INTO `script_texts` VALUES (-1780191, 'Nope, not here.', NULL, NULL, NULL, '不，不在这里。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'quest1222and1270pnj4880');
INSERT INTO `script_texts` VALUES (-1780192, 'Ah! There\'s one!', NULL, NULL, NULL, '啊!有一个！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'quest1222and1270pnj4880');
INSERT INTO `script_texts` VALUES (-1780193, 'Come, $N! Let\'s go over there and collect it!', NULL, NULL, NULL, '来吧，$N！我们去那里收集吧！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'quest1222and1270pnj4880');
INSERT INTO `script_texts` VALUES (-1780194, 'Ok, now let\'s get out of here!', NULL, NULL, NULL, '好吧，现在我们离开这里吧！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'quest1222and1270pnj4880');
INSERT INTO `script_texts` VALUES (-1780195, 'I can make it from here. Thanks $N! And talk to my employer about a reward!', NULL, NULL, NULL, '我可以从这里开始。谢谢$N！并与我的雇主讨论奖励事宜！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'quest1222and1270pnj4880');
INSERT INTO `script_texts` VALUES (-1780196, 'Ah freedom! Although brief, so sweet it is...', NULL, NULL, NULL, '啊自由！虽然简短，但是很甜蜜……', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780197, 'Well duties call, yes? First, the bell... to give you vigor!', NULL, NULL, NULL, '好吧，职责召唤，是吗？首先，铃声...给你活力！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780198, 'Next, I\'ll place the wheel... to protect you from harm.', NULL, NULL, NULL, '接下来，我将放置轮子...以保护您免受伤害。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780199, 'And finally the candle... to burn those who could thwart you!', NULL, NULL, NULL, '最后是蜡烛……烧掉那些可能阻碍你的人！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780200, '$N, my duties are complete and I shall now take my leave. Luck to you my $gfellow:lady;, and remember to keep the Bell, Wheel and Candle working!', NULL, NULL, NULL, '$N，我的职责已经完成，我现在要离开了。祝你好运，我的 $gfellow:lady;，并记住让铃、轮和蜡烛保持工作！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'DreadsteedRitualpnj14500');
INSERT INTO `script_texts` VALUES (-1780201, 'Who dares steal my precious mount? You will pay for your insolence, mortal!', NULL, NULL, NULL, '谁敢偷我的宝贝坐骑？你将为你的无礼付出代价，凡人！', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'DreadsteedRitualpnj14506');
INSERT INTO `script_texts` VALUES (-1780202, 'Xorothian Imp is pulled back to Xoroth!', NULL, NULL, NULL, '克索罗斯小恶魔被拉回克索罗斯！', NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'DreadsteedRitualpnj14482');
INSERT INTO `script_texts` VALUES (-1780203, 'Dread Guard is pulled back to Xoroth!', NULL, NULL, NULL, '恐惧守卫被撤回克索罗斯！', NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'DreadsteedRitualpnj14483');
INSERT INTO `script_texts` VALUES (-1000601, 'Sleepy... so sleepy...', NULL, 'Sommeil... tellement sommeil...', NULL, '困了……好困啊……', NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lazy peon SAY_SPELL_HIT2');
INSERT INTO `script_texts` VALUES (-1000600, 'Ow! Ok, I\'ll get back to work, $N!', NULL, 'Aie! D\'accord, je retourne au travail, $N!', NULL, '噢！好的，我会回去工作的，$N！', NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'lazy peon SAY_SPELL_HIT');
INSERT INTO `script_texts` VALUES (-1000948, 'Well then, let\'s get this started. The longer we\'re here, the more damage the undead could be doing back in Hillsbrad.', NULL, NULL, NULL, '那么，让我们开始吧。我们在这里待的时间越长，不死族对希尔斯布莱德造成的伤害就越大。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kinelory SAY_START');
INSERT INTO `script_texts` VALUES (-1000949, 'All right, this is where we really have to be on our paws. Be ready!', NULL, NULL, NULL, '好吧，这就是我们真正必须采取行动的地方。准备好！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kinelory SAY_REACH_BOTTOM');
INSERT INTO `script_texts` VALUES (-1000950, 'Attack me if you will, but you won\'t stop me from getting back to Quae.', NULL, NULL, NULL, '如果你愿意的话可以攻击我，但你不会阻止我回到奎伊。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kinelory SAY_AGGRO_KINELORY');
INSERT INTO `script_texts` VALUES (-1000951, 'You have my word that I shall find a use for your body after I\'ve killed you, Kinelory.', NULL, NULL, NULL, '我向你保证，在我杀了你之后，我会为你的身体找到用途，Kinelory。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'jorell SAY_AGGRO_JORELL');
INSERT INTO `script_texts` VALUES (-1000952, 'Watch my rear! I\'ll see what I can find in all this junk...', NULL, NULL, NULL, '注意我的后方！我看看能在这些垃圾中找到什么……', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kinelory SAY_WATCH_BACK');
INSERT INTO `script_texts` VALUES (-1000953, '%s begins rummaging through the apothecary\'s belongings.', NULL, NULL, NULL, '%s开始翻找药剂师的物品。', NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'kinelory EMOTE_BELONGINGS');
INSERT INTO `script_texts` VALUES (-1000954, 'I bet Quae\'ll think this is important. She\'s pretty knowledgeable about these things--no expert, but knowledgable.', NULL, NULL, NULL, '我敢打赌奎伊会认为这很重要。她对这些事情非常了解——不是专家，但知识渊博。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kinelory SAY_DATA_FOUND');
INSERT INTO `script_texts` VALUES (-1000955, 'Okay, let\'s get out of here quick quick! Try and keep up. I\'m going to make a break for it.', NULL, NULL, NULL, '好吧，我们快点离开这里吧！尝试并跟上。我要暂时休息一下。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kinelory SAY_ESCAPE');
INSERT INTO `script_texts` VALUES (-1000956, 'We made it! Quae, we made it!', NULL, NULL, NULL, '我们做到了！奎，我们成功了！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'kinelory SAY_FINISH');
INSERT INTO `script_texts` VALUES (-1000957, '%s hands her pack to Quae.', NULL, NULL, NULL, '%s 将她的包裹交给了奎。', NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'kinelory EMOTE_HAND_PACK');
INSERT INTO `script_texts` VALUES (-1047000, 'Woo hoo! Finally getting out of here. It\'s going to be rough though. Keep your eyes peeled for trouble.', NULL, NULL, NULL, '呜呼！终于可以离开这里了。不过，这会很艰难。一定要擦亮眼睛以防出现麻烦。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'willix SAY_READY');
INSERT INTO `script_texts` VALUES (-1047001, 'Up there is where Charlga Razorflank resides. Blasted old crone.', NULL, NULL, NULL, '上面就是查尔加·剃刀弗兰克 (Charlga Razorflank) 居住的地方。该死的老太婆。', NULL, NULL, NULL, NULL, 0, 0, 0, 25, 'willix SAY_1');
INSERT INTO `script_texts` VALUES (-1047002, 'There\'s blueleaf tuber in this trench! It\'s like gold waiting to be mined I tell you!', NULL, NULL, NULL, '这条沟里有蓝叶块茎！我告诉你，这就像等待开采的黄金！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'willix SAY_2');
INSERT INTO `script_texts` VALUES (-1047003, 'There could be danger around every corner here.', NULL, NULL, NULL, '这里的每个角落都可能存在危险。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'willix SAY_3');
INSERT INTO `script_texts` VALUES (-1047004, 'I don\'t see how these foul animals live in this place... sheesh it smells!', NULL, NULL, NULL, '我不明白这些肮脏的动物是如何生活在这个地方的……天哪，臭死了！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'willix SAY_4');
INSERT INTO `script_texts` VALUES (-1047005, 'I think I see a way for us to get out of this big twisted mess of a bramble.', NULL, NULL, NULL, '我想我找到了一种方法可以让我们摆脱这个混乱的荆棘丛。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'willix SAY_5');
INSERT INTO `script_texts` VALUES (-1047006, 'Glad to be out of that wretched trench. Not much nicer up here though!', NULL, NULL, NULL, '很高兴能脱离那条悲惨的战壕。不过这里也好不了多少！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'willix SAY_6');
INSERT INTO `script_texts` VALUES (-1047007, 'Finally! I\'ll be glad to get out of this place.', NULL, NULL, NULL, '最后！我会很高兴离开这个地方。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'willix SAY_7');
INSERT INTO `script_texts` VALUES (-1047008, 'I think I\'ll rest a moment and catch my breath before heading back to Ratchet. Thanks for all the help!', NULL, NULL, NULL, '我想我会休息一下，喘口气，然后再返回棘齿城。感谢您的帮助！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'willix SAY_END');
INSERT INTO `script_texts` VALUES (-1047009, '$N heading this way fast! To arms!', NULL, NULL, NULL, '$N 快速朝这边走！拿起武器！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'willix SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES (-1047010, 'Eek! $N coming right at us!', NULL, NULL, NULL, '哎呀！ $N 正向我们袭来！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'willix SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES (-1047011, 'Egads! $N on me!', NULL, NULL, NULL, '哎呀！ $N 对我来说！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'willix SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES (-1047012, 'Help! Get this $N off of me!', NULL, NULL, NULL, '帮助！把这N美元从我身上拿走！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'willix SAY_AGGRO_4');
INSERT INTO `script_texts` VALUES (-1189022, 'The master has fallen! Avenge him my brethren!', NULL, 'Le maÃ®tre est tombÃ©! Vengez-le mes frÃ¨res!', NULL, '主人倒下了！我的弟兄们，为他报仇吧！', NULL, NULL, NULL, NULL, 5834, 1, 0, 0, 'trainee SAY_TRAINEE_SPAWN');
INSERT INTO `script_texts` VALUES (-1090021, 'FIRE IN THE HOLE!', NULL, NULL, NULL, '洞里着火了！', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'emi shortfuse SAY_BLOW_2');
INSERT INTO `script_texts` VALUES (-1090022, 'Superb! Because of your help, my people stand a chance of re-taking our beloved city. Three cheers to you!', NULL, NULL, NULL, '高超！由于您的帮助，我的人民有机会重新夺回我们心爱的城市。为你喝彩三声！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'emi shortfuse SAY_FINISH_2');
INSERT INTO `script_texts` VALUES (-1090023, 'We come from below! You can never stop us!', NULL, NULL, NULL, '我们来自下面！你永远无法阻止我们！', NULL, NULL, NULL, NULL, 0, 1, 0, 1, 'grubbis SAY_GRUBBIS_SPAWN');
INSERT INTO `script_texts` VALUES (-1090028, '$n attacking! Help!', NULL, NULL, NULL, '$n攻击！帮助！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'emi shortfuse SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES (-1780204, 'Get me out of here!', NULL, NULL, NULL, '带我离开这里！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'q2904npc7850');
INSERT INTO `script_texts` VALUES (-1780205, 'Self-destruct sequence activated.', NULL, NULL, NULL, '自毁序列已激活。', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'q2904npc7897');
INSERT INTO `script_texts` VALUES (-1780206, 'Anti-escape countermeasures activated. Self-destruct in t-minus 20 seconds...', NULL, NULL, NULL, '反逃逸对策启动。 t-20 秒内自毁...', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'q2904npc7897');
INSERT INTO `script_texts` VALUES (-1780207, 'I see the exit! Hurry, hurry!', NULL, NULL, NULL, '我看到出口了！快点快点！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'q2904npc7850');
INSERT INTO `script_texts` VALUES (-1780208, 'Thank you for rescuing me! Word of your deed will not go unnoticed. ', NULL, NULL, NULL, '谢谢你救了我！你的言行不会被忽视。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'q2904npc7850');
INSERT INTO `script_texts` VALUES (-1129005, 'All right, stay close. These fiends will jump right out of the shadows at you if you let your guard down.', NULL, NULL, NULL, '好吧，保持靠近。如果你放松警惕，这些恶魔就会从阴影中跳出来攻击你。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'belnistrasz SAY_READY');
INSERT INTO `script_texts` VALUES (-1129006, 'Okay, here we go. It\'s going to take about five minutes to shut this thing down through the ritual. Once I start, keep the vermin off of me or it will be the end of us all!', NULL, NULL, NULL, '好的，我们开始吧。通过仪式关闭这东西大约需要五分钟。一旦我开始，请让害虫远离我，否则这将是我们所有人的末日！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'belnistrasz SAY_START_RIT');
INSERT INTO `script_texts` VALUES (-1129007, 'You\'ll rue the day you crossed me, $N', NULL, NULL, NULL, '你会后悔那天你伤害了我，$N', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'belnistrasz SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES (-1129008, 'Incoming $N - look sharp, friends!', NULL, NULL, NULL, '即将到来的 $N - 看起来很敏锐，朋友们！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'belnistrasz SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES (-1129009, 'Three minutes left -- I can feel the energy starting to build! Keep up the solid defense!', NULL, NULL, NULL, '还剩三分钟——我能感觉到能量开始积聚！继续坚守防守！', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'belnistrasz SAY_3_MIN');
INSERT INTO `script_texts` VALUES (-1129010, 'Just two minutes to go! We\'re half way there, but don\'t let your guard down!', NULL, NULL, NULL, '只剩下两分钟了！我们已经成功了一半，但请不要放松警惕！', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'belnistrasz SAY_2_MIN');
INSERT INTO `script_texts` VALUES (-1129011, 'One more minute! Hold on now, the ritual is about to take hold!', NULL, NULL, NULL, '还有一分钟！坚持住，仪式即将举行！', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'belnistrasz SAY_1_MIN');
INSERT INTO `script_texts` VALUES (-1129012, 'That\'s it -- we made it! The ritual is set in motion, and idol fires are about to go out for good! You truly are the heroes I thought you would be!', NULL, NULL, NULL, '就是这样——我们成功了！仪式开始，偶像之火即将永远熄灭！你们确实是我想象中的英雄！', NULL, NULL, NULL, NULL, 0, 1, 0, 4, 'belnistrasz SAY_FINISH');
INSERT INTO `script_texts` VALUES (-1000011, 'The Sons of Thaurissan shall watch you perish in the Ring of the Law!', '', 'Les Fils de Thaurissan vous verront pÃ©rir dans le Cercle de loi !', '', '索瑞森之子将眼睁睁看着你在法则之环中灭亡！', '', '', '', '', 0, 0, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1000010, 'You have been sentenced to death for crimes against the Dark Iron nation!', '', 'Vous subirez la peine de mort pour vos crimes contre la nation Sombrefer !', '', '你因危害黑铁国家的罪行而被判处死刑！', '', '', '', '', 0, 0, 0, 0, '');
INSERT INTO `script_texts` VALUES (-1001106, 'Don\'t forget to get my bell out of the chest here. And remember, if do happen to wander off, just ring it and I\'ll find you again.', NULL, NULL, NULL, '别忘了把我的铃铛从这里的箱子里拿出来。请记住，如果你碰巧走丢了，只需按响它，我就会再次找到你。', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'shay leafrunner SAY_ESCORT_START');
INSERT INTO `script_texts` VALUES (-1001107, 'Are we taking the scenic route?', NULL, NULL, NULL, '我们走风景路线吗？', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_1');
INSERT INTO `script_texts` VALUES (-1001108, 'Oh, what a beautiful flower over there...', NULL, NULL, NULL, '哦，那儿的花多漂亮啊……', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_2');
INSERT INTO `script_texts` VALUES (-1001109, 'Are you sure this is the right way? Maybe we should go this way instead...', NULL, NULL, NULL, '你确定这是正确的方法吗？也许我们应该走这条路...', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_3');
INSERT INTO `script_texts` VALUES (-1001110, 'Hmmm, I wonder what\'s over this way?', NULL, NULL, NULL, '嗯，我想知道这是怎么回事？', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_4');
INSERT INTO `script_texts` VALUES (-1001111, 'This is quite an adventure!', NULL, NULL, NULL, '这真是一次冒险！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_DONE_1');
INSERT INTO `script_texts` VALUES (-1001112, 'Oh, I wandered off again. I\'m sorry.', NULL, NULL, NULL, '哦，我又走神了。对不起。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_DONE_2');
INSERT INTO `script_texts` VALUES (-1001113, 'The bell again, such a sweet sound.', NULL, NULL, NULL, '铃声再次响起，多么悦耳的声音。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'shay leafrunner SAY_WANDER_DONE_3');
INSERT INTO `script_texts` VALUES (-1001114, '%s begins to wander off.', NULL, NULL, NULL, '%s开始走失。', NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'shay leafrunner EMOTE_WANDER');
INSERT INTO `script_texts` VALUES (-1001115, 'Oh, here you are, Rockbiter! I\'m sorry, I know I\'m not supposed to wander off.', NULL, NULL, NULL, '哦，你在这里，石比特！对不起，我知道我不应该迷失。', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'shay leafrunner SAY_EVENT_COMPLETE_1');
INSERT INTO `script_texts` VALUES (-1001116, 'I\'m so glad yer back Shay. Please, don\'t ever run off like that again! What would I tell yer parents if I lost ya?', NULL, NULL, NULL, '我很高兴你回来了，谢伊。拜托，以后不要再这样逃跑了！如果我失去了你，我会告诉你的父母什么？', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'rockbiter SAY_EVENT_COMPLETE_2');
INSERT INTO `script_texts` VALUES (-1000784, 'Thanks $N. Now let\'s get out of here!', NULL, NULL, NULL, '谢谢$N。现在我们离开这里吧！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'melizza SAY_MELIZZA_START');
INSERT INTO `script_texts` VALUES (-1000785, 'We made it! Thanks again! I\'m going to run ahead!', NULL, NULL, NULL, '我们成功了！再次感谢！我要往前跑！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'melizza SAY_MELIZZA_FINISH');
INSERT INTO `script_texts` VALUES (-1000786, 'Hey Hornizz! I\'m back! And there are some people behind me who helped me out of a jam.', NULL, NULL, NULL, '嘿霍尼兹！我回来了！我身后还有一些人帮助我摆脱困境。', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'melizza SAY_MELIZZA_1');
INSERT INTO `script_texts` VALUES (-1000787, 'We\'re going to have to scratch the Maraudines off our list. Too hard to work with...', NULL, NULL, NULL, '我们得把马劳丁人从我们的名单上划掉。一起工作太难了...', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'melizza SAY_MELIZZA_2');
INSERT INTO `script_texts` VALUES (-1000788, 'Well, I\'m off to the Gelkis. They\'re not as dumb as the Maraudines, but they\'re more reasonable.', NULL, NULL, NULL, '好吧，我要去吉尔吉斯了。他们不像马劳丁人那么愚蠢，但他们更通情达理。', NULL, NULL, NULL, NULL, 0, 0, 0, 3, 'melizza SAY_MELIZZA_3');
INSERT INTO `script_texts` VALUES (-1409020, 'You think you\'ve won already? Perhaps you\'ll need another lesson in pain!', NULL, 'Vous croyez avoir deja gagne? Vous avez sans doute besoin que je vous inflige quelques douleurs supplementaires.', NULL, '你以为你已经赢了？也许你还需要再一次痛苦的教训！', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'majordomo SAY_LAST_ADD');
INSERT INTO `script_texts` VALUES (-1001159, 'Please, help me to get through this cursed forest, $r.', NULL, NULL, NULL, '请帮助我穿过这片被诅咒的森林，$r。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_ESCORT_START');
INSERT INTO `script_texts` VALUES (-1001160, 'This creature suffers from the effect of the fel... We must end its misery.', NULL, NULL, NULL, '这种生物受到邪能的影响……我们必须结束它的痛苦。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_ATTACK_IRONTREE');
INSERT INTO `script_texts` VALUES (-1001161, 'The corruption of the fel has not left any of the creatures of Felwood untouched, $N. Please, be on your guard.', NULL, NULL, NULL, '邪能的腐化并没有让费伍德森林的任何生物都受到影响，$N。请大家保持警惕。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_ATTACK_TOXIC_HORROR');
INSERT INTO `script_texts` VALUES (-1001162, 'I sense the taint of corruption upon this $N. Help me detroy it.', NULL, NULL, NULL, '我感觉到这个$N身上有腐败的污点。帮我摧毁它。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_EXIT_WOODS');
INSERT INTO `script_texts` VALUES (-1001163, 'That I must fight against my own kind deeply saddens me.', NULL, NULL, NULL, '我必须与自己的同类作战，这让我深感悲伤。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_CLEAR_PATH');
INSERT INTO `script_texts` VALUES (-1001164, 'I can sense it now, $N. Ashenvale lies down this path.', NULL, NULL, NULL, '我现在能感觉到了，$N。灰谷就位于这条小路上。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_ASHENVALE');
INSERT INTO `script_texts` VALUES (-1001165, 'I feel... something strange...', NULL, NULL, NULL, '我感觉……有什么奇怪的……', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_TRANSFORM');
INSERT INTO `script_texts` VALUES (-1001166, '$N my form has now changed! The true strength of my spirit is returing to me now... The cursed grasp of the forest is leaving me.', NULL, NULL, NULL, '$N 我的表格现在已经改变了！我的灵魂的真正力量现在又回到了我身边……那片被诅咒的森林之力正在离我而去。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_LIFT_CURSE');
INSERT INTO `script_texts` VALUES (-1001167, 'Thank you, $N. Now my spirit will finally be at peace.', NULL, NULL, NULL, '谢谢你，$N。现在我的精神终于可以得到安宁了。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'arei SAY_ESCORT_COMPLETE');
INSERT INTO `script_texts` VALUES (-1001148, 'I am ready, $N. Let\'s find my equipment and get out of here. I think I know where it is.', NULL, NULL, NULL, '我准备好了，$N。我们找到我的装备然后离开这里吧。我想我知道它在哪里。', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'captured arko\'narin SAY_ESCORT_START');
INSERT INTO `script_texts` VALUES (-1001149, 'Oh my! Look at this... all these candles. I\'m sure they\'re used for some terrible ritual or dark summoning. We best make haste.', NULL, NULL, NULL, '天啊！看看这个...所有这些蜡烛。我确信它们被用于一些可怕的仪式或黑暗召唤。我们最好抓紧时间。', NULL, NULL, NULL, NULL, 0, 0, 0, 18, 'captured arko\'narin SAY_FIRST_STOP');
INSERT INTO `script_texts` VALUES (-1001150, 'There! Over there!', NULL, NULL, NULL, '那里！在那边！', NULL, NULL, NULL, NULL, 0, 0, 0, 25, 'captured arko\'narin SAY_SECOND_STOP');
INSERT INTO `script_texts` VALUES (-1001151, 'You will not stop me from escaping here, $N!', NULL, NULL, NULL, '你不会阻止我逃离这里，$N！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'captured arko\'narin SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1001152, 'All I need now is a golden lasso.', NULL, NULL, NULL, '我现在需要的只是一根金色套索。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'captured arko\'narin SAY_EQUIPMENT');
INSERT INTO `script_texts` VALUES (-1001153, 'DIE DEMON DOGS!', NULL, NULL, NULL, '恶魔狗去死吧！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'captured arko\'narin SAY_ESCAPE');
INSERT INTO `script_texts` VALUES (-1001154, 'Ah! Fresh air at last! I never thought I\'d see the day.', NULL, NULL, NULL, '啊!终于有新鲜空气了！我从没想过我会看到这一天。', NULL, NULL, NULL, NULL, 0, 0, 0, 4, 'captured arko\'narin SAY_FRESH_AIR');
INSERT INTO `script_texts` VALUES (-1001155, 'BETRAYER!', NULL, NULL, NULL, '背叛者！', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'spirit of trey lightforge SAY_BETRAYER');
INSERT INTO `script_texts` VALUES (-1001156, 'What was that?! Trey? TREY?', NULL, NULL, NULL, '那是什么？！特雷？特雷？', NULL, NULL, NULL, NULL, 0, 0, 0, 22, 'captured arko\'narin SAY_TREY');
INSERT INTO `script_texts` VALUES (-1001157, 'You kept me in the cell for too long, monster!', NULL, NULL, NULL, '你把我关在牢房里太久了，怪物！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'captured arko\'narin SAY_ATTACK_TREY');
INSERT INTO `script_texts` VALUES (-1001158, 'No! My friend... what\'s happened? This is all my fault...', NULL, NULL, NULL, '不！我的朋友……发生了什么事？这都是我的错...', NULL, NULL, NULL, NULL, 0, 0, 0, 18, 'captured arko\'narin SAY_ESCORT_COMPLETE');
INSERT INTO `script_texts` VALUES (-1000873, 'I know the way, insect. There is no need to prod me as if I were cattle.', NULL, NULL, NULL, '我认得路，昆虫。没有必要像牛一样刺激我。', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'grark SAY_START');
INSERT INTO `script_texts` VALUES (-1000874, 'Surely you do not think that you will get away with this incursion. They will come for me and you shall pay for your insolence.', NULL, NULL, NULL, '你肯定不认为这次入侵能够逃脱惩罚。他们会来抓我的，你将为你的无礼付出代价。', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'grark SAY_PAY');
INSERT INTO `script_texts` VALUES (-1000875, 'RUN THEM THROUGH BROTHERS!', NULL, NULL, NULL, '通过兄弟来管理他们！', NULL, NULL, NULL, NULL, 0, 0, 0, 5, 'grark SAY_FIRST_AMBUSH_START');
INSERT INTO `script_texts` VALUES (-1000876, 'I doubt you will be so lucky the next time you encounter my brethren.', NULL, NULL, NULL, '我怀疑下次你遇到我的弟兄们时你会这么幸运。', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'grark SAY_FIRST_AMBUSH_END');
INSERT INTO `script_texts` VALUES (-1000877, 'They come for you, fool!', NULL, NULL, NULL, '他们是来找你的，傻瓜！', NULL, NULL, NULL, NULL, 0, 0, 0, 5, 'grark SAY_SEC_AMBUSH_START');
INSERT INTO `script_texts` VALUES (-1000878, 'What do you think you accomplish from this, fool? Even now, the Blackrock armies make preparations to destroy your world.', NULL, NULL, NULL, '你认为你能从中获得什么成就，傻瓜？即使是现在，黑石军队也在准备摧毁你们的世界。', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'grark SAY_SEC_AMBUSH_END');
INSERT INTO `script_texts` VALUES (-1000879, 'On darkest wing they fly. Prepare to meet your end!', NULL, NULL, NULL, '他们用最黑暗的翅膀飞翔。准备好迎接你的结局吧！', NULL, NULL, NULL, NULL, 0, 0, 0, 5, 'grark SAY_THIRD_AMBUSH_START');
INSERT INTO `script_texts` VALUES (-1000880, 'The worst is yet to come!', NULL, NULL, NULL, '最糟的还没来！', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'grark SAY_THIRD_AMBUSH_END');
INSERT INTO `script_texts` VALUES (-1000881, '%s laughs.', NULL, NULL, NULL, '%s笑了。', NULL, NULL, NULL, NULL, 0, 2, 0, 11, 'grark EMOTE_LAUGH');
INSERT INTO `script_texts` VALUES (-1000882, 'Time to make your final stand, Insect.', NULL, NULL, NULL, '是时候做出最后的抵抗了，昆虫。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'grark SAY_LAST_STAND');
INSERT INTO `script_texts` VALUES (-1000883, 'Kneel, Grark', NULL, NULL, NULL, '跪下，格拉克', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'lexlort SAY_LEXLORT_1');
INSERT INTO `script_texts` VALUES (-1000884, 'Grark Lorkrub, you have been charged and found guilty of treason against Horde. How you plead is unimportant. High Executioner Nuzrak, step forward.', NULL, NULL, NULL, '格拉克·洛克鲁布，你被指控并被判犯有叛国罪。你如何辩护并不重要。高阶刽子手努兹拉克，向前一步。', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'lexlort SAY_LEXLORT_2');
INSERT INTO `script_texts` VALUES (-1000885, '%s raises his massive axe over Grark.', NULL, NULL, NULL, '%s向格拉克举起了他的巨斧。', NULL, NULL, NULL, NULL, 0, 2, 0, 27, 'nuzark EMOTE_RAISE_AXE');
INSERT INTO `script_texts` VALUES (-1000886, '%s raises his hand and then lowers it.', NULL, NULL, NULL, '%s举起手，然后又放下。', NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'lexlort EMOTE_LOWER_HAND');
INSERT INTO `script_texts` VALUES (-1000887, 'End him...', NULL, NULL, NULL, '结束他...', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'lexlort SAY_LEXLORT_3');
INSERT INTO `script_texts` VALUES (-1000888, 'You, soldier, report back to Kargath at once!', NULL, NULL, NULL, '你，士兵，立即向卡加斯报告！', NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'lexlort SAY_LEXLORT_4');
INSERT INTO `script_texts` VALUES (-1000889, '%s submits.', NULL, NULL, NULL, '%s 提交。', NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'grark EMOTE_SUBMIT');
INSERT INTO `script_texts` VALUES (-1000890, 'You have come to play? Then let us play!', NULL, NULL, NULL, '你是来玩的吗？那我们就来玩吧！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'grark SAY_AGGRO');
INSERT INTO `script_texts` VALUES (-1780211, 'Beware, $N! Look to the west!', NULL, NULL, NULL, '当心，$N！向西看去！', NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'q4021npc3389 BEWARE');
INSERT INTO `script_texts` VALUES (-1780212, 'A defender has fallen!', NULL, NULL, NULL, '一名后卫倒地！', NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'q4021npc3389 DEFENDER_FALLLEN');
INSERT INTO `script_texts` VALUES (-1780213, 'Kolkar Invader charges!', NULL, NULL, NULL, '科卡尔入侵者冲锋！', NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'q4021npc9524 CHARGE');
INSERT INTO `script_texts` VALUES (-1780214, 'Half way there...', NULL, NULL, NULL, '已经走到一半了...', NULL, NULL, NULL, NULL, 0, 1, 1, 0, 'q4021npc9990 HALF_WAY');
INSERT INTO `script_texts` VALUES (-1780215, 'Defend the bunkers!', NULL, NULL, NULL, '保卫掩体！', NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'q4021npc9458 DEFEND');
INSERT INTO `script_texts` VALUES (-1780216, 'Our foes will fall!', NULL, NULL, NULL, '我们的敌人将会倒下！', NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'q4021npc9458 FOES');
INSERT INTO `script_texts` VALUES (-1780217, 'For the Horde!', NULL, NULL, NULL, '为了部落！', NULL, NULL, NULL, NULL, 0, 0, 1, 0, 'q4021npc9458 HORDE');
INSERT INTO `script_texts` VALUES (-1780218, 'The Kolkar are the strongest!', NULL, NULL, NULL, '科卡人是最强的！', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'q4021npc9456 KOLKAR_STRONGEST');
INSERT INTO `script_texts` VALUES (-1780219, 'The Kolkar invaders are retreating!', NULL, NULL, NULL, '科卡尔入侵者正在撤退！', NULL, NULL, NULL, NULL, 0, 1, 1, 0, 'q4021npc3389 RETREATING');
INSERT INTO `script_texts` VALUES (-1000159, 'For the Legion! For Kil\'Jaeden!', NULL, 'Pour la Legion! Pour Kil\'Jaeden!', NULL, '为了军团！为了基尔加丹！', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'kazzak SAY_AGGRO3');
INSERT INTO `script_texts` VALUES (-1000160, 'Your own strength feeds me!', NULL, 'Votre propre force me nourrie!', NULL, '你自己的力量养活了我！', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'kazzak SAY_KILL4');
INSERT INTO `script_texts` VALUES (-1000161, 'Kazzak is Supreme!', NULL, 'Kazzak est Immortel!', NULL, '卡扎克是至高无上的！', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'kazzak SAY_SUPREME3');
INSERT INTO `script_texts` VALUES (-1108999, 'It is true. The wheels of doom now turn, inevitably, toward the true return of Hakkar!', NULL, 'Il est vrai. La roue du destin tournent dÃ©sormais , inÃ©vitablement , vers le vrai retour d\'Hakkar !', NULL, '是真的。厄运之轮现在不可避免地转向哈卡的真正回归！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Yehkinya event quest');
INSERT INTO `script_texts` VALUES (-1108998, 'Blood and souls for Hakkar! HAHAHAH!', NULL, 'Du sang et des Ã¢mes pour Hakkar ! HAHAHAH !', NULL, '为哈卡奉献鲜血和灵魂！哈哈哈！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Yehkinya event quest');
INSERT INTO `script_texts` VALUES (-1000650, '%s is immobilized.', NULL, NULL, NULL, '%s 被固定住了。', NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'npc_solenor EMOTE_IMMOBILIZED');
INSERT INTO `script_texts` VALUES (-1000651, '%s is stricken by a virulent poison.', NULL, NULL, NULL, '%s中了剧毒。', NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'npc_artorius EMOTE_POISON');
INSERT INTO `script_texts` VALUES (-1000652, '%s is silenced by the venemous sting.', NULL, NULL, NULL, '%s被毒刺弄得沉默了。', NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'npc_simone EMOTE_SILENCE');
INSERT INTO `script_texts` VALUES (-1289010, 'You dare interfere with this being\'s testing? The battle must be fought alone! You shall all pay for this interference!', NULL, NULL, NULL, '你竟然敢干涉这个生命的测试？战斗必须是孤军奋战！你们都要为这种干扰付出代价！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'the_cleaner SAY_CLEANER_AGGRO');
INSERT INTO `script_texts` VALUES (-1532033, 'This I promise... This I vow...', NULL, NULL, NULL, '这是我发誓……这是我发誓……', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532032, ' Your death will not have been in vain, Taelan. A new Order is born on this day... an Order which will dedicate itself to extinguising the evil that plagues this world. An evil that cannot hide behind politics and pleasantries.', NULL, NULL, NULL, '你的死不会白费，泰兰。一个新的秩序在这一天诞生……这个秩序将致力于消灭困扰这个世界的邪恶。这是一种无法隐藏在政治和寒暄背后的邪恶。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532031, 'Too long have I sat idle, gripped in this haze... this malaise, lamenting what could have been... what should have been.', NULL, NULL, NULL, '我已经闲坐太久了，被这种阴霾所笼罩……这种不适，哀叹本来可以发生的事情……本来应该发生的事情。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532030, 'Look what they did to my boy.', NULL, NULL, NULL, '看看他们对我儿子做了什么。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532029, 'A thousand more like him exist. Ten thousand. Should one fall, another will rise to take the seat of power.', NULL, NULL, NULL, '像他这样的人还有一千个。万。如果一个人倒台，另一个人就会崛起并掌权。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532028, 'Then come, hermit!', NULL, NULL, NULL, '那么来吧，隐士！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532027, 'Face me, coward. Face the faith and strength that you once embodied.', NULL, NULL, NULL, '面对我，胆小鬼。面对你曾经体现的信念和力量。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532026, 'May your soul burn in anguish, Isillien! Light give me strength to battle this fiend.', NULL, NULL, NULL, '愿你的灵魂在痛苦中燃烧，伊森利安！光给我力量来对抗这个恶魔。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532025, 'Tragic. The elder Fordring lives on... You are too late, old man. Retreat back to your cave, hermit, unless you wish to join your son in the Twisting Nether.', NULL, NULL, NULL, '悲惨。老弗丁还活着……你来得太迟了，老人。退回你的洞穴吧，隐士，除非你想和你儿子一起去扭曲虚空。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532024, 'What have you done, Isillien? You once fought with honor, for the good of our people... and now... you have murdered my boy...', NULL, NULL, NULL, '你做了什么，伊森利安？你曾经为了我们人民的利益而光荣地战斗……而现在……你谋杀了我的孩子……', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532023, 'Did you really believe that you could defeat me? Your friends are soon to join you, Taelan.', NULL, NULL, NULL, '你真的相信你能打败我吗？你的朋友很快就会加入你，泰兰。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532022, 'Enough!', NULL, NULL, NULL, '足够的！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532021, 'The end is now, Fordring.', NULL, NULL, NULL, '现在就是结束了，弗丁。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532020, 'Grand Inquisitor Isillien calls for his guardsman.', NULL, NULL, NULL, '大审判官伊森利恩呼叫他的卫兵。', NULL, NULL, NULL, NULL, 0, 2, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532019, 'The Grand Crusader has charged me with destroying you and your newfound friends, Taelan, but know this: I do this for pleasure, not of obligation or duty.', NULL, NULL, NULL, '大十字军指控我消灭你和你的新朋友泰兰，但你要知道：我这样做是为了快乐，而不是出于义务或责任。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532018, 'It is as they say; Like father, like son. You are as weak of will as Tirion... perhaps more so. I can only hope my assassins finally succeeded in ending his pitiful life.', NULL, NULL, NULL, '正如他们所说；有其父必有其子。你和提里奥一样意志薄弱……或许更甚。我只能希望我的刺客最终能成功结束他可怜的生命。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532017, 'You disappoint me, Taelan. I had plans for you... grand plans. Alas, it was only a matter of time before your filthy bloodline would catch up with you.', NULL, NULL, NULL, '你让我失望了，泰兰。我为你制定了计划……宏伟的计划。唉，你的肮脏血统迟早会追上你的。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532016, 'You will not make it to the forest\'s edge, Fordring.', NULL, NULL, NULL, '你不会到达森林边缘的，弗丁。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532015, 'This is not your fight, stranger. Protect yourself from the attacks of the Crimson Elite. I shall battle the Grand Inquisitor.', NULL, NULL, NULL, '这不是你的战斗，陌生人。保护自己免受猩红精英的攻击。我将与大审判官战斗。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532014, 'Isillien!', NULL, NULL, NULL, '伊西利安！', NULL, NULL, NULL, NULL, 0, 1, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532013, 'It\'s not much further. The main road is just up ahead', NULL, NULL, NULL, '已经不远了。主要道路就在前面', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532012, 'Highlord Taelan Fordring calls for his mount.', NULL, NULL, NULL, '大领主泰兰·弗丁召唤他的坐骑。', NULL, NULL, NULL, NULL, 0, 2, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532011, 'Halt.', NULL, NULL, NULL, '停。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
INSERT INTO `script_texts` VALUES (-1532010, 'Remove your disguise, lest you feel the bite of my blade when the fury has taken control.', NULL, NULL, NULL, '卸下你的伪装，以免当愤怒控制时你感受到我的剑刃的咬伤。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
