INSERT INTO `script_texts` VALUES (-1532007, 'There is no escape for you. The Crusade shall destroy all who carry the Scourge\'s taint.', NULL, NULL, NULL, '你无处可逃。远征将消灭所有携带天灾军团污点的人。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, NULL);
