INSERT INTO `script_texts` VALUES (-1533140, 'Brazenly you have disregarded powers beyond your understanding.', NULL, NULL, NULL, '你公然无视超出你理解范围的力量。', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'gothik SAY_SPEECH_2');
INSERT INTO `script_texts` VALUES (-1533141, 'You have fought hard to invade the realm of the harvester.', NULL, NULL, NULL, '为了入侵收割机的领域，你进行了艰苦的战斗。', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'gothik SAY_SPEECH_3');
INSERT INTO `script_texts` VALUES (-1533142, 'Now there is only one way out - to walk the lonely path of the damned.', NULL, NULL, NULL, '现在只有一条出路——走孤独的地狱之路。', NULL, NULL, NULL, NULL, 0, 1, 0, 0, 'gothik SAY_SPEECH_4');
INSERT INTO `script_texts` VALUES (-1533143, 'An aura of necrotic energy blocks all healing!', NULL, NULL, NULL, '坏死能量的光环阻碍了所有治疗！', NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'Loatheb EMOTE_AURA_BLOCKING');
INSERT INTO `script_texts` VALUES (-1533144, 'The power of Necrotic Aura begins to wane!', NULL, NULL, NULL, '死灵光环的力量开始减弱！', NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'Loatheb EMOTE_AURA_WANE');
INSERT INTO `script_texts` VALUES (-1533145, 'The aura fades away, allowing healing once more!', NULL, NULL, NULL, '光环消散，再次治愈！', NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'Loatheb EMOTE_AURA_FADING');
