INSERT INTO `script_texts` VALUES (-1533154, '%s begins to unleash an insect swarm!', NULL, NULL, NULL, '%s开始释放昆虫群！', NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'anubrekhan EMOTE_INSECT_SWARM');
INSERT INTO `script_texts` VALUES (-1533155, 'Corpse Scarabs appear from a Crypt Guard\'s corpse!', NULL, NULL, NULL, '尸体圣甲虫从地穴守卫的尸体中出现！', NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'anubrekhan EMOTE_CORPSE_SCARABS');
INSERT INTO `script_texts` VALUES (-1533156, '%s casts Unyielding Pain on everyone!', NULL, NULL, NULL, '%s对所有人施展不屈的痛苦！', NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'lady_blaumeux EMOTE_UNYIELDING_PAIN');
INSERT INTO `script_texts` VALUES (-1533157, '%s casts Condemation on everyone!', NULL, NULL, NULL, '%s对所有人施展谴责！', NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'sir_zeliek EMOTE_CONDEMATION');
INSERT INTO `script_texts` VALUES (-1533158, '%s injects you with a mutagen!', NULL, NULL, NULL, '%s给你注射了诱变剂！', NULL, NULL, NULL, NULL, 0, 5, 0, 0, 'grobbulus EMOTE_INJECTION');
INSERT INTO `script_texts` VALUES (-1533159, '%s lets loose a triumphant shout.', NULL, NULL, NULL, '%s发出胜利的呼喊。', NULL, NULL, NULL, NULL, 0, 3, 0, 0, 'razuvious EMOTE_SHOUT');
INSERT INTO `script_texts` VALUES (-1999927, 'Have you come to save the world? To cleanse it?', NULL, NULL, NULL, '你是来拯救世界的吗？来净化它？', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1999928, 'And it begins...', NULL, NULL, NULL, '事情开始了……', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1999916, 'Ashbringer...', NULL, NULL, NULL, '灰烬使者...', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1999917, 'Kneel! Kneel before the Ashbringer!', NULL, NULL, NULL, '下跪！在灰烬使者面前跪下！', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1999918, 'My Lord, please allow me to live long enough to see you purge this world of the infidles.', NULL, NULL, NULL, '主啊，请允许我活得足够长，让我能看到你清除这个世界上的异教徒。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1999919, 'Take me with you, Sir.', NULL, NULL, NULL, '带我一起去吧，先生。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1999920, 'You hold my father\'s blade. My soldiers are yours to control, my Lord. Take them... Lead them... The impure must be purged. They must be cleansed of their taint.', NULL, NULL, NULL, '你握着我父亲的剑。我的士兵由您指挥，陛下。带着他们……带领他们……不纯洁的人必须被清除。他们的污点必须被清除。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1999921, 'Father... But... How?', NULL, NULL, NULL, '父亲……但是……怎么办？', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1999922, 'Forgive me, father! Please...', NULL, NULL, NULL, '原谅我，父亲！请...', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1999923, 'Renault...', NULL, NULL, NULL, '雷诺...', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1999924, 'Did you think that your betrayal would be forgotten? Lost in the carefully planned cover up of my death? Blood of my blood, the blade felt your cruelty long after my heart had stopped beating. And in death, I knew what you had done. But now, the chains of Kel\'thuzad hold me no more. I come to serve justice. I AM ASHBRINGER.', NULL, NULL, NULL, '你以为你的背叛会被忘记吗？迷失在精心策划的掩盖我死亡的事件中吗？我的血，我的血，在我的心脏停止跳动很久之后，刀刃仍感受到你的残酷。在死亡中，我知道你做了什么。但现在，克尔苏加德的锁链不再束缚我了。我是来主持正义的我是灰烬使者。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1999925, 'You are forgiven...', NULL, NULL, NULL, '你被原谅了...', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1999926, 'Bow down! Kneel before the Ashbringer! A new dawn approaches, brothers and sisters! Our message will be delivered to the filth of this world through the chosen one!', NULL, NULL, NULL, '跪拜！在灰烬使者面前跪下！兄弟姐妹们，新的黎明即将来临！我们的信息将通过被选中的人传递给这个世界的污秽！', NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'Ashbringer event');
INSERT INTO `script_texts` VALUES (-1780300, 'Sandstorm coming from the south, take co... That\'s no sandstorm! INCOMING QIRAJI ARMY!', NULL, NULL, NULL, '沙尘暴从南方来了，一起来吧……那不是沙尘暴！其拉军队来袭！', NULL, NULL, NULL, NULL, 0, 6, 0, 93, 'AQ Saurfang CH attack');
INSERT INTO `script_texts` VALUES (-1780301, 'You know me to be the Supreme Commander of the Might of Kalimdor', NULL, NULL, NULL, '你知道我是卡利姆多力量的最高指挥官', NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 1');
INSERT INTO `script_texts` VALUES (-1780302, 'An orc - a true orc warrior - wishes for one thing: To die in the glory of battle gainst a hated enemy.', NULL, NULL, NULL, '一个兽人——一个真正的兽人战士——渴望一件事：在与可恶的敌人战斗的荣耀中死去。', NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 2');
INSERT INTO `script_texts` VALUES (-1780303, 'Some of you have not fought in battles. Peace has been with us for many years. Many years we sat idle, but many years we battled.', NULL, NULL, NULL, '你们中有些人没有参加过战斗。和平已经伴随我们很多年了。我们闲置了很多年，但我们也奋斗了很多年。', NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 3');
INSERT INTO `script_texts` VALUES (-1780304, 'In those years - where strife filled the land as Legion and Scource sacked our homes, killed our families - these insects dwelled beneath us. Beneath our homes - waiting', NULL, NULL, NULL, '在那些年里，军团和斯库斯洗劫了我们的家园，杀害了我们的家人，冲突充斥着这片土地，这些昆虫就栖息在我们的脚下。在我们的家园下面——等待', NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 4');
INSERT INTO `script_texts` VALUES (-1780305, 'Waiting to crush the life from our little ones. To slay all in their path. This they do for their god.', NULL, NULL, NULL, '等待着摧毁我们孩子的生命。杀死他们路上的所有人。他们这样做是为了他们的神。', NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 5');
INSERT INTO `script_texts` VALUES (-1780306, 'And for our gods?', NULL, NULL, NULL, '那么为了我们的神呢？', NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 6');
INSERT INTO `script_texts` VALUES (-1780307, 'We defend. We stand. We show that as one. United. We destroy. Their god will fall.', NULL, NULL, NULL, '我们捍卫。我们站着。我们将其作为一个整体来展示。团结的。我们摧毁。他们的神将会陨落。', NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 7');
INSERT INTO `script_texts` VALUES (-1780308, 'To die today, on this field of battle, is to die an orcish death. To die today is to die for our little ones. Our old ones. Our... loved ones', NULL, NULL, NULL, '今天，死在这个战场上，就是兽人般的死亡。今天的死就是为我们的孩子而死。我们的旧人。我们的...亲人', NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 8');
INSERT INTO `script_texts` VALUES (-1780309, 'Would any of you deny yourselves such a death? Such an honor?', NULL, NULL, NULL, '你们中有人会否认自己这样的死亡吗？这么荣幸？', NULL, NULL, NULL, NULL, 0, 0, 0, 93, 'AQ Saurfang speech 9');
INSERT INTO `script_texts` VALUES (-1780310, 'ONWARD! TO AHN\'QIRAJ!', NULL, NULL, NULL, '向前！前往安其拉！', NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'AQ Saurfang speech 10');
INSERT INTO `script_texts` VALUES (-1780311, 'The final battle for Kalimdor is upon us. The supreme commander of the Might of Kalimdor orders his troops to march upon Ahn\'Qiraj and destroy all in their path..', NULL, NULL, NULL, '卡利姆多的最后一战即将来临。卡利姆多之力的最高指挥官命令他的军队向安其拉进军，摧毁路上的一切。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'AQ Final battle world text');
INSERT INTO `script_texts` VALUES (-1780312, 'The Might of Kalimdor is victorious! The last of the Qiraji forces are defeated, retreating back to the ancient city-fortress of Ahn\'Qiraj. The Qiraji Resonating Crystals littering Kalimdor vanish as quickly as they appeared, leaving behind nothing but sundered earth.', NULL, NULL, NULL, '卡利姆多之力取得了胜利！最后的其拉军队被击败，撤回安其拉古城堡垒。卡利姆多散落的其拉共鸣水晶一出现就消失得很快，只留下破碎的大地。', NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'AQ War over');
INSERT INTO `script_texts` VALUES (-1999950, 'We must maintain our production! WORK HARDER!', NULL, NULL, NULL, '我们必须维持我们的生产！努力工作！', NULL, NULL, NULL, NULL, 60349, 6, 0, 0, 'BARGUL_BLACKHAMMER_OOC_0');
INSERT INTO `script_texts` VALUES (-1999951, 'Shadowforge shall reward us all for our work here in the Quarry!', NULL, NULL, NULL, '影炉将奖励我们所有人在采石场所做的工作！', NULL, NULL, NULL, NULL, 60350, 6, 0, 0, 'BARGUL_BLACKHAMMER_OOC_1');
INSERT INTO `script_texts` VALUES (-1999952, 'I don\'t see enough of you busy out there, We don\'t have all month!', NULL, NULL, NULL, '我没看到你们在外面忙碌，我们没有整个月的时间！', NULL, NULL, NULL, NULL, 60351, 6, 0, 0, 'BARGUL_BLACKHAMMER_OOC_2');
INSERT INTO `script_texts` VALUES (-1999953, 'You think you contend with the High Foreman? Feel the fury of the Dark Iron!', NULL, NULL, NULL, '你认为你能与高级工头抗衡吗？感受黑铁的愤怒！', NULL, NULL, NULL, NULL, 60348, 0, 0, 0, 'BARGUL_BLACKHAMMER_AGGRO');
INSERT INTO `script_texts` VALUES (-1999954, 'Curse you.. Damn you... The work, must go on.', NULL, NULL, NULL, '诅咒你……该死的……工作，必须继续。', NULL, NULL, NULL, NULL, 60352, 0, 0, 0, 'BARGUL_BLACKHAMMER_DEAD');
INSERT INTO `script_texts` VALUES (-1999955, 'You foolish outsiders! You\'re not supposed to be here interrupting my great work. Broody, get them, destroy them!', NULL, NULL, NULL, '你们这些愚蠢的外人！你不应该在这里打扰我的伟大工作。布罗迪，抓住他们，消灭他们！', NULL, NULL, NULL, NULL, 60353, 0, 0, 0, 'Engineer_Figgles_AGGRO');
INSERT INTO `script_texts` VALUES (-1999956, 'I.. Was going to build an army... I would\'ve never been stopped!', NULL, NULL, NULL, '我..要建立一支军队...我永远不会被阻止！', NULL, NULL, NULL, NULL, 60354, 0, 0, 0, 'Engineer_Figgles_DEAD');
INSERT INTO `script_texts` VALUES (-1999957, 'Unidentified intruders, defensive measures, engaged.', NULL, NULL, NULL, '身份不明的入侵者，防御措施，已投入使用。', NULL, NULL, NULL, NULL, 60355, 0, 0, 0, 'Hatereaver_Annihilator_AGGRO');
INSERT INTO `script_texts` VALUES (-1999958, 'Activate emergency power... Emergency power activation failed... Commence... Shut... Do..wn...', NULL, NULL, NULL, '激活紧急电源...紧急电源激活失败...开始...关闭...Do..wn...', NULL, NULL, NULL, NULL, 60356, 0, 0, 0, 'Hatereaver_Annihilator_DEAD');
